UPDATE `command` SET `name`='explorecheat',`security`=3,`help`='Sintaxis: .explorecheat #flag\r\n\r\nRevela o esconde todos los mapas para el jugador seleccionado. Si no hay un jugador selecionado, esconderá o revelará los mapas a ti.\r\n\r\nUsar un #flag con valor de 1 para revelar\r\nUsar #flag con valor de 0 para esconder todos los mapas.' WHERE `command`.`name`='explorecheat';
UPDATE `command` SET `name`='npc changelevel',`security`=2,`help`='Sintaxis: .npc changelevel #Nivel\r\n\r\nCambia el nivel de la criatura seleccionada a #Nivel.\r\n\r\n#Nivel debe estar en el rango 1 y 63.' WHERE `command`.`name`='npc changelevel';
