UPDATE `command` SET `name`='modify energy',`security`=3,`help`='Sintaxis: .modify energy #energia\r\n\r\nModifica la energía del jugador seleccionado. Si ningún jugador es seleccionado, modificará tu energía.' WHERE `command`.`name`='modify energy';
