UPDATE `command` SET `name`='guild invite',`security`=2,`help`='Sintaxis: .guild invite [$NombrePersonaje] "$NombreHermandad"\r\n\r\nAgrega un personaje $NombrePersonaje (o el seleccionado) a una hermandad "$NombreHermandad".\r\nEl nombre de la hermandad debe ir entre dobles comillas.' WHERE `command`.`name`='guild invite';
UPDATE `command` SET `name`='groupgo',`security`=1,`help`='Sintaxis: .groupgo [$nombrepersonaje]\r\n\r\nTeletransporta al personaje dado y su grupo hacia tu posición.\r\nTeletransporta únicamente personajes conectados pero el personaje nombrado puede estar desconectado.' WHERE `command`.`name`='groupgo';
UPDATE `command` SET `name`='flusharenapoints',`security`=3,`help`='Sintaxis: .flusharenapoints\r\n\r\nSe usa para distribuir los puntos de arena basado en el indice de los equipos de arena, y para empezar una nueva semana.' WHERE `command`.`name`='flusharenapoints';
UPDATE `command` SET `name`='learn all_default',`security`=1,`help`='Sintaxis: .learn all default [$NomJugador]\r\n\r\nEnseña al jugador seleccionado $NomJugador todos los hechizos para la raza/clase a la que pertenece.\r\nTambien como los hechizos otorgados al completar las misiones de entrenamiento.' WHERE `command`.`name`='learn all_default';
UPDATE `command` SET `name`='modify rep',`security`=2,`help`='Sintaxis: .modify rep #repId (#repvalor | $nombreRango [#delta])\r\n\r\nModifica la reputación del jugador seleccionado #repId con la facción a #repvalor ó a $nombreRango.\r\nSi es proporcionado el nombre del rango de reputación, la reputación resultante será la más baja de ese rango más la cantidad delta, si se especifica.\r\nPuedes usar ".pinfo rep" para listar todas las ids de reputación conocidas, o usar ".lookup faction $name" para localizar un id específico de facción.' WHERE `command`.`name`='modify rep';
UPDATE `command` SET `name`='modify honor',`security`=1,`help`='Sintaxis: .modify honor $Cantidad\r\n\r\nAñade $Cantidad puntos de honor al jugador seleccionado.' WHERE `command`.`name`='modify honor';
