UPDATE `command` SET `name`='combatstop',`security`=2,`help`='Sintaxis: .combatstop [$Nombre]\r\n\r\nDetiene el combate del personaje selecionado. Si no se seleciona ningun personaje se aplica sobre ti mismo.\r\nSi se aporta el $Nombre entonces será aplicado sobre el personaje encontrado por $Nombre.' WHERE `command`.`name`='combatstop';
