UPDATE `command` SET `name`='lookup account ip',`security`=2,`help`='Sintaxis: .lookup account ip $ippart [#limite] \r\n\r\nBusca cuentas, que ultimamente hallan usado la IP incluida en $ippart (textual) con el parámetro opcioanl #$limite of results.\r\nSi #limite no se falicita se tomará 100 como límite.' WHERE `command`.`name`='lookup account ip';
