UPDATE `command` SET `name`='ban account',`security`=3,`help`='Sintaxis: .ban account $cuenta $tiempodeban $razon\r\n\r\nBanea la cuenta y expulsa al jugador.\r\n$tiempodeban: Colocar un valor negativo conlleva a baneo permanente, de otra manera usar una línea de tiempo como la siguiente "4d20h3s".' WHERE `command`.`name`='ban account';
