UPDATE `db_script_string` SET content_loc6='Oye... ¡esto no es un pez!' WHERE entry=2000000173;
UPDATE `db_script_string` SET content_loc6='Conseguí intestinos de gusano en mis zapatos.' WHERE entry=2000000174;
UPDATE `db_script_string` SET content_loc6='Creo que veo algo.' WHERE entry=2000000175;
UPDATE `db_script_string` SET content_loc6='¡Espero que esto sea un pez!' WHERE entry=2000000176;
UPDATE `db_script_string` SET content_loc6='El gusano va en el anzuelo, el anzuelo entra al agua, el Pez está en el agua, nuestro pez.' WHERE entry=2000000177;
UPDATE `db_script_string` SET content_loc6='¿Puedes imaginarlo?' WHERE entry=2000000178;
UPDATE `db_script_string` SET content_loc6='¿De veras?' WHERE entry=2000000179;
UPDATE `db_script_string` SET content_loc6='Vaya.' WHERE entry=2000000180;
UPDATE `db_script_string` SET content_loc6='Mi padre dice que esa es sólo una historia.' WHERE entry=2000000181;
UPDATE `db_script_string` SET content_loc6='Eso está limpio.' WHERE entry=2000000182;
UPDATE `db_script_string` SET content_loc6='No me detendré. Nunca...' WHERE entry=2000000183;
UPDATE `db_script_string` SET content_loc6='Hola, Charys. Aquí mi lista, ¿puedes buscarme todo eso, y especialmente el último ingrediente?' WHERE entry=2000000184;
UPDATE `db_script_string` SET content_loc6='Por supuesto, Paige. Pero se bueno.' WHERE entry=2000000185;
UPDATE `db_script_string` SET content_loc6='Gracias, Charys. Déjenos ir a Paige, mi ángel.' WHERE entry=2000000186;
UPDATE `db_script_string` SET content_loc6='¿Cuándo llegaremos?' WHERE entry=2000000187;
UPDATE `db_script_string` SET content_loc6='¿A dónde vamos?' WHERE entry=2000000188;
UPDATE `db_script_string` SET content_loc6='¿Por qué vamos por aquí?' WHERE entry=2000000189;
UPDATE `db_script_string` SET content_loc6='Billy dice que antiguamente Fizzle fue un gran mago. Pero, se transformó en conejo, a causa de un hechizo fallido.' WHERE entry=2000000190;
UPDATE `db_script_string` SET content_loc6='Quiero ver la Torre de los magos' WHERE entry=2000000191;
UPDATE `db_script_string` SET content_loc6='¿Por qué siempre hacemos el mismo camino?' WHERE entry=2000000192;
UPDATE `db_script_string` SET content_loc6='Me duelen los pies.' WHERE entry=2000000193;
UPDATE `db_script_string` SET content_loc6='Pobre Dorius. Si alguna vez tengo en mis manos a los Hierro Negro, así que ayúdame...' WHERE entry=2000000194;
UPDATE `db_script_string` SET content_loc6='¡Se escapa!' WHERE entry=2000000195;
UPDATE `db_script_string` SET content_loc6='Sé que estás ahí, pícaro, deja mi casa o unete a los demás al pie del Árbol del mundo.' WHERE entry=2000000196;
UPDATE `db_script_string` SET content_loc6='¡Porqueria! ¡Suciedad por todas partes! Los bosques deben limpiarse' WHERE entry=2000000197;
UPDATE `db_script_string` SET content_loc6='¡Venga, vengan todos! ¡Bienvenidos a la Feria de la Luna Negra! ¿Se te antoja una aventura? ¿Buscas tesoros exóticos y misteriosos? ¡Entonces no busques más! Tú, mi $Gamigo:amiga;, ¡has venido al lugar indicado! ¡Sumérgete y toma parte en todo lo que la Feria tiene para ofrecer! Vamos a estar en este lugar toda la semana, ¡así que asegúrense de decirselo a sus amigos y seres queridos!' WHERE entry=2000000198;
UPDATE `db_script_string` SET content_loc6='¡Bienvenidos todos y cada uno a la Feria de la Luna Negra, el más grande evento de todos los mundos! Lo tenemos todo... ¡comida deliciosa, bebidas fuertes, artefactos exóticos, lectura de la fortuna, premios increíbles y emociones sin fin! ¡No olvides canjear tus vales de la Feria de la Luna Negra con Gelvas Roñoso! Todo lo que pide son cinco o más y ya estarás recibiendo los premios más maravillosos de todo Terrallende. ¡Todo el mundo gana!' WHERE entry=2000000199;
UPDATE `db_script_string` SET content_loc6='Mamá, ¿Puedo quedarme con Esfumador?' WHERE entry=2000000200;
UPDATE `db_script_string` SET content_loc6='¡Pato!' WHERE entry=2000000201;
UPDATE `db_script_string` SET content_loc6='¡No vayas cerca del lago! ¡está encantada!' WHERE entry=2000000202;
UPDATE `db_script_string` SET content_loc6='Haré mi mejor esfuerzo para cuidar de los niños, abuela.' WHERE entry=2000000203;
UPDATE `db_script_string` SET content_loc6='¡Bien, encárgate y vamos a ver lo que consigues!' WHERE entry=2000000204;
UPDATE `db_script_string` SET content_loc6='¡El Gran Gazlowe!' WHERE entry=2000000205;
UPDATE `db_script_string` SET content_loc6='Tengo otra lectura del interior.' WHERE entry=2000000206;
UPDATE `db_script_string` SET content_loc6='Vale. Acabamos destrozando nuestras colas para transportar todas estas cosas aquí, ¿y quieres que trabajemos más?' WHERE entry=2000000207;
UPDATE `db_script_string` SET content_loc6='El viento nos habla de los movimientos de nuestros enemigos, Ashyen. Los Naga atacarán nuevamente.' WHERE entry=2000000208;
UPDATE `db_script_string` SET content_loc6='Muy bien. Cuando esa decisión se tome, estaré listo. Esperemos que no sea demasiado tarde.' WHERE entry=2000000209;
UPDATE `db_script_string` SET content_loc6='Estoy buscando en esa recompensa después de este cambio.' WHERE entry=2000000210;
UPDATE `db_script_string` SET content_loc6='¿Quieres ir a pesar que la recompensa de este cartel de buscar se ha acabado después de este cambio?' WHERE entry=2000000211;
UPDATE `db_script_string` SET content_loc6='¡Se el primero de tu bloque y consigue la increíble Electrostatic Maculation Blaster!' WHERE entry=2000000212;
UPDATE `db_script_string` SET content_loc6='Derivado de la tecnología militar de vanguardia de la Ofensiva Mano Devastada, ¡quita manchas dificiles de sacar!' WHERE entry=2000000213;
UPDATE `db_script_string` SET content_loc6='¡Manchas de vino, manchas de hierba, incluso las manchas de las mascotas no son rival para la  Electrostatic Maculation Blaster!' WHERE entry=2000000214;
UPDATE `db_script_string` SET content_loc6='¡No espere más! ¡Encargue ahora y reciba mi Ultra-Kinetic Waffleiron sin cargos adicionales!' WHERE entry=2000000215;
UPDATE `db_script_string` SET content_loc6='Falta poco, Wafflefry. ¿No tendrás una muestra del producto para hacer o algo así?' WHERE entry=2000000216;
UPDATE `db_script_string` SET content_loc6='¡Dinos dónde se esconde tu líder!' WHERE entry=2000000217;
UPDATE `db_script_string` SET content_loc6='¡Por favor! Debo alimentarme de algo pronto... Un cristal de maná... un fragmento... ¡Cualquier cosa! ¡El dolor es insoportable!' WHERE entry=2000000218;
UPDATE `db_script_string` SET content_loc6='No me preguntes donde lo conseguí...' WHERE entry=2000000219;
UPDATE `db_script_string` SET content_loc6='Yo sólo podría ir a la caza de esa bestia miserable ahora que hay una recompensa por su cabeza.' WHERE entry=2000000220;
UPDATE `db_script_string` SET content_loc6='Acercate, compra.' WHERE entry=2000000221;
UPDATE `db_script_string` SET content_loc6='¡Comida caliente!' WHERE entry=2000000222;
UPDATE `db_script_string` SET content_loc6='¡Los muros traseros literálmente han volado!' WHERE entry=2000000223;
UPDATE `db_script_string` SET content_loc6='¡Eso se llama una soldadura!...' WHERE entry=2000000224;
UPDATE `db_script_string` SET content_loc6='Esto debe repararse...' WHERE entry=2000000225;
UPDATE `db_script_string` SET content_loc6='ME LLEVARÉ TODAS LAS VIDAS' WHERE entry=2000000226;
UPDATE `db_script_string` SET content_loc6='PUEDO OLER LA CARNE FRITA' WHERE entry=2000000227;
UPDATE `db_script_string` SET content_loc6='REZAD MIS SIERVOS' WHERE entry=2000000228;
UPDATE `db_script_string` SET content_loc6='¡Boletos! ¡Canjea tus boletos premiados de la Feira de la Luna Negra aquí! ¡Exóticos y marabillosos premios te esperan!' WHERE entry=2000000229;
UPDATE `db_script_string` SET content_loc6='¡Sin miedo!  Se pueden discirnir todas las fortunas para los que conocen el camino.' WHERE entry=2000000230;
UPDATE `db_script_string` SET content_loc6='¡Eh, alguien me ayuda a coger esta cosa!' WHERE entry=2000000231;
UPDATE `db_script_string` SET content_loc6='¿Todo el mundo está disfrutando? ¡Genial! ¡Bienvenidos a la Feria de la Luna Negra, el espectáculo más grande en toda Terrallende! Asegurate aquí de hablar con Yebb y sus aminos mienbras visitas al Tesorillos en el Zoo de los extraños de la Luna Negra.' WHERE entry=2000000232;
UPDATE `db_script_string` SET content_loc6='Saludos amigos, y bienvenidos al mayor espectáculo de Azeroth!$B$BPor favor, pasen y tomen de todo lo que le ofrecemos. ¡Desen un paseo por las atracciones y sitios de interes! Sorpréndase de las maravillas que la Feria de la Luna Negra ha descubierto en este vasto y misterioso mundo! ¡No hemos escatimado en gastos para traer emociones con las que los niños de todas las edades se deleitarán!' WHERE entry=2000000233;
UPDATE `db_script_string` SET content_loc6='Estos datos serán muy útiles para el Proyecto Libro Azul, Quiero decir.., esto será muy bueno para mi nuevo look. A ver si puedes recoger más datos.' WHERE entry=2000000234;
UPDATE `db_script_string` SET content_loc6='YO CRUEL OGR' WHERE entry=2000000235;
UPDATE `db_script_string` SET content_loc6='SIENTO LA MUERTE A MI LADO' WHERE entry=2000000236;
UPDATE `db_script_string` SET content_loc6='¡Venid a mí verdaderos creyentes, y vereis que nos depara el futuro!' WHERE entry=2000000237;
UPDATE `db_script_string` SET content_loc6='OS VOY A DESTRUIR A TODOS' WHERE entry=2000000238;
UPDATE `db_script_string` SET content_loc6='NADIE ME SUPERARÁ' WHERE entry=2000000239;
UPDATE `db_script_string` SET content_loc6='Cuanto más esperes, menos futuro tendrás para predecirme.' WHERE entry=2000000240;
UPDATE `db_script_string` SET content_loc6='La Plaga es una mancha sobre estas tierras vírgenes. Por el poder de mis creadores, Aplastaré hasta el último de sus decrépitos huesos bajo mi talón.$b$BQuiero detener la Plaga tanto como tú. ¿Cómo puedo ayudar?' WHERE entry=2000000241;
UPDATE `db_script_string` SET content_loc6='Los titanes no crearon la vida del día a la noche. El proceso fue gradual y requirió de una gran experiencia e iteración.$B$BPor esa razón existen lugares altamente protegidos como la Cuenca de Sholazar.$B$BPara garantizar la seguridad de sus lugares experimentales, los titanes crearon un mecanismo de defensa. Su fuerza destructiva es del todo inconmensurable, y no te voy a revelar nada más a no ser que jures lealtad a mi causa.$B$BPuedes confiar en mí. Yo no soy amigo del Rey Exánime.' WHERE entry=2000000242;
UPDATE `db_script_string` SET content_loc6='Rozo el sacrilegio al revelar estos secretos a un mortal como tú, pero no tengo otra elección.$B$BDesde que Freya, hija de los titanes, me dejó aquí, no he tenido otra tarea y no fallaré. La vida debe protegerse a toda costa.$B$BHay una puerta que une este lugar con otro muy parecido a éste. Es allí donde puede encontrarse el arma que buscamos.$B$BNo fracasaré.' WHERE entry=2000000243;
UPDATE `db_script_string` SET content_loc6='Toma una vela de señalización de este cajón, $N.' WHERE entry=2000000244;
UPDATE `db_script_string` SET content_loc6='Yo... no me siento... muy... bien...' WHERE entry=2000000245;
UPDATE `db_script_string` SET content_loc6='Mi mente... mi carne... ¡me estoy descomponiendo...!' WHERE entry=2000000246;
UPDATE `db_script_string` SET content_loc6='¡Alzo mi cerveza y espero liberarme de los de tu calaña! No tan buen canalla, $N. ¡Salud!' WHERE entry=2000000247;
UPDATE `db_script_string` SET content_loc6='El Maestro ha pasado por grandes esfuerzos para obtener información sobre el paradero del Exodar. Podrías haber echado por alto toda la operación, incluyendo la tapadera de nuestro espía en el interior.' WHERE entry=2000000248;
UPDATE `db_script_string` SET content_loc6='Onus estudia el pergamino del Escrito Crepuscular...' WHERE entry=2000000249;
UPDATE `db_script_string` SET content_loc6='¡Onus es golpeado por la magia desatada!' WHERE entry=2000000250;
UPDATE `db_script_string` SET content_loc6='Mmm...' WHERE entry=2000000251;
UPDATE `db_script_string` SET content_loc6='Terenthis empieza a lanzar un hechizo cuando te llevas su capa.' WHERE entry=2000000252;
UPDATE `db_script_string` SET content_loc6='Marcha ahora, $N. Encuentra Volcor... y recuerda, las magia que te protege fallará si interactúas con alguien, solo habla.' WHERE entry=2000000253;
UPDATE `db_script_string` SET content_loc6='Abuela Viento de Halcón hace gestos al cántaro de agua colocada al borde del pozo.' WHERE entry=2000000254;
UPDATE `db_script_string` SET content_loc6='¡Una trabajo fácil el romper esta Gema de alma!' WHERE entry=2000000255;
UPDATE `db_script_string` SET content_loc6='¡Gemas de alma brillantes reviéntalas!' WHERE entry=2000000256;
UPDATE `db_script_string` SET content_loc6='Entonces tenemos que poner fin a Athrikus Narassin. Dime, espíritu, ¿dónde se encuentran las otras Gemas de alma, para deshacer su magia?' WHERE entry=2000000257;
UPDATE `db_script_string` SET content_loc6='Sarath se desvanece en la niebla...' WHERE entry=2000000258;
UPDATE `db_script_string` SET content_loc6='Empezaré de inmediato, $N.' WHERE entry=2000000259;
UPDATE `db_script_string` SET content_loc6='Ahora... ¿dónde estaba mi hilo y aguja?. ¡Ah! Ahí está...' WHERE entry=2000000260;
UPDATE `db_script_string` SET content_loc6='Date prisa, $N. ¡Usa el portal!' WHERE entry=2000000261;
UPDATE `db_script_string` SET content_loc6='Muy bien, echémosle un vistazo a esto...' WHERE entry=2000000262;
UPDATE `db_script_string` SET content_loc6='Oh, darn...' WHERE entry=2000000263;
UPDATE `db_script_string` SET content_loc6='Los espíritus del Espolón siguen causando estragos, $N.' WHERE entry=2000000264;
UPDATE `db_script_string` SET content_loc6='Me temo que nunca podremos calmarlos...' WHERE entry=2000000265;
UPDATE `db_script_string` SET content_loc6='Vale, ¡empecemos!' WHERE entry=2000000266;
UPDATE `db_script_string` SET content_loc6='...ups.' WHERE entry=2000000267;
UPDATE `db_script_string` SET content_loc6='¡Hey $N! ¡No te olvides de agarrar un cajón, un manual y una Vara de mando!' WHERE entry=2000000268;
UPDATE `db_script_string` SET content_loc6='Gracias de nuevo por tu ayuda. Que el viento siempre este a tu favor en tus viajes.' WHERE entry=2000000269;
UPDATE `db_script_string` SET content_loc6='%s escucha la súplica de $N.' WHERE entry=2000000270;
UPDATE `db_script_string` SET content_loc6='Hiciste bien en venir. Mi sabiduría será tuya.' WHERE entry=2000000271;
UPDATE `db_script_string` SET content_loc6='%s crea su toxina...' WHERE entry=2000000272;
UPDATE `db_script_string` SET content_loc6='Allá vamos, $N. la toxina está lista.' WHERE entry=2000000273;
UPDATE `db_script_string` SET content_loc6='%s se llena un recipiente con la toxina.' WHERE entry=2000000274;
UPDATE `db_script_string` SET content_loc6='La toxina está lista, $N.' WHERE entry=2000000275;
UPDATE `db_script_string` SET content_loc6='Bueno, tripulación. Pónganse a trabajar en conseguir instalar esos huesos.' WHERE entry=2000000276;
UPDATE `db_script_string` SET content_loc6='%s estornuda una pizca de polvo onírico por la nariz...' WHERE entry=2000000277;
UPDATE `db_script_string` SET content_loc6='%s está atontado...' WHERE entry=2000000278;
UPDATE `db_script_string` SET content_loc6='¡AAAACHUU!' WHERE entry=2000000279;
UPDATE `db_script_string` SET content_loc6='¡Uuau! $N, ¡este polvo onírico es material potente!' WHERE entry=2000000280;
UPDATE `db_script_string` SET content_loc6='%s toma un gran trago de malta.' WHERE entry=2000000281;
UPDATE `db_script_string` SET content_loc6='Fue refrescante. Ahora hay información que necesita contarse...' WHERE entry=2000000282;
UPDATE `db_script_string` SET content_loc6='Creo que los Renegados engañan a los aliados de la Horda... espera... me siento algo... mareado...' WHERE entry=2000000283;
UPDATE `db_script_string` SET content_loc6='%s se retuerce de dolor.' WHERE entry=2000000284;
UPDATE `db_script_string` SET content_loc6='AAAAAAAAAAAAAAAARGH!' WHERE entry=2000000285;
UPDATE `db_script_string` SET content_loc6='Umm... Me pregunto...' WHERE entry=2000000286;
UPDATE `db_script_string` SET content_loc6='¿Dónde está ahora?' WHERE entry=2000000287;
UPDATE `db_script_string` SET content_loc6='Estúpidos magos... siempre con componentes caducados y nunca los sustituyen.' WHERE entry=2000000288;
UPDATE `db_script_string` SET content_loc6='El suero sólo tarda unos minutos en prepararse, $N.' WHERE entry=2000000289;
UPDATE `db_script_string` SET content_loc6='Casi está hecho...' WHERE entry=2000000290;
UPDATE `db_script_string` SET content_loc6='Al fín, el suero está completo, $N. Ahora, acércate más. Tenemos que hablar.' WHERE entry=2000000291;
UPDATE `db_script_string` SET content_loc6='¿Relájate? ¿Sabes Geezle, lo que Kael\'thas hace a los que le fracasan? Dolor y el sufrimiento eterno... NO me lies con esto, loco.' WHERE entry=2000000292;
UPDATE `db_script_string` SET content_loc6='Nuestros exploradores Bruma de Sangre han localizado a nuestro contacto. ¡El tonto Velen, pronto quedará al descubierto e indefenso - lo suficiente para que ataquemos! Ahora piérdete de mi vista antes de que te vaporice...' WHERE entry=2000000293;
UPDATE `db_script_string` SET content_loc6='Tienes toda mi atención, dragón. Ya verás que no me asusto tan fácilmente como los aldeanos de abajo.' WHERE entry=2000000294;
UPDATE `db_script_string` SET content_loc6='¿Te atreves a desafiarme a mi propia morada? ¡Tu arrogancia es asombra, incluso para un dragón!' WHERE entry=2000000295;
UPDATE `db_script_string` SET content_loc6='No sé de que hablas, dragón... pero no me vas a intimidar con tu osadía. ¡Dejaré Karazhan cuando me de la gana!' WHERE entry=2000000296;
UPDATE `db_script_string` SET content_loc6='No deberías haberme enfurecido. Debo irme... a recuperar mi fuerza...' WHERE entry=2000000297;
UPDATE `db_script_string` SET content_loc6='Tus andanzas en lo arcano han llegado demasiado lejos, Medivh. Has atraído la atención de poderes más allá de tu comprensión. ¡Debes dejar Karazhan de una vez!' WHERE entry=2000000298;
UPDATE `db_script_string` SET content_loc6='¡Pretendes usar un poder oscuro, Medivh! Si te quedas, nos vendrán días terribles. ¡Debes darte prisa, no nos queda mucho tiempo!' WHERE entry=2000000299;
UPDATE `db_script_string` SET content_loc6='No me dejas otra alternativa. ¡Te detendré por la fuerza si te atienes a razones!' WHERE entry=2000000300;
UPDATE `db_script_string` SET content_loc6='¿Qué me has hecho, mago? ¡No puede ser! ¡Me estoy quemando... por dentro!' WHERE entry=2000000301;
UPDATE `db_script_string` SET content_loc6='¡Tenemos un ganador! ¡$N es el Maestro pescador!' WHERE entry=2000000302;
UPDATE `db_script_string` SET content_loc6='Espécimen metálico de extraño origen detectado.' WHERE entry=2000000303;
UPDATE `db_script_string` SET content_loc6='Comenzando el análisis...' WHERE entry=2000000304;
UPDATE `db_script_string` SET content_loc6='Muestra identificada como una aleación de saronita y hierro.' WHERE entry=2000000305;
UPDATE `db_script_string` SET content_loc6='El origen del mineral usado en esta muestra está en las profundidades de la corteza de Azeroth y fue descubierto recientemente.' WHERE entry=2000000306;
UPDATE `db_script_string` SET content_loc6='La saronita de esta muestra es casi pura. Un mineral como este no comun en la superficie de Azeroth.' WHERE entry=2000000307;
UPDATE `db_script_string` SET content_loc6='Este mineral sólo puede haber sido creado por una poderosa y maligna fuerza. Intentaré acceder a las bases de datos para obtener más información...' WHERE entry=2000000308;
UPDATE `db_script_string` SET content_loc6='¡¿Acceso denegado?!' WHERE entry=2000000309;
UPDATE `db_script_string` SET content_loc6='Información adicional únicamente se puede obtener en los archivos internos de Ulduar, por orden del Guardián Loken.' WHERE entry=2000000310;
UPDATE `db_script_string` SET content_loc6='Pero una cosa es cierta: el tamaño de esta placa, el espesor, y los agujeros de los tornillos apuntan a un uso como armadura de una estructura colosal.' WHERE entry=2000000311;
UPDATE `db_script_string` SET content_loc6='Orden completada. Volviendo al modo de espera.' WHERE entry=2000000312;
UPDATE `db_script_string` SET content_loc6='¡Yo... ahora te entiendo! ¡Bien, ahora que podemos hablar, tienes que darme algunas explicaciones!' WHERE entry=2000000313;
UPDATE `db_script_string` SET content_loc6='¿Cómo consegiste mi comunicador de la Liga de Expedicionarios?' WHERE entry=2000000314;
UPDATE `db_script_string` SET content_loc6='Tienes mi agradecimiento por despachar los enanos férreos. Pero ¿por qué la Horda tiene este interés en mí?' WHERE entry=2000000315;
UPDATE `db_script_string` SET content_loc6='Esto no será cosa del Explorador Vor\'takh, ¿verdad? conozco su fama de cruel. Entonces, ¿quiere decir que me secuestran?' WHERE entry=2000000316;
UPDATE `db_script_string` SET content_loc6='Si has visto el diario, entonces sabes lo que he ido descubriendo. ¡Las mismas creaciones de los Titanes están en guerra unas con otras!' WHERE entry=2000000317;
UPDATE `db_script_string` SET content_loc6='Loken y sus secuaces enanos de hierro han expulsado a los terráneos de Ulduar y se han apoderado de la ciudad.' WHERE entry=2000000318;
UPDATE `db_script_string` SET content_loc6='Si gastamos nuestro tiempo y fuerzas en pelearnos unos con otros, Loken utilizará los recursos de Ulduar para destruir tanto a la Horda como a la Alianza.' WHERE entry=2000000319;
UPDATE `db_script_string` SET content_loc6='Habla con el comandante en tu puesto, $g;muchacho:muchacha, y persuádelo a abandonar el insensato plan de Vor\'takh.' WHERE entry=2000000320;
UPDATE `db_script_string` SET content_loc6='Si la Liga de Expedicionarios envió hombres, no he visto ni rastro de ellos. Encontré tu nota enterrada en un campo de invadido por enanos férreos.' WHERE entry=2000000321;
UPDATE `db_script_string` SET content_loc6='Un explorador encontró los restos de tu diario en Thor Modan. Te hemos estado buscando desde entonces.' WHERE entry=2000000322;
UPDATE `db_script_string` SET content_loc6='Corbett, querido. Harlan necesita una carga de camisas tricotadas y pantalones tan pronto como podemos conseguirla.' WHERE entry=2000000323;
UPDATE `db_script_string` SET content_loc6='Corbett, ¿estás ahí? Harlan necesita otra carga de géneros tricotados. ¿Puedes atenderle?' WHERE entry=2000000324;
UPDATE `db_script_string` SET content_loc6='El placer es mío, dulce de azúcar. Volveré pronto:' WHERE entry=2000000325;
UPDATE `db_script_string` SET content_loc6='El bazar debe hacer buenos negocios. ¡Voy a reabastecerle de inmediato!' WHERE entry=2000000326;
UPDATE `db_script_string` SET content_loc6='Hm ... después de zanjar esto, creo que iré a la tienda de quesos a por la merienda.' WHERE entry=2000000327;
UPDATE `db_script_string` SET content_loc6='Debería sobrarme dinero de esta venta. Tal vez me voy a comprar algo de comer...' WHERE entry=2000000328;
UPDATE `db_script_string` SET content_loc6='Hey Harlan. Aquí hay una carga de géneros tricotados para ti.' WHERE entry=2000000329;
UPDATE `db_script_string` SET content_loc6='¡Uoph! Aquí está carga de suministros. ¡Se debe atender rápido!' WHERE entry=2000000330;
UPDATE `db_script_string` SET content_loc6='Ah, sí, y con rapidez. Como siempre, es un placer hacer negocios contigo, Corbett.' WHERE entry=2000000331;
UPDATE `db_script_string` SET content_loc6='Ahh, mí apreciado Corbett. Los llevaremos a los bastidores de inmediato.' WHERE entry=2000000332;
UPDATE `db_script_string` SET content_loc6='Me alegra ver que lo estás haciendo muy bien, Harlan. Y espero volverte a ver pronto:' WHERE entry=2000000333;
UPDATE `db_script_string` SET content_loc6='Bueno, me voy. ¡Ten cuidado, Harlan!' WHERE entry=2000000334;
UPDATE `db_script_string` SET content_loc6='Ahora, a por esa merienda:' WHERE entry=2000000335;
