UPDATE `db_script_string` SET content_loc6='¿Y bien? ¿Quién está a la escucha en el otro lado de la señal? ¿Qué va a pasar?' WHERE entry=2000000389;
UPDATE `db_script_string` SET content_loc6='¿Oísteis muchachos? ¡Sólo es el fin del mundo! Bien ¿que esperáis de una máquina oxidada? ¡Inicia código de respuesta Alfa! ¡Unas Pintas aquí!' WHERE entry=2000000390;
UPDATE `db_script_string` SET content_loc6='Bueno, ¿qué estará buscando Algalon con su diagnóstico? ¿Cuáles son nuestras posibilidades?' WHERE entry=2000000391;
