UPDATE `db_script_string` SET content_loc6='[Furbolg] ¡La profecia es cierta!' WHERE entry=2000000494;
UPDATE `db_script_string` SET content_loc6='[Furbolg] ¡Los que quedamos en el Bastión Semprepino te daremos la bienvenida de $gun héroe:una heronina;!' WHERE entry=2000000495;
UPDATE `db_script_string` SET content_loc6='[Furbolg] ¡Los furbolgs Semprepino no olvidaremos tu valentía!' WHERE entry=2000000496;
UPDATE `db_script_string` SET content_loc6='¿Qué es esto?' WHERE entry=2000000497;
UPDATE `db_script_string` SET content_loc6='¿El... lo guardaba? ¿Durante todo este tiempo, se lo guardó!' WHERE entry=2000000498;
UPDATE `db_script_string` SET content_loc6='¡Lo sabía!' WHERE entry=2000000499;
UPDATE `db_script_string` SET content_loc6='¡Sentía que una parte de él aún vivía! ¡Atrapado... luchando... Oh, Arthas!' WHERE entry=2000000500;
UPDATE `db_script_string` SET content_loc6='Tal vez - tal vez algún día pueda recordar lo que un vez fue.' WHERE entry=2000000501;
UPDATE `db_script_string` SET content_loc6='Por la luz, es posible que por fin encuentre descanso, libre de garra helada de esa terrible espada.' WHERE entry=2000000502;
UPDATE `db_script_string` SET content_loc6='Así que, se hace.' WHERE entry=2000000503;
UPDATE `db_script_string` SET content_loc6='No me hubiese atrevido en confiar mis sentidos - tubo varias ocasiones el Rey Exánime para hacerme un necio.' WHERE entry=2000000504;
UPDATE `db_script_string` SET content_loc6='Finalmente, se le ha hecho pagar por las atrocidades que le impuso a la gente.' WHERE entry=2000000505;
UPDATE `db_script_string` SET content_loc6='Azeroth nunca debe olvidar el terrible precio que pagamos por nuestra flaqueza... por nuestro orgullo.' WHERE entry=2000000506;
UPDATE `db_script_string` SET content_loc6='¿Pero ahora qué, $ghéroe:heronina? ¿Qué será de los liberados de sus garras, pero aún encadenados a sus cuerpos mortales?' WHERE entry=2000000507;
UPDATE `db_script_string` SET content_loc6='Déjame.' WHERE entry=2000000508;
UPDATE `db_script_string` SET content_loc6='Tengo mucho en que pensar.' WHERE entry=2000000509;
UPDATE `db_script_string` SET content_loc6='Arthas...' WHERE entry=2000000510;
UPDATE `db_script_string` SET content_loc6='Ah, $ghéroe:heronina; de Azeroth, me dás un regalo más grande de lo que crees.' WHERE entry=2000000511;
UPDATE `db_script_string` SET content_loc6='Largo tiempo he luchado para perdonar al principe por sus terribles transgresiones.' WHERE entry=2000000512;
UPDATE `db_script_string` SET content_loc6='Mi alma ha sido azotada con insoportable angustia, pensamientos oscuros... distanciandome de la Luz.' WHERE entry=2000000513;
UPDATE `db_script_string` SET content_loc6='Me acuerdo claramente de su soberbia y el resplandor de sus ojos, mientras permanecía de pie frente a mí, dispuesto a derrotar a los enemigos de la Luz...' WHERE entry=2000000514;
UPDATE `db_script_string` SET content_loc6='Ansioso por defender a su gente, sin importar lo que cueste.' WHERE entry=2000000515;
UPDATE `db_script_string` SET content_loc6='Esa es la memoria de Arthas que decido guardar en mi corazón.' WHERE entry=2000000516;
UPDATE `db_script_string` SET content_loc6='Estaré siempre en deuda contigo, amig$go:a;.' WHERE entry=2000000517;
UPDATE `db_script_string` SET content_loc6='Gracias.' WHERE entry=2000000518;
UPDATE `db_script_string` SET content_loc6='Oh, muchach$go:a;...' WHERE entry=2000000519;
UPDATE `db_script_string` SET content_loc6='Cómo añoro esos interminables días en Lordaeron...' WHERE entry=2000000520;
UPDATE `db_script_string` SET content_loc6='...afinando tu\' habilidade\'h con la hoja mate...' WHERE entry=2000000521;
UPDATE `db_script_string` SET content_loc6='...fo\'jandote un arma de\'htinada a z\'oporta\' la\'h demanda\'h de un gran destino.' WHERE entry=2000000522;
UPDATE `db_script_string` SET content_loc6='¿Heh... Sí seguro que la pone\'h a prueba, no es así, muchach$go:a;?' WHERE entry=2000000523;
UPDATE `db_script_string` SET content_loc6='Si te hubiese para\'o ese día, la\'h cosa\'h hubisen sido diferente\'.' WHERE entry=2000000524;
UPDATE `db_script_string` SET content_loc6='Si nunca hubiese descubierto esa maldita hoja' WHERE entry=2000000525;
UPDATE `db_script_string` SET content_loc6='Adiós, Arthas... hermano mío.' WHERE entry=2000000526;
UPDATE `db_script_string` SET content_loc6='Darion, hijo mío.' WHERE entry=2000000527;
UPDATE `db_script_string` SET content_loc6='Al fin, puedo poner mis ojos en ti otra vez.' WHERE entry=2000000528;
UPDATE `db_script_string` SET content_loc6='El Rey Exánime me atormentaba sin fin, Darion.' WHERE entry=2000000529;
UPDATE `db_script_string` SET content_loc6='Sin cesar, trató de romper mi voluntad, para obligarme a servirle, para unirme a su espada...' WHERE entry=2000000530;
UPDATE `db_script_string` SET content_loc6='Finalmente, cuando los acontecimientos exigieron toda su atención, me dejó.' WHERE entry=2000000531;
UPDATE `db_script_string` SET content_loc6='Me aferré al único recuerdo, Darion... El único pensamiento que me impidió dar...' WHERE entry=2000000532;
UPDATE `db_script_string` SET content_loc6='Fue tu sacrificio, hijo mío, el que de nuevo me salvó de un peligro eterno.' WHERE entry=2000000533;
UPDATE `db_script_string` SET content_loc6='Padre...' WHERE entry=2000000534;
UPDATE `db_script_string` SET content_loc6='Padre. Temí por... tu cordura.' WHERE entry=2000000535;
UPDATE `db_script_string` SET content_loc6='Padre... por tí, daría mi vida mil veces.' WHERE entry=2000000536;
UPDATE `db_script_string` SET content_loc6='¡%s huele el elixir entonces con impaciencia entierra!' WHERE entry=2000000537;
UPDATE `db_script_string` SET content_loc6='Y gracias, $N. Sin tu ayuda nunca hubiese encontrado mi camino a los Renegados.' WHERE entry=2000000538;
UPDATE `db_script_string` SET content_loc6='Adiós, mi amigo.' WHERE entry=2000000539;
UPDATE `db_script_string` SET content_loc6='Durante mucho tiempo, has sido mi amigo, Bethor.' WHERE entry=2000000540;
UPDATE `db_script_string` SET content_loc6='Cuando el tiempo lo permita, tenemos que hablar largo y tendido. Ya que tenemos mucho de lo que discutir.' WHERE entry=2000000541;
UPDATE `db_script_string` SET content_loc6='%s invoca a un mensajero...' WHERE entry=2000000542;
UPDATE `db_script_string` SET content_loc6='Vete, siervo. Envia el recapo a la boticaria Zamah del Espolón.' WHERE entry=2000000543;
UPDATE `db_script_string` SET content_loc6='%s da el mensaje a la boticaria Zamah.' WHERE entry=2000000544;
UPDATE `db_script_string` SET content_loc6='La boticaria Zamah ríe.' WHERE entry=2000000545;
UPDATE `db_script_string` SET content_loc6='¡Pero este es la única costrucción en el que Brogg entra! ¡Los Goblins hacen costrucciones muy pequeñas!' WHERE entry=2000000546;
UPDATE `db_script_string` SET content_loc6='Creí que ya hablamos de esto, Brogg. No puedes quedarte en esta costrucción. ¡Seguro, podria ser tu casa, pero no hay espacio para nadie más!' WHERE entry=2000000547;
UPDATE `db_script_string` SET content_loc6='Sí, digimos que podias quedarte aquí, ¡pero este edificio es nuestro puesto de mando! ¡Todos nuestros manuales y planos se almacenan aquí!' WHERE entry=2000000548;
UPDATE `db_script_string` SET content_loc6='¡Me inclino a decir que hicieron los ogros demasiado grandes! ¡Veo que ahora, no te voy a convercer de que te muevas, pero dudo que esto dure mucho!' WHERE entry=2000000549;
UPDATE `db_script_string` SET content_loc6='¡Tú has dicho a Brogg que podía quedarme aquí! ¡Prometiste a Brogg que le ayudarías a recuperar el montículo del clan Quebrantarrocas!' WHERE entry=2000000550;
UPDATE `db_script_string` SET content_loc6='En las convulsiones del Cataclismo, Ameth\'Aran estaba a merced del terror que se apoderó de la tierra. Había poca esperanza para la supervivencia.' WHERE entry=2000000551;
UPDATE `db_script_string` SET content_loc6='Athrikus vino a nosotros. Nos dijo que podía salvarnos del peligro. Lanzó un hechizo sobre nosotros para que nos protegiera de cualquier daño.' WHERE entry=2000000552;
UPDATE `db_script_string` SET content_loc6='Cuando el temblor se detuvo, sus verdaderos motivos fueron revelados. Estábamos atrapados, y poco a poco fue drenando nuestra energía.' WHERE entry=2000000553;
UPDATE `db_script_string` SET content_loc6='Había cientos de encarcelados por su hechizo. Ahora sólo unos pocos permanecen en sus cárceles. Él nos habla a veces sobre ello.' WHERE entry=2000000554;
UPDATE `db_script_string` SET content_loc6='Le preocupaba que su poder se debilitara, incluso que su última piedra de alma se desvaneciera y muriera.' WHERE entry=2000000555;
UPDATE `db_script_string` SET content_loc6='Su lugarteniente, Ilkurd Magthrull, posee un tomo que podría indicarnos la ubicación de las gemas de alma restantes.' WHERE entry=2000000556;
UPDATE `db_script_string` SET content_loc6='Ummm ... ¿Dónde está mi anzuelo? Oh, está ahí.' WHERE entry=2000000557;
UPDATE `db_script_string` SET content_loc6='Vamos a ver si los peces pican.' WHERE entry=2000000558;
UPDATE `db_script_string` SET content_loc6='Ummm... Creo que necesito un cebo mejor. Voy a mirar en el carro de mi padre.' WHERE entry=2000000559;
UPDATE `db_script_string` SET content_loc6='¡Trozos de Jabaespines! Estos deberían hacer el engaño. El pescado ama los Jabaespines.' WHERE entry=2000000560;
UPDATE `db_script_string` SET content_loc6='¡Pillé uno! Voy a ver si alguno de los guardias está hambriento. ¡Vamos colmillos!' WHERE entry=2000000561;
UPDATE `db_script_string` SET content_loc6='Pillé un pez, si tienes hambre...' WHERE entry=2000000562;
UPDATE `db_script_string` SET content_loc6='¡Eso es una buena captura! ¡Honras a la Horda chico, te lo agradecemos!' WHERE entry=2000000563;
UPDATE `db_script_string` SET content_loc6='¿Quiere algo más de comida señor?' WHERE entry=2000000564;
UPDATE `db_script_string` SET content_loc6='¡Más pan chico!' WHERE entry=2000000565;
UPDATE `db_script_string` SET content_loc6='En seguida señor.' WHERE entry=2000000566;
UPDATE `db_script_string` SET content_loc6='Aquí está su pan señor.' WHERE entry=2000000567;
UPDATE `db_script_string` SET content_loc6='Aaahh... Muy bien. ¡Ahora largo!' WHERE entry=2000000568;
UPDATE `db_script_string` SET content_loc6='Como desee, señor.' WHERE entry=2000000569;
UPDATE `db_script_string` SET content_loc6='¿Puedo ir a recoger más piedras para mi collar, papá?' WHERE entry=2000000570;
UPDATE `db_script_string` SET content_loc6='Si puedes, $N, pero no te alejes demasiado de casa.' WHERE entry=2000000571;
UPDATE `db_script_string` SET content_loc6='¡Dabu! ¡Tendré cuidadoPapa!' WHERE entry=2000000572;
UPDATE `db_script_string` SET content_loc6='Ooohh... Esta roca es bonita y colorida. Voy a ver si puedo encontrar más como esta...' WHERE entry=2000000573;
UPDATE `db_script_string` SET content_loc6='Umm... Esa roca bonita, pero muy pequeña.' WHERE entry=2000000574;
UPDATE `db_script_string` SET content_loc6='Esta es una roca brillante. Lo pondré en mi cubo. Ok, sólo necesito algunas más...' WHERE entry=2000000575;
UPDATE `db_script_string` SET content_loc6='¡Apuesto que a mi amigo Gruk le gustaría esta roca para la lanza que está haciendo! Está muy afilada... Espero que le guste.' WHERE entry=2000000576;
UPDATE `db_script_string` SET content_loc6='Allí... debe haber bastantes rocas como para hacer un collar. Quizás debería volver a casa de todos modos.' WHERE entry=2000000577;
UPDATE `db_script_string` SET content_loc6='¡Vas a pagar por esta afrenta, $R!' WHERE entry=2000000578;
UPDATE `db_script_string` SET content_loc6='¡Tu cuerpo es mío! ¡Nuestros destinos están unidos para siempre!' WHERE entry=2000000579;
UPDATE `db_script_string` SET content_loc6='La Cruzada Argenta te invita a probar tus habilidades en honorable combate contra  otros héroes de Azeroth en el Torneo Argenta.' WHERE entry=2000000580;
UPDATE `db_script_string` SET content_loc6='Los campeones de las grandes naciones del mundo se han reunido en los Campos del Torneo Argenta en Corona de Hielo Rasganorte para que se enfrenten todos los aspirantes.' WHERE entry=2000000581;
UPDATE `db_script_string` SET content_loc6='Eres lento, sobrestante.' WHERE entry=2000000582;
UPDATE `db_script_string` SET content_loc6='El destino no esperará. Tus artesanos deben aumentar su producción de golems de guerra antes de que el líder de los gigantes de piedra y su ejército alcancen Ulduar.' WHERE entry=2000000583;
UPDATE `db_script_string` SET content_loc6='Dile a tus forjadores de runas que siguán convirtiendo a lo los gigantes de piedra que puedan. Aquellos que no se sometan deben ser destruidos.' WHERE entry=2000000584;
UPDATE `db_script_string` SET content_loc6='Si los gigantes de piedra interfieren con nuestro trabajo en Ulduar, te responsabilizaré a ti y a tu feudo. No se debe permitir a los mortales que acudan en ayuda de los gigantes.' WHERE entry=2000000585;
UPDATE `db_script_string` SET content_loc6='Vuelve a tus obligaciones, sobrestante. Asegúrate de convencer a tus peones de la urgencia de la faena.' WHERE entry=2000000586;
UPDATE `db_script_string` SET content_loc6='Así que el oráculo ha decidido ayudarte, extranjero.  Muy bien, te diré lo que sé.' WHERE entry=2000000587;
UPDATE `db_script_string` SET content_loc6='Tu compañero busca al patriarca trozocuerno Farunn, que se mantiene distante de sus hermanos.' WHERE entry=2000000588;
UPDATE `db_script_string` SET content_loc6='Farunn recorre las tierras del norte de lo que tu gente llama las cataratas Olamarga. Lo hallarás solo, pero verás que no necesita quién lo defienda.' WHERE entry=2000000589;
UPDATE `db_script_string` SET content_loc6='Regresa con tu compañero. Dile que vaya adelante y reclame su premio con Farunn.' WHERE entry=2000000590;
UPDATE `db_script_string` SET content_loc6='Una vez que lo haya hecho, dile que deje esta tierra en paz. El no pertenece aquí.' WHERE entry=2000000591;
UPDATE `db_script_string` SET content_loc6='[Furbolg] Doy fe de los salvajes actos de nuestros adversarios, $N. Se llevan nuestras jóvenes... nuestra debilidad... Encarcelándolos aquí, los dejamos ha merced de los dispositivos de Brazolanudo. ¡Déjalos en libertad, prométemelo!' WHERE entry=2000000592;
UPDATE `db_script_string` SET content_loc6='Verdaderamente no he averiguado lo que es, pero hay algo raro en estos balnearios...' WHERE entry=2000000593;
UPDATE `db_script_string` SET content_loc6='%s tira la espada al estanque.' WHERE entry=2000000594;
UPDATE `db_script_string` SET content_loc6='He descubierto que cuando lanzo algo al agua, cuando lo recojo mejora increíblemente.' WHERE entry=2000000595;
UPDATE `db_script_string` SET content_loc6='¡Bueno, de cualquier forma, no está nada mal!' WHERE entry=2000000596;
UPDATE `db_script_string` SET content_loc6='Y estoy decidido a averiguar más... Así que si necesitas mi ayuda de nuevo, ya sabes dónde estaré.' WHERE entry=2000000597;
UPDATE `db_script_string` SET content_loc6='Por favor, sígueme.' WHERE entry=2000000598;
UPDATE `db_script_string` SET content_loc6='¿Esos son jacintos de invierno? ¿Para mí?' WHERE entry=2000000599;
UPDATE `db_script_string` SET content_loc6='Hace tanto tiempo desde que un viajante vino aquí con flores.' WHERE entry=2000000600;
UPDATE `db_script_string` SET content_loc6='El lago ha estado muy sollitario estos últimos años. Los viajeros dejaron de venir y el mal llegó a estas aguas.' WHERE entry=2000000601;
UPDATE `db_script_string` SET content_loc6='Es un raro acto de gentileza tu regalo, viajer$go:a;. Por favor, tome esta espada, con mi gratitud. Hace mucho tiempo, otro viajero la dejó aquí, pero yo la uso poco.' WHERE entry=2000000602;
UPDATE `db_script_string` SET content_loc6='Gracias, mortal, por liberarme de esta maldición. Te lo ruego, toma esta hoja' WHERE entry=2000000603;
UPDATE `db_script_string` SET content_loc6='No ha traído más que maldad. Tal vez puedas encontrar a alguien que contenga su poder.' WHERE entry=2000000604;
UPDATE `db_script_string` SET content_loc6='Tan pronto como el agente neutralizante es mezclado en las aguas del lago, su cristalina agua, parece haberse limpiado.' WHERE entry=2000000605;
UPDATE `db_script_string` SET content_loc6='¡Cachorros traicioneros! ¡Sironas os destruirá a ti y a tu gente!' WHERE entry=2000000606;
UPDATE `db_script_string` SET content_loc6='Mi hermano, Pezuña Tempestuosa, fue un guerrero mucho más grande que yo.  Le entrené bien para que fuese el jefe de nuestro clan, se preparó para una gran misión.' WHERE entry=2000000607;
UPDATE `db_script_string` SET content_loc6='¿Cómo podía ser peligroso? Lo único que buscaba era darnos poder sobre los elementos que nos hacian aquí la vida imposible.' WHERE entry=2000000608;
UPDATE `db_script_string` SET content_loc6='Buscaba un artefacto... un cuerno, creo. Dejó el pueblo para un largo viaje en su búsqueda.' WHERE entry=2000000609;
UPDATE `db_script_string` SET content_loc6='Yo sé que un terrible enemigo le persiguió después de haber conseguido el cuerno, pero...  no recuerdo ... lo que pasó... después.' WHERE entry=2000000610;
UPDATE `db_script_string` SET content_loc6='Es como si mis recuerdos estuviesen envueltos en niebla. Ni siquiera recuerdo lo que le pasó a mi hermano.  Así que ¿cómo piensas que está en peligro?' WHERE entry=2000000611;
UPDATE `db_script_string` SET content_loc6='Las perturbaciones en la tumba, ellos están involucrados. Mira alrededor, $N. ¿Los ves?' WHERE entry=2000000612;
UPDATE `db_script_string` SET content_loc6='Si, son culpables pero no puedo hacer nada para detenerlos. ¿Advertirás a la gente de Tunka\'lo, extranjero, de que su pasado y sus ancestros están en peligro?' WHERE entry=2000000613;
UPDATE `db_script_string` SET content_loc6='El martillo de conocimiento está reaccionando. Debe ser de tiempos de Pezuña Tempestuosa.' WHERE entry=2000000614;
UPDATE `db_script_string` SET content_loc6='El martillo de conocimiento silencia. Esto no es de tiempos de Pezuña Tempestuosa.' WHERE entry=2000000615;
UPDATE `db_script_string` SET content_loc6='Buena suerte... ¡La necesitarás!' WHERE entry=2000000616;
UPDATE `db_script_string` SET content_loc6='¡Que gane el mejor!' WHERE entry=2000000617;
UPDATE `db_script_string` SET content_loc6='¡No me vencerás!' WHERE entry=2000000618;
UPDATE `db_script_string` SET content_loc6='¡No eres digno de Thorim!' WHERE entry=2000000619;
UPDATE `db_script_string` SET content_loc6='Adivinando sobre varias situaciones sin inportancia dentro de la Corona de Hielo, $B. Tropiezas con algo interesante...' WHERE entry=2000000620;
UPDATE `db_script_string` SET content_loc6='El cultista prácticamente escupe.' WHERE entry=2000000621;
UPDATE `db_script_string` SET content_loc6='Mi señora.' WHERE entry=2000000622;
UPDATE `db_script_string` SET content_loc6='Informe.' WHERE entry=2000000623;
UPDATE `db_script_string` SET content_loc6='Hay noticias de Jotunheim. Los vigías del sueño de allí creen que han encontrado a alguien importante.' WHERE entry=2000000624;
UPDATE `db_script_string` SET content_loc6='Describe este vrykul. ¿Qué aspecto tiene?' WHERE entry=2000000625;
UPDATE `db_script_string` SET content_loc6='¿Parecerse, mi lady? Un vrykul, supongo. En realidad ellos no me lo enseñaron. Después de la Cámara de las Sombras...' WHERE entry=2000000626;
UPDATE `db_script_string` SET content_loc6='No me interesan en las excusas. ¿Quizá te dio un nombre?' WHERE entry=2000000627;
UPDATE `db_script_string` SET content_loc6='¿Un nombre? ¡Oh, sí, el nombre! Creo que era Iskalder.' WHERE entry=2000000628;
UPDATE `db_script_string` SET content_loc6='¿Iskalder? ¡Idiota! ¿No tienes ni idea de quién es? ¡Es el guerreo vrykul más grande que jamás haya existido!' WHERE entry=2000000629;
UPDATE `db_script_string` SET content_loc6='Vuelve a Jotunheim y diles que lo mantengan dormido hasta que llegue. Juzgaré a ese  vrykul con mis propios ojos.' WHERE entry=2000000630;
UPDATE `db_script_string` SET content_loc6='En seguida, mi lady.' WHERE entry=2000000631;
UPDATE `db_script_string` SET content_loc6='Morae... ¿Eres tú?' WHERE entry=2000000632;
UPDATE `db_script_string` SET content_loc6='No... tu no eres Morae, pero me da la sensación que la conoces. Cuando la veas, dile que sobreviví al accidente, solo para hacerme de los infiltrados.' WHERE entry=2000000633;
UPDATE `db_script_string` SET content_loc6='Díselo... mi último pensamiento fue de ella...' WHERE entry=2000000634;
UPDATE `db_script_string` SET content_loc6='¡Grek lo prueba!' WHERE entry=2000000635;
UPDATE `db_script_string` SET content_loc6='¡Este aceite no es bueno para Grek! ¿Quién crees que es Grek, un montón de trapos sucios?' WHERE entry=2000000636;
UPDATE `db_script_string` SET content_loc6='%s se rie' WHERE entry=2000000637;
UPDATE `db_script_string` SET content_loc6='¿Te asusta  saber que hay quienes servirían  a la Legión con una dedicación tan inquebrantable como para dejar a los ciudadanos de sus inútiles civilizaciones ante su llamada?' WHERE entry=2000000638;
UPDATE `db_script_string` SET content_loc6='Vivir atemorizado, morir por voluntad de Kael\'thas... Es la misma cosa.' WHERE entry=2000000639;
UPDATE `db_script_string` SET content_loc6='¡Y ahora, te corto!' WHERE entry=2000000640;
UPDATE `db_script_string` SET content_loc6='¡MUERE!' WHERE entry=2000000641;
UPDATE `db_script_string` SET content_loc6='%s coge la bandera naga.' WHERE entry=2000000642;
UPDATE `db_script_string` SET content_loc6='¡No fracasaremos!' WHERE entry=2000000643;
UPDATE `db_script_string` SET content_loc6='%s deja el cajón de piezas en el suelo.' WHERE entry=2000000644;
UPDATE `db_script_string` SET content_loc6='%s agarra a una pieza y lo pone en el bolsillo...' WHERE entry=2000000645;
UPDATE `db_script_string` SET content_loc6='Allí, eso debe ser...' WHERE entry=2000000646;
UPDATE `db_script_string` SET content_loc6='¿Anaia?... ¿Me engañan mis ojos? ¿Eres tú de verdad?' WHERE entry=2000000647;
UPDATE `db_script_string` SET content_loc6='El ha sido cruel con nosotros, amor mío, pero ten por seguro que ahora sí permaneceremos unidos.' WHERE entry=2000000648;
UPDATE `db_script_string` SET content_loc6='Cruel destino el que nos permite sólo esto después de mil años separados...' WHERE entry=2000000649;
UPDATE `db_script_string` SET content_loc6='¿Me odias, amor mío? Por haber tenido que destruir tu forma viviente y por liberar tu espíritu de tan triste cautiverio...' WHERE entry=2000000650;
UPDATE `db_script_string` SET content_loc6='No permitas que esto atormente tu corazón, amor mío. Me has liberado de la esclavitud y por ello te amo más aún.' WHERE entry=2000000651;
UPDATE `db_script_string` SET content_loc6='Desafortunadamente, esto también debe interrumpirse... Los lazos que me atan a este mundo se debilitan y me arrastran...' WHERE entry=2000000652;
UPDATE `db_script_string` SET content_loc6='¡No! Anaia... ¡Anaia! ¡No me abandones! Por favor...' WHERE entry=2000000653;
UPDATE `db_script_string` SET content_loc6='Adiós, Cerellean, hasta que volvamos a reunirnos...' WHERE entry=2000000654;
UPDATE `db_script_string` SET content_loc6='La dulce voz de Anaya se disemina en la niebla. \"Que sepas que siempre te querré...\"' WHERE entry=2000000655;
UPDATE `db_script_string` SET content_loc6='¿Cómo, amor mío? ¿Cómo encontraré fuerzas para enfrentarme a las edades del mundo sin ti a mi lado...' WHERE entry=2000000656;
UPDATE `db_script_string` SET content_loc6='Gracias , $N. Y mucha suerte.' WHERE entry=2000000657;
UPDATE `db_script_string` SET content_loc6='El Salto de Fonroca está a lo largo de las montañas hacia el este y el norte.' WHERE entry=2000000658;
UPDATE `db_script_string` SET content_loc6='¡Son míos los hechos, hermano! ¡Pronto estarás fuera de mi camino para siempre!' WHERE entry=2000000659;
UPDATE `db_script_string` SET content_loc6='¡Honrar vuestros héroes! ¡En este día, han asestado un gran golpe contra uno de nuestros enemigos más odiados! ¡Ha caído, el Jefe de guerra  Rend Puño Negro!' WHERE entry=2000000670;
UPDATE `db_script_string` SET content_loc6='¡Bañaos en mi poder! ¡Beber en mi poder! ¡Luchad por la gloria de la Horda!' WHERE entry=2000000671;
UPDATE `db_script_string` SET content_loc6='Ve rápido $N. Mi destino está en tus manos.' WHERE entry=2000000672;
UPDATE `db_script_string` SET content_loc6='¡Espera, $N! Parece que aquí necesitaremos algo de ayuda...' WHERE entry=2000000673;
UPDATE `db_script_string` SET content_loc6='%s comienza a bailar.' WHERE entry=2000000674;
UPDATE `db_script_string` SET content_loc6='¡Jajajaja! ¡$N, haces una buena pareja!' WHERE entry=2000000675;
UPDATE `db_script_string` SET content_loc6='¿Aun no has liberado a Drull? No se donde está encerrado.' WHERE entry=2000000676;
UPDATE `db_script_string` SET content_loc6='¡Sólo te retrasaré, ningún humano de piel-rosada me privará de libertad!' WHERE entry=2000000677;
UPDATE `db_script_string` SET content_loc6='No culpes a A\'dal con cuestiones mundanas, $r. Su voluntad es todo lo que impide a nuestros enemigos aplastar esta ciudad.' WHERE entry=2000000678;
UPDATE `db_script_string` SET content_loc6='Arriba en la colina, $N.' WHERE entry=2000000679;
UPDATE `db_script_string` SET content_loc6='Deja que te ayude con ellos.' WHERE entry=2000000680;
UPDATE `db_script_string` SET content_loc6='Tómate esto, te ayudará.' WHERE entry=2000000681;
UPDATE `db_script_string` SET content_loc6='Te pondrás bien.' WHERE entry=2000000682;
UPDATE `db_script_string` SET content_loc6='Oh dulces, deliciosos, huevos moteados seréis míos...' WHERE entry=2000000684;
UPDATE `db_script_string` SET content_loc6='¿Tú te has visto? ¡Esta es la escusa más patética para arrojar la espada que he visto nunca!' WHERE entry=2000000685;
UPDATE `db_script_string` SET content_loc6='Estás muerto, sin entierro. ¡Ahora muéstrame lo mejor que sabes!' WHERE entry=2000000686;
UPDATE `db_script_string` SET content_loc6='Bien lo siento saco de huesos. Veamos que tienes. Pega a tu contrincante, y no pares hasta que te lo diga.' WHERE entry=2000000687;
UPDATE `db_script_string` SET content_loc6='¡Espero que los Elfos de sangre crucen el camino no creo que piensen que estoy espiándolos!' WHERE entry=2000000688;
UPDATE `db_script_string` SET content_loc6='Si fuese listo, vendería esta investigación por un asiento en el cohete. ¡Me da la sensación de que pronto tendremos que salir de aquí!' WHERE entry=2000000689;
UPDATE `db_script_string` SET content_loc6='!Si eso es cierto, no tenemos mucho tiempo! Oh bueno.' WHERE entry=2000000690;
UPDATE `db_script_string` SET content_loc6='Es la última vez que compro un equipamiento de etéreal de segunda mano' WHERE entry=2000000691;
UPDATE `db_script_string` SET content_loc6='Te he traído un regalo de bienvenida, Jarl.' WHERE entry=2000000692;
UPDATE `db_script_string` SET content_loc6='Vamos, ábrelo, creo que te gustará.' WHERE entry=2000000693;
UPDATE `db_script_string` SET content_loc6='¿Jarl?' WHERE entry=2000000694;
UPDATE `db_script_string` SET content_loc6='A menos que sea algo comestible, no lo quiero.' WHERE entry=2000000695;
UPDATE `db_script_string` SET content_loc6='No has sido el mismo desde que llegué, Jarl ¿Qué está pasando?' WHERE entry=2000000696;
UPDATE `db_script_string` SET content_loc6='Nada. ¿Quieres ayudarme prepararmos algo de guiso de anca de rana?' WHERE entry=2000000697;
UPDATE `db_script_string` SET content_loc6='¡Jarl, vine a verte! Y te encuentro viviendo en una camarilla vacía en mitad de ninguna parte, ¿balbuceando sobre ranas? ¡No tienes remedio!' WHERE entry=2000000698;
UPDATE `db_script_string` SET content_loc6='Edward. Tyler. Preparaos para vuestro primer desafío.' WHERE entry=2000000699;
UPDATE `db_script_string` SET content_loc6='Lysta, invoca a los cautivos.' WHERE entry=2000000700;
UPDATE `db_script_string` SET content_loc6='No todo lo que parece es un desafío. Veamos como os manejáis en la segunda prueba. Lysta, saca a los esbirros del Rey Exánime.' WHERE entry=2000000701;
UPDATE `db_script_string` SET content_loc6='Lysta, invoca a los cautivos no-muertos.' WHERE entry=2000000702;
UPDATE `db_script_string` SET content_loc6='¡Es hora de forzar vuestro desafío final jóvenes guerreros! Preparaos para una lucha aun más dura.' WHERE entry=2000000703;
UPDATE `db_script_string` SET content_loc6='¡Lysta, invoca a... la abominación!' WHERE entry=2000000704;
UPDATE `db_script_string` SET content_loc6='Bien hecho Edward y Tyler. Habéis progresado bastante bien a lo largo de vuestro entrenamiento. Pronto probaremos vuestra valía.' WHERE entry=2000000705;
UPDATE `db_script_string` SET content_loc6='¡Gracias por salvarme, $N! ¡Mi padre estará encantado!' WHERE entry=2000000706;
UPDATE `db_script_string` SET content_loc6='¿TÚ eres el que ha interrumpido mi comida? Bueno, ¿pues?' WHERE entry=2000000707;
UPDATE `db_script_string` SET content_loc6='¿Eres demasiado fea y flaca para ser una hembra yeti? Y me está volviendo loco...' WHERE entry=2000000708;
UPDATE `db_script_string` SET content_loc6='Gracias de nuevo, $n. Ahora esperaré aquí hasta que sea seguro salir.' WHERE entry=2000000709;
UPDATE `db_script_string` SET content_loc6='¡El brebaje fallido se vierte en el suelo y parece cobrar vida!' WHERE entry=2000000710;
UPDATE `db_script_string` SET content_loc6='¡Está vivo!' WHERE entry=2000000711;
UPDATE `db_script_string` SET content_loc6='¡La mezcla! ¡Se mueve!' WHERE entry=2000000712;
UPDATE `db_script_string` SET content_loc6='¡Relaja el pulso, tonto! ¡Estos son componentes raros y los estás derramando en el suelo!' WHERE entry=2000000713;
UPDATE `db_script_string` SET content_loc6='El brebaje de $N se echa a perder. Debe empezarse uno nuevo desde cero.' WHERE entry=2000000714;
UPDATE `db_script_string` SET content_loc6='¡Urtrak te mataré!' WHERE entry=2000000715;
UPDATE `db_script_string` SET content_loc6='¡La venganza es nuestra! ¡Atacad hermanos!' WHERE entry=2000000716;
UPDATE `db_script_string` SET content_loc6='Denala quita las semillas de la Fruta resplandeciente...' WHERE entry=2000000717;
UPDATE `db_script_string` SET content_loc6='Veamos como crecen estas semillas en mi suelo cultivado...' WHERE entry=2000000718;
UPDATE `db_script_string` SET content_loc6='Denalan planta semillas de la fruta.' WHERE entry=2000000719;
UPDATE `db_script_string` SET content_loc6='¡Ciénagas se alzan desde el suelo!' WHERE entry=2000000720;
UPDATE `db_script_string` SET content_loc6='Denalan se ehca sobre su planta.' WHERE entry=2000000721;
UPDATE `db_script_string` SET content_loc6='Espero que esta fronda ech2 raíces...' WHERE entry=2000000722;
UPDATE `db_script_string` SET content_loc6='%s comienza a manipular la Piedra de Relu sobre los dos fósiles.' WHERE entry=2000000723;
UPDATE `db_script_string` SET content_loc6='¿Quién ha invocado a Aman?' WHERE entry=2000000724;
UPDATE `db_script_string` SET content_loc6='Ah, veo que trasteas con reliquias del pasado.' WHERE entry=2000000725;
UPDATE `db_script_string` SET content_loc6='Te advierto que incluso tus creadores no son infalibles.' WHERE entry=2000000726;
UPDATE `db_script_string` SET content_loc6='Escarbando muy hondo en tu pasado podrías llevar tu futuro a un brusco final.' WHERE entry=2000000727;
UPDATE `db_script_string` SET content_loc6='Aman desaparece ante tus ojos.' WHERE entry=2000000728;
UPDATE `db_script_string` SET content_loc6='¡Ah, seguro que eso dio en el clavo! Creo que me conseguiré un par más... ¿puedes mirar estos barriles por mí, $N?' WHERE entry=2000000729;
UPDATE `db_script_string` SET content_loc6='¿Cómo va la observación de barriles...?' WHERE entry=2000000730;
UPDATE `db_script_string` SET content_loc6='Bueno, de vuelta al negocio. ¡Seguro que estaría bien tomarse un breve descanso, y siempre tomandose una buena Cerveza del Trueno!' WHERE entry=2000000731;
UPDATE `db_script_string` SET content_loc6='%s viste sus vestimentas de piel de lobo.' WHERE entry=2000000732;
UPDATE `db_script_string` SET content_loc6='%s aúlla respondiendo a la llamada de Malukaz.' WHERE entry=2000000733;
UPDATE `db_script_string` SET content_loc6='%s expresa su aprobación.' WHERE entry=2000000734;
UPDATE `db_script_string` SET content_loc6='Que nuestro vínculo no se rompa de nuevo, espíritu.' WHERE entry=2000000735;
UPDATE `db_script_string` SET content_loc6='¡Lo logramos! Thrall estaria orgulloso.' WHERE entry=2000000736;
UPDATE `db_script_string` SET content_loc6='¿Qué significado tiene esto, Mor\'ghor?' WHERE entry=2000000737;
UPDATE `db_script_string` SET content_loc6='¡SILENCIO!' WHERE entry=2000000738;
UPDATE `db_script_string` SET content_loc6='No digas tonterías idiota. Bufón incomprensiblemente incompetente...' WHERE entry=2000000739;
UPDATE `db_script_string` SET content_loc6='¿ESTE es tu héroe?' WHERE entry=2000000740;
UPDATE `db_script_string` SET content_loc6='Te han engañado, imbécil.' WHERE entry=2000000741;
UPDATE `db_script_string` SET content_loc6='Toda... esta... operación... ¡SE HA COMPROMETIDO!' WHERE entry=2000000742;
UPDATE `db_script_string` SET content_loc6='Espero ver el cadáver de este insecto en pedazos en mi guarida dentro de la hora. Falla y sufrirás un destino mucho peor que la muerte.' WHERE entry=2000000743;
UPDATE `db_script_string` SET content_loc6='¡No le haras daño al chico, Mor\'ghor! ¡Rápido, $N, sube en mi espalda!' WHERE entry=2000000744;
UPDATE `db_script_string` SET content_loc6='¡No me puedo creer que imbéciles como tú vallan a la escuela para esto! ¿Alguien de aquí sabe lo que están haciendo?' WHERE entry=2000000745;
UPDATE `db_script_string` SET content_loc6='¿No acabas? ¿De que has hablado tan condenadamente tiempo?' WHERE entry=2000000746;
UPDATE `db_script_string` SET content_loc6='¿Todavía es de esa maldita cosa a la que asueguras que vas?' WHERE entry=2000000747;
UPDATE `db_script_string` SET content_loc6='Verdad que no.' WHERE entry=2000000748;
UPDATE `db_script_string` SET content_loc6='¿Vas a dejar de preguntar eso?' WHERE entry=2000000749;
UPDATE `db_script_string` SET content_loc6='Muy bien, parece seguro. ¡Métete ahí, ingeniero!' WHERE entry=2000000750;
UPDATE `db_script_string` SET content_loc6='¡Vamos allá!' WHERE entry=2000000751;
UPDATE `db_script_string` SET content_loc6='Esto va ha dejar huella...' WHERE entry=2000000752;
UPDATE `db_script_string` SET content_loc6='%s se cachea para asegurarse de que está de una pieza.' WHERE entry=2000000753;
UPDATE `db_script_string` SET content_loc6='¡Lo logré! ¡Lo logré!' WHERE entry=2000000754;
UPDATE `db_script_string` SET content_loc6='%s mueve la cabeza con desaprobación.' WHERE entry=2000000755;
UPDATE `db_script_string` SET content_loc6='De cualquier modo no me gusta. Mucho hablar, poco \"hacer\"' WHERE entry=2000000756;
UPDATE `db_script_string` SET content_loc6='¡Bonito viviendo de idiotas incompetentes! ¡Otro de nuestros ingenieros vaporizado!' WHERE entry=2000000757;
UPDATE `db_script_string` SET content_loc6='¡No nos impedirás contar la verdad sobre la cobardía de Jaina!' WHERE entry=2000000758;
UPDATE `db_script_string` SET content_loc6='Si crees que puedes detenernos, te equivocas, \'amigo\'' WHERE entry=2000000759;
UPDATE `db_script_string` SET content_loc6='¡Esta gente sabrá el verdadero héroe que fue el Almirante Valiente!' WHERE entry=2000000760;
UPDATE `db_script_string` SET content_loc6='Solo con algo de edición creativa, estos resultan mucho más útiles...' WHERE entry=2000000761;
UPDATE `db_script_string` SET content_loc6='Sería una lástima dejar perder estos folletos, ¿no?' WHERE entry=2000000762;
UPDATE `db_script_string` SET content_loc6='... para crear un fondo para la compra de gel para el pelo? ¡Me gusta mi pelo así, gracias!' WHERE entry=2000000763;
UPDATE `db_script_string` SET content_loc6='... como un desertor, disfrutarás de actividades como la natación atada y cepillado de perros? ¡Qué ridículo!' WHERE entry=2000000764;
UPDATE `db_script_string` SET content_loc6='nunca me uniría a lago como esto. Es mejor dejarlo, creo. Será ultil para la letrina...' WHERE entry=2000000765;
UPDATE `db_script_string` SET content_loc6='... los desertores buscan Theramore, donde los ciudadanos són libres de hacer malabarismos a todas horas del día? ¿Qué es esta tontería?' WHERE entry=2000000766;
UPDATE `db_script_string` SET content_loc6='Es la cosa más graciosa que le leido en mucho tiempo. ¡Me has alegrado el día!' WHERE entry=2000000767;
UPDATE `db_script_string` SET content_loc6='Esto... esto es  broma, ¿verdad?' WHERE entry=2000000768;
UPDATE `db_script_string` SET content_loc6='Sonidos de pies presurosos suenan en la cueva.' WHERE entry=2000000769;
UPDATE `db_script_string` SET content_loc6='golpea las retorcidas garras.' WHERE entry=2000000999;
UPDATE `db_script_string` SET content_loc6='Yo había planeado una cumbre secreta entre el Jefe de Guerra Thrall y el Rey Wyrn, aquí en Theramore.' WHERE entry=2000001000;
UPDATE `db_script_string` SET content_loc6='El rey viajaba hasta aquí en barco, en secreto. Pero el barco nunca llegó, y el Rey Wrynn desapareció.' WHERE entry=2000001001;
UPDATE `db_script_string` SET content_loc6='Su ayuda nos permitió conectar los Defias al cerebro que hay tras el secuestro, Onyxia, que se hacía pasar como miembro de la corte real.' WHERE entry=2000001002;
UPDATE `db_script_string` SET content_loc6='Mientras tanto, el rey logró escapar de sus captores, y volvía a Ventormenta para hacer frente a Onyxia.' WHERE entry=2000001003;
UPDATE `db_script_string` SET content_loc6='Enviaré recado de lo que has descubierto del enlace de los Defias. Fui un idiota al créeles roto y derrotado a Edwin Vancleef, y el Rey Wrynn quiere escarmentar a todos los que se queden.' WHERE entry=2000001004;
UPDATE `db_script_string` SET content_loc6='Entre los cráneos ningún Yenniku, $N. Eso es excelente para mi alívio.' WHERE entry=2000001005;
UPDATE `db_script_string` SET content_loc6='¿Que fuerzas me atraviesan? ¿Quizá sea el poder chamánico, del que habló Tor\'gan? ..' WHERE entry=2000001006;
UPDATE `db_script_string` SET content_loc6='¡Increíble! Me siento renacer. ¡Obligado a volver!' WHERE entry=2000001007;
UPDATE `db_script_string` SET content_loc6='Gracias, $N.' WHERE entry=2000001008;
UPDATE `db_script_string` SET content_loc6='Yo, por supuesto, encontré aplicaciones de la mezcla...' WHERE entry=2000001009;
UPDATE `db_script_string` SET content_loc6='¡Ja! $N, ¡No se muevan de sus asientos!' WHERE entry=2000001010;
UPDATE `db_script_string` SET content_loc6='Intenta abrir la pata de palo de Maury...' WHERE entry=2000001011;
UPDATE `db_script_string` SET content_loc6='Mostrando El Ojo de la mente y Cristales de canto...' WHERE entry=2000001012;
UPDATE `db_script_string` SET content_loc6='Gemas de Alma Yenniku listas, $N...' WHERE entry=2000001013;
UPDATE `db_script_string` SET content_loc6='Quizá tenga explicación...' WHERE entry=2000001014;
UPDATE `db_script_string` SET content_loc6='Azul con rojo... Lo que me temía...' WHERE entry=2000001015;
UPDATE `db_script_string` SET content_loc6='¡Aquí está, $N! ¡Al final, lo entendí!' WHERE entry=2000001016;
UPDATE `db_script_string` SET content_loc6='Mira nervioso.' WHERE entry=2000001017;
UPDATE `db_script_string` SET content_loc6='¿Estás seguro que no ha seguido al nuestro? Tengo un mal presentimiento...' WHERE entry=2000001018;
UPDATE `db_script_string` SET content_loc6='¡Aguarda!' WHERE entry=2000001019;
UPDATE `db_script_string` SET content_loc6='¡Jamás se me hubiese ocurrido pensar que este aceite fuera maná! Oh, eso es todo lo que necesitas, a exepción de Grek, por supuesto.' WHERE entry=2000001020;
UPDATE `db_script_string` SET content_loc6='¡Vete!' WHERE entry=2000001021;
UPDATE `db_script_string` SET content_loc6='No, así no. ¿Qué pasó aquí?' WHERE entry=2000001022;
UPDATE `db_script_string` SET content_loc6='El metal perdió su mágia. Pude ver su arma, pero no diferirá de la hoja convencional.' WHERE entry=2000001023;
UPDATE `db_script_string` SET content_loc6='La información que estás buscando... No me es tan simple hacerla. Demuestre que tenemos enemigos comunes.' WHERE entry=2000001024;
UPDATE `db_script_string` SET content_loc6='Recuerda, $N, el polvo de Sanguiespora se puede usar para debilitar a todos magnatauros en Gammothra.' WHERE entry=2000001025;
UPDATE `db_script_string` SET content_loc6='¡Miserables mortales, Onyxia no contesta a nadie!' WHERE entry=2000001026;
UPDATE `db_script_string` SET content_loc6='Tú patético desafío no pasará inadvertido. Disfrutaré jugando contigo hasta tu muerte.' WHERE entry=2000001027;
UPDATE `db_script_string` SET content_loc6='¡Gracias! Ni que decir tiene lo que esas brutos me podrían haber hecho.' WHERE entry=2000001028;
UPDATE `db_script_string` SET content_loc6='Asegúrete de entrar en el santuario y salir por el paso sur, $N. Ya sabes pasa donde está la batalla para protegerlos de la abominación... mantenlos alejados.' WHERE entry=2000001030;
UPDATE `db_script_string` SET content_loc6='chamuscar los troolls de la plaga...' WHERE entry=2000001031;
UPDATE `db_script_string` SET content_loc6='...en una hoguera...' WHERE entry=2000001032;
UPDATE `db_script_string` SET content_loc6='eructa con fuerza...' WHERE entry=2000001033;
UPDATE `db_script_string` SET content_loc6='¡Eso es todo! Hasta donde yo se ... ¿Qué te parece?' WHERE entry=2000001034;
UPDATE `db_script_string` SET content_loc6='Dioses del pasado, os apelamos. ¿Qué hacemos? ¿Cómo podemos salvar a los dioses de la tierra?' WHERE entry=2000001035;
UPDATE `db_script_string` SET content_loc6='¡Excelentes cuernos! Resultaría difícil elegir cual es mejor...' WHERE entry=2000001036;
UPDATE `db_script_string` SET content_loc6='Quizá elija este cuerno. ¡Ahora sólo tienes que cortarlo correctamente! Tal vez no sea fácil. ¡Porque ni siquiera yo sabría cómo sujetar a la hembra yeti!' WHERE entry=2000001037;
UPDATE `db_script_string` SET content_loc6='Corté el cuerno, pero antes de poner en él la talla artesanal, me aseguraré de que funcione. ¿Me ayudarás a probarlo?' WHERE entry=2000001038;
UPDATE `db_script_string` SET content_loc6='¡Excelente $N! ¡Esto es algo que te deberemos! Y ahora - al trabajo.' WHERE entry=2000001039;
UPDATE `db_script_string` SET content_loc6='¡Es...suficiente por hoy!' WHERE entry=2000001040;
UPDATE `db_script_string` SET content_loc6='Recuerda, $N, este no es el carrera. Esta verificación de los cazadores, es una prueba de resistencia de una larga expedición.' WHERE entry=2000001041;
UPDATE `db_script_string` SET content_loc6='Te equivocas en todo, $N. En las historias que la Domadora del viento Barah me contó, Pezuña Tempestuosa era un héroe, no de los villanos.' WHERE entry=2000001042;
UPDATE `db_script_string` SET content_loc6='Viento del Norte – el antiguo enemigo taunka, maestro de los elementos y la misma fuerza que priva la vida de este antiguo pueblo.' WHERE entry=2000001043;
UPDATE `db_script_string` SET content_loc6='Preferirán morir antes que inclinarse ante Viento del norte. Hay algo mal.' WHERE entry=2000001044;
UPDATE `db_script_string` SET content_loc6='Martillo del conocimiento...' WHERE entry=2000001045;
UPDATE `db_script_string` SET content_loc6='Esto confirma nuestras sospechas. La línea de tiempo está dañada y tejiendo en el patrón incorrecto. Pero, ¿cómo?' WHERE entry=2000001046;
UPDATE `db_script_string` SET content_loc6='¡Oh, son pegajosas! ¿Me pregunto cómo se disparan las correas?' WHERE entry=2000001047;
UPDATE `db_script_string` SET content_loc6='¡ACK! ¡Oh, no, me enredé! ¡Es pegajosa. MI PELO!' WHERE entry=2000001048;
UPDATE `db_script_string` SET content_loc6='No te preocupes, amiga. Rokhan se encargará de todo. No hay escapatoria por ninguna parte.' WHERE entry=2000001049;
UPDATE `db_script_string` SET content_loc6='¡Espíritus escucharnos! ¿Los sientes, $N?' WHERE entry=2000001050;
UPDATE `db_script_string` SET content_loc6='El bosque se encuentra en el lado oeste de la Conquista del Invierno. Busca la sombra del corazón contráctil. Sin duda que se alegrarán de tu llegada...' WHERE entry=2000001051;
UPDATE `db_script_string` SET content_loc6='¡Mátalos a todos, $N!' WHERE entry=2000001052;
UPDATE `db_script_string` SET content_loc6='Y ahora voy a llenar este luminoso cristal, de manera que descartaremos todas las mentiras y el engaños.' WHERE entry=2000001053;
UPDATE `db_script_string` SET content_loc6='Sepamos la verdad. Si entre nosotros se esconde un asesino, y señala la dirección correcta para buscarlo.' WHERE entry=2000001054;
UPDATE `db_script_string` SET content_loc6='Escucha el informe de $N...' WHERE entry=2000001055;
UPDATE `db_script_string` SET content_loc6='¿Qué? ¿Hoja de fuego amplía el área de influencia? ¡Tenemos que explorar lo que sea!' WHERE entry=2000001056;
UPDATE `db_script_string` SET content_loc6='Al este de las Cascadas Glaciales, hermano. Trae aquí la mejor armadura que encuentres. A la Alianza no le interesa quienes de ellos atacaron.' WHERE entry=2000001057;
UPDATE `db_script_string` SET content_loc6='Escucha, caballero de la muerte... ¡Debes atender a la llamada de tu amo! ¡Él te está llamando!' WHERE entry=2000001058;
UPDATE `db_script_string` SET content_loc6='¡Guerreros de la Plaga, escuchad mi llamada! ¡Hoy daremos fin a la Orden escarlata! ¡Vaciar las tierras! ¡Afincad aquí permanentemente la muerte!' WHERE entry=2000001059;
UPDATE `db_script_string` SET content_loc6='Parad...' WHERE entry=2000001060;
UPDATE `db_script_string` SET content_loc6='Es sólo que...' WHERE entry=2000001061;
UPDATE `db_script_string` SET content_loc6='¡Cerveza! ¡La cerveza siempre!' WHERE entry=2000001062;
UPDATE `db_script_string` SET content_loc6='No hay nada raro.' WHERE entry=2000001063;
UPDATE `db_script_string` SET content_loc6='¡Hey, tú! ¡Solo trata de derramar los frijoles sobre Grick! ¿Me oyes?' WHERE entry=2000001064;
UPDATE `db_script_string` SET content_loc6='Guardan las herramientas aquí, en estas cajas. Busca en las que hay cerca de las tiendas.' WHERE entry=2000001065;
UPDATE `db_script_string` SET content_loc6='Sólo recorté esas runas, te ayudará a controlar el gigante.' WHERE entry=2000001066;
UPDATE `db_script_string` SET content_loc6='No voy a cortar las runas \"norte\" y \"oeste\" quien sabe el impacto que pueden tener en los gigantes. Aquí... sólo quedaba incluir la lana, lo determinará en cuanto que tome el control de esa criatura.' WHERE entry=2000001067;
UPDATE `db_script_string` SET content_loc6='Espero haberlo cortado bien. Ella, por supuesto, no es tan mortal como las runas de los enanos, pero también puede que algo lo sea.' WHERE entry=2000001068;
UPDATE `db_script_string` SET content_loc6='Por el amor de Dios, ¡mató a Joe el cojo y se llevó la carne! ¿Cómo sabes dónde encontrarnos?' WHERE entry=2000001069;
UPDATE `db_script_string` SET content_loc6='Bueno, si has venido a matarme, ¿al menos podrías esperar hasta que alimente a Griselda? Pobrecita no ha comido durante varios días.' WHERE entry=2000001070;
UPDATE `db_script_string` SET content_loc6='Aquí, niña. ¡Puedes durarle su última comida antes de que matemos al asesino Joe el cojo!' WHERE entry=2000001071;
UPDATE `db_script_string` SET content_loc6='¿Cómo has podido matar a la pobre Joe? ¿Y por qué traes de vuelta la carne?' WHERE entry=2000001072;
UPDATE `db_script_string` SET content_loc6='Que - una adivinanza, sí, un verdadero misterio... Aquí está... no soy muy aficionado a las adivinanzas.' WHERE entry=2000001073;
UPDATE `db_script_string` SET content_loc6='Será mejor que te vayas, mientras Griselda no te quiera de postre...' WHERE entry=2000001074;
UPDATE `db_script_string` SET content_loc6='La introducción de las runas \"Omega\" se ha completado. La activación fue exitosa.' WHERE entry=2000001075;
