-- ===================== --
--   LOCALES_PAGE_TEXT   --
-- ===================== --

SET NAMES utf8;
ALTER TABLE `locales_page_text` CONVERT TO CHARACTER SET utf8 COLLATE utf8_general_ci;
INSERT IGNORE INTO `locales_page_text` (`entry`) SELECT `entry` FROM `page_text`;

UPDATE `locales_page_text` SET `Text_loc6`='Aquí está el bote de cenizas. Estas son las cenizas de mi cordura, mi pasión, y mi conducta. Todo, cada uno de ellos absolutamente destruido. Que todos aquellos que vean estas tierras desoladas del fuego del infierno recuerden este peón caído. El derramó la sangre de la Alianza, y se sacrificó por la Horda hasta enloquecer completamente a causa de los malvados y desalmados que devoraban todo aquello que consideraba más querido. Que mientras festejen su victoria, sufran su ira. Quizá no en este mundo, pero en cualquier otro venidero. Es mi declaración, mi solemne juramento, y mi eterna promesa. Vengaré mi sufrimiento.' WHERE `entry`='1';
UPDATE `locales_page_text` SET `Text_loc6`='Desde vuestra salida de Soto Eterno, he continuado mis estudios del Libro del Cuervo y he descubierto alguna información que te ayudará a derrotar al dios cuervo.$BEstas obligado a liberar a los espíritus, así que espero que voluntariamente ofrezcan su ayuda en la lucha contra el dios cuervo.$BAl principio cuando libéreles a los espíritus, estarán inactivos, como lo han estado durante siglos. Usando tu hechizo de Sanación durante el tiempo, puedes infundirles con energía, trayéndolos a la vida y conseguir su ayuda en el combate. Cuando la magia se desvanezca, volverán a su letargo.$BLo que sigue es una colección de mis apuntes sobre cada espíritu de ave:' WHERE `entry`='2';
UPDATE `locales_page_text` SET `Text_loc6`='El Espíritu Halcón$B$BEl espíritu del halcón está impregnado con increíble velocidad y agilidad. Muy apreciado entre los Arakkoa por su hábil afición a la caza. Te favorecerá un montón con su velocidad en el lanzamiento de hechizos y combate cuerpo a cuerpo si energizas su espíritu con un hechizo de Sanación durante el tiempo.' WHERE `entry`='3';
UPDATE `locales_page_text` SET `Text_loc6`='El Espíritu del Águila$B$BLa tradición Arakkoa mantiene que el espíritu del águila no es tan rápido como el de su hermano halcón. Pero posee una mayor habilidad en la caza. El espíritu del águila utilizará sus habilidades frente a tus enemigos si utilizas un hechizo de Sanación durante el tiempo para despertarlo de su sueño.' WHERE `entry`='4';
UPDATE `locales_page_text` SET `Text_loc6`='El Espíritu del Milano$B$BLa tabla habla muy bien del Milano. Como si alguna vez hubiese competido con el cuervo. Un ancestral símbolo de venganza Arakkoa, Suele a menudo el espíritu del Milano ser invocado por las víctimas de una crueldad. El uso de un hechizo de Sanación durante el tiempo para despertar su espíritu te permitirá compartir su poder, dañando los que te hagan daño.' WHERE `entry`='5';
UPDATE `locales_page_text` SET `Text_loc6`='Hola, Morgan,$B$BLos negocios en Villadorada van muy bien, tan bien que no he tenido tiempo de hacerte ningún envío.$B$BHe encargado al portador de esta nota que entregue un paquete de velas de cera grandes (sabes a cuáles me refiero, ¿no?, las que los kóbolds suelen llevar en sus cabezas).$B$BTe ruego que se lo agradezcas a esta persona de nuestra parte y le pagues lo que sea justo.' WHERE `entry`='15';
UPDATE `locales_page_text` SET `Text_loc6`='Para el honorable rector Crillian:$B$BMi antiguo maestro, te escribo para que sepas lo que tu antiguo alumno ha estado haciendo últimamente. Siguiendo tu consejo, he procurado cultivar mis conocimientos y aumentar mi sabiduría viajando fuera de nuestra amada Ventormenta. Los viajes me han llevado a muchos lugares, pero he decidido instalarme en el bello poblado de Arroyo de la Luna. Los campos que rodean los Páramos de Poniente son más bellos a medida que se avecina la cosecha.' WHERE `entry`='16';
UPDATE `locales_page_text` SET `Text_loc6`='A los pocos días de mi visita, ya estaba dando clases a los niños que habitan los cultivos vecinos. Las lecciones tuvieron tanto éxito que el alcalde del pueblo me encargó que abriera una escuela, de manera que se inició la construcción de unas aulas totalmente nuevas. De Argénteos a Ventormenta, y ahora Arroyo de la Luna. ¡Quién iba a imaginar que conocería tanto Azeroth!$B$BUn saludo afectuoso,$B$BStalvan Mantoniebla' WHERE `entry`='17';
UPDATE `locales_page_text` SET `Text_loc6`='Querido y noble señor:$B$BA la Posada Orgullo de León de Villadorada, donde resido temporalmente, me han llegado noticias de vuestra necesidad de un tutor para vuestros hijos. Debido a los desafortunados acontecimientos que han tenido lugar en la región, me he visto obligado a abandonar mi cargo de rector de la Escuela de Arroyo de la Luna. Os ruego que aceptéis mi solicitud para trabajar como tutor de vuestros hijos. Si es necesario, el rector Crillian de la Academia os proporcionará referencias de mi trabajo.' WHERE `entry`='18';
UPDATE `locales_page_text` SET `Text_loc6`='Iré a conoceros en persona cuando las lluvias invernales amainen y los caminos vuelvan a ser transitables.$B$BHasta pronto,$B$BStalvan Mantoniebla de Argénteos' WHERE `entry`='19';
UPDATE `locales_page_text` SET `Text_loc6`='... Giles, el chico, parece un poco arrogante y su educación será todo un desafío. No obstante, Tilloa, la hija mayor, parece extraordinariamente inteligente. Por otra parte, no he podido obviar su cautivadora belleza. Está en la flor de la vida. Al parecer, el señor ha fijado su boda para el próximo año. Pero cambio de tema. Esta semana acompañaré a la familia a su casita de verano junto al aserradero de la Vega del Este en Elwynn, cerca de las montañas Crestagrana. Espero escribir con mayor asiduidad cuando esté allí.' WHERE `entry`='20';
UPDATE `locales_page_text` SET `Text_loc6`='... qué sentimiento tan extraño e incontrolable. No había sentido nunca lo que sentí hoy. Mientras ayudaba a Giles con sus lecciones de historia, Tilloa cuidaba del jardín de flores. Transcurridos unos minutos, entró en casa y colocó una begonia escarlata en la palma de mi mano y me sonrió de un modo tan especial, que sentí cómo mi corazón palpitaba con fuerza...' WHERE `entry`='21';
UPDATE `locales_page_text` SET `Text_loc6`='... estoy totalmente seguro de que ahora ella comparte los mismos sentimientos por mí. Esta mañana, incluso, posó su mano en la mía. Cuando sonríe, sus ojos brillan como diamantes. Nos entendemos con solo mirarnos. Puedo sentirla en los latidos de mi corazón y en mis acaloradas venas.' WHERE `entry`='22';
UPDATE `locales_page_text` SET `Text_loc6`='... ¡una rabia y furia como no creía que pudieran existir! ¡Cómo se atreve! Cuando enseñaba a Giles el misterio de los números, Tilloa apareció con un pretendiente, ¡los dos cogidos de la mano, y en público, nada menos! Qué joven más zafio. En lugar de presentarnos como es debido, Tilloa simplemente dijo: \"Oh, este es el tío Stalvan, mi tutor. Es un viejo muy agradable.\" ¡Viejo! Esa palabra hizo que la sangre estallara en mis mejillas. Si solo soy unos cuantos años mayor que ella y, aun así, ella me traiciona de esa manera. . .' WHERE `entry`='23';
UPDATE `locales_page_text` SET `Text_loc6`='... he entrado en una espiral de desesperación. Primero se burla de mí y ahora está comprometida. Esa charlatana descortés fingía que me amaba cuando en realidad solo deseaba herirme. Me abruma un oscuro vacío que crece con el tiempo y se hace más intenso al despertar. La sangre que derramaré palidece frente a las lágrimas que he vertido. . .' WHERE `entry`='24';
UPDATE `locales_page_text` SET `Text_loc6`='Tommy Joe,$B$BCada hora que pasa, mi corazón se marchita sin tu presencia. Oh, si nuestros padres pudieran ver más allá de los absurdos crímenes que han cometido unos contra otros, sabrían que el único verdadero crimen es el odio. El odio consume, y me temo que, en estos tiempos oscuros, se apoderará no solo de nuestras familias.$B$BSi se dieran cuenta de ello, al fin podríamos estar juntos. Sueño con que llegue ese día, vivo para ese día.$B$BCon todo mi amor,$BMaybell' WHERE `entry`='25';
UPDATE `locales_page_text` SET `Text_loc6`='Hola, William,$B$BHan pasado años desde que hablamos la última vez, pero espero que tú y tu hermano estéis bien y que prosperes como boticario.$B$BWilliam, tengo que pedirte un favor. Para ser breve, mi hijo Tommy Joe se ha enamorado locamente de la joven Maybell Maclure. Y a pesar del gran amor que siente el uno por el otro, nuestras familias... verás, nuestras familias llevan años enemistadas.' WHERE `entry`='26';
UPDATE `locales_page_text` SET `Text_loc6`='Honorable Magistrado Salomón:$B$BMe uno a usted en el dolor de los caídos. Procuraré personalmente que el Rey tenga conocimiento de la situación y mi mayor esperanza es que pueda traer refuerzos a Villa del Lago. Su Majestad ha sido algo escasa en los últimos tiempos y actúa de manera bastante extraña. Comparto esto con ustedes en confianza.$B$BHasta entonces.$B$BGeneral Marcus Jonathan Reino de Ventormenta' WHERE `entry`='27';
UPDATE `locales_page_text` SET `Text_loc6`='Argus:$B$BComo ya sabéis, estoy hasta arriba de solicitudes de reparación por parte del ejército. No puedo quejarme por tanto trabajo, pero lo cierto es que están agotando mis reservas de hierro.$B$BNo tengo suficiente hierro para herraduras. Sé que siempre dispones de abundantes reservas, por eso quería pedirte prestados 50 pares de herraduras hasta que consiga mi próximo envío de hierro.$B$BTe lo agradezco de antemano,$BVerner' WHERE `entry`='28';
UPDATE `locales_page_text` SET `Text_loc6`='El cajón lleva una nota adjunta. La nota dice:$B$B\"Verner, lamento saber que Crestagrana tiene tantos problemas. Aquí están las herraduras que necesitas. Por favor, no olvides pagarme 100 monedas de plata en cuanto te sea posible.\"$B$B\"O bien, si lo prefieres, puedes pagarme con escamas de vientre de cría (he oído que las crías de dragón son comunes en las montañas Crestagrana). Porque somos amigos... 4 escamas serán suficiente. Gracias, Argus\"' WHERE `entry`='29';
UPDATE `locales_page_text` SET `Text_loc6`='Mi señor:$B$BLa guerra se cierne sobre nosotros. La fatalidad nos acecha.$B$BComo magistrado del municipio de Villa del Lago, es mi deber informar acerca de la reciente actividad militar que ha tenido lugar en la colonia de Vuestra Majestad, situada en el extremo este.$B$BEn estas páginas encontraréis un informe sobre el estado de Villa del Lago, enclavado en las montañas Crestagrana.' WHERE `entry`='30';
UPDATE `locales_page_text` SET `Text_loc6`='La pérdida del Fuerte de Petravista nos ha afectado terriblemente. Anteriormente os informé acerca de la alianza existente entre el clan de orcos Roca Negra y el brujo conocido como Morganth. Los orcos Roca Negra lucharon salvajemente, reforzados por la magia perversa de Morganth. Muchos de los más valientes soldados de Su Majestad perecieron intentando defender el fuerte, pero finalmente no se pudo hacer nada.' WHERE `entry`='31';
UPDATE `locales_page_text` SET `Text_loc6`='Curiosamente, Morganth se volvió contra sus seguidores orcos tras su nefasta victoria. El brujo construyó una torre arcana en el noreste y usó sus poderes para crear un pequeño ejército de gnolls Pielsombra a su servicio. Tanto los orcos como los hombres fueron víctimas de las bestias viscosas que servían al brujo. En cuanto a la traición entre Morganth y Gath\'Ilzogg, Señor de la Guerra Roca Negra, solo caben especulaciones.' WHERE `entry`='32';
UPDATE `locales_page_text` SET `Text_loc6`='Para nuestra preocupación, los apestosos orcos parecen dedicarse a matar a los honrados ciudadanos de Su Majestad. Durante la última quincena, el clan Roca Negra desató una implacable ofensiva sobre Villa del Lago. Muchos hombres valientes perdieron la vida aquel aciago día. Las fuerzas de los orcos avanzaron tan rápidamente y con tanta furia que destruyeron el puente sobre el lago Sempiterno. Nuestros suministros de guerra disminuyeron ostensiblemente. Carecemos de vendas para curar las heridas de los caídos.' WHERE `entry`='33';
UPDATE `locales_page_text` SET `Text_loc6`='Ahora solicito a Su Majestad el apoyo para esta buena gente de Villa del Lago en estos aciagos momentos. Necesitamos nuevos soldados para defender el territorio, así como nuevas armas de guerra para sustituir las que hemos perdido en el campo de batalla. Asimismo, escasean los alimentos y los suministros para la construcción. Mi señor, te ruego nos ayudes a mantener en pie el último bastión que queda entre los hombres y los orcos. El enemigo debe permanecer fuera del Reino de Ventormenta.' WHERE `entry`='34';
UPDATE `locales_page_text` SET `Text_loc6`='Temo que un mal aún más siniestro y terrible se cierna sobre la caldera fétida de las tierras del norte. Resulta crucial que el Reino de Ventormenta entre en acción para evitar convertirnos en un triste recuerdo para los vivos o en esclavos del enemigo.$B$BFirmado,$B$BMagistrado Solomon$BMunicipio de Villa del Lago$BReino de Ventormenta' WHERE `entry`='35';
UPDATE `locales_page_text` SET `Text_loc6`='Honorable Magistrado Solomon:$B$BTe acompaño en el sentimiento por la muerte de nuestros compañeros. Me encargaré personalmente de informar al Rey sobre esta situación y deseo fervientemente poder llevar refuerzos a Villa del Lago. Últimamente, Su Majestad ha resultado inaccesible y actúa de un modo bastante extraño. Te comento esto por la confianza que te tengo.$B$BHasta pronto,$B$BGeneral Marcus Jonathan$BReino de Ventormenta' WHERE `entry`='36';
UPDATE `locales_page_text` SET `Text_loc6`='Honorable Magistrado Salomón:$B$BMe uno a usted en el dolor de los caídos. Procuraré personalmente que el Rey tenga conocimiento de la situación y mi mayor esperanza es que pueda traer refuerzos a Villa del Lago. Su Majestad ha sido algo escasa en los últimos tiempos y actúa de manera bastante extraña. Comparto esto con ustedes en confianza.$B$BHasta entonces.$B$BGeneral Marcus Jonathan Reino de Ventormenta' WHERE `entry`='37';
UPDATE `locales_page_text` SET `Text_loc6`='Mantorrecio:$B$BHay que tener osadía para enviar a un total desconocido a tratar el asunto de la banda Defias. Salvaste mi vida una vez y ahora la pones gravemente en peligro.$B$BNo obstante, estoy en deuda contigo. La Hermandad Defias es más grande de lo que crees. Todas las minas de los Páramos de Poniente al bosque de Elwynn están bajo su control. Kóbolds y gnolls han sido reclutados para hacer el trabajo sucio. Tienen a los goblins fabricando monstruos metálicos para colocarlos en los campos de los Páramos de Poniente y así alimentar las supersticiones de los habitantes de la zona.' WHERE `entry`='38';
UPDATE `locales_page_text` SET `Text_loc6`='Han mandado construir una extensa red subterránea. Más grande de lo que te puedes imaginar, Mantorrecio. De la Bahía del Botín hasta el Castillo de Ventormenta. Podrás ser un valiente paladín, pero no estás en tu sano juicio si crees que puedes detenerlos. Están trabajando en la construcción de armas de destrucción masiva. Después de todo, si hay algo que la hermandad de los Albañiles sabe es cómo erigir grandes construcciones. No habrás creído que se trataba de granjas de hortalizas o de viñedos, ¿verdad? Te he devuelto el favor. ¡Ahora déjame en paz!$B$B--W' WHERE `entry`='39';
UPDATE `locales_page_text` SET `Text_loc6`='Han mandado construir una extensa red subterránea. Más grande de lo que te puedes imaginar, Mantorrecio. De la Bahía del Botín hasta el Castillo de Ventormenta. Podrás ser un valiente paladín, pero no estás en tu sano juicio si crees que puedes detenerlos. Están trabajando en la construcción de armas de destrucción masiva. Después de todo, si hay algo que la hermandad de los Albañiles sabe es cómo erigir grandes construcciones. No habrás creído que se trataba de granjas de hortalizas o de viñedos, ¿verdad? Te he devuelto el favor. ¡Ahora déjame en paz!$B$B--W' WHERE `entry`='40';
UPDATE `locales_page_text` SET `Text_loc6`='Señor Mantorrecio:$B$BLa Hermandad de los Albañiles, encabezada por Edwin VanCleef, estaba compuesta por los albañiles más hábiles entre los hombres. Los Albañiles ayudaron a reconstruir los restos de la Ciudad de Ventormenta, arrasada por los orcos durante la Primera Guerra. VanCleef y sus mercaderes fueron inigualables albañiles: su obra y su arte se ponen de manifiesto en la grandeza de la Catedral de la Luz y en el propio Castillo de Ventormenta.' WHERE `entry`='41';
UPDATE `locales_page_text` SET `Text_loc6`='No obstante, los nobles de Ventormenta acumularon una gran deuda al aumentar la presencia militar del reino en Elwynn y en Tuercespina. Esta enorme deuda paralizó la economía del reino y despojó a VanCleef y a sus Albañiles de sus prometidas recompensas. Tras varios años de intenso trabajo para reconstruir el antiguo esplendor de la ciudad, los Albañiles se arruinaron y fueron olvidados por los funcionarios corruptos que la gobernaban.' WHERE `entry`='42';
UPDATE `locales_page_text` SET `Text_loc6`='Habiendo conocido a Edwin VanCleef a lo largo mi vida, puedo deciros que resulta sobrecogedor tenerlo de enemigo. Verá, él fue mi amigo de infancia y yo personalmente lo instruí en el pensamiento oscuro cuando decidió iniciar una carrera paralela a la mía. Si VanCleef dirige la Hermandad Defias, que la Luz se apiade de nuestras almas.$B$BMaestro Mathias Shaw$BHermandad de Asesinos de Ventormenta' WHERE `entry`='43';
UPDATE `locales_page_text` SET `Text_loc6`='Después de haber conocido personalmente a Edwin VanCleef durante mi vida, puedo decir que tenerlo frente como un enemigo es una tarea de enormes proporciones. Tu veras, él era mi amigo de la infancia, y yo personalmente lo entrené en los caminos de las sombras pensando que un día se podría hacer carrera a mi lado. Si VanCleef está al frente de la Hermandad Defias, que la Luz se apiade de nuestras almas.$B$BMaestro Mathias Shaw hermandad de asesinos de Ventormenta' WHERE `entry`='44';
UPDATE `locales_page_text` SET `Text_loc6`='¡Buen trabajo, colega! Ahora tienes que dirigirte al este. A los acantilados del este, al este del camino. Busca las viejas ruinas de la chimenea que están al pie del camino. Allí encontrarás un viejo barril con la siguiente pista.' WHERE `entry`='45';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando hayas llegado hasta el barril, dirígete hacia el norte. Recto como el vuelo de un cuervo, sigue tu camino hasta la tinaja vacía que está junto al molino de viento del acantilado. Si husmeas en la jarra, tal vez encuentres lo que estabas buscando.' WHERE `entry`='46';
UPDATE `locales_page_text` SET `Text_loc6`='¡Ahora que has encontrado la vieja jarra de whisky, estás a punto de dar con el tesoro! Mira hacia el oeste desde ahí y camina hasta llegar a la costa. Cuando hayas llegado al agua, ¡no te detengas! ¡Nada hacia el oeste hasta que encuentres la isla con mi cofre del tesoro!' WHERE `entry`='47';
UPDATE `locales_page_text` SET `Text_loc6`='Uñasnegras,$B$BLa mar nos aguarda. Necesitamos tus suministros procedentes del norte. Desmantélalo todo: rieles de ferrocarril, cubos de agua... Necesitamos metal. Hace tiempo que tenía que haber llegado un cargamento de BB. No me falles, Uñasnegras. Esta vez, envía tus provisiones directamente al \"granero\". No hay tiempo que perder.$B$BVanCleef' WHERE `entry`='48';
UPDATE `locales_page_text` SET `Text_loc6`='Señor Mantorrecio:$B$BEl municipio de Villa del Lago solicita humildemente la ayuda de tu milicia. Mientras escribo estas líneas, estamos bajo el asedio de los orcos Roca Negra y los gnolls Pielsombra de Morganth. No haría esta petición si nuestra situación no fuera límite. Esperamos ayuda de Ventormenta, pero mientras no la recibamos, te ruego vuestro apoyo. Si el Reino se derrumba, toda la humanidad estará en peligro.$B$BMagistrado Solomon' WHERE `entry`='49';
UPDATE `locales_page_text` SET `Text_loc6`='Su Señoría:$B$BEn los Páramos de Poniente lloramos vuestra pérdida, pero también libramos nuestra propia guerra. Los granjeros han sido atacados por ladrones y golems mecánicos. Ventormenta retiró sus tropas, dejando a la gente desprotegida. He reunido a las Milicias del Pueblo tras el abandono de Ventormenta para preservar lo que ha quedado de esta tierra. Conducir a las milicias hasta Crestagrana supone una catástrofe para los Páramos de Poniente.$B$BRecibid mis condolencias,$BGryan Mantorrecio' WHERE `entry`='50';
UPDATE `locales_page_text` SET `Text_loc6`='Señor Cerranegro:$B$BEl municipio de Villa del Lago solicita humildemente la ayuda de la Guardia Nocturna. Estamos bajo el asedio de los orcos Roca Negra y los gnolls Pielsombra de Morganth. No haría esta petición si nuestra situación no fuera límite. Esperamos ayuda de Ventormenta, pero hasta que la recibamos, os ruego vuestro apoyo. Si el Reino se resquebraja, toda la humanidad estará en peligro.$B$BMagistrado Solomon' WHERE `entry`='51';
UPDATE `locales_page_text` SET `Text_loc6`='Su Señoría:$B$BLamento informaros que en estos momentos, la Guardia Nocturna no puede abandonar su posición en Villa Oscura. Tal vez no os han informado que Ventormenta ya no presta apoyo militar en esta región. La Guardia Nocturna está formada en su totalidad por los habitantes del bosque para defender Villa Oscura en ausencia de Ventormenta. Sin la Guardia Nocturna, la ciudad sucumbiría. Os deseo suerte en vuestra crisis.$B$BLord Ello Cerranegro' WHERE `entry`='52';
UPDATE `locales_page_text` SET `Text_loc6`='Las letras de esta nota parecen parpadear y bailar sobre la superficie. Resulta imposible encontrarles significado...' WHERE `entry`='53';
UPDATE `locales_page_text` SET `Text_loc6`='Saludos, Ello Cerranegro, Alcalde de Villa Oscura. Me temo que traigo noticias para tu ciudad. Noticias muy serias.$B$BSabes, soy un creador. He engañado al portador de esta nota para que me ayudase en mi creación definitiva: ¡un demonio de carne, hueso y metal retorcido! Cuando leas esto, esta bestia estará junto a mi humilde morada, rechinando sus dientes y esperando mis órdenes para masacrar toda criatura viviente.$B$BPronto tendrás noticias mías.$B$BEl embalsamador' WHERE `entry`='54';
UPDATE `locales_page_text` SET `Text_loc6`='Estas son las escrituras de un terreno en la región de los Páramos de Poniente. Otorga a los firmantes derechos de propiedad sobre el área en cuestión, así como sobre todos los objetos fabricados y todas las estructuras construidas dentro de sus fronteras.$B$BFirman este documento:$B$BTheodore Cejade$BVerna Cejade' WHERE `entry`='55';
UPDATE `locales_page_text` SET `Text_loc6`='[Las palabras que aparecen a continuación fueron garabateadas en la otra cara de las escrituras]$B$BPresionamos a Cejade y conseguimos estas escrituras. Pensé que, si quieres falsificar una para ti, te vendría bien. Los Cejade no nos darán problemas. La última vez que los vi, abandonaban los Páramos de Poniente con un carro roto.' WHERE `entry`='56';
UPDATE `locales_page_text` SET `Text_loc6`='[Las palabras que aparecen a continuación fueron garabateadas en la otra cara de las escrituras]$B$BPresionamos a Cejade y conseguimos estas escrituras. Pensé que, si quieres falsificar una para ti, te vendría bien. Los Cejade no nos darán problemas. La última vez que los vi, abandonaban los Páramos de Poniente con un carro roto.' WHERE `entry`='57';
UPDATE `locales_page_text` SET `Text_loc6`='Brilla la luna sobre el valle,$B$Bresplandeciendo sobre la selva$B$Bvalientes guerreros escuchan el clamor$B$Bpara defender nuestra nación y sus sagradas tierras.$B$B$B$BBrilla la luna sobre el valle$B$Ben lo alto, sobre el fragor de la batalla$B$Ballí donde se funde la sangre$B$Bdel enemigo con la del compañero.' WHERE `entry`='58';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando nuestros hermanos marchen$B$Bhacia reinos de otro mundo,$B$Bel alma y el espíritu se curtirán$B$Ben las profundidades del valle.$B$B$B$BCuando nuestros hermanos marchen$B$Bhasta el templo de la montaña,$B$Bprotegeremos su espíritu eterno$B$Btallado en el sagrado añil del cristal.$B$B$B$BCuando nuestros hermanos marchen,$B$Bla luna brillará sobre el valle.$B$B' WHERE `entry`='59';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando nuestros hermanos marchen$B$Bhacia reinos de otro mundo,$B$Bel alma y el espíritu se curtirán$B$Ben las profundidades del valle.$B$B$B$BCuando nuestros hermanos marchen$B$Bhasta el templo de la montaña,$B$Bprotegeremos su espíritu eterno$B$Btallado en el sagrado añil del cristal.$B$B$B$BCuando nuestros hermanos marchen,$B$Bla luna brillará sobre el valle.$B$B' WHERE `entry`='61';
UPDATE `locales_page_text` SET `Text_loc6`='Bajo la luna y el fuego,$Ben la carne y el hueso,$Bescrito con sangre,$Bgrabado en la piedra.$B$BAbandona este lugar$Bo aguarda tu fin.$BLa muerte vigila$Bla tumba del Emperador.' WHERE `entry`='62';
UPDATE `locales_page_text` SET `Text_loc6`='Maestro Carevin,$B$B$GEl portador:La portadora; de esta nota ha probado su integridad al amparo de la Luz, demostrando su destreza en el combate contra los no-muertos y los demonios que asolan las lindes del Bosque del Ocaso, $Gdeseoso:deseosa; de unirse a la familia Carevin en la causa de la Luz.$B$BPor esta razón sugiero que asignes a $Geste guerrero:esta guerrera; de la Luz nuevas tareas, por ejemplo, investigar a algunos de los habitantes del lugar, sospechosos de colaborar con los enemigos de la Luz.$B$BTe saluda atentamente,$BCalor' WHERE `entry`='63';
UPDATE `locales_page_text` SET `Text_loc6`='Morgan Ladimor fue un noble caballero que luchó en defensa de los inocentes, los pobres y los afligidos. Durante años, trabajó diligentemente en la periferia de Azeroth, aliviando a las víctimas y castigando a los malhechores.$B$BEn el verano en el que cumplió dieciocho años, contrajo matrimonio con una joven llamada Lys. Estaban muy enamorados y tuvieron tres hijos, un varón y dos chicas.$B$BMorgan tenía treinta y dos años cuando estalló la guerra en' WHERE `entry`='64';
UPDATE `locales_page_text` SET `Text_loc6`='Lordaeron. Morgan fue reclutado por el legendario Uther el Iluminado para combatir contra los orcos y los no-muertos. Tras dejar a su mujer y a sus hijos a salvo en su hogar, Morgan partió hacia la guerra.$B$BPasaron los años y la guerra se prolongó; Morgan presenció espantosas escenas, incluyendo la desbandada de los paladines de la Mano de Plata, la muerte de Uther y la propagación de la peste. Lo único que lo mantenía a salvo de la locura era la certeza de que algún día' WHERE `entry`='65';
UPDATE `locales_page_text` SET `Text_loc6`='se reuniría con su mujer y sus hijos.$B$BMorgan regresó finalmente a su hogar, pero lo que encontró nada tenía que ver con lo que recordaba. El bosque, antes verdeante, ahora estaba contaminado, infestado de no-muertos y otras fuerzas oscuras. Por doquier había casas y granjas destruidas, y el Cementerio del Cerro del Cuervo cubría gran parte del territorio. Morgan, indignado y perplejo, llegó hasta su casa, encontrándola en ruinas. Ignorando lo que le había ocurrido a su tierra,' WHERE `entry`='66';
UPDATE `locales_page_text` SET `Text_loc6`='se dirigió al pueblo para encontrar respuesta, deseoso de hallar a su mujer y a sus hijos.$B$BMorgan preguntó por su familia, pero no recibió respuesta alguna. Un sacerdote de Villa Oscura, nombre con el que ahora se designaba al lugar, le sugirió que buscara una lápida en el Cementerio del Cerro del Cuervo. Morgan se negó a aceptar que su familia había muerto y continuó buscando en vano por todas las granjas y casas de Bosque del Ocaso.$B$BAsí, cabalgó desde Villa Oscura hasta las afueras de Villa del Lago, deseando que su familia hubiera podido escapar. En el camino,' WHERE `entry`='67';
UPDATE `locales_page_text` SET `Text_loc6`='obviando el sentido común, decidió parar en el cementerio del Cerro del Cuervo. Morgan paseó durante horas entre las lápidas. Reconoció los nombres de muchas personas que había conocido y cada vez estaba más consternado por el dolor. Entonces los vio: una descuidada parcela en medio de tantas otras, con tres pequeñas lápidas. A medida que se acercaba, una ola de pánico se apoderaba de él. Morgan quitó el polvo de la lápida más grande para leer el nombre grabado en su superficie. Las letras sobre la tumba constataron' WHERE `entry`='68';
UPDATE `locales_page_text` SET `Text_loc6`='el temido presagio:$B$BLys Ladimor$BAmada esposa y madre$B$BEl temor de Morgan se transformó en consternación y luego en dolor, postrándolo de rodillas mientras vertía lágrimas amargas. Permaneció durante horas frente a la tumba, suplicando perdón a la fría piedra y disculpándose entre sollozos. Luego, horas más tarde, algo se resquebrajó en su interior y la emprendió a golpes contra todo lo que lo rodeaba. Desenfundó la espada y empezó a golpear las lápidas una y otra vez, gritando de rabia. Ciego de ira, intentaba golpear algo y arremetía contra todo, llamando la atención' WHERE `entry`='69';
UPDATE `locales_page_text` SET `Text_loc6`='de tres guardas del cementerio. Cuando intentaron detenerlo, se abalanzó sobre ellos, acusando a los pobres guardas inocentes, y luego les dio muerte.$B$BMás tarde, cuando la ira se había disipado, Morgan tomó conciencia de lo ocurrido y vio su espada ensangrentada, clavada en el pecho de uno de los guardas. Al borde del límite, extrajo el puñal de su cinturón y lo hundió en su propio pecho.$B$BEl cuerpo de Morgan Ladimor y los de sus víctimas fueron hallados' WHERE `entry`='70';
UPDATE `locales_page_text` SET `Text_loc6`='al día siguiente. Fue enterrado rápidamente, sin ceremonia, en una tumba excavada en las afueras del cementerio. Puesto que había cometido asesinato contra personas inocentes, algo que iba totalmente en contra de sus creencias y su naturaleza, y por el dolor que sentía por haber sido incapaz de salvar a su familia, Morgan no tuvo una muerte pacífica y vagó por el mundo como un muerto viviente.$B$BPocos días más tarde, su tumba fue profanada y su cuerpo había desaparecido. Quien un día fuera Morgan ahora' WHERE `entry`='71';
UPDATE `locales_page_text` SET `Text_loc6`='vaga por el Bosque del Ocaso, consumido por la pena de haber perdido a su mujer y sus hijos, así como por el desprecio hacia sí mismo. Mor\'Ladim, nombre actual de Morgan, vaga ciego de venganza y odio por el Bosque del Ocaso, cometiendo asesinatos indiscriminadamente.' WHERE `entry`='72';
UPDATE `locales_page_text` SET `Text_loc6`='El terror de estas últimas semanas ha supuesto más de lo que puedo soportar, sin embargo, siento que escribir lo que he vivido me reconforta en cierta medida. Por eso lo hago y es el único consuelo que tengo en estos días aciagos.' WHERE `entry`='73';
UPDATE `locales_page_text` SET `Text_loc6`='PRIMER DÍA$B$BMi aprendiz y yo hemos llegado hoy al Valle de Crestanevada. El recorrido por la caverna prácticamente no tuvo incidencias. Acampamos bastante lejos de la cueva en la que, según nos dijeron, se habían reunido los trols.$B$BSEGUNDO DÍA$B$BEl aullido de un maldito lobo me mantuvo despierto toda la noche.$B$BEn un par de días tendré un nuevo traje de piel de lobo.$B$BEl chico se enfrascó en una pelea con un trogg hoy y' WHERE `entry`='74';
UPDATE `locales_page_text` SET `Text_loc6`='PRIMER DÍA$B$BMi aprendiz y yo hemos llegado hoy al Valle de Crestanevada. El recorrido por la caverna prácticamente no tuvo incidencias. Acampamos bastante lejos de la cueva en la que, según nos dijeron, se habían reunido los trols.$B$BSEGUNDO DÍA$B$BEl aullido de un maldito lobo me mantuvo despierto toda la noche.$B$BEn un par de días tendré un nuevo traje de piel de lobo.$B$BEl chico se enfrascó en una pelea con un trogg hoy y' WHERE `entry`='77';
UPDATE `locales_page_text` SET `Text_loc6`='Este marcador de armaduras es útil para marcar una armadura de cuero o tela, canjeable en el la casa de los Timberlain, en el Aserradero de la Vega del Este.' WHERE `entry`='78';
UPDATE `locales_page_text` SET `Text_loc6`='Abajo se describe el proceso y el programa de excavación y transporte del oro de las minas de Elwynn a nuestro puesto de mando en los Páramos de Poniente.$B$BPrograma de excavación:$B$BDomingo: 12.30$BMiércoles: 12.30$B$BDurante todos los días señalados, el oro extraído de las minas de Elwynn se recogerá en la plantación de calabazas de Brackwell. El agente encargado de estos cargamentos o \"Recolector\" se reconocerá por su anillo grabado. Un anillo que yo le di.' WHERE `entry`='79';
UPDATE `locales_page_text` SET `Text_loc6`='Grelin:$B$BMe queda poco tiempo y muchos asuntos me preocupan, así que espero que tu investigación sobre los trols no sea una preocupación más. Por esta razón, permitiré que utilices mi autoridad como mejor te parezca para tratar con los trols, sobre todo si te permite encontrar una solución oportuna.$B$B$BMagni Barbabronce' WHERE `entry`='80';
UPDATE `locales_page_text` SET `Text_loc6`='logró extraer lo mejor de la bestia espantosa, pero ahora se está curando de una herida inmunda en el brazo. Todo indica que tendré que ocuparme de las tareas domésticas durante un tiempo.$BTERCER DÍA$B$BHace frío. Ha nevado un poco cerca del mediodía.$B$BCUARTO DÍA$B$BEl chico volvió hoy y de buen humor, así que echamos un primer vistazo a los trols. Pertenecían al clan Peloescarcha, a juzgar por las marcas en la piel y los diversos abalorios que llevaban en el cuerpo.' WHERE `entry`='81';
UPDATE `locales_page_text` SET `Text_loc6`='QUINTO DÍA$B$BEncargué al chico que vigilara el campamento mientras yo husmeaba en la cueva y echaba un vistazo por los alrededores. Son muchos trols y esto genera ciertos problemas. Tal vez haya que convocar a los montaraces por si resulta necesario limpiar la cueva de la plaga de trols.$BSoy consciente de no haber mencionado a los troggs últimamente. Aparecen por la zona en grandes grupos, pero son tan primitivos que no suponen una amenaza. El tiempo dirá si esto es verdad o no.' WHERE `entry`='82';
UPDATE `locales_page_text` SET `Text_loc6`='Ni gota de cerveza durante casi una semana.$B$BSEXTO DÍA$B$BVuelve a hacer frío. Nieva. Trols y nada de cerveza.$B$BSÉPTIMO DÍA$B$BLa nieve es blanca,$Bel cielo es añil,$Blos lobos aúllan de noche,$By yo sin cerveza de barril.$B$BOCTAVO DÍA$B$BQuizás los trols no representen un problema tan grande como el que inicialmente supusimos. Son bastantes,' WHERE `entry`='83';
UPDATE `locales_page_text` SET `Text_loc6`='pero no están bien armados ni organizados. Una pequeña demostración de fuerza deberá ser más que suficiente para neutralizarlos.' WHERE `entry`='84';
UPDATE `locales_page_text` SET `Text_loc6`='Mi diario:$B$BEl terror experimentado durante las últimas semanas casi supera lo que soy capaz de soportar. ¿Por qué cuando lo escribo todo en este libro, de alguna manera evito caer en la locura? Quizás sea como confesar mis pecados a un compañero silencioso, liberando mi mente de tortuosos pensamientos y confinándolos en el papel.$B$BEmpecé un diario antes de escribir este, pero permanece en un lugar al que no podré regresar. Por eso empezaré desde cero. Pero esta vez, empezaré desde el verdadero principio.' WHERE `entry`='85';
UPDATE `locales_page_text` SET `Text_loc6`='Informe sobre el estado de los trols Peloescarcha en el área general del Valle de Crestanevada$B$BElaborado por Grelin Barbablanca, enviado especial del senado$B$BDesde el momento en que empecé a observar el movimiento de los trols Peloescarcha en el área del Valle de Crestanevada, he determinado que no representan una especial amenaza para los asentamientos enanos de la zona. De hecho, constituyen esa clase de amenazas que pueden eliminarse con un pequeño apoyo adicional por parte del ejército. Con la ayuda de los montaraces instalados en' WHERE `entry`='86';
UPDATE `locales_page_text` SET `Text_loc6`='el Valle de Crestanevada y unos cuantos mercenarios (pagados con fondos apartados por el senado antes de mi envío), confío en que el problema se resolverá muy pronto.$B$BEsta acción ha sido autorizada mediante la sanción que me otorgó el rey Barbabronce.' WHERE `entry`='87';
UPDATE `locales_page_text` SET `Text_loc6`='Informe sobre el estado de los trols Peloescarcha en el área general de Dun Morogh$B$BLos trols situados en Dun Morogh están fundamentalmente ubicados en el poblado Peloescarcha, una cueva de montaña en la frontera oeste. Son lo suficientemente numerosos como para causar cierta preocupación, no obstante, parecen estar más que contentos dentro de su cueva. Sin duda, esto se debe a que no desean volver a desatar la ira de los enanos, arriesgándose al exterminio total de su raza. Sus acciones pueden considerarse de carácter territorial, por así decirlo, y creo' WHERE `entry`='88';
UPDATE `locales_page_text` SET `Text_loc6`='que no suponen una verdadera amenaza para nosotros, siempre que no invadamos su territorio. Esta situación puede resultar poco agradable para el pueblo de los enanos, pero dada la dispersión de los recursos militares, puede ser prudente relegar el exterminio de los trols a un segundo plano, para poder centrarnos en la amenaza de los troggs y los Hierro Negro.$B$BAdjunto copia del informe de mi hermano Grelin sobre Yunquemar.$B$BFirmado,$BSenir Barbablanca' WHERE `entry`='89';
UPDATE `locales_page_text` SET `Text_loc6`='Informe sobre el estado de los trols Peloescarcha en el área general del Valle de Crestanevada$B$BElaborado por Grelin Barbablanca, enviado especial del senado$B$BDesde el momento en que empecé a observar el movimiento de los trols Peloescarcha en el área del Valle de Crestanevada, he determinado que no representan una especial amenaza para los asentamientos enanos de la zona. De hecho, constituyen esa clase de amenaza que puede eliminarse con un pequeño apoyo adicional por parte del ejército. Con la ayuda de los montaraces instalados en' WHERE `entry`='90';
UPDATE `locales_page_text` SET `Text_loc6`='el Valle de Crestanevada y unos cuantos mercenarios (pagados con fondos apartados por el senado antes de mi envío), confío en que el problema se resolverá muy pronto.$B$BEsta acción ha sido autorizada por la sanción otorgada por el rey Barbabronce.' WHERE `entry`='91';
UPDATE `locales_page_text` SET `Text_loc6`='Mi querido Tarrel:$B$BTe ruego disculpes las prisas con las que escribo esta carta, pero el tiempo no es un lujo del que dispongamos por aquí.$B$BEl equipo de excavación ha realizado grandes avances desenterrando importantes artefactos de los titanes. Pero empezamos a descubrir otros objetos antiguos, especialmente huesos largos.' WHERE `entry`='92';
UPDATE `locales_page_text` SET `Text_loc6`='Poco después de excavar los huesos, fuimos víctimas del ataque de raptores. El batallón de hombres del regimiento Largatrenza luchó valientemente. Pero, finalmente, fueron arrollados. Casi todos murieron. Solo 3 de nosotros sobrevivimos. Un obrero llamado Ormer Trenzaferro demostró mayor valentía, pues se encargó de velar por mi seguridad y la del prospector Whelgar.' WHERE `entry`='93';
UPDATE `locales_page_text` SET `Text_loc6`='Ahora nos hemos refugiado en una cueva protegida, al parecer a salvo del peligro. Pero, sin duda, hasta que no recibamos refuerzos para hacer frente a los raptores, estamos atrapados. Whelgar intenta continuar trabajando, pero sospecho que sigue bastante asustado. Esa es nuestra situación, Tarrel. Dile a Largatrenza que reúna un comando de rescate inmediatamente.$B$BMi amor por ti sigue siendo fuerte, incluso en estos azarosos tiempos.$B$BMerrin' WHERE `entry`='94';
UPDATE `locales_page_text` SET `Text_loc6`='Las verdes colinas de Tuercespina$B$BNuestro primer día transcurrió perfectamente, como era de esperar. La mayor parte del tiempo lo dedicamos a instalar el campamento base. Encontré un paraje ideal junto a un río de aguas cristalinas. A juzgar por los muelles viejos y abandonados, este lugar fue habitado en el pasado. En cuanto a los primeros habitantes, su historia solo puede contarse ahora que el tiempo ha transcurrido.' WHERE `entry`='95';
UPDATE `locales_page_text` SET `Text_loc6`='Las verdes colinas de Tuercespina$B$BNuestro primer día transcurrió perfectamente, como era de esperar. La mayor parte del tiempo lo dedicamos a instalar el campamento base. Encontré un paraje ideal junto a un río de aguas cristalinas. A juzgar por los muelles viejos y abandonados, este lugar fue habitado en el pasado. En cuanto a los primeros habitantes, su historia solo puede contarse ahora que el tiempo ha transcurrido.' WHERE `entry`='97';
UPDATE `locales_page_text` SET `Text_loc6`='En los años a venir, después de que Ventormenta traicionara a la Hermandad de los Albañiles, Sir Elrgadin estaba cada vez menos de acuerdo con el papel de los nobles en el Reino. Ya no deseaba ostentar la posición que la línea de sangre de su padre le había legado en la Casa de Nobles.$B$BPero me estoy apartando del tema. Esta historia no pretende ser un tratado político o una biografía. Este es el relato de mis experiencias de caza en las verdes colinas de Tuercespina.' WHERE `entry`='98';
UPDATE `locales_page_text` SET `Text_loc6`='En los años a venir, después de que Ventormenta traicionara a la Hermandad de los Albañiles, Sir Elrgadin estaba cada vez menos de acuerdo con el papel de los nobles en el Reino. Ya no deseaba ostentar la posición que la línea de sangre de su padre le había legado en la Casa de Nobles.$B$BPero me estoy apartando del tema. Esta historia no pretende ser un tratado político o una biografía. Este es el relato de mis experiencias de caza en las verdes colinas de Tuercespina.' WHERE `entry`='99';
UPDATE `locales_page_text` SET `Text_loc6`='Justo cuando iba a cuestionar la falta de interés de Ajeck por la estrategia a seguir ese día, ella extrajo una flecha de su carcaj y la lanzó directamente hacia el pobre Barnil. Sin embargo, Barnil no era el objetivo de Ajeck. Cuando este se apartó boquiabierto, un gran crocolisco de río flotaba en la superficie con la flecha perfectamente clavada en medio de sus grandes ojos.' WHERE `entry`='100';
UPDATE `locales_page_text` SET `Text_loc6`='Justo cuando iba a cuestionar la falta de interés de Ajeck por la estrategia a seguir ese día, ella extrajo una flecha de su carcaj y la lanzó directamente hacia el pobre Barnil. Sin embargo, Barnil no era el objetivo de Ajeck. Cuando este se apartó boquiabierto, un gran crocolisco de río flotaba en la superficie con la flecha perfectamente clavada en medio de sus grandes ojos.' WHERE `entry`='101';
UPDATE `locales_page_text` SET `Text_loc6`='Durante semejante tropiezo, Erlgadin posó su mano pesadamente sobre el hombro de Barnil. Ajeck y yo apenas advertimos la escena, suponiendo que el hombre solo estaba dando una merecida reprimenda a Barnil por su descuido. No obstante, Erlgadin, hizo un gesto lento con la cabeza en dirección a un árbol que estaba a punto de caer. Desde allí nos acechaban dos ojos negros penetrantes justo encima de un par de afilados colmillos.' WHERE `entry`='102';
UPDATE `locales_page_text` SET `Text_loc6`='Durante semejante tropiezo, Erlgadin posó su mano pesadamente sobre el hombro de Barnil. Ajeck y yo apenas advertimos la escena, suponiendo que el hombre solo estaba dando una merecida reprimenda a Barnil por su descuido. No obstante, Erlgadin, hizo un gesto lento con la cabeza en dirección a un árbol que estaba a punto de caer. Desde allí nos acechaban dos ojos negros penetrantes justo encima de un par de afilados colmillos.' WHERE `entry`='103';
UPDATE `locales_page_text` SET `Text_loc6`='La caza generó euforia en la expedición. Barnil sirvió hidromiel a todos los presentes. Sin embargo, la alegría duró poco. Cuando preparábamos el cadáver para llevarlo al campamento base, un espantoso gruñido nos cogió desprevenidos. Jamás había oído nada capaz de helarme la sangre como aquello.' WHERE `entry`='104';
UPDATE `locales_page_text` SET `Text_loc6`='En un rocoso precipicio en lo alto, perfilado por el sol poniente, podía distinguir el felino depredador más grande que he visto jamás. Pude liberar una descarga con mi rifle, pero el felino siguió su camino. Rugió una vez más, en esta ocasión vez con más fuerza que la anterior, y desapareció.$B$BRecogimos nuestras pertenencias y nos dirigimos solemnemente de vuelta al campamento.' WHERE `entry`='105';
UPDATE `locales_page_text` SET `Text_loc6`='En un rocoso precipicio en lo alto, perfilado por el sol poniente, podía distinguir el felino depredador más grande que he visto jamás. Pude liberar una descarga con mi rifle, pero el felino siguió su camino. Rugió una vez más, en esta ocasión vez con más fuerza que la anterior, y desapareció.$B$BRecogimos nuestras pertenencias y nos dirigimos solemnemente de vuelta al campamento.' WHERE `entry`='107';
UPDATE `locales_page_text` SET `Text_loc6`='Aquel día nos internamos hacia el sur, siguiendo las pistas recientes de una pantera. Pronto encontramos un enorme puente de soga que atravesaba un barranco. Cuando vi aquella magnífica obra de ingeniería, no pude evitar recordar los textos descriptivos de Brann sobre esta región. Con demasiada frecuencia se decía que los trols que habitaban aquella zona eran una raza primitiva e inculta, pero cuando vi la maestría con que había sido construido aquel puente, pude admirar la destreza de los trols que habían realizado aquella hazaña inimaginable.' WHERE `entry`='108';
UPDATE `locales_page_text` SET `Text_loc6`='Aquel día nos internamos hacia el sur, siguiendo las pistas recientes de una pantera. Pronto encontramos un enorme puente de soga que atravesaba un barranco. Cuando vi aquella magnífica obra de ingeniería, no pude evitar recordar los textos descriptivos de Brann sobre esta región. Con demasiada frecuencia se decía que los trols que habitaban aquella zona eran una raza primitiva e inculta, pero cuando vi la maestría con que había sido construido aquel puente, pude admirar la destreza de los trols que habían realizado aquella hazaña inimaginable.' WHERE `entry`='109';
UPDATE `locales_page_text` SET `Text_loc6`='Tanto Ajeck como Sir Erlgadin se mantuvieron erguidos, con las armas al nivel de la hirsuta maleza que había bajo unos árboles que se mecían de un lado a otro. El sol del mediodía ardía con fuerza sobre nosotros. Una lenta gota de sudor rodó por la sien de Erlgadin, al tiempo que retiraba el gatillo. Tras el chasquido, las hojas exuberantes se dividieron y una pantera negra, espécimen hermoso, saltó veloz sobre la llanura.' WHERE `entry`='110';
UPDATE `locales_page_text` SET `Text_loc6`='Tanto Ajeck como Sir Erlgadin se mantuvieron erguidos, con las armas al nivel de la hirsuta maleza que había bajo unos árboles que se mecían de un lado a otro. El sol del mediodía ardía con fuerza sobre nosotros. Una lenta gota de sudor rodó por la sien de Erlgadin, al tiempo que retiraba el gatillo. Tras el chasquido, las hojas exuberantes se dividieron y una pantera negra, espécimen hermoso, saltó veloz sobre la llanura.' WHERE `entry`='111';
UPDATE `locales_page_text` SET `Text_loc6`='El arma dio una violenta sacudida. El cañón rodó lateralmente y cayó bajo el rifle de Ajeck. Ajeck había elegido ese preciso momento para apretar el gatillo. El rifle, con el mira orientada torpemente hacia la fila de árboles, se disparó con un estruendo inconfundible. Una bandada de pájaros chilló desde la copa de un árbol, dispersándose por todas partes. Una columna de humo emergió del árbol. Sobrecogidos, vimos cómo una tremenda rama caía sobre la pantera que intentaba escapar, partiéndole el lomo.' WHERE `entry`='112';
UPDATE `locales_page_text` SET `Text_loc6`='El arma dio una violenta sacudida. El cañón rodó lateralmente y cayó bajo el rifle de Ajeck. Ajeck había elegido ese preciso momento para apretar el gatillo. El rifle, con el mira orientada torpemente hacia la fila de árboles, se disparó con un estruendo inconfundible. Una bandada de pájaros chilló desde la copa de un árbol, dispersándose por todas partes. Una columna de humo emergió del árbol. Sobrecogidos, vimos cómo una tremenda rama caía sobre la pantera que intentaba escapar, partiéndole el lomo.' WHERE `entry`='113';
UPDATE `locales_page_text` SET `Text_loc6`='Nos fuimos al alba en dirección al sur, más allá de las ruinas de Tkashi. Barnil expresó su preocupación sobre la posibilidad de encontrarnos con miembros de la tribu Sangrapellejo. Recordé a Barnil que los Sangrapellejo estaban ocupados intentando destruir a su enemigo tribal, los Machacacráneos. De más está decir que Barnil no estaba en absoluto tranquilo. Yo, por mi parte, tenía el rifle cargado, un saco lleno de pólvora y tres cazadores infalibles que me acompañaban para aplacar mis preocupaciones sobre una posible emboscada.' WHERE `entry`='114';
UPDATE `locales_page_text` SET `Text_loc6`='Una vez, en el campo de batalla, apareció un infernal altísimo, con el ejército de la Legión Ardiente avanzando desde todas las direcciones. Una banda de trols revoltosos parece tan inofensiva como una liebre en las colinas de Dun Morogh.$B$BPasamos por las ruinas de Tkashi sin contratiempos, lo que tranquilizó a Barnil. El grupo avanzó hacia el oeste, en dirección al Mare Magnum, bordeando las ruinas de Zul\'Kunda, situadas al sur. Al subir por los altos acantilados, divisamos el primer raptor.' WHERE `entry`='115';
UPDATE `locales_page_text` SET `Text_loc6`='Me detuve frente a una torre infernal en el campo de batalla, el ejército de la Legión Ardiente avanza desde todas direcciones. Una panda de rebeldes Trolls parecen tan inofensivos como una liebre en las montañas de Dun Morogh. Pasamos por las Ruinas Tkashi sin novedades, con gran alivio para Barnil. La partida continuó dirigiéndose hacia el oeste, hacia el Gran Mar, bordeando las ruinas de Zul\'Kunda justo al sur. Conforme nos adentramos en las crestas de alta mar vimos nuestro primer Raptor.' WHERE `entry`='117';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestros disparos, que dirigimos apresuradamente, fueron suficientes para comprar la huida de Barnil. Barnil lanzó un clamor colina abajo y se volvió a unir al grupo. Corrimos a refugiarnos en la jungla; una manada de feroces raptores colazote acechaba todos nuestros movimientos.$B$BLos cazadores eran, ahora, los cazados.' WHERE `entry`='118';
UPDATE `locales_page_text` SET `Text_loc6`='Con el fin de que la costa nos protegiera de los raptores, conduje a la expedición hasta el mar. En nuestro apresuramiento, nos desviamos y acabamos en el norte, en un monte peligrosamente elevado. Pero nos equivocamos. Fue culpa mía. Nos detuvimos delante de un escarpado acantilado, con los raptores justo detrás de nosotros.' WHERE `entry`='119';
UPDATE `locales_page_text` SET `Text_loc6`='Avancé lentamente con el arma levantada. Había conducido a estos valientes cazadores a su fin. Tenía que morir defendiéndolos. Los raptores colazote son particularmente feroces, conocidos por ser unos sanguinarios implacables. Eran muchos más que nosotros. Pero si hubiera dejado que nos mataran a mis camaradas y a mí, sin antes derramar parte de su propia sangre, no me lo habría perdonado.' WHERE `entry`='120';
UPDATE `locales_page_text` SET `Text_loc6`='Ajeck y Sir Erlgadin prepararon sus armas, flanqueándome a ambos lados, con las espaldas orientadas al mar. Barnil lanzó un suspiro de derrota y sacó su hacha. Los colazotes estaban casi encima de nosotros. Sus firmes zancadas eran ahora más pausadas. Estaban observando a su presa, ya que sabían que nos tenían acorralados.' WHERE `entry`='121';
UPDATE `locales_page_text` SET `Text_loc6`='Si ves necesario mi hechizo para invocar al gran Cyclonian. Que mi suerte te acompañe.$BBath\'rah el Vigía del viento.' WHERE `entry`='149';
UPDATE `locales_page_text` SET `Text_loc6`='Ajeck y Sir Erlgadin prepararon sus armas, flanqueándome por ambos lados, de espaldas al mar. Barnil abatido dejó escapar un suspiro y sacó su hacha. Los Calazote ya los tenemos encima. Había decaído su paso firme paso. Aguardaban a su presa, ahora que sabían que nos tenían acorralados.' WHERE `entry`='152';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestro primer día fue todo lo bien que se puede esperar que vayan los primeros días. La mayor parte del tiempo nos preocupamos de realizar los preparativos necesarios para establecer un campamento base. Encontré un lugar ideal, junto a la ensenada de un río de agua dulce. A juzgar por los viejos y abandonados muelles que hay cerca, este emplazamiento estuvo habitado hace algún tiempo. Por lo que respecta a los habitantes primitivos, solo el tiempo puede contar esa historia.' WHERE `entry`='153';
UPDATE `locales_page_text` SET `Text_loc6`='Para esta expedición he reunido a Ajeck Rouack y a Sir S. J. Erlgadin, junto con mi sirviente de confianza, Barnil Jarropetra. Libré muchas batallas al lado del padre de Ajeck en defensa de la Alianza. Verla crecida es bastante especial. Su padre la instruyó bien en las artes de las armas. Su habilidad con el arco me hace preguntarme si corre sangre élfica por esas venas.' WHERE `entry`='154';
UPDATE `locales_page_text` SET `Text_loc6`='Sir S. J. Erlgadin proviene de la aristocracia humana. Su padre, el conde Erlgadin, era célebre por su generosidad. Fue el conde quien presionó por una mejora en las condiciones de trabajo de la Hermandad de los Albañiles durante la restauración de Ventormenta después de la Segunda Gran Guerra.' WHERE `entry`='155';
UPDATE `locales_page_text` SET `Text_loc6`='En los años que siguieron, después de que Ventormenta traicionara a la hermandad de los albañiles, Sir Erlgadin se fue volviendo más rencoroso sobre el papel de los nobles en el Reino. Ya no deseaba defender la posición que la estirpe de su padre había ganado para él en la Casa de Nobles.$B$BPero me estoy yendo por las ramas. El propósito de esta historia no es que sirva como tratado político o biografía. Este es el relato de mis experiencias en busca de caza mayor por las verdes colinas de Tuercespina....' WHERE `entry`='156';
UPDATE `locales_page_text` SET `Text_loc6`='Nos levantamos al amanecer. Barnil empezó a preparar el desayuno. Advertí que Ajeck estaba un poco distraída. La caminata del día prometía ser larga y nos esperaban peligrosos episodios de caza. Cualquier descuido podía ponernos en una situación difícil. No obstante, Ajeck parecía incapaz de desviar su mirada de Barnil, que estaba de pie frente al río, limpiando su equipo.' WHERE `entry`='157';
UPDATE `locales_page_text` SET `Text_loc6`='Justo cuando estaba a punto de cuestionar la falta de interés de Ajeck en la estrategia de caza del día, alargó la mano hacia su arco, lo tensó con una flecha y la soltó justo hacia el pobre Barnil. Pero no era a Barnil a quien Ajeck disparaba, pues, cuando Barnil se echó a un lado boquiabierto, un gran crocolisco de río flotó a la superficie con la flecha de Ajeck perfectamente colocada entre sus grandes ojos.' WHERE `entry`='158';
UPDATE `locales_page_text` SET `Text_loc6`='Nos pusimos en camino hacia el oeste, a través de la espesa maleza de la enmarañada jungla. Con pasos lentos y cuidadosos, avanzamos a través de un espeso follaje en busca de presa. La mañana pasó en un silencio frustrante. Nada se agitaba en la Vega, ni una brisa. Hacia la tarde, la expedición se mostraba impaciente. Barnil ya no andaba con los pasos cautelosos de un depredador que rastrea a su presa. En su lugar, caminaba pesada y torpemente por el camino, a menudo pisando ruidosamente hojas secas o ramas caídas.' WHERE `entry`='159';
UPDATE `locales_page_text` SET `Text_loc6`='Durante semejante tropiezo, Erlgadin posó su mano pesadamente sobre el hombro de Barnil. Ajeck y yo apenas advertimos la escena, suponiendo que el hombre solo estaba dando una merecida reprimenda a Barnil por su descuido. No obstante, Erlgadin, hizo un gesto lento con la cabeza en dirección a un árbol que estaba a punto de caer. Desde allí nos acechaban dos ojos negros penetrantes justo encima de un par de afilados colmillos.' WHERE `entry`='160';
UPDATE `locales_page_text` SET `Text_loc6`='La bestia era un tigre macho de Tuercespina. Antes de que pudiera amartillar el rifle, Erlgadin levantó la ballesta y disparó en dirección al animal. El virote dejó su marca y sorprendió a la bestia con fuerza en el costado izquierdo. El tigre hizo un intento en vano por huir, pero la herida era demasiado grave. La bestia fue dando traspiés durante varios trágicos segundos, hasta que Barnil lo remató lanzando un hacha.' WHERE `entry`='161';
UPDATE `locales_page_text` SET `Text_loc6`='La matanza provocó un ánimo festivo en la expedición. Barnil sirvió aguamiel para disfrute de todos. Pero nuestros festejos fueron efímeros. Mientras preparábamos el cadáver para transportarlo de vuelta al campamento base, un rugido espantoso nos cogió desprevenidos a todos. En todos mis años nunca he oído nada que helara la sangre de tal modo.' WHERE `entry`='162';
UPDATE `locales_page_text` SET `Text_loc6`='En un rocoso precipicio en lo alto, perfilado por el sol poniente, podía distinguir el felino depredador más grande que he visto jamás. Pude liberar una descarga con mi rifle, pero el felino siguió su camino. Rugió una vez más, en esta ocasión vez con más fuerza que la anterior, y desapareció.$B$BRecogimos nuestras pertenencias y nos dirigimos solemnemente de vuelta al campamento.' WHERE `entry`='163';
UPDATE `locales_page_text` SET `Text_loc6`='Había prometido a la expedición que pasaríamos el día siguiente cazando panteras, ya que sus pieles son muy valoradas en Azeroth. Esta demanda se debe a que todos los cazadores, tramperos y comerciantes de pieles arriesgan valientemente su vida en nombre de la Alianza.' WHERE `entry`='164';
UPDATE `locales_page_text` SET `Text_loc6`='Ajeck y Sir Erlgadin estaban ansiosos por aprender a cazar eficazmente con un rifle de los enanos. Hice que los dos humanos dejaran sus primitivas armas en el campamento base. Barnil y yo los equipamos con algunas de las mejores armas de fuego de Forjaz.' WHERE `entry`='165';
UPDATE `locales_page_text` SET `Text_loc6`='Ese día nos aventuramos al sur, siguiendo algunos rastros recientes de pantera. Pronto llegamos a un barranco que se cruzaba por un enorme puente colgante. No pude evitar pensar en la descriptiva obra de Brann sobre aquella región cuando vi aquella maravilla de la ingeniería. Se suponía tan a menudo que los trols nativos eran una raza primitiva e inculta, pero, mientras observaba fijamente la artesanía maestra del puente, pude reconocer la habilidad con la que los albañiles trols superaron la hazaña, aparentemente imposible.' WHERE `entry`='166';
UPDATE `locales_page_text` SET `Text_loc6`='Al poco tiempo, Ajeck rastreó a la pantera en dirección al sudoeste. Anduvimos en silencio, con las armas preparadas, anticipándonos a nuestra presa. Un chasquido de ramas en un bosquecillo cercano llamó nuestra atención de inmediato. Allí había algo. Una mirada seria a Barnil bastó para transmitirle mis pensamientos. Barnil bajó su rifle lentamente. Esta presa no era para nosotros, sino para nuestros compañeros humanos. Innumerables panteras habían perdido la vida al enfrentarse a nuestros humeantes cañones. Esta presa era para los humanos.' WHERE `entry`='167';
UPDATE `locales_page_text` SET `Text_loc6`='Tanto Ajeck como Sir Erlgadin se mantuvieron erguidos, con las armas al nivel de la hirsuta maleza que había bajo unos árboles que se mecían de un lado a otro. El sol del mediodía ardía con fuerza sobre nosotros. Una lenta gota de sudor rodó por la sien de Erlgadin, al tiempo que retiraba el gatillo. Tras el chasquido, el follaje exuberante se abrió en dos y una pantera negra, espécimen hermoso, saltó veloz sobre la llanura.' WHERE `entry`='168';
UPDATE `locales_page_text` SET `Text_loc6`='Los humanos tenían la mira puesta en la pantera mientras esta corría rodeando la arboleda. Los cañones de los rifles se movían en paralelo con el animal. Barnil me miró impaciente y yo negué con la cabeza para que no abriera fuego. Esta caza era para los humanos, no para Barnil ni para mí. Erlgadin lanzó un disparo, sin dar en el blanco. Por lo visto, no estaba preparado para soportar el efecto de la descarga.' WHERE `entry`='169';
UPDATE `locales_page_text` SET `Text_loc6`='El arma dio una violenta sacudida. El cañón rodó lateralmente y cayó bajo el rifle de Ajeck. Ajeck había elegido ese preciso momento para apretar el gatillo. El rifle, con la mira orientada torpemente hacia la fila de árboles, se disparó con un estruendo inconfundible. Una bandada de pájaros chilló desde la copa de un árbol, dispersándose. Una columna de humo emergió del árbol. Sobrecogidos, vimos cómo una tremenda rama caía sobre la pantera que intentaba escapar, partiéndole el lomo.' WHERE `entry`='170';
UPDATE `locales_page_text` SET `Text_loc6`='Unas semanas después, nuestras reservas de piel de pantera y tigre crecieron ostensiblemente. Decidí que era hora de que la expedición cambiara de objetivo: Raptores.$B$BLos humanos, valorando la instrucción que Barnil y yo les proporcionamos, decidieron abstenerse de cazar con armas de fuego. Ajeck estaba mucho más cómoda con un arco de cuerda fina, mientras que Sir Erlgadin nunca abandonaba el campamento sin su maciza ballesta.' WHERE `entry`='171';
UPDATE `locales_page_text` SET `Text_loc6`='Nos fuimos al alba en dirección al sur, más allá de las ruinas de Tkashi. Barnil expresó su preocupación ante la posibilidad de encontrarnos con miembros de la tribu Sangrapellejo. Recordé a Barnil que los Sangrapellejo estaban ocupados intentando destruir a su enemigo tribal, los Machacacráneos. De más está decir que Barnil no estaba en absoluto tranquilo. Yo, por mi parte, tenía el rifle cargado, un saco lleno de pólvora y tres cazadores infalibles que me acompañaban para aplacar mis preocupaciones ante una posible emboscada.' WHERE `entry`='172';
UPDATE `locales_page_text` SET `Text_loc6`='Una vez, en el campo de batalla, tuve delante a un infernal altísimo, con el ejército de la Legión Ardiente avanzando desde todas partes. Una banda de trols revoltosos parece tan inofensiva como una liebre en las colinas de Dun Morogh.$B$BPasamos por las ruinas de Tkashi sin contratiempos, lo que tranquilizó a Barnil en gran medida. El grupo avanzó hacia el oeste, en dirección al Mare Magnum, bordeando las ruinas de Zul\'Kunda, situadas al sur. Al subir por los altos acantilados, divisamos al primer raptor.' WHERE `entry`='173';
UPDATE `locales_page_text` SET `Text_loc6`='La bestia ni siquiera detectó nuestra presencia. De hecho, el único saludo que recibió por parte de la expedición fue una bala en medio de la frente. Sir Erlgadin lanzó un grito de alegría, mientras Ajeck asentía y me miraba, haciendo un gesto de entusiasta aprobación. Revolví mi mochila en busca de mi pipa, esperando celebrar la hazaña con el humo de la victoria. Barnil empezó a correr por la cuesta para recuperar el cadáver del raptor. Miré a la bestia abatida con la satisfacción que produce una caza importante.' WHERE `entry`='174';
UPDATE `locales_page_text` SET `Text_loc6`='Sin embargo, no pude disfrutar de la victoria del cazador por mucho tiempo, pues al contemplar el horizonte, numerosas siluetas aparecieron en la cresta de la colina, justo encima del pobre Barnil.$B$B*¡Huye, Barnil!*, grité. Ajeck, Sir Erlgadin y yo lanzamos una descarga de balas y flechas por encima de Barnil, directo sobre los raptores. Alguien soltó la presa en medio de la confusión.' WHERE `entry`='175';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestros disparos apresurados fueron suficientes para provocar la huida de Barnil. Este regresó gritando por la colina y se reincorporó al grupo. Nos internamos en la selva a toda prisa, con una panda de feroces raptores colazote vigilando cada uno de nuestros movimientos.$B$BLos cazadores se habían convertido en la presa.' WHERE `entry`='176';
UPDATE `locales_page_text` SET `Text_loc6`='Conduje a la expedición hasta el mar, con el fin de que la costa nos protegiera de los raptores. En nuestro apresuramiento, nos desviamos y acabamos en el norte, en un monte peligrosamente elevado. Pero nos equivocamos. Fue culpa mía. Nos detuvimos delante de un escarpado acantilado, con los raptores justo detrás de nosotros.' WHERE `entry`='177';
UPDATE `locales_page_text` SET `Text_loc6`='Avancé lentamente con el arma levantada. Había conducido a estos valientes cazadores a su fin. Tenía que morir defendiéndolos. Los raptores colazote son particularmente feroces, conocidos por ser unos sanguinarios implacables. Eran muchos más que nosotros. Pero no me habría perdonado dejar que nos mataran a mis camaradas y a mí, sin antes derramar parte de su propia sangre.' WHERE `entry`='178';
UPDATE `locales_page_text` SET `Text_loc6`='Ajeck y Sir Erlgadin prepararon sus armas, flanqueándome a ambos lados, con las espaldas orientadas al mar. Barnil lanzó un suspiro de derrota y sacó su hacha. Los colazotes estaban casi encima de nosotros. Sus firmes zancadas eran ahora más pausadas. Estaban observando a su presa, ya que sabían que nos tenían acorralados.' WHERE `entry`='179';
UPDATE `locales_page_text` SET `Text_loc6`='Entonces ocurrió algo milagroso. Desde donde estábamos escuchamos el inconfundible y aterrador rugido del gran tigre blanco. A pesar de ser numerosos, los raptores dieron media vuelta y se dispersaron. Solo vimos el breve destello blanco de un tigre que salió disparado junto a nosotros, abalanzándose sobre uno de los raptores. No fue necesario dar ninguna orden. Los cuatro miembros de la expedición sabíamos que era el momento de salir corriendo.' WHERE `entry`='180';
UPDATE `locales_page_text` SET `Text_loc6`='Corrimos a toda velocidad hasta el campamento base, sin reducir la marcha en ningún momento. Más tarde aquella noche, nos sentamos en silencio alrededor de la hoguera, sabiendo que un extraño golpe de suerte nos había salvado la vida. Esos son los riesgos que corre el cazador de caza mayor. Jugamos con la suerte, desafiándola. No obstante, todos, en algún momento de nuestras vidas, debemos afrontar las afiladas garras del destino. Este enano se alegra, porque ese momento aún no ha llegado a las verdes colinas de Tuercespina.' WHERE `entry`='181';
UPDATE `locales_page_text` SET `Text_loc6`='Honorables compañeros y sabios buscadores de la verdad$B$BLa excavación continúa, pero su ritmo ha disminuido debido a los troggs que mencioné en mi último informe. Confío en que los neutralizaremos, aunque algunos son más agresivos de lo habitual.$B$BRecientemente se encontraron más artefactos, si bien en todos los casos, salvo en uno (que en breve detallaré), los recientes hallazgos tienen la misma relevancia que los anteriores: son interesantes, pero no esclarecedores.' WHERE `entry`='182';
UPDATE `locales_page_text` SET `Text_loc6`='Hubo una excepción. Los ídolos tallados recientemente encontrados (una breve descripción de los mismos aparece en mi último informe) parecen afectar de algún modo a los troggs. Se sienten atraídos por la talla en piedra y algunos troggs enloquecen ante su presencia.$B$BEs preciso estudiar este fenómeno con mayor profundidad para llegar a una conclusión válida en relación con estos ídolos, si bien espero que el estudio arroje luz sobre un posible vínculo entre los troggs y los titanes.' WHERE `entry`='183';
UPDATE `locales_page_text` SET `Text_loc6`='Finalmente, debo reiterar mi petición para que se me suministre más pólvora. Mis suministros son muy escasos, lo que dificulta seriamente el éxito de la excavación. Me dijeron que un nuevo suministro de pólvora estaba en camino, sin embargo aún no he recibido nada.$B$B¿Puedo preguntar a qué se debe este retraso?$B$BRespetuosamente,$BProspector Darteus Vetaferro' WHERE `entry`='184';
UPDATE `locales_page_text` SET `Text_loc6`='Todo empezó con el hallazgo de esa maldita guadaña en la Condena de Roland. Antes de que apareciera la guadaña, los terrores de este lugar parecían tan mansos como el valle de Villanorte.$B$BPero desde que encontré el mango de la guadaña sobresaliendo entre la pila de escombros en la mina y lo saqué de ahí, ¡ay de mí!, ¡la Condena de Roland se ha convertido en un lugar de destrucción y muerte!' WHERE `entry`='185';
UPDATE `locales_page_text` SET `Text_loc6`='Todo empezó con el hallazgo de esa maldita hoz en la mina conocida como Condena de Roland. Sí, ahí empezó todo. Antes de que aquello ocurriera, la Hermandad Defias estaba satisfecha con nuestros avances en el Bosque del Ocaso. Antes de que apareciera la guadaña, los terrores de este lugar parecían tan mansos como el valle de Villanorte.$B$BPero desde que encontré el mango de la guadaña sobresaliendo entre la pila de escombros en la mina y lo saqué de ahí, ¡ay de mí!, ¡la Condena de Roland se ha convertido en un lugar de destrucción y muerte!' WHERE `entry`='186';
UPDATE `locales_page_text` SET `Text_loc6`='Si hubiera sabido lo que iba a suceder, habría cortado mi propia mano antes de tocar ese trozo de madera tallada con runas. ¡Cuánto arrepentimiento! Pensé que ese era solo un privilegio de los ancianos. Ahora sé que no son los ancianos, sino los desesperados, los que visten el manto del arrepentimiento, incapaces y sin deseos de quitárselo de los hombros, encorvados por el sufrimiento.$B$BAhora basta de poesía barata. Debo continuar con esta crónica...' WHERE `entry`='187';
UPDATE `locales_page_text` SET `Text_loc6`='Tras el rescate de la guadaña, en la mina empezaron a ocurrir cosas extrañas. La luz procedente de nuestras antorchas parpadeantes se deformaba y la fuerza de nuestras voces parecía escapar a nuestro control. A veces, el susurro de un hombre provocaba un estruendo que atravesaba los túneles, obligando a los demás a taparse los oídos y, en otras ocasiones, nuestros gritos apenas se oían a unos cuantos metros antes de apagarse como un soplo en el viento.$B$BCiertamente, era desconcertante, pero rápidamente dejamos de extrañarnos por lo insólito del asunto. Lo que en realidad nos apartó de la mina, fue un presagio. El huargen.' WHERE `entry`='188';
UPDATE `locales_page_text` SET `Text_loc6`='Emergieron desde todos los rincones, clavando sus zarpas en nuestros tobillos desde ocultos agujeros y cayendo sobre nosotros desde lo alto. La mitad de nuestros hombres cayeron en esos primeros minutos de pánico. El resto, entre los que me incluyo, intentaron escapar. A medida que corría, veía a mis hermanos atrapados por la zarpa y el colmillo, oí tantos alaridos truncados o desvaneciéndose en el silencio.$B$BHasta donde tengo conocimiento, fui el único humano que escapó de aquel lugar.' WHERE `entry`='189';
UPDATE `locales_page_text` SET `Text_loc6`='Solo puedo suponer por qué sobreviví aquella noche. Siempre he sido cuidadoso, rápido a la hora de escapar de las fauces de alguna bestia, o cuando se trataba de saltar algún escollo. Mi sobrenombre, Alterio, obedece a esta característica. Así que tal vez fue el don de la prudencia lo que me salvó...$B$BO tal vez fue la guadaña que extraje de los escombros. No puede ser la guadaña, puesto que la perdí durante mi frenética huida. Pero si fui yo quien atrajo los huargen al Bosque del Ocaso, quizás me hayan hecho un extraño obsequio. ¡Los maldigo!' WHERE `entry`='190';
UPDATE `locales_page_text` SET `Text_loc6`='O quizás, estoy condenado a presenciar los cambios que he traído al Bosque del Ocaso. Quizás sea mi destino contemplar la invasión de huargen en estas tierras, contaminándolas paulatinamente con su podredumbre.$B$BSi esa es mi suerte, tiene dos caras. Porque los huargen no son la única fuerza que se ha apoderado del Bosque del Ocaso: los demonios del Paso de la Muerte también han reclamado su parte.$B$BEse será el último capítulo de mi historia, y rezo porque sea el último...' WHERE `entry`='191';
UPDATE `locales_page_text` SET `Text_loc6`='Tras sobrevivir a la huida de la Condena de Roland, me oculté en el granero de un hombre llamado Sven. Pasé unos cuantos días en el granero y aquel horror caló tan hondo en mí que nunca me presenté ante Sven o su familia. Pero considerando lo que vi desde mi escondite, me di cuenta de que aquellos granjeros eran personas bastante decentes. De haber salido de mi refugio, seguramente me habrían aceptado, pero me cuesta confiar en los demás. Y mucho más después de aquella impresión en la mina.$B$BAsí que permanecí escondido. Y por ello salvé mi vida.' WHERE `entry`='192';
UPDATE `locales_page_text` SET `Text_loc6`='Pocos días después de llegar al granero, Sven abandonó su granja para partir a Villa Oscura. Besó a su mujer, sonrió a sus hijos y prometió volver pronto con regalos y golosinas. Pobre hombre. Fue la última vez que vio a su familia sana y entera.$B$BAl menos se despidieron contentos. Y por suerte, su mujer fue la primera en morir, evitando el dolor de ver cómo asesinaban a sus hijos. Pero estas supuestas contrapartidas no me compensarán tanto dolor. Vi lo que ocurrió y la escena siempre me perseguirá en sueños.' WHERE `entry`='193';
UPDATE `locales_page_text` SET `Text_loc6`='Me tiemblan las manos cuando recuerdo los detalles de aquella noche, Sven estaba ausente y su familia tuvo que enfrentarse sola a los jinetes negros. El remordimiento vuelve a consumirme, pues yo estaba allí y pude haber luchado contra aquellos demonios del Paso de la Muerte. Pero es falso remordimiento. Es el mismo que abruma a cualquier superviviente de una tragedia. Sé que de haber abandonado mi escondite también habría muerto desgarrado y descuartizado, los miembros esparcidos por todas partes hasta quedar irreconocible.' WHERE `entry`='194';
UPDATE `locales_page_text` SET `Text_loc6`='Pero a pesar de saber que no habría podido hacer nada para detener aquel atroz asesinato, perdura el sentimiento de culpabilidad: Fui yo quien atrajo a los jinetes negros hasta la granja de Sven. Mi descubrimiento de la guadaña no solo desató a los huargen hasta el Bosque del Ocaso, sino que atrajo a los jinetes del Paso de la Muerte.$B$BLo sé porque antes de iniciar la masacre, formularon una pregunta a la mujer de Sven mientras esta aferraba a sus hijos, ofreciéndoles consuelo, a pesar de presentir la inminencia de su muerte.' WHERE `entry`='195';
UPDATE `locales_page_text` SET `Text_loc6`='\"La Guadaña de Elune\", gritó uno de los jinetes en un tono áspero y estridente a la vez, como el chirrido de un hacha sobre la piedra. La última palabra, Elune, brotó en un grito ahogado.$B$BEl terror se apoderó de mí cuando escuché aquella voz, no solo por su tono espantoso, sino porque sabía a qué guadaña se refería el jinete. Debe de tratarse del mismo objeto abominable que extraje de las rocas en la Condena de Roland hace unos días, pensé. ¡Es lo que los jinetes negros estaban buscando!$B$BY es lo que mató a la familia de Sven.' WHERE `entry`='196';
UPDATE `locales_page_text` SET `Text_loc6`='Nunca supe el nombre de la mujer de Sven, ya que su marido y sus hijos solo la llamaban \"querida\", \"mi amor\" o \"mami\". Pero me habría gustado saberlo. Soy el único recuerdo viviente de su valerosa actuación aquel fatídico día y, a pesar de tratarse tan solo de la mujer de un granjero, jamás vi a hombre o mujer proceder con tanta valentía. Si tan solo hubiera servido para algo.' WHERE `entry`='197';
UPDATE `locales_page_text` SET `Text_loc6`='\"¿La Guadaña?\", dijo tranquilamente. \"Claro que sí. ¿Quién no la ha visto por aquí?\" Miró a los jinetes con firmeza y, de no conocer los hechos, yo habría jurado que decía la verdad. ¡Era imposible que supiera lo de la Guadaña.$B$BSu estrategia daba resultado. El mismo jinete que formuló la pregunta se inclinó ligeramente hacia ella y chilló: \"¿Dónde?\"$B$B\"Te llevaré hasta ella. Todos vosotros,\" dijo, y pude observar un destello de esperanza que asomaba a sus ojos.' WHERE `entry`='198';
UPDATE `locales_page_text` SET `Text_loc6`='\"Pero el camino es largo y mis hijos solo nos harán ir más despacio. Debemos dejarlos aquí.\"$B$BSu coartada era simple, pero son ese tipo de coartadas las que mayor éxito tienen. Si funcionaba, al menos alejaría a los jinetes de la granja. Ella habría sucumbido, pero sus hijos estarían a salvo. Y, de no ser porque los jinetes se creyeron sus nobles mentiras, habría funcionado.$B$BA pesar de que nunca he sido estudioso de la Luz, recé con fuerza por la vida de la mujer de Sven mientras plantaba cara a aquellos terribles jinetes.$B$B\"Por favor\", recé. \"Haz que se lo crean.\"' WHERE `entry`='199';
UPDATE `locales_page_text` SET `Text_loc6`='Se quedaron ahí, paralizados, mientras ella los miraba a los ojos con tranquilidad. Uno de los jinetes miró hacia arriba, como si escuchase una llamada a lo lejos. Extrajo de sus ropajes una pequeña gema y miró en su interior. Luego hizo un gesto, apuntando con la gema hacia la mujer de Sven. Una luz se deslizó desde el jinete hasta la mujer, adquiriendo la forma de una mano blanca y lúgubre. Ella contempló la luz, inmutable, pero pude observar cierta incertidumbre tras su máscara de confianza. Cuando la mano la alcanzó, rodeó su cabeza con los dedos.$B$BLuego la apretó con fuerza.' WHERE `entry`='200';
UPDATE `locales_page_text` SET `Text_loc6`='La mujer de Sven permaneció rígida como una tabla y sus ojos se abrieron al máximo. A pesar de que sus labios hicieron la mueca de un grito, no dejaron escapar sonido alguno. Tras unos minutos de tortura, la mano soltó a la mujer, dejándola caer de rodillas. El jinete que sujetaba el abalorio se irguió sobre su silla y una potente voz emergió de su interior.$B$B\"Esta mujer miente\", afirmó en un tono que aún retumba en mis sueños. \"No ha visto la Guadaña.\"' WHERE `entry`='201';
UPDATE `locales_page_text` SET `Text_loc6`='Tras este suceso, los hombros del jinete se encorvaron ligeramente, como si un espíritu hubiese abandonado su cuerpo. Luego, en el antiguo tono chirriante, pronunció las siguientes palabras:$B$B\"El Señor ha hablado. Matadlos.\"' WHERE `entry`='202';
UPDATE `locales_page_text` SET `Text_loc6`='No puedo describir lo que ocurrió a continuación. Los recuerdos son vívidos, pero mi alma desgraciada no puede plasmar sobre el papel lo que allí sucedió en aquellos espeluznantes momentos.$B$BSolo puedo escribir que la familia de Sven fue asesinada. Poco después, Sven regresó a este escenario de muerte y horror. Estaba tan destrozado, que tuve miedo de salir de mi escondite. Tanto temía que me encontrara, que huí de mi refugio en el granero. No sé dónde está Sven ahora, pero espero que, algún día encuentre la paz.' WHERE `entry`='203';
UPDATE `locales_page_text` SET `Text_loc6`='No puedo describir lo que ocurrió a continuación. Los recuerdos son vívidos, pero mi alma desgraciada no puede plasmar sobre el papel lo que allí sucedió en aquellos espeluznantes momentos.$B$BSolo puedo escribir que la familia de Sven fue asesinada. Poco después, Sven regresó a este escenario de muerte y horror. Estaba tan destrozado, que tuve miedo de salir de mi escondite. Tanto temía que me encontrara, que huí de mi refugio en el granero. No sé dónde está Sven ahora, pero espero que, algún día encuentre la paz.' WHERE `entry`='204';
UPDATE `locales_page_text` SET `Text_loc6`='Después de liberar la Guadaña, emergieron desde todos los rincones, clavando sus zarpas en nuestros tobillos desde ocultos agujeros, o cayendo sobre nosotros desde lo alto. La mitad de nuestros hombres cayeron en esos primeros minutos de pánico. El resto, entre los que me incluyo, intentaron escapar. A medida que corría, veía a mis hermanos atrapados por la zarpa y el colmillo, oí tantos alaridos truncados o desvaneciéndose en el silencio.' WHERE `entry`='205';
UPDATE `locales_page_text` SET `Text_loc6`='Solo puedo suponer por qué sobreviví aquella noche. Siempre he sido cuidadoso, rápido a la hora de escapar de las fauces de alguna bestia, o cuando se trataba de saltar algún escollo. Mi sobrenombre tiene que ver con esta característica. Así que tal vez fue el don de la prudencia el que me salvó...$B$BO tal vez fue la guadaña que extraje de los escombros. No puede ser la guadaña, puesto que la perdí durante mi frenética huida. Pero si fui yo quien atrajo a los huargen al Bosque del Ocaso, quizás me hayan hecho un extraño obsequio. ¡Los maldigo!' WHERE `entry`='206';
UPDATE `locales_page_text` SET `Text_loc6`='Tras sobrevivir a la huida de la Condena de Roland, me oculté en el granero de un hombre llamado Sven. Pasé unos cuantos días en el granero y aquel horror caló tan hondo en mí que nunca me presenté ante Sven o su familia. Pero considerando lo que vi desde mi escondite, me di cuenta de que aquellos granjeros eran personas bastante decentes. De haber salido de mi refugio, seguramente me habrían aceptado, pero me cuesta confiar en los demás. Y mucho más después de aquella impresión en la mina.$B$BAsí que permanecí escondido.' WHERE `entry`='207';
UPDATE `locales_page_text` SET `Text_loc6`='<El resto del libro está en blanco>' WHERE `entry`='208';
UPDATE `locales_page_text` SET `Text_loc6`='INFORME: Kóbolds$B$BLa actividad de los kóbolds ha aumentado en las cercanías del valle de Villanorte. Un alarmante número de ellos acampa al norte de la abadía y dentro de la Mina del Eco. He reunido a paladines y a otros habitantes de la zona para limpiarla de estas alimañas.$B$BLa labor progresa adecuadamente. Yo, por mi parte, realizaré informes según sea necesario.' WHERE `entry`='209';
UPDATE `locales_page_text` SET `Text_loc6`='INFORME: Ladrones$B$BMás peligrosos que los kóbolds, una banda de ladrones humanos ha surgido en el valle. Llevan pañuelos rojos y se consideran una hermandad. Su nivel de organización puede generar problemas aquí y en el bosque de Elwynn.$B$BHemos identificado a uno de sus líderes, un tal Garrick Piesuaves, al que esperamos llevar pronto ante el tribunal.' WHERE `entry`='210';
UPDATE `locales_page_text` SET `Text_loc6`='INFORME: Lobos$B$BSegún los informes, los ataques de estos animales han aumentado en Villanorte. Eagan cree que los lobos que hay en el valle fueron desterrados desde el Bosque del Ocaso.$B$BAunque los lobos rara vez cazan humanos, su elevado número provocó ciertos incidentes con los granjeros. Y varias reses desaparecieron.' WHERE `entry`='211';
UPDATE `locales_page_text` SET `Text_loc6`='DISTINCIÓN:$B$BEl portador de estos documentos será condecorado con el rango de Ayudante de Alguacil del ejército de Ventormenta, por haber servido a Villanorte con entusiasmo y especial atención. Confío en que esta persona os resulte útil en el bosque de Elwyyn.$B$BFirmado:$BAlguacil Douglas McBride,$BEjército de Ventormenta, Villanorte' WHERE `entry`='212';
UPDATE `locales_page_text` SET `Text_loc6`='Estofado de los Páramos de Poniente$B$B3 partes de carne fibrosa de buitre$B3 morros de dentosangre$B3 ojos de múrloc$B3 okras$B$BMezclar todos los ingredientes y poner a hervir. Hervir a fuego lento al menos durante dos horas antes de servir.' WHERE `entry`='213';
UPDATE `locales_page_text` SET `Text_loc6`='Querida Yvette,$B$BEncomendaré esta carta a $Gnuestro corredor:nuestra corredora; más veloz. Espero que pueda avanzar sin contratiempos por las zonas en que habitan las fuerzas que nos rodean. También ruego para que puedas leer estas palabras, pues son lo último que sabrás de mí.$B$BMi único consuelo en estos días aciagos es el alivio que me produce saber que no tendrás la misma suerte que yo.' WHERE `entry`='214';
UPDATE `locales_page_text` SET `Text_loc6`='Querida Yvette,$B$BEncomendaré esta carta a $Gnuestro corredor:nuestra corredora; más veloz. Espero que pueda avanzar sin contratiempos por las zonas en que habitan las fuerzas que nos rodean. También ruego para que puedas leer estas palabras, pues son lo último que sabrás de mí.$B$BMi único consuelo en estos días aciagos es el alivio que me produce saber que no tendrás la misma suerte que yo.' WHERE `entry`='217';
UPDATE `locales_page_text` SET `Text_loc6`='Temo que la guerra entre los Pedregosa y los Maclure acabe con el incipiente romance entre Tommy Joe y Maybell, así que, en tiempos como estos, en los que los rumores de guerra se ciernen sobre nosotros, es preciso cultivar la juventud y el amor.$B$BDe ahí el favor que te pido: Te pido que uses tus habilidades y fabriques una poción o elixir para ayudar a estos dos jóvenes en su misión de reunirse.$B$BGracias, William. Y por favor, si puedes apartarte un poco del trabajo, ven a visitarme. Te invito a que nos riamos un poco recordando el pasado.$B$BMildred' WHERE `entry`='218';
UPDATE `locales_page_text` SET `Text_loc6`='De la mano de Baros Alexston,$BOficina del arquitecto de la ciudad$BVentormenta$B$BVuestra Majestad,$B$BHay muchas razones por las que consideré que era preciso presentar un informe sobre los asuntos recientes de la Hermandad Defias y sus actividades en todo el reino.$B$BPara poneros en antecedentes, haré un poco de historia. Como sabréis, o quizás desconocéis, empecé a servir a la ciudad de Ventormenta como miembro de la Hermandad de los Albañiles. Durante años de intenso trabajo, terminamos la reconstrucción de' WHERE `entry`='219';
UPDATE `locales_page_text` SET `Text_loc6`='Ventormenta, momento en el que las facturas y los honorarios de la Hermandad de los Albañiles quedaron sin pagar y sin explicación alguna.$B$BEn aquella época, Edwin VanCleef había sido elegido Maestro de la Hermandad de los Albañiles y exigió las retribuciones por el trabajo realizado. En respuesta, la Casa de Nobles de Ventormenta ordenó la disolución de la Hermandad de los Albañiles, lo que molestó a VanCleef, como es de suponer. VanCleef encabezó un motín de los Albañiles en las afueras de la ciudad.$B$BAntes de continuar, debo mencionar otros acontecimientos que tuvieron lugar durante' WHERE `entry`='220';
UPDATE `locales_page_text` SET `Text_loc6`='esta época, que conviene resaltar.$B$BEn primer lugar, fue en aquel momento cuando me ofrecieron trabajar como arquitecto de la ciudad si no me unía a VanCleef. Debido a ciertas diferencias ideológicas, elegí permanecer en Ventormenta.$B$BDurante el levantamiento, el teniente y hombre de confianza de VanCleef, Bazil Thredd, fue capturado y recluido en prisión. A la espera de ser juzgado e interrogado, Thredd estuvo a punto de ser olvidado en las mazmorras.$B$BAl regresar con VanCleef, después de dirigir lo que quedaba de' WHERE `entry`='221';
UPDATE `locales_page_text` SET `Text_loc6`='los Albañiles fuera de Ventormenta, aprovechó la relativa falta de protección de los Páramos de Poniente y utilizó su mano de obra considerable para expulsar a muchos granjeros y tomar posesión de un puñado de minas de oro.$B$BUtilizando los recursos de que disponía, VanCleef urdió un plan de retribución contra el gobierno de Ventormenta.$B$BEsta información fue revelada recientemente con la ayuda del portador de este documento, cuya actuación resultó decisiva a la hora de esclarecer esta importante conspiración.' WHERE `entry`='222';
UPDATE `locales_page_text` SET `Text_loc6`='Difundid esta información todo lo que consideréis necesario.$B$BComando Regional de Tirisfal$BCapitán Melrose$BCapitán Vachon$BCapitán Perrine$B$BÓrdenes del Gran Señor.$B$BCapitán Perrine, es preciso reforzar vuestras posiciones en la torre del sudoeste (tal como se especificó anteriormente). En envíos posteriores mandaremos suministros adicionales. Mientras tanto, deberán obtenerse en las granjas vecinas. Asimismo, es preciso realizar nuevos trabajos de reconocimiento, al tiempo que' WHERE `entry`='223';
UPDATE `locales_page_text` SET `Text_loc6`='deberá reunirse información sobre la organización de los no-muertos en Rémol.$B$BCapitán Vachon, al parecer, los no-muertos han intensificado sus maniobras cerca de la torre del norte. Hay que contener esta rebelión de forma rápida y contundente.$B$BCapitán Melrose, nos preocupa el nivel de organización de los no-muertos cerca de las fronteras de las Tierras de la Peste. En las próximas semanas le enviaremos un nuevo contingente de hombres.$B$BGloria a todos en la Luz' WHERE `entry`='224';
UPDATE `locales_page_text` SET `Text_loc6`='deberá reunirse información sobre la organización de los no-muertos en Rémol.$B$BCapitán Vachon, al parecer, los no-muertos han intensificado sus maniobras cerca de la torre del norte. Hay que contener esta rebelión de forma rápida y contundente.$B$BCapitán Melrose, nos preocupa el nivel de organización de los no-muertos cerca de las fronteras de las Tierras de la Peste. En las próximas semanas le enviaremos un nuevo contingente de hombres.$B$BGloria a todos en la Luz' WHERE `entry`='228';
UPDATE `locales_page_text` SET `Text_loc6`='Primer día$B$BSe me ha roto el depósito, pero sé que por estas colinas encontraré el mineral de Quiron. Le dije a Podomartillo que se quedara para vigilar nuestros depósitos.$B$BContinuaré buscando yo solo. Si no logro regresar, este diario será mi último testamento.$B$BBuron Hondocuesta, piloto$BBrigada de Tanques de Vapor de Forjaz' WHERE `entry`='229';
UPDATE `locales_page_text` SET `Text_loc6`='Segundo día$B$BMi búsqueda continúa y sigo sin encontrar el mineral. Me pregunto si lo del mineral es solo un rumor o una broma de Roscapiedra. Si es así, ¡cuando vuelva al Almacén de Brasacerada le voy a partir el cráneo!$B$BAnoche, cuando preparaba mi campamento, escuché el eco de un gruñido desde los cañones.$B$BNo era un lobo. Antes parecía un oso.' WHERE `entry`='230';
UPDATE `locales_page_text` SET `Text_loc6`='Tercer día$B$BLos gruñidos se prolongaron durante toda la noche y me han seguido a lo largo del día, distrayéndome de mi labor de búsqueda del mineral. ¡Creo que un oso me está siguiendo!$B$BEspero que se acerque todo lo posible. ¡Así podré enterrarle mi pico en la cabeza!' WHERE `entry`='231';
UPDATE `locales_page_text` SET `Text_loc6`='Cuarto día$B$BMis provisiones se están agotando. Aún me queda bastante comida, pero no había planeado quedarme tanto tiempo en la selva, lejos de mi tanque, con tan solo dos barriles de cerveza.$B$BHe permanecido despierto las últimas dos noches, escuchando ese maldito gruñido y ¡casi me bebo los barriles enteros!$B$BTengo que regresar mañana con Podomartillo y nuestros tanques de vapor. No quiero perderme aquí, sin cerveza.' WHERE `entry`='232';
UPDATE `locales_page_text` SET `Text_loc6`='Quinto día$B$B¡El maldito oso me dejó destrozado! Me atacó al mediodía, gruñendo y embistiéndome por la espalda.$B$BDe haberse aproximado contra el viento, habría percibido su olor... ¡su hedor era espantoso! ¡El olor de su pelaje roñoso y su aliento podrido bastaban para aniquilar a cualquiera!$B$B¡Luché contra esa bestia, pero al final dio un buen bocado a mi pierna. Ahora no puedo moverme, me he quedado sin cerveza y, finalmente, no he logrado encontrar el mineral. ¡Maldición!' WHERE `entry`='233';
UPDATE `locales_page_text` SET `Text_loc6`='Sexto día$B$BEl oso aún no ha vuelto... ¡Debí darle una buena paliza! Pero sigo escuchando sus gruñidos. ¡Creo que está esperando a que muera!$B$BSi encuentras este libro, debo encomendarte una tarea. Mata a ese oso asqueroso. Mátalo y luego lleva el libro a mi amigo Podomartillo. Él querrá saber qué ocurrió.$B$BTambién puedes coger mi armadura. ¡La necesitarás para luchar contra Sarnagarra!' WHERE `entry`='234';
UPDATE `locales_page_text` SET `Text_loc6`='Saludos, Ello Cerranegro, Alcalde de Villa Oscura. Me temo que traigo noticias para tu ciudad. Noticias graves.$B$BSabes, soy un creador. He engañado al portador de esta nota para que me ayudase en mi última y más directa creación: ¡un demonio de carne, hueso y metal retorcido! Cuando leas esto, esta bestia estará junto a mi humilde morada, rechinando sus dientes y esperando mis órdenes para masacrar a alguien.$B$BPronto tendrás noticias mías.$B$BEl embalsamador' WHERE `entry`='235';
UPDATE `locales_page_text` SET `Text_loc6`='El estudio de diversas criaturas fosilizadas me ha permitido deducir que, en la antigüedad, una gran peste asoló las aguas del lago Lordamere. ¿Qué la causó? Tal vez nunca lo sepamos. Pero el nivel de contaminación parece ser muy alto, considerando las elevadas concentraciones de restos adheridos en el fondo del lago.' WHERE `entry`='236';
UPDATE `locales_page_text` SET `Text_loc6`='En un intento por descubrir los secretos del pasado, he empezado a examinar las criaturas del presente con la intención de encontrar el eslabón que falta para resolver el misterio. Los vagadores del Lago y los trepadores del Lago son antiguas bestias que habitan las islas que pueblan el centro del lago Lordamere. Hay un musgo que crece en ellas y que se asemeja a los restos de algunos fósiles. Debo indagar más para especular sobre el significado de este vínculo.' WHERE `entry`='237';
UPDATE `locales_page_text` SET `Text_loc6`='Al intentar recoger muestras de musgo, presencié una sangrienta batalla. La tribu de los múrlocs Anca Vil estaba siendo asediada por una banda de maleantes gnoll. Había cadáveres gnoll y múrloc desperdigados por el campo de batalla. Cuando pasé junto a un múrloc destrozado, advertí que tenía un tumor endurecido que sobresalía de la herida. Al mirar el tumor más de cerca, me di cuenta que tenía propiedades similares a las del musgo que he estado recogiendo. Desafortunadamente, no pude encontrar más tumores, salvo este.' WHERE `entry`='238';
UPDATE `locales_page_text` SET `Text_loc6`='Al intentar recoger muestras de musgo, presencié una sangrienta batalla. La tribu de los múrlocs Anca Vil estaba siendo asediada por una banda de maleantes gnoll. Había cadáveres gnoll y múrloc desperdigados por el campo de batalla. Cuando pasé junto a un múrloc destrozado, advertí que tenía un tumor endurecido que sobresalía de la herida. Al mirar el tumor más de cerca, me di cuenta que tenía propiedades similares a las del musgo que he estado recogiendo. Desafortunadamente, no pude encontrar más tumores, salvo este.' WHERE `entry`='243';
UPDATE `locales_page_text` SET `Text_loc6`='Lord Bethor Duroyelo,$B$BSiguiendo vuestras instrucciones, os mando al agente que habéis solicitado. Es el portador de esta nota.$B$BEsta persona ha demostrado ser de fiar, es capaz de seguir órdenes y puede enfrentarse a las fuerzas de la Plaga, por lo que lo he elegido para vuestra misión.$B$BPor la gloria de la Reina,$BMagistrado Sevren' WHERE `entry`='244';
UPDATE `locales_page_text` SET `Text_loc6`='Desaparecido: Cabo Keeshan$B$BFue visto por última vez durante la sangrienta batalla del Fuerte de Petravista. Según los informes, fue arrastrado por los orcos Roca Negra.$B$BSe cree que permanece retenido como prisionero de guerra en el campamento Roca Negra, al norte de Villa del Lago, justo al oeste del paso que conduce a las Estepas Ardientes.$B$BPor mandato del magistrado Solomon, todo aquel que colabore en el rescate del cabo Keeshan será recompensado por el Alguacil Marris.' WHERE `entry`='245';
UPDATE `locales_page_text` SET `Text_loc6`='He pasado las últimas semanas de un lado para otro, sin permanecer mucho tiempo en ningún sitio por miedo a los jinetes. Ahora me encuentro en la ciudad abandonada de Cerro del Cuervo, oculto, como siempre. No podría enfrentarme al poder que utilizaron contra la mujer de Sven y sé que continúan buscando la Guadaña en el Bosque del Ocaso. La he perdido y, gracias a la Luz esto es así, puesto que, de conservarla, sé que me habrían encontrado. De hecho, en lo más profundo sigo convencido de que me encontrarán.$B$BEstoy tan cansado.' WHERE `entry`='246';
UPDATE `locales_page_text` SET `Text_loc6`='He pasado las últimas semanas de un lado para otro, sin permanecer mucho tiempo en ningún sitio por miedo a los jinetes. Ahora me encuentro en la ciudad abandonada de Cerro del Cuervo, oculto, como siempre. No podría enfrentarme al poder que utilizaron contra la mujer de Sven y sé que continúan buscando la Guadaña en el Bosque del Ocaso. La he perdido y, gracias a la Luz esto es así, puesto que, de conservarla, sé que me habrían encontrado. De hecho, en lo más profundo sigo convencido de que me encontrarán.$B$BEstoy tan cansado.' WHERE `entry`='247';
UPDATE `locales_page_text` SET `Text_loc6`='Saludos, maestro:$B$BTe pido disculpas por el sistema de envío de este mensaje. Mis magos de la sombra en Alterac están concentrados en sus tareas, obligándome a utilizar un mensajero extraño, reclutado entre nuestros nuevos \"aliados\", para hacerte llegar este informe. Pero he encriptado esta carta con uno de nuestros cifrados más esotéricos. Sus palabras están a salvo de estos palurdos.' WHERE `entry`='248';
UPDATE `locales_page_text` SET `Text_loc6`='Valik,$B$BVigila al esclavo hasta que regresemos. No es seguro mantenerlo aquí más tiempo. Lo llevaremos al norte, lo más lejos posible para evitar que lo vean.$B$BSon animales, pero no obstante son animales despiadados. Si fueran bastantes, tendríamos motivos para preocuparnos. Lo mejor es evitar cualquier señal que pueda entenderse como una provocación. Lo último que queremos es darles razones para que nos ataquen en masa.' WHERE `entry`='249';
UPDATE `locales_page_text` SET `Text_loc6`='Un grupo procedente del puesto de mando de los Defias se pondrá en contacto con el Coleccionista y él trasladará el oro recogido.$B$BAsegúrate de que este proceso se lleve a cabo con cuidado y con la mayor discreción. El Coleccionista es responsable del traslado del oro, pero, en última instancia, es responsabilidad de todos los miembros de la Hermandad Defias garantizar que su papel se desempeñe con atención y disciplina.' WHERE `entry`='250';
UPDATE `locales_page_text` SET `Text_loc6`='Recordad, hermanos míos que un día fuimos orgullosos artesanos. Realizaremos nuestro trabajo con la misma precisión que utilizamos en nuestro oficio anterior.$B$BEVC' WHERE `entry`='251';
UPDATE `locales_page_text` SET `Text_loc6`='Creo que mi padre cometió un grave error cuando decidió quedarse en casa para defenderla. Las fuerzas de la Plaga asolan los Claros de Tirisfal y, aunque estamos fortificados y bien abastecidos, después de la traición de mi hermano Devlin carecemos de fuerza y tropas suficientes para hacer frente a sus continuos asaltos.$B$BYvette, si recibes esta carta, deberás saber que Devlin es un traidor.' WHERE `entry`='252';
UPDATE `locales_page_text` SET `Text_loc6`='Devlin perdió la esperanza e intentó escapar a nuestro infortunio, por lo que hizo un pacto con la Plaga. No sé qué se le prometió a cambio de su traición, pero lo que sí puedo asegurar es que estaba tejido de mentiras.$B$BSi ves a mi hermano, huye de él. Es un demonio. Hace un par de noches asesinó a dos de nuestros vigilantes y ayudó a un pequeño grupo de Soldados de la Plaga a entrar por la brecha, conduciéndolos hacia donde muchos de los nuestros dormían.' WHERE `entry`='253';
UPDATE `locales_page_text` SET `Text_loc6`='Su emboscada fue brutal. Asesinaron a un tercio de nuestras tropas antes de que pudiéramos responder. Ahora solo contamos con hombres para vigilar nuestras fronteras. Solo es cuestión de tiempo: en cuanto bajemos la guardia, la Plaga nos atacará.$B$BY aunque vi el rostro de Devlin en medio de todos esos hombres desesperados y las fuerzas de la Plaga aquella noche maldita, se escabulló antes de que pudiera atraparlo. Tal vez esté muerto, o viva aún como agente de la Plaga.$B$BEn cualquier caso, es un monstruo.' WHERE `entry`='254';
UPDATE `locales_page_text` SET `Text_loc6`='Devlin nos advirtió que si intentábamos enfrentarnos a la Plaga, estaríamos condenados. Nos lo dijo cuando entró por primera vez en los Claros de Tirisfal, antes de que rodearan nuestro hogar. Quizás estaba suplicando que nos marcháramos, o quizás se deleitaba.$B$BSiempre ha sido difícil comprender las razones de Devlin. Desde que era niño, siempre fue un misterio para nosotros.' WHERE `entry`='255';
UPDATE `locales_page_text` SET `Text_loc6`='Pero tenía razón. Estábamos perdidos. La familia Agamand está condenada a la desaparición. Aunque yo viva aún, sé que pronto moriré.$B$BNo tengo miedo, y no lamento haberme quedado con los míos. Pero debes saber que mi último pensamiento te lo dedicaré a ti.$B$BSigue con vida, mi amada Yvette, para poder irme en paz,$B$BThurman' WHERE `entry`='256';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestros planes avanzan adecuadamente: como dije antes, nos aliamos con un grupo de humanos que se hacen llamar la Hermandad. Sus líderes son antiguos nobles de Alterac y ansían volver a dominar estos territorios.$B$BPor eso hemos llegado a un acuerdo. Un acuerdo que lamentarán.' WHERE `entry`='257';
UPDATE `locales_page_text` SET `Text_loc6`='La Hermandad apenas tiene influencia en estas tierras, pero conocen bien la zona y cuentan con suficiente mano de obra para realizar los próximos trabajos. Con un buen guía, confío en que pronto la Hermandad volverá a reinar en Alterac.$B$BAdemás, les hemos pedido que vuelvan a tomar la ciudad de Stromgarde, cerca de las Tierras Altas de Arathi. Esta ciudad es campo de batalla para nuestras fuerzas, ogros y humanos de Stromgarde, pero creemos que con tácticas bien coordinadas recuperaremos nuestra ciudad, antiguamente hermosa.' WHERE `entry`='258';
UPDATE `locales_page_text` SET `Text_loc6`='Y cuando esto ocurra, podremos iniciar la siguiente etapa de nuestro plan. Con una nación propia y una base de poder en esta región, allanaremos el camino para el Tercer Ejército.$B$B¡Pronto, los Señores de la Legión nos volverán a recompensar con su lluvia de fuego!' WHERE `entry`='259';
UPDATE `locales_page_text` SET `Text_loc6`='Y cuando esto ocurra, podremos iniciar la siguiente etapa de nuestro plan. Con una nación propia y una base de poder en esta región, allanaremos el camino para el Tercer Ejército.$B$B¡Pronto, los Señores de la Legión nos volverán a recompensar con su lluvia de fuego!' WHERE `entry`='260';
UPDATE `locales_page_text` SET `Text_loc6`='Pero, ¿por qué digo todo esto? Lo afirmo para dar credibilidad a lo que voy a escribir, ya que estas fueron palabras de Kegan y es mi deseo que, después de leer este diario, mis compañeros entiendan por qué creo en ellas:$B$B\"Vestigios de dioses antiguos yacen en las huecas profundidades del mundo. Ahora, las nuevas fuerzas intentan utilizar aquel antiguo poder, por lo que aquellos que lo consigan, tendrán en sus manos un arma terrible para combatir a sus enemigos.\"' WHERE `entry`='261';
UPDATE `locales_page_text` SET `Text_loc6`='Magistrado Rutherford Ladoquemao$BTodas las deudas saldadas.$B$BHerrero Avery Verrintan$BTodas las deudas saldadas.$B$BDependiente Horracio Corzalbal$BTodas las deudas saldadas.$B$BConcejal Giligil$BTodas las deudas saldadas.$B$BConcejal Garfios$BTodas las deudas saldadas.$B$BGranjero Getz$BTodas las deudas saldadas.$B$BGranjero Ray$BDeuda pendiente. Promesa de pago en productos agrícolas durante la cosecha.$B$BGranjero Lyion$B$BDeuda pendiente. Expulsado de sus tierras.' WHERE `entry`='262';
UPDATE `locales_page_text` SET `Text_loc6`='Granjera Kalaba$BDeuda pendiente. Pago de bienes agrícolas prometidos en época de cosecha.$B$BCiudadano Wilkes$BTodas las deudas pagadas.$B$BCiudadano Bonoan$BTodas las deudas pagadas.$B$BMinero Wellty$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.$B$BMinero Sidney$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.$B$BMinero Hackett$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.' WHERE `entry`='263';
UPDATE `locales_page_text` SET `Text_loc6`='Minero Orwell$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.$B$BMinero Fitzgerald$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.$B$BCiudadano Netherand$BTodas las deudas pagadas.$B$BCiudadana May$BTodas las deudas pagadas.Supervisor Bonds$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.' WHERE `entry`='264';
UPDATE `locales_page_text` SET `Text_loc6`='Minero Orwell$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.$B$BMinero Fitzgerald$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.$B$BCiudadano Netherand$BTodas las deudas saldadas.$B$BCiudadano May$BTodas las deudas saldadas.$B$BCapataz Bonds$BDeuda pendiente. Pago en espera de la próxima entrega de mineral de Mina Azur.' WHERE `entry`='265';
UPDATE `locales_page_text` SET `Text_loc6`='Kegan Darkmar, líder de una pequeña banda de no-muertos que vino a vernos en busca de asilo para escapar de sus \"hermanos\", desafía nuestras actitudes habituales en relación con los de su especie. Puede que su piel se esté pudriendo o que la sangre hace tiempo que no fluye por sus venas, pero actúa con nobleza, mostrando más preocupación por la seguridad de sus compatriotas que por la suya propia.$BDe hecho, percibo en él una humanidad que, siendo franco, a veces echo de menos en algunos humanos que conozco.' WHERE `entry`='266';
UPDATE `locales_page_text` SET `Text_loc6`='Pero, ¿por qué digo todo esto? Lo afirmo para dar credibilidad a lo que voy a escribir, ya que estas fueron palabras de Kegan y es mi deseo que, después de leer este diario, mis compañeros entiendan por qué creo en ellas:$B$B\"Vestigios de antiguos dioses yacen en las huecas profundidades del mundo. Ahora, las nuevas fuerzas intentan utilizar aquel antiguo poder, por lo que aquellos que lo consigan, tendrán en sus manos un arma terrible para combatir a sus enemigos.\"' WHERE `entry`='267';
UPDATE `locales_page_text` SET `Text_loc6`='Tales fueron las palabras pronunciadas por Kegan cuando extendió su colgante de sangrita hacia mí, y había en sus ojos temor, tal vez reverencia. Cuando sus manos se unieron a las mías, permanecieron inmóviles, como si se negara a entregar el colgante. Sentí cierta repugnancia, pero no recuerdo si mi rechazo se debía a la carne muerta apretando mi mano o si era el colgante el que me hacía estremecer.$B$BPero puedo asegurar que notaba una fuerza en su interior. Una fuerza profunda, oculta, sedienta. Anhelando ser liberada.' WHERE `entry`='268';
UPDATE `locales_page_text` SET `Text_loc6`='Aunque mis compañeros en Dalaran tomaron la precaución de estudiar las sangritas que Kegan y sus seguidores llevaban encima, en lugar de poner a los cuatro refugiados en cuarentena y dejar las sangritas en sus cuerpos, la sinceridad de Kegan me obligó a estudiar su colgante.' WHERE `entry`='269';
UPDATE `locales_page_text` SET `Text_loc6`='Deseaba comunicar a mis compañeros que este tipo de piedra tenía propiedades mágicas, y si nosotros, los zahoríes de Dalaran, no teníamos la intención de aprovechar los poderes de las sangritas, al menos debíamos conocer sus propiedades, porque estaba claro que nuestros enemigos las utilizarían contra nosotros tarde o temprano.$B$BY así fue como inicié mi investigación.' WHERE `entry`='270';
UPDATE `locales_page_text` SET `Text_loc6`='Realicé algunas pruebas, suponiendo que la sangrita era un tipo de roca, como el cuarzo o la obsidiana. Por ello llevé a cabo una serie de procedimientos para determinar lo siguiente: qué minerales contenía la sangrita, qué fuerzas actuaban para producir su color y dureza, así como otras propiedades comunes a las rocas y a los minerales. Pero para mi decepción, el colgante de sangrita no reaccionó a mis pruebas como lo haría un mineral corriente.' WHERE `entry`='271';
UPDATE `locales_page_text` SET `Text_loc6`='De hecho, ¡solía reaccionar de la forma contraria a la esperada! Era como si el colgante estuviera boicoteando mis experimentos deliberadamente.$B$BComo si tuviera vida y mente propias.$B$B$GEnfadado:Enfadada;, pero sin desanimarme, descarté la idea de que el colgante fuera un trozo de roca inerte, cobrando mayor fuerza la teoría de que se trataba de un ser vivo.$B$BPero nuevamente, me equivoqué.' WHERE `entry`='272';
UPDATE `locales_page_text` SET `Text_loc6`='Ninguna de mis pruebas reveló información alguna sobre el origen de la sangrita. Aquella vez, lo único que saqué en claro era que la sangrita no estaba ni viva... ni muerta.$B$BPero fue en ese momento, a punto de fracasar, cuando se produjo el mayor avance. En mi última prueba, utilicé un vaso de precipitados cuyo borde estaba roto, quedando un pequeño orificio irregular en la parte superior. Al acabar la prueba, sin resultado alguno, fui a limpiar la mesa de trabajo y me corté con el vaso.' WHERE `entry`='273';
UPDATE `locales_page_text` SET `Text_loc6`='El corte no era profundo, sin embargo, brotó mucha sangre de la herida. Antes de ponerme un vendaje, buena parte de mi sangre cayó sobre la mesa de trabajo.$B$BY cuando volví a limpiarla, advertí algo muy extraño...' WHERE `entry`='274';
UPDATE `locales_page_text` SET `Text_loc6`='La sangre que había derramado cerca del colgante de sangrita se desplazaba lentamente hacia la gema, como si esta poseyese una extraña fuerza de atracción. La sangre que había entrado en contacto con el colgante aparentemente desapareció y el tono púrpura de la piedra aumentaba a medida que bebía de mi sangre.' WHERE `entry`='275';
UPDATE `locales_page_text` SET `Text_loc6`='Después de presenciar este fenómeno, la cabeza empezó a darme vueltas, quizás por mi herida reciente (aunque realmente no creí que fuera la causa, puesto que no había perdido tanta sangre) o quizás por mi descubrimiento, tras innumerables intentos, de una de las propiedades de la sangrita. Tanteando a mis espaldas, arrastré mi taburete, me senté y cavilé durante un rato. Numerosas preguntas rondaban mi cabeza, mareándome y amenazando con hacerme perder el equilibrio.$B$B¿Acaso la sangrita bebe sangre? ¿Está sedienta de sangre? ¿Atrae la sangre?' WHERE `entry`='276';
UPDATE `locales_page_text` SET `Text_loc6`='¿O acaso está hecha de sangre? Y si es así, ¿de quién es la sangre? ¿La mía? ¿O la de cualquier humano? ¿La de algún animal?$B$BO quizás esta gema sea la sangre de algo desconocido, algo que Kegan temía y veneraba al mismo tiempo cuando me dio su colgante.$B$BEsta es la pregunta que necesita respuesta. Es la clave.' WHERE `entry`='277';
UPDATE `locales_page_text` SET `Text_loc6`='La llama de la curiosidad se reavivó en mi interior y volví a mis experimentos. Esta vez no hice conjeturas, realizando las pruebas de forma metódica. Esto requirió mucho más trabajo, pero había mayores probabilidades de hacer descubrimientos.$B$BY aunque mi laboratorio es pequeño y no dispongo de ayudantes, descubrí otra cualidad más inquietante en la sangrita...' WHERE `entry`='278';
UPDATE `locales_page_text` SET `Text_loc6`='Además de sangre, hay fuerzas elementales fundidas en la piedra. Fuego, agua, truenos y roca se mezclan con la sangre (sí, pero ¿la sangre de quién?) y, si bien esta mezcla es inerte en el exterior, todas estas fuerzas parecen chocar entre sí en el interior. Este sorprendente y premonitorio material planteaba nuevas preguntas.' WHERE `entry`='279';
UPDATE `locales_page_text` SET `Text_loc6`='Pero para responder a estas preguntas, era necesario realizar más estudios y experimentos en el colgante y mucho me temo que el Campo de Reclusión de Lordamere no puede conseguir ni la mano de obra ni el equipo para llevar a cabo esta tarea. Así que envié el colgante de sangrita con un mensajero a Dalaran con instrucciones específicas sobre el tipo de pruebas que era necesario realizar para evitarles mis iniciales decepciones.' WHERE `entry`='280';
UPDATE `locales_page_text` SET `Text_loc6`='Mientras esperaba los resultados de estas pruebas, me dediqué a hablar con Kegan. Aunque constantemente le presioné para que me revelara lo que sabía sobre las sangritas, nunca me contó nada distinto de lo que me dijo el día que me entregó el colgante. Y no solía hablar sobre la época que pasó con el grupo de \"Renegados\", nombre con el que se refería a su clan de no-muertos.' WHERE `entry`='281';
UPDATE `locales_page_text` SET `Text_loc6`='Pero Kegan estaba ansioso de hablar de otras cosas, particularmente de su infancia en Lordaeron, antes de su caída.$B$BSigue amando aquel reino perdido, aunque ahora esté arruinado y muerto.$B$BMi creciente afecto por Kegan me llenó de paciencia, mientras esperaba los resultados de mis pruebas.' WHERE `entry`='282';
UPDATE `locales_page_text` SET `Text_loc6`='Pero transcurridas varias semanas sin tener noticias, mi paciencia se agotó, así que, tras realizar varias averiguaciones sobre Dalaran, supe que la sangrita nunca había llegado a su destino. Mi emisario se había perdido en el camino y el colgante de sangrita ¡se había perdido con él!$B$BNo eran graves noticias, porque, aunque Kegan y sus seguidores siguen conservando ejemplares de sangrita con los que hacer experimentos, temo que el colgante haya caído en las manos equivocadas.' WHERE `entry`='283';
UPDATE `locales_page_text` SET `Text_loc6`='He enviado a otro mensajero a Dalaran y he oído que, hasta el momento, siguen buscando el colgante en las ruinas que están fuera de nuestra esfera de protección.$B$BSolo espero que no sea demasiado tarde.' WHERE `entry`='284';
UPDATE `locales_page_text` SET `Text_loc6`='Cuento de Gri\'lek el Trotamundos$B$B[...El comienzo de la tablilla estaba desgastado y borrado. Pero el final era legible...]$B$BGri\'lek caminaba ruidosamente por la jungla. Sus ojos eran dos llamas y su pecho rugía, pues la ira lo consumía.$B$BEnfurecido, rugió mirando al cielo y levantó un brazo. Alzó el brazo izquierdo, que era fuerte y certero porque cazaba sin su gemelo.$B$BY es que el brazo derecho de Gri\'lek ya no estaba y no volvería nunca más.' WHERE `entry`='285';
UPDATE `locales_page_text` SET `Text_loc6`='LEYENDAS DE LOS TROLS, VOLUMEN III$BPiedra de las Mareas$B$BEscrito por$BArchimago Ansirem Tejepista$BDalaran$B$BINTRODUCCIÓN$B$BEl antiguo Imperio Gurubashi alimentó leyendas inquietantes y fascinantes que, sin duda, pueden rastrearse en la zona, ya que el estudio de sus sistemas de creencias y prácticas sociales han señalado la gran importancia del entorno natural.$B$BMientras ahondaba en muchos' WHERE `entry`='286';
UPDATE `locales_page_text` SET `Text_loc6`='Kurdran Martillo Salvaje$B$BAfamado cazador de dragones. Maestro de grifos de Pico Nidal. Comandante de la División de jinetes de grifos adscrita a la Expedición de la Alianza que marchó hacia Draenor, tierra de los orcos. Presuntamente fallecido.$B$B\"Oiremos la llamada de Cielo-Ree surcar los vientos. Escucharemos el rugir de tu martillo bordeando las cumbres. Cabalga hasta el más allá, hermano. Las salas de nuestros ancestros te aguardan.\"$B$BGran señor feudal Falstad Martillo Salvaje' WHERE `entry`='287';
UPDATE `locales_page_text` SET `Text_loc6`='Capitana forestal Alleria Brisaveloz$B$BAfamada Cazatroles de Quel\'Thalas. Jefa de exploradores y agente de inteligencia para la Expedición de la Alianza que marchó hacia Draenor, tierra de orcos. Presuntamente fallecida.$B$BQuerida hermana, tu corazón atravesó el viento cual hiriente flecha. Fuiste la más brillante de nuestra Orden, la más amada entre nosotros.$B$BSylvanas Brisaveloz - General forestal de Quel\'Thalas' WHERE `entry`='288';
UPDATE `locales_page_text` SET `Text_loc6`='Archimago Khadgar del Kirin Tor$B$BAntiguo aprendiz de Medivh. Comandante Mayor de la Expedición de la Alianza que marchó hacia Draenor, tierra de orcos. Presuntamente fallecido.$B$BNadie exploró tan altruistamente las oscuras entrañas de la magia y la guerra. Te deseamos lo mejor, audaz aventurero... estés donde estés.$B$B- Antónidas - Archimago de Dalaran' WHERE `entry`='289';
UPDATE `locales_page_text` SET `Text_loc6`='General Turalyon$B$BAntiguo teniente de Lord Anduin Lothar. Caballero de la orden de la Mano de Plata. Alto general de la Expedición de la Alianza que marchó hacia Draenor, tierra de orcos. Presuntamente fallecido.$B$BEsarus thar no\'Darador\' - Servimos por la sangre y el honor.$BViejo amigo, fuiste la mano derecha de la justicia y la virtud. Honraremos tu nombre en nuestras salas por siempre.$B$BLord Uther el Iluminado, Caballero de la orden de la Mano de Plata$B' WHERE `entry`='290';
UPDATE `locales_page_text` SET `Text_loc6`='Danath Aterratrols$B$BComandante de Milicia de Stromgarde. Consejero táctico del general Turalyon, Comandante de Fuerza de la Expedición de la Alianza que marchó hacia Draenor, tierra de orcos. Presuntamente fallecido.$B$BQuerido sobrino, honramos tu memoria y valoramos tu sacrificio. Desde la fundación de nuestro glorioso imperio, la senda del valor siempre se ha abonado con la sangre de los héroes.$B$BThoras Aterratrols, Señor de Stromgarde' WHERE `entry`='291';
UPDATE `locales_page_text` SET `Text_loc6`='Aquí yace Uther el Iluminado$BPrimer paladín, fundador de la orden de la Mano de Plata$B$BUther vivió y murió para defender el reino de Lordaeron. Aunque fue traicionado por su discípulo más amado, creemos que su espíritu pervive. Continúa observándonos, aun cuando las sombras acechen nuestra tierra desolada. Su luz es la luz de toda la humanidad y, siempre que hagamos honor a su memoria, esta nunca se perderá.$BAnónimo' WHERE `entry`='292';
UPDATE `locales_page_text` SET `Text_loc6`='aspectos de su adoración a las culebras, en volúmenes anteriores de este estudio, analizo aquí la interesante y peculiar relación de los trols con el mar.$B$B$BEL MARE MAGNUM$B$BEl Imperio Gurubashi estaba rodeado por el mar en tres de sus costados, así que no resulta sorprendente que el agua desempeñara un papel preponderante en su sociedad. Si bien los trols eran capaces de ir de un lado a otro y controlar vastas extensiones de jungla dentro de su imperio, el mar los eludía. Era vasto e inconmensurable, sin' WHERE `entry`='293';
UPDATE `locales_page_text` SET `Text_loc6`='duda un vecino desconcertante para los poderosos trols.$B$BEs preciso aclarar que recientes descubrimientos sugieren que los trols no mostraban demasiado interés en explorar las tierras que había más allá del Mare Magnum. Aunque se han hallado especies de trols en toda la extensión de Azeroth, Khaz Modan y Lordaeron, apenas se han encontrado vestigios de su civilización en las tierras recientemente descubiertas de Kalimdor o en las islas de los Mares del Sur. Tanto si esto demuestra la nula disposición de' WHERE `entry`='294';
UPDATE `locales_page_text` SET `Text_loc6`='los trols a aventurarse fuera de sus tierras, como si se trata de la ausencia de tecnologías para acometer tal empresa, es preciso una investigación y un análisis que escapan al alcance de estos escritos.$B$BNo obstante, resulta difícil ignorar una presencia tan importante. De hecho, nuevos hallazgos en las extensas ruinas trol de la Vega de Tuercespina muestran un aspecto de su relación con el mar anteriormente desconocido y no documentado.$B$B$BLA PIEDRA DE LAS MAREAS$B$BRecientes descubrimientos efectuados durante el reconocimiento' WHERE `entry`='295';
UPDATE `locales_page_text` SET `Text_loc6`='de las ruinas trol de la Vega de Tuercespina contienen referencias a un objeto conocido como la \"Piedra de las Mareas\". Diversos fragmentos de leyendas trol pueden unirse para pintar un cuadro bastante completo de la Piedra y establecer su significado para el Imperio Gurubashi.$B$BAl parecer, la Piedra de las Mareas ofrecía a su portador el control del agua en sus diversas formas: ríos, lluvia y mareas. Dadas las historias relacionadas con el uso de la Piedra de las Mareas, he conjeturado que se trata' WHERE `entry`='296';
UPDATE `locales_page_text` SET `Text_loc6`='en realidad, de una manifestación física de los poderes del Señor del Agua, un poderoso elemental de los mares. La razón y la manera en que un objeto de estas características escapa al control del Señor del Agua y cae en manos de los trols es otra cuestión que está fuera de mi alcance.$B$B$BEL PORTAMAREAS$B$BImitando el movimiento de las olas, la Piedra de las Mareas entró y salió del mundo de los trols, sin permanecer en manos de ellos más de una generación cada vez.' WHERE `entry`='297';
UPDATE `locales_page_text` SET `Text_loc6`='Según las leyendas trol, la primera vez que la Piedra de las Mareas entró en el Imperio Gurubashi, fue encontrada por un guerrero trol que caminaba por la costa de Tuercespina. Halló una misteriosa piedra azul que contenía unos filamentos lechosos que flotaban y fluían en su interior. Intrigado por la piedra, el guerrero la guardó y continuó su camino.$B$BTras unas semanas, o quizás meses, el guerrero descubrió que la piedra le había otorgado el control sobre el agua. Podía invocar' WHERE `entry`='298';
UPDATE `locales_page_text` SET `Text_loc6`='elementales de agua, criaturas hechas totalmente de agua, realizando hazañas solo propias de los poderosos magos del Kirin Tor.$B$BEl guerrero viajó a Zul\'Gurub, en pleno corazón del Imperio, para mostrar sus nuevas habilidades al Emperador. Le escucharon en la corte tras demostrar sus poderes en el centro de la capital imperial. Estos poderes sorprendieron al Emperador Gurubashi, quien de inmediato le otorgó un sitio de honor en la corte, nombrándolo guerrero \"Portamareas\", dejando atrás su' WHERE `entry`='299';
UPDATE `locales_page_text` SET `Text_loc6`='antiguo nombre.$B$BDurante años, el Portamareas sirvió al Imperio Gurubashi, invocando en la batalla a los elementales de agua sometidos a su voluntad y manipulando el cauce del agua en la Vega de Tuercespina para beneficio del Imperio. Pero con el paso de los años, el Portamareas se volvió cada vez más solitario, tendiendo a permanecer largos periodos fuera de la corte.$B$BEl Portamareas ocultaba un secreto a la atenta mirada la corte. Las cualidades que le otorgaba la Piedra de las Mareas incluían una maldición. Con el paso de' WHERE `entry`='300';
UPDATE `locales_page_text` SET `Text_loc6`='los años, el Portamareas se fue desvaneciendo. Cada vez que la marea subía y bajaba, el Portamareas iba perdiendo paulatinamente su forma corpórea original, cobrando y perdiendo vida de forma intermitente. Hasta que, en el ocaso de su existencia, viajó al lugar en el que había encontrado la Piedra, se internó en el mar y desapareció para siempre.$B$BGeneraciones más tarde, la Piedra de las Mareas regresó a las playas de Tuercespina y eligió a otro Portamareas, un trol que la condujo hasta' WHERE `entry`='301';
UPDATE `locales_page_text` SET `Text_loc6`='Zul\'Gurub. Este proceso se repitió durante generaciones: la Piedra aparecía con la marea y, posteriormente, el Portamareas desaparecía en el mar.$B$BEn varios sitios se cuentan versiones modernas del mito de la Piedra de las Mareas, pero lo cierto es que resulta sorprendente que un objeto tan poderoso apareciera con tanta regularidad, por designios de un ser desconocido.' WHERE `entry`='302';
UPDATE `locales_page_text` SET `Text_loc6`='Órdenes de los Velasangre$B$BPoned atención a estas palabras, mis valientes, ya que solo podremos echar un trago en la Bahía del Botín.$B$BHe enviado al viejo Dunken para que explore el norte y le he dicho que espere a la señal.$B$BLa Damisela Afortunada fondeará en el cabo del sur por motivos de reabastecimiento. El retaco del Capitán Aguaserena va a preparar a su tripulación para reunir todo el botín robado. Estoy seguro de que el burro de Brutus estará al frente de todo.' WHERE `entry`='303';
UPDATE `locales_page_text` SET `Text_loc6`='Ordenes de los Velasangre presta atención a lo que aquí dice, mis valientes, ya que sólo conseguimos un proyectil en la Bahía del Botín. He enviado al viejo borracho de exploración al norte y le he dicho que espere a la señal. La damisela afortunada será anclada en el cabo sur con objeto de abastecernos. Capitán aguasmansas, poco a paco, debe hacer que su grupo transporte todo el botín saqueado. Seguro que Brutus, que alcornoque, estará encima de todas las cosas.' WHERE `entry`='305';
UPDATE `locales_page_text` SET `Text_loc6`='Quiero que el capitán Volcayecto lleve el Mareas Vivas a la costa desde el sudeste. Enviaremos a su tripulación a pie cuando se dé la señal.$B$BEl bruto de Fausal puede convencer a los hombres fácilmente. Quiero que marquéis a Revilgaz y a Cuernomarino. Por mí que se pudran en el fondo del mar.$B$BOs hago responsables de que rescatéis todo lo que podamos usar en nuestro pequeño proyecto con VanCleef en las Minas de la Muerte.' WHERE `entry`='306';
UPDATE `locales_page_text` SET `Text_loc6`='Daré la orden a Parchierro para que abra fuego. La descarga de artillería evitará que los barcos de los Aguasnegras entren o salgan del puerto.$B$BDespués de que el Velo Carmesí haya descargado sus cañones, lo llevaré junto al Mareas Vivas para que sirva de apoyo y proporcione suministros.$B$BUna vez que Fausal y sus hombres hayan bombardeado la ciudad y todos estén en cubierta, zarparemos y cañonearemos la bahía una vez más desde la Costa Salvaje para asegurarnos.$B$BMaestro de flota Firallon' WHERE `entry`='307';
UPDATE `locales_page_text` SET `Text_loc6`='Escapar de Zanzil en ese buque destartalado parecía buena idea en otros tiempos, pero creo que la muerte habría sido un destino más apropiado.$B$BPensé que mis ancestros me han ayudado al permitirme escapar de su ira. Pero ahora me pregunto qué gano con vivir aquí encarcelado.' WHERE `entry`='308';
UPDATE `locales_page_text` SET `Text_loc6`='La bestia que gobierna esta isla es terriblemente poderosa. Las otras criaturas que la habitan se encogen de miedo ante su presencia: El gorila Cieloleno y el acechador Jaguero, ambos por igual. Todavía puedo oír esos rugidos que me hacen estremecer.$B$BEstas criaturas seguro que detendrían a cualquiera que viniera a la isla. Debo tener fe en mis ancestros.' WHERE `entry`='309';
UPDATE `locales_page_text` SET `Text_loc6`='Echo de menos a mi tribu, mi hogar. Prefiero mil veces el viento de las extensas planicies o el atardecer en Cima del Trueno al calor de esta jungla.$B$BPuedo ver el océano desde aquí, pero esto no refresca mi cuerpo ni alivia mi ánimo. Solo me recuerda mi encarcelamiento.' WHERE `entry`='310';
UPDATE `locales_page_text` SET `Text_loc6`='Primer día:$B6.00 a 11.00: campamento alto$B11.00 a 11.30: descanso para comer$B11.30 a 4.00: paso del cañón$B4.00 a 7.30: campamento alto$B$BSegundo día:$BEl mismo itinerario que el del primer día$B$BTercer día:$BEl mismo itinerario$B$BCuarto día:$BEl mismo itinerario, con una excepción. Justo antes del atardecer, Thorsen me pidió que lo cubriera mientras atravesaba el paso del cañón. Supongo que fue a encontrarse con nuestros agentes.$B$BPero regresó una hora más tarde. Esto me sorprendió. Esperaba que se reuniera con nuestros agentes y que se reincorporara a Kurzen. Algo va mal.' WHERE `entry`='311';
UPDATE `locales_page_text` SET `Text_loc6`='Quinto día:$BThorsen estuvo muy inquieto hoy. Aquello que vio o la persona que encontró en su excursión de ayer debió de asustarle mucho.$B$BCreí que nuestros agentes habían hablado con él, pero cuando pregunté a Thorsen adónde había ido mientras yo lo cubría, permaneció callado. Él se considera mi amigo y cree que me está protegiendo de un secreto peligroso.$BSu lealtad es errónea, pero fuerte. Cuando se reincorpore a nosotros, será una adquisición valiosa para las fuerzas de Kurzen.' WHERE `entry`='312';
UPDATE `locales_page_text` SET `Text_loc6`='Sexto día:$BA primera hora de la mañana, Thorsen habló largamente con el sargento Yohwa. Tuve que fingir que dormía para permanecer cerca y oír la conversación. Pero supongo que hablaron de la excursión de Thorsen.$B$BLo observé atentamente durante la comida y vi que Yohwa hacía lo mismo. Más adelante, Doren llamó a Thorsen. Los tres comparten un secreto. Doren y Yohwa deben saber que Thorsen se está quedando con tus agentes.$B$BHace de espía para los rebeldes.' WHERE `entry`='313';
UPDATE `locales_page_text` SET `Text_loc6`='No podemos darle a Thorsen más información y debemos matarlo. Ya no es útil para nuestro Coronel.$B$BAcabaré con Thorsen en cuanto tenga la oportunidad, pero si recibes esta carta, sabrás que aún no he podido hacerlo.$B$BEn tal caso, propongo que la siguiente reunión de Thorsen con nuestros agentes sea decisiva.$B$BSalve Kurzen,$BM' WHERE `entry`='314';
UPDATE `locales_page_text` SET `Text_loc6`='El Consejo fue sabio al nombrarte líder del Despertar de Argus para defender sus estrategias en tierra de humanos y enanos. Y me honra servirte en Alterac.$B$BEn el nombre de Sargeras,$B $BNagaz, $BHeirofante de Argus' WHERE `entry`='315';
UPDATE `locales_page_text` SET `Text_loc6`='¡Idiotas inútiles!$B$BMi plan era destruir ambos puentes simultáneamente. ¿Y qué habéis hecho vosotros, estúpidos ingenuos? Habéis hecho solo la mitad del trabajo, alertando a toda la Alianza sobre nuestras intenciones. ¡So animales!$B$BTenéis una única oportunidad de enmendar vuestro error. Tenemos explosivos de reserva en las Tierras Altas de Arathi, cerca del puente.$B$B¡Acabad el trabajo si valoráis vuestras míseras vidas!$B$BKam Furiahonda' WHERE `entry`='316';
UPDATE `locales_page_text` SET `Text_loc6`='Dun Modr de los Humedales$B$BMi Querida Sara,$B$BLas señales son muy fuertes en pocos días nos moveremos para tomar el Puente Thandol. No sé si podre escribirte de nuevo. Me siento impulsado a escribir unas líneas que puede que sean las últimas que tus ojos lean.' WHERE `entry`='317';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestra tarea puede que solo sea de unos pocos días, y me colmaría, y puede que sea una que me cueste la vida. Si es necesario estoy listo para caer en el campo de batalla de la Alianza.$B$BNo me cabe duda de la causa a la que estoy comprometido y de que no vacilaré ante ella.' WHERE `entry`='318';
UPDATE `locales_page_text` SET `Text_loc6`='Sé con qué fuerza el Reino de Forjaz apoya en el triunfo de la Alianza, y cuán grande es la deuda que tenemos con aquellos que nos han precedido con su sangre y sufrimiento de las grandes guerras, y yo estoy dispuesto, perfectamente dispuesto a sacrificar todas las alegrías en esta vida para ayudar a mantener esta alianza y pagar esa deuda.' WHERE `entry`='319';
UPDATE `locales_page_text` SET `Text_loc6`='Sara, mi amor por ti es inmortal, parece como si me atasen poderosos cables que ni la omnipotencia podría romper, pero el amor de mi Reino me sobreviene como un viento fuerte y me lleva irresistiblemente con todas estas cadenas al campo de batalla.' WHERE `entry`='320';
UPDATE `locales_page_text` SET `Text_loc6`='Los recuerdos de todos esos momentos felices que he pasado contigo vienen arrastrándose sobre mí y me siento más profundamente agradecido a la Luz y a ti por haber disfrutado tanto y lo difícil que es para mí renunciar a ellos y reducir a cenizas la esperanza de los años venideros que aún podríamos haber vivido y haberno amado juntos y ver crecer a nuestros hijos en nuestra aldea como honorables enanos' WHERE `entry`='321';
UPDATE `locales_page_text` SET `Text_loc6`='Sé que tengo poco que pedirle a la Divina Providencia, pero algo me murmura - tal vez es la plegaria de mi pequeño Edgar - que voy a volver sano y salvo con mis seres queridos. Si no lo hiciese, nunca mi querida Sara olvides lo mucho que te quiero y cuando mi último aliento escape en el campo de batalla susurrare tu nombre.$B$BPerdona mis muchos defectos y el dolor que pude causarte, lo desconfiado, y lo tonto he sido muchas veces.' WHERE `entry`='322';
UPDATE `locales_page_text` SET `Text_loc6`='Con qué gusto limpiaré con mis lágrimas cada pequeño momento de tu felicidad y lucharé con todas las desgracias de este mundo para protegerte a ti y a mis queridos hijos del mal.$B$BPero no puedo.$B$BTe miraré desde el Vacío Abisal y pasaré cerca de ti mientras te zarandea la tormenta con tu preciada carga y espero triste con paciencia hasta que nos encontremos para no separarnos más.' WHERE `entry`='323';
UPDATE `locales_page_text` SET `Text_loc6`='Una torre de agua se eleva desde el océano... Neptulon ha enviado descomunales Krakken al desolado I\'lalai. Eran tan descomunales sus formas que selvas de algas colgaban de sus miembros, y estos leviatanes nadaban entre los cuerpos.$B$BEl Krakken más grande levantó sus brazos y los estrelló contra el océano, lanzando enormes olas a su alrededor. Y estas azotaron las costas de I\'lalai.' WHERE `entry`='324';
UPDATE `locales_page_text` SET `Text_loc6`='Los Krakken rugieron y sus voces resonaron como una tormenta marina:$B$B\"Allá vamos.\"$B$BSin titubear, Min\'loth invocó su magia. Las olas que llegaron hasta I\'lalai se separaron a ambos lados, inundando la jungla en toda su extensión. Min\'loth pidió entonces a sus esbirros que pronunciaran hechizos de vínculo y se escuchó un intenso barullo cuando decenas de trols elevaron sus cánticos.$B$BUna voz destacó sobre las demás.' WHERE `entry`='325';
UPDATE `locales_page_text` SET `Text_loc6`='Min\'loth bramó y su magia recogió el poder de los hechizos de su esbirro, lanzándolos hacia los Krakken que se aproximaban.$B$BLas aguas se separaron y el hechizo de Min\'loth viajó veloz hacia los siervos de Neptulon. Los relámpagos desgarraron el cielo y el hechizo alcanzó a los monstruos. Miles de truenos cayeron sobre el agua hirviendo y los cráteres ardieron en la tierra.$B$BMin\'loth aulló victorioso, seguro de que su hechizo derrotaría a las enormes bestias.' WHERE `entry`='326';
UPDATE `locales_page_text` SET `Text_loc6`='Pero los Krakken son viejos, muy viejos. Recordaban cuando la tierra había surgido por primera vez del mar.$B$BRecordaban cuando los Antiguos reinaban y cuando los Viajeros llegaron y los expulsaron. Recordaban cuando la magia era una novedad.$B$BSon viejos y guardan muchos secretos. Y aunque el hechizo de Min\'loth era poderoso, al igual que el trol, era mortal.$B$BPor eso falló.' WHERE `entry`='327';
UPDATE `locales_page_text` SET `Text_loc6`='No pudo atrapar a los Krakken, pero logró enfurecerlos. Durante miles de años, ningún mortal les había causado daño y el encantamiento de los trols resultó doloroso.$B$BY así, se liberaron de los vínculos del encantamiento de Min\'loth, pero luego, bramaron y se enfurecieron.$B$BSe oyó un gran estruendo cuando se elevaron grandes olas desde las profundidades, dirigiéndose a la tierra. Cuando alcanzaron l\'lalai, proyectaron una sombra sobre la ciudad.$B$BPero antes de destruirla, los Krakken se detuvieron, quedando suspendidos.' WHERE `entry`='328';
UPDATE `locales_page_text` SET `Text_loc6`='Los médicos brujos trol temblaron y profirieron gritos a su maestro. Min\'loth contempló las montañas del mar, con aire fatal y desafiante. Se volvió hacia sus adeptos y susurró unas palabras, y los trols grabaron sus últimas palabras en piedra. Luego, Min\'loth se volvió hacia los Krakken que se avecinaban.$B$BHizo una mueca y agitó su báculo, su último acto enérgico.$B$BLuego, los Krakken hicieron caer toda su furia en Min\'loth y el océano se precipitó sobre l\'lalai.$B$BY desapareció.' WHERE `entry`='329';
UPDATE `locales_page_text` SET `Text_loc6`='A continuación, las aguas cayeron sobre la jungla, inundándolo todo a su paso. Los trols y las bestias gritaron mientras las aguas los golpeaban y los sumergían.$B$BMuchos Gurubashi se preguntaban por qué el océano se los tragaba, pero morían igualmente, sin conocer la causa.$B$BY, finalmente, cuando las aguas alcanzaron las montañas, se detuvieron. Cuando se aplacaron, se retiraron hacia la costa y dejaron un rastro de muerte.$B$BLas aguas se retiraron, pero rodearon completamente l\'lalai, que permaneció anegada para siempre.' WHERE `entry`='330';
UPDATE `locales_page_text` SET `Text_loc6`='Y el jefe Var\'gazul, a salvo tras las montañas en Zul\'Gurub, se dirigió a la jungla y vio que su gente había desaparecido.$B$BSe sintió desesperado, ya que sus sueños de conquista se habían roto.$B$BY Min\'loth, la Serpiente, nunca fue encontrada.' WHERE `entry`='331';
UPDATE `locales_page_text` SET `Text_loc6`='Oh Sara, pero si los muertos pueden volver a Azeroth y volar alrededor de los que aman, voy a estar más cerca de tí tanto en los días más agradables como en las noches más oscuras para que tengas momentos de felicidad siempre, siempre. Y si sientes la suave brisa sobre tus mejillas esta sea mi respiración, como los ventiladores de aire fresco a tu palpitante templo, asi sea mi espíritu para tí.$B$BSara, no llores mi muerte, piensa que me he ido y espero por tí, para que nos reunamos nuevamente.$B$B--Sully' WHERE `entry`='332';
UPDATE `locales_page_text` SET `Text_loc6`='A mi Honorable Rey:$B$BEste no es lugar para que de la esposa de un humilde soldado cuestione las acciones de un gran líder como usted. Las batallas que rugen través de nuestras tierras están siendo luchadas para preservar nuestro estado de bienestar, desde las frías aguas del Loch Modan a los imponentes picos nevados de Dun Morogh.' WHERE `entry`='333';
UPDATE `locales_page_text` SET `Text_loc6`='Pero las guerras que emprendemos en nombre de la preservación ¿qué precio pagamos por ellas? La muerte del soldado caído, acaso ¿no nos damos cuenta de que la misma vida que nos esforzarnos en guardar se nos escapa en el campo de batalla?$B$B¿Bajo cuantas espadas y hachas debemos lanzar nuestro futuro? ¿Bajo cuántos pies de suelo patrio tenemos que enterrar nuestro pasado?' WHERE `entry`='334';
UPDATE `locales_page_text` SET `Text_loc6`='Este no es lugar para que la esposa de un humilde soldado haga tales preguntas.$B$BSin embargo, os escribo ahora como la viuda que soy de un soldado humilde. Mi esposo fue uno más de los miles de soldados que marchan con usted. Para mí él era el gran Balloo Sully, el padre de mis hijos y mi pareja en esta vida.$B$BPerdóname si mi pregunta a usted, oh gran líder, suena inapropiada. Pero los muertos no pueden hacer estas preguntas por sí mismos ....$B$BSu Leal súbdito ,$BSara Balloo' WHERE `entry`='335';
UPDATE `locales_page_text` SET `Text_loc6`='A los Schneider,$B$BBuenos días: Confío en que hacer negocios en el Distrito del Canal resulta conveniente. Estamos en auge, en la plaza de los comerciantes.$B$BNecesitamos prendas de vestir tejidas, ya que nuestras existencias se han acabado. Por favor, envíad un cargamento estándar tan pronto como podáis.$B$BBuena suerte y confío en que los estudios de vuestro hijo Thurman progresen adecuadamente,$B$BHarlan Bagley' WHERE `entry`='336';
UPDATE `locales_page_text` SET `Text_loc6`='Órdenes oficiales para la Flota Kul\'Tiras$B$BYo, el almirante Valiente, dirigiré el primer desembarco en las costas de Kalimdor.$B$BEn mi ausencia, el teniente Benedict será el segundo en la cadena de mando. Todas las cuestiones militares de Kul\'Tiras de carácter doméstico serán gestionadas por él. Si no envío mensaje alguno desde el Fuerte de Tiragarde transcurridos 12 meses, el teniente Benedict dirigirá la segunda oleada para conquistar la posición.' WHERE `entry`='337';
UPDATE `locales_page_text` SET `Text_loc6`='Mientras tanto, envío al teniente Alverold en una misión de exploración por el continente de Kalimdor. Sus fuerzas deben completar la misión y luego deben reunirse con nuestras fuerzas en el Fuerte de Tiragarde.$B$BSe da por supuesto que la misión del teniente Alverold tardará tiempo en realizarse. La nueva tierra parece vasta y peligrosa. Pero si la mala fortuna nos alcanza a Benedict o a mí, las tropas de Alverold volverán a tomar el Fuerte.$B$B¡Enviaremos oleada tras oleada hasta que el fuerte sea nuestro!$B$BAlmirante Valiente' WHERE `entry`='338';
UPDATE `locales_page_text` SET `Text_loc6`='Mientras tanto, envío al teniente Alverold en una misión de exploración por el continente de Kalimdor. Sus fuerzas deben completar la misión y luego deben reunirse con nuestras fuerzas en el Fuerte de Tiragarde.$B$BSe da por supuesto que la misión del teniente Alverold tardará tiempo en realizarse. La nueva tierra parece vasta y peligrosa. Pero si la mala fortuna nos alcanza a Benedict o a mí, las tropas de Alverold volverán a tomar el Fuerte.$B$B¡Enviaremos oleada tras oleada hasta que el fuerte sea nuestro!$B$BAlmirante Valiente' WHERE `entry`='339';
UPDATE `locales_page_text` SET `Text_loc6`='Registro de Reclutamiento de la Horda$B$BEsta persona ha demostrado tener una gran fuerza y destreza en nombre de la Horda. Debería ser asignada para trabajar inmediatamente en El Cruce.$B$BRespetuosamente,$B- Takrin Abrecaminos' WHERE `entry`='340';
UPDATE `locales_page_text` SET `Text_loc6`='Registro de Reclutamiento de la Horda$B$BEsta persona ha demostrado tener una gran fuerza y destreza en nombre de la Horda. Debería ser asignada para trabajar inmediatamente en El Cruce.$B$BRespetuosamente,$B- Takrin Abrecaminos' WHERE `entry`='341';
UPDATE `locales_page_text` SET `Text_loc6`='La excavación de Bael Modan$B$BEl equipo de exploración original enviado por la Liga de Expedicionarios acertó en sus descubrimientos. El suelo oculta artefactos de valor ilimitado para nuestra gente. Indudablemente, encontraremos muchas respuestas en nuestra búsqueda de sabiduría entre las arenas y las rocas de Bael Modan.$B$BSin embargo, la geología de la región implica que debemos extremar la precaución. Muchos de los depósitos de arena se han solidificado, debido a las duras condiciones y al cambiante clima.' WHERE `entry`='342';
UPDATE `locales_page_text` SET `Text_loc6`='Sin embargo, la solución resulta muy simple. Utilizando pasta de madera como absorbente, podemos combinar partes de nitroglicerina con nitrato sódico para desarrollar una potente carga explosiva, capaz de pulverizar incluso las masas más densas.$B$BTodo esto será ruidoso y perturbador, pero nuestra búsqueda tiene mucha más importancia que la comodidad de los habitantes locales. De hecho, ya hemos expulsado a una banda de minotauros de la zona, que resultaron ser un incordio.' WHERE `entry`='343';
UPDATE `locales_page_text` SET `Text_loc6`='El hecho de que estemos tratando con verdaderos bribones me lleva a creer que necesitaremos la ayuda del ejército del rey. La excavación no solo requerirá apoyo militar. De hecho, teniendo en cuenta el mundo de la política en su estado actual, tengo la sensación de que la propia situación de Bael Modan podría tener un alto valor estratégico para la Alianza.$B$BVaya, me he apartado de la cuestión. Bajo estas rocas, hay mucho trabajo que hacer. He estado demasiado tiempo escribiendo, en lugar de excavar...' WHERE `entry`='344';
UPDATE `locales_page_text` SET `Text_loc6`='El hecho de que estemos tratando con verdaderos bribones me lleva a creer que necesitaremos la ayuda del ejército del rey. La excavación no solo requerirá apoyo militar. De hecho, teniendo en cuenta el mundo de la política en su estado actual, tengo la sensación de que la propia situación de Bael Modan podría tener un alto valor estratégico para la Alianza.$B$BVaya, me he apartado de la cuestión. Bajo estas rocas, hay mucho trabajo que hacer. He estado demasiado tiempo escribiendo, en lugar de excavar...' WHERE `entry`='349';
UPDATE `locales_page_text` SET `Text_loc6`='La consola de control de churumbele puede utilizarse para regular automáticamente el flujo de los combustibles, gases, líquidos y otra maquinaria relacionada. Una vez que se haya completado la configuración inicial, debería funcionar sin problemas, con un mantenimiento mínimo.$B$BBreve listado de los componentes de la consola de control (de izquierda a derecha)$B$BI. Palanca de potencia principal$B$BLa posición de conexión activa el churumbele. Cuando la palanca esté en posición desactivada, el mecanismo de control no se desconectará de forma automática. Simplemente, esto hará que' WHERE `entry`='350';
UPDATE `locales_page_text` SET `Text_loc6`='La consola de control de churumbele puede utilizarse para regular automáticamente el flujo de los combustibles, gases, líquidos y otra maquinaria relacionada. Una vez que se haya completado la configuración inicial, debería funcionar sin problemas, con un mantenimiento mínimo.$B$BBreve listado de los componentes de la consola de control (de izquierda a derecha)$B$BI. Palanca de potencia principal$B$BLa posición de conexión activa el churumbele. Cuando la palanca esté en posición desactivada, el mecanismo de control no se desconectará de forma automática. Simplemente, esto hará que' WHERE `entry`='351';
UPDATE `locales_page_text` SET `Text_loc6`='Supervisor Fizsprocket: Junto a esta nota, encontrarás un listado del equipamiento que has solicitado, aprobado por el presidente Razdunk.$B$BMe ha pedido que te recuerde que haga hincapié en el especial interés que tiene Ventura y Cía. en realizar operaciones en Kalimdor. Nuestros puestos de perforación y actividades mineras en los Baldíos han demostrado ser rentables y eficaces, pero aún esperamos conseguir mejores cifras de producción de Mulgore, tu jurisdicción.$B$BEl consejo consultivo comprende las dificultades que supone poner en marcha' WHERE `entry`='352';
UPDATE `locales_page_text` SET `Text_loc6`='una operación a gran escala tan cerca de la tierra natal de los tauren, pero no creas que esto supone un gran impedimento para nuestros planes generales de negocio en el área.$B$BNos complace oír que han comenzado los trabajos de minería en las ricas planicies mineras de Mulgore. Por esta razón, el consejo ha aprobado concederte el equipo que solicitas. Además de las riquezas mineras, creemos que Mulgore tiene mucho más que ofrecer en lo que respecta a los aserraderos, por tanto, te enviaremos alguna de nuestras trituradoras último modelo y materias primas para construir un' WHERE `entry`='353';
UPDATE `locales_page_text` SET `Text_loc6`='molino.' WHERE `entry`='354';
UPDATE `locales_page_text` SET `Text_loc6`='comienza el proceso de enfriamiento y, a partir de entonces, tardará aproximadamente uno o dos minutos en desactivarse, dependiendo de la situación.$B$BNOTA IMPORTANTE: La palanca de alimentación principal solo puede utilizarse cuando se hayan desconectado las tres válvulas de control en la maquinaria real. Dichas válvulas pueden desconectarse en cualquier orden.$B$BLa primera es la VÁLVULA DE CONTROL PRINCIPAL, que se encuentra en la base de la gran chimenea vertical. A continuación, la VÁLVULA DE CONTROL DE COMBUSTIBLE se encuentra en la gran tubería que conecta los' WHERE `entry`='355';
UPDATE `locales_page_text` SET `Text_loc6`='tanques de combustible (debe haber tres) con la chimenea. Finalmente, la VÁLVULA REGULADORA se encuentra en la parte inferior de los dos grandes tanques cilíndricos que se introducen en la chimenea.$B$BII. Indicadores de combustible$B$BMuestra la presión actual en los tanques de combustibles primario, secundario y terciario.$B$BIII. Indicador de flujo$B$BMuestra la tasa de flujo actual dentro del sistema.' WHERE `entry`='356';
UPDATE `locales_page_text` SET `Text_loc6`='IV. Controles de flujo$B$BAumentan o disminuyen las tasas de flujo. Es controlado automáticamente por el mecanismo de control del acelerador.$B$BV. Indicador de temperatura$B$BMuestra la temperatura operativa del sistema en general.' WHERE `entry`='357';
UPDATE `locales_page_text` SET `Text_loc6`='Durante mucho tiempo, los hijos de las estrellas han vivido entre los bancos de las relucientes aguas del Pozo Eterno. Para todos es sabido que Elune, luz del crepúsculo eterno, Aspecto y diosa de la luna, permanecía en sus aguas cuando descansaba de sus tareas. Sobre las orillas del Pozo, los hijos de las estrellas, favorecidos por Elune, construyeron sus moradas, si bien sus miradas se dirigían al cielo en la noche iluminada por la luna.$B$BNo obstante, había muchas ciudades y asentamientos.' WHERE `entry`='358';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando los viejos árboles del bosque encantado fueron arrancados y derribados, la tierra tembló. Los bosquecillos y los claros al cuidado de los hijos y las hijas de Cenarius, y las torres de piedra de los hijos de las estrellas cayeron por tierra. Estaba nuestra reina, radiante incluso en la desesperación, en el caos de las batallas. El cielo encantado mudó sus colores con la descarga de la magia, con explosiones que amenazaban con destruir el mundo.$B$BEl hermano luchó contra el hermano. Los elegidos lucharon' WHERE `entry`='359';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando los viejos árboles del bosque encantado fueron arrancados y derribados, la tierra tembló. Los bosquecillos y los claros al cuidado de los hijos y las hijas de Cenarius, y las torres de piedra de los hijos de las estrellas cayeron por tierra. Estaba nuestra reina, radiante incluso en la desesperación, en el caos de las batallas. El cielo encantado mudó sus colores con la descarga de la magia, con explosiones que amenazaban con destruir el mundo.$B$BEl hermano luchó contra el hermano. Los elegidos lucharon' WHERE `entry`='360';
UPDATE `locales_page_text` SET `Text_loc6`='Maestro Delgren,$B$BEs como te temías. Tu teoría acerca de la Torre de Althalaxx ha resultado ser cierta. Se ha reunido un culto en la torre, incluyendo a muchos brujos, así como a un nauseabundo sátiro. Se llaman a sí mismos el Culto de la Facción Oscura. No puedo imaginarme qué vil propósito los ha reunido, pero me preocupa.$B$BDisculpa el carácter lacónico de esta carta, pero el peligro merodea en el bosque y solo puedo decir un par de palabras para describir la situación. Yo...' WHERE `entry`='361';
UPDATE `locales_page_text` SET `Text_loc6`='Maestro Delgren,$B$BEs como te temías. Tu teoría acerca de la Torre de Althalaxx ha resultado ser cierta. Se ha reunido un culto en la torre, incluyendo a muchos brujos, así como a un nauseabundo sátiro. Se llaman a sí mismos el Culto de la Facción Oscura. No puedo imaginarme qué vil propósito los ha reunido, pero me preocupa.$B$BDisculpa el carácter lacónico de esta carta, pero el peligro merodea en el bosque y solo puedo decir un par de palabras para describir la situación. Yo...' WHERE `entry`='363';
UPDATE `locales_page_text` SET `Text_loc6`='sobre la orilla del lago, Ameth\'Aran y Bashal\'Aran eran los primeros, con la inspiración de Azshara, la amada reina de los kaldorei, en su creación. Ella llevó hasta sus ciudades gemelas a sus siervos privilegiados, los Altonato y, para gobernarlos, designó a Athrikus Narassin, aunque él prefería pasar sus días en la Torre de Althalaxx, alejado del bullicio.$B$BSu mano derecha, un mago conocido por su destreza en las artes Arcanas, así como por sus habilidades físicas,' WHERE `entry`='364';
UPDATE `locales_page_text` SET `Text_loc6`='era Asterion, el cual vivió con la gente de Bashal\'Aran y fue muchas veces a Ameth\'Aran para combrobar los deseos y necesidades de sus habitantes.$B$BY fue así, a orillas del Pozo de la Eternidad, como las dos ciudades hermanas crecieron y prosperaron, mientras el mundo bajo las estrellas se sumía lentamente en la locura.' WHERE `entry`='365';
UPDATE `locales_page_text` SET `Text_loc6`='bendito. No todos los Altonato lucharon. Algunos se quedaron paralizados, los llevo la locura. Poderosas ciudades y humildes pueblos por igual se talaron a su alrededor. En Ameth\'Aran, todo estaba en ruinas, sus habitantes se aferran a la tierra como si eso pudiese salvarlos de la destrucción. Fue entonces cuando Athrikus Narassin, suplicado por Azshara apareció para guiarlos a la seguridad. Con un hechizo que pudiese protegerlos, decía. Mientras que alrededor de ellos, hermano y hermana, padre y madre murieron,' WHERE `entry`='366';
UPDATE `locales_page_text` SET `Text_loc6`='ellos tendrían vida eterna y no tenían nada que temer de El Cataclismo que estaba al llegar...' WHERE `entry`='367';
UPDATE `locales_page_text` SET `Text_loc6`='L. F. ordenó incrementar la agresión contra los tontos de la Alianza. A continuación hay un listado de los materiales necesarios para una importante misión contra Costasur:$B$Braciones de viaje de 1 semana para 20 hombres$B20 trajes de noche de color negro$B20 pares de zapatos nuevos$B25 espadas nuevas$B30 cuchillos para lanzar$B20 máscaras negras$B10 viales de aceite inflamatorio$B50 dosis de veneno paralizante$B5 uniformes de guardia de Costasur robados, si están disponibles$B100 monedas de plata$B$BBarón V.$BIntendente' WHERE `entry`='371';
UPDATE `locales_page_text` SET `Text_loc6`='La Estrella de Xil\'yeh$B$BGrel\'borg el Avaro tiene la Estrella. Es un ogro avaricioso que pasa sus días en las montañas Alterac, en las Ruinas de Alterac, buscando adornos. La mayor parte de su colección es inútil, pero un objeto, la Estrella de Xil\'yeh, tiene valiosas propiedades.' WHERE `entry`='372';
UPDATE `locales_page_text` SET `Text_loc6`='La Mano de Dagun$B$BLos textos antiguos afirman que las zarpas de Dagun excretan un veneno que no mata a sus presas, pero las hace mutar en algo distinto: un miembro de las razas antiguas. Su valor para nosotros está fuera de cuestión.$B$BDagun vive en las profundidades del mar, pero es atraído regularmente por una tribu de múrlocs Ancalodos en el Marjal Revolcafango. Sus oráculos lo invocaron con un alga marina encantada. Si matas suficientes oráculos, podrás encontrar el alga. Luego, colócala en el altar múrloc y Dagun aparecerá.' WHERE `entry`='373';
UPDATE `locales_page_text` SET `Text_loc6`='El Corazón Heredado$B$BMogh el Eterno es un médico brujo trol de Tuercespina. Posee el Corazón Heredado, del que se afirma que aquellos que desvelen sus secretos pueden sortear a la muerte. Encontrarás a Mogh en las Ruinas de Zul\'Mamwe. Derrótalo, si puedes, y tráeme el Corazón Heredado.' WHERE `entry`='374';
UPDATE `locales_page_text` SET `Text_loc6`='Tráeme estos tres objetos: la Estrella, la Mano y el Corazón, y el Compendio de Yagyin será tuyo.$B$BG.B.' WHERE `entry`='375';
UPDATE `locales_page_text` SET `Text_loc6`='La Estrella de Xil\'yeh$B$BUn ogro de Alterac, Grel\'borg el Avaro, fue el último poseedor de esta gema, de la que se dice que transmite a su dueño la facultad de entrar en contacto con otros mundos. Hay fuentes que afirman que Grel\'borg merodea en las Ruinas de Alterac en las montañas del mismo nombre, rebuscando mas reliquias entre los escombros y las basuras.' WHERE `entry`='376';
UPDATE `locales_page_text` SET `Text_loc6`='La Mano de Dagun$B$BDagun es una criatura de aguas profundas, adorada por los múrlocs en el marjal Revolcafango. Las atraen a la superficie con un alga marina especial, encantada por sus chamanes. Atrapa a los múrlocs, recoge sus algas encantadas y luego colócalas sobre el altar de los múrlocs. Dagun aparecerá y te quedarás esperando.$B$BQuiero esa mano.' WHERE `entry`='377';
UPDATE `locales_page_text` SET `Text_loc6`='El Corazón Heredado$B$BLos viejos textos afirman que quien posea el Corazón Heredado no debe tener temor a la tumba. Tal vez esto sea cierto, ya que el dueño del corazón, el trol Mogh, es conocido como Mogh el Eterno. Mogh, médico brujo de la tribu Machacacráneos, habita en Tuercespina en las Ruinas de Zul\'Mamwe.' WHERE `entry`='378';
UPDATE `locales_page_text` SET `Text_loc6`='Tráeme estos objetos: la Estrella, la Mano y el Corazón, y tendrás una copia del Compendio de Yagyin.$B$BEl guardián' WHERE `entry`='379';
UPDATE `locales_page_text` SET `Text_loc6`='Informe de Misiones de Mortacechador$B$BAgentes: Rane Yorick, Quinn Yorick, Erland McKree$B$BMisión Principal: Realizar un reconocimiento en el norte de los Argénteos, y determinar los niveles de amenaza de la vida silvestre y la Plaga.' WHERE `entry`='380';
UPDATE `locales_page_text` SET `Text_loc6`='Los agentes comenzaron sus investigaciones y descubrieron una importante presencia de huargos. Se recomienda enviar equipos de caza para eliminar el problema.$B$BGnolls no muertos fueron encontrados en la granja de la zona llamada El Campo Muerto. La razón de su presencia aquí es desconocida, pero sus movimientos y acciones sugiere una acción militar inminente. Durante nuestras observaciones, ningún líder notable fue detectado entre los Gnoll. Se deduce que esperan la llegada de líderes o refuerzos.' WHERE `entry`='381';
UPDATE `locales_page_text` SET `Text_loc6`='Continuando la investigación, nuestros agentes fueron detenidos en la granja de Ivar. Ivar, la mayor parte de su familia y sirvientes sucumbieron a la Plaga y se convirtieron en sus esbirros.$B$BNuestros agentes han sido atacados y, aunque se las arreglaron para derrotar a sus atacantes, algunos sufrieron graves heridas, en particular, el agente Quinn.' WHERE `entry`='382';
UPDATE `locales_page_text` SET `Text_loc6`='Adoptaron una posición defensiva en la vieja casa de Ivar, y mientras Rane hacía guardia, Erland continuó la misión de reconocimiento.$B$BErland fue clavado por los huargos en un huerto del norte, y logró escapar con la ayuda de otro agente de Renegados. Este agente es el portador de este informe.' WHERE `entry`='383';
UPDATE `locales_page_text` SET `Text_loc6`='Cabe señalar aquí que el portador de este informe fue fundamental, no sólo para el éxito de nuestra misión, sino también en la derrota de Ivar el Hediondo, el rescate de Erland, y la recuperación de este informe. Hacemos llegar nuestra gratitud, y recomendamos al mando que reconozca a este individuo y le premie acorde a su conducta meritoria, que mostró en el campo.$BMortacechadora Rane Yorick,$BJefe de la Misión' WHERE `entry`='384';
UPDATE `locales_page_text` SET `Text_loc6`='Cabe señalar que el portador de este informe no sólo desempeñó un papel decisivo en el éxito de nuestra misión, sino que también en la derrota de Ivar el Hediondo, el rescate de Erland, y la entrega de este informe.$B$BHacemos llegar nuestra gratitud, y recomendar que el comando reconozca a este individuo otorgándole el premio acorde a su conducta meritoria, como demostró en el campo.$B$BMortacechador Rane Yorick,$BLíder de la Misión' WHERE `entry`='386';
UPDATE `locales_page_text` SET `Text_loc6`='Y así deambuló y continuó buscando. Y su brazo permaneció perdido. Y, mientras andaba, maldijo y rugió.$B$BPero hace mucho tiempo que Gri\'lek dejó de escuchar a los espíritus, y estos estaban airados y no querían atender sus maldiciones.$B$BGri\'lek estaba condenado. Condenado a vagar inerme.' WHERE `entry`='387';
UPDATE `locales_page_text` SET `Text_loc6`='Y así deambuló y continuó buscando. Y su brazo permaneció perdido. Y, mientras andaba, maldijo y rugió.$B$BPero hace mucho tiempo que Gri\'lek dejó de escuchar a los espíritus, y estos estaban airados y no querían atender sus maldiciones.$B$BGri\'lek estaba condenado. Condenado a vagar inerme.' WHERE `entry`='393';
UPDATE `locales_page_text` SET `Text_loc6`='Una fuerza maligna los creó,$BSe sienta cómodamente en la Cima Calígine,$BSe ríe de ti y de tu amigo,$BSus elementales no tienen fin,$B$BPronto deberás enfrentarte a este mago,$BSu furia solo se aplacará con la muerte.' WHERE `entry`='394';
UPDATE `locales_page_text` SET `Text_loc6`='El Santuario de Mel\'Thandris$B$B\"Que la luz de Elune brille sobre este valle eterno e ilumine los caminos del bosque como ha ocurrido desde tiempo inmemorial.\"' WHERE `entry`='395';
UPDATE `locales_page_text` SET `Text_loc6`='Elune ha atendido a mis deseos. Las vidas de mis camaradas caídos no habrán sido en vano, y vengaré sus muertes utilizando la Guadaña de Elune. Es un objeto antiguo, una rama torcida y nudosa que puede ser incluso más antigua que los kaldorei.$B$BDurante muchas horas he examinado la herramienta que me ha concedido la diosa. No es un arma ordinaria, esto está claro, ni se trata de un simple aditamento mágico. Con ella... incluso es posible traspasar las barreras del tiempo y el espacio.$B$BCon la Guadaña' WHERE `entry`='396';
UPDATE `locales_page_text` SET `Text_loc6`='en mis manos, he tenido una visión del caos. Lobizones... los huargen... combatían contra un enemigo increíble. Los huargen luchaban salvajemente, como corresponde a su raza primitiva, pero su enemigo permanecía intacto: Los señores de la Llama Esmeralda.$B$BY así fue como me di cuenta del verdadero poder de la Guadaña. Centrándome en ella, logré llegar a comunicarme con los huargen. Sin embargo, el contacto no se realizaba mediante lenguaje hablado. Era algo distinto... no podría describirlo.$B$BLo que importa es que los huargen me oían' WHERE `entry`='397';
UPDATE `locales_page_text` SET `Text_loc6`='y me entendían. Canalizando la energía hacia la Guadaña, las barreras se debilitaron aún más y pude traer huargen a nuestro mundo. En mi primer intento pude invocar a treinta. Me siguieron en la batalla y, con su fuerza bruta y bajo mis órdenes, arremetimos contra los demonios de Frondavil.$B$BElune ha atendido a mis deseos. El bosque quedará libre de alimañas.$B$BQue Elune me conceda sus bendiciones hasta que logre terminar mi trabajo.' WHERE `entry`='398';
UPDATE `locales_page_text` SET `Text_loc6`='Todo marcha bien. He reunido a un gran número de huargen para que luchen a mi lado. Aunque son de pensamiento simple, han demostrado una impresionante habilidad para coordinar sus ataques y actúan muy bien en grupo.$B$BHe identificado a los líderes de estos grupos. Los he organizado en pequeños grupos de guerreros. Atacamos el bosque desde varios lugares y mi campaña contra los demonios es bastante provechosa.$B$BQue Elune me conceda sus bendiciones hasta que logre terminar mi trabajo.' WHERE `entry`='399';
UPDATE `locales_page_text` SET `Text_loc6`='Un grupo se perdió. He enviado a los huargen a que recorran el bosque e identifiquen áreas infestadas por demonios, pero no han regresado. He empezado a preocuparme por los líderes, a los que encomendé el control. Tienen una mente simple y seguramente han llevado al extremo la escasa autoridad que les confié. ¿Estoy perdiendo el control? Tal vez debería dejar de invocar más huargen.$B$BQue Elune me conceda sus bendiciones hasta que logre terminar mi trabajo.' WHERE `entry`='400';
UPDATE `locales_page_text` SET `Text_loc6`='Aunque no he invocado nuevos huargen, su número continúa aumentando. La Guadaña ya no requiere mi intervención para el proceso de invocación. Esto resulta problemático. He reunido tantas manadas como pude encontrar y las he ordenado para que permanezcan en el Santuario de Mel’Thandris.$B$BMientras tanto, he buscado en las bibliotecas de Darnassus y he consultado con el Círculo de los Ancestros en Costa Oscura. No encontré ningún dato sobre los huargen. He escuchado informes,' WHERE `entry`='401';
UPDATE `locales_page_text` SET `Text_loc6`='murmullos, procedentes de un zahorí del Kirin Tor llamado Arugal. Según he oído, es posible que él también haya invocado huargen. Consultaré con este zahorí, a pesar de lo desagradable que pueda ser, y veré qué es lo que sabe.$B$BLos huargen son peligrosos y me gustaría tenerlos controlados. Mañana bajaré al puerto en los Baldíos y reservaré un pasaje para el nuevo mundo.$B$BQue Elune me conceda sus bendiciones hasta que logre terminar mi trabajo.' WHERE `entry`='402';
UPDATE `locales_page_text` SET `Text_loc6`='Comencé a escribir este diario para guardar notas acerca de mi investigación para ayudar a Vallefresno. Estos acontecimientos ocurridos no hace mucho están envueltos en un misterio y, con suerte, podré desvelar algunos de ellos para así ayudar a mi gente.$B$BEmpezaré comentando ciertos hechos para evitar olvidarlos más adelante.$B$BMe he dado cuenta de que los rumores acerca de los esfuerzos del zahorí Dartol son ciertos.' WHERE `entry`='403';
UPDATE `locales_page_text` SET `Text_loc6`='Si bien antes pensé que sería absurdo que un humano llegara a nuestras tierras y aprendiera nuestras costumbres, este zahorí, este hombre malvado, llegó aquí y no solo sobrevivió, sino que obtuvo parte de nuestra sabiduría. Todavía no sé bien cómo lo hizo...$B$BDurante mi investigación, no he podido descubrir hasta ahora sus objetivos, pero su plan parece simple: utilizar magia druídica, intentó primero hacer amigos y luego controlar a los fúrbolgs. Si hubo un intento de causar daño a mis parientes o cualquier otro propósito malicioso, espero encontrar la explicación antes de acabar mis pesquisas.' WHERE `entry`='404';
UPDATE `locales_page_text` SET `Text_loc6`='Sin embargo, por lo visto había tenido algo de éxito al llevar a cabo sus planes. ¿Quería probar sus planes con los fúrbolg antes de ponerlos en práctica con mi gente? ¿Tenían los fúrbolg algo que él codiciaba? ¿Quién sabe?$B$BUsando un objeto que creó (una vara o bastón de algún tipo), intentó infiltrarse en las tribus de fúrbolg de Vallefresno. En algún momento, aquí los detalles aún no están claros, los fúrbolg lo descubrieron mientras intentaba manipularlos para que actuaran contra' WHERE `entry`='405';
UPDATE `locales_page_text` SET `Text_loc6`='otros. Como estoy seguro de que ocurriría con los elfos, los fúrbolgs estaban airados, pero reaccionaron con mucha mayor fuerza y mataron a Dartol.$B$BLos fúrbolgs, al menos en ese momento, no eran las mismas criaturas corruptas que llegaron a ser posteriormente. Entonces, aún eran racionales e inteligentes, y su relación con nuestra gente era amistosa.$B$BTengo la esperanza de que si vuelvo a crear la vara con la ayuda de un dríade llamado Shael\'dryn, podré utilizar su poder para ayudar a nuestra gente... y a los fúrbolgs.' WHERE `entry`='406';
UPDATE `locales_page_text` SET `Text_loc6`='No todos los fúrbolgs han sido corrompidos. Algunos aún viven en el bosque e intentan protegerlo. Durante el periodo en el que se corrompieron, algunos de los fúrbolgs huyeron, muchos fueron asesinados y otros fueron desterrados de sus tribus.$B$BEncontré a un venerables fúrbolg de la tribu Maderiza, viviendo por encima del lago Mystral. Su nombre es Krolg.$B$BEstuve observándolo durante días y creo que, si me hubiera acercado a él disfrazado de fúrbolg, podría haberle ayudado y habría descubierto una forma de restablecer los lazos entre nuestros pueblos.' WHERE `entry`='407';
UPDATE `locales_page_text` SET `Text_loc6`='Además de matar al Dartol, el fúrbolg partió la vara en tres partes para que no pudiera volver a usarse en su contra. Fue difícil, pero tengo una idea bastante clara de dónde se colocaron las tres partes.$B$BLa gema$BLa primera parte de la vara, una gema resplandeciente, se ocultó en un Santuario del noroeste de Vallefresno. Creo que ahora este santuario está en ruinas en el centro del lago Falathim. $GLos:Las; centinelas me han dicho que ahora está habitado por múrlocs.' WHERE `entry`='408';
UPDATE `locales_page_text` SET `Text_loc6`='Si no puedo localizar la gema oculta en el santuario, puede que se encuentre en posesión de uno de los múrlocs.$B$BLa parte de la vara$BEsta vara fue confiada a los antárboles cerca de lo que hoy se conoce como Frondavil. Ni los fúrbolgs ni los míos podríamos haber sospechado nunca que estas magníficas criaturas pudieran caer en las garras de la corrupción que asoló la tierra.$B$BMis investigaciones me indican que los guardianes conservan la pieza en un pequeño claro, dentro de un arcón. Para averiguar más detalles, tendré que hablar con Shael\'dryn.' WHERE `entry`='409';
UPDATE `locales_page_text` SET `Text_loc6`='1. Toma uno u ocho hombres$B2. Golpea al hombre con fuerza$B3. Golpea más al hombre$B4. Coloca al hombre en el fuego$B5. Cómete al hombre' WHERE `entry`='410';
UPDATE `locales_page_text` SET `Text_loc6`='Shael\'dryn es una dríade que protege la Poza de la Luna al noreste del Lago Iris. Se niega a abandonar el bosque o a dejarlo caer en la corrupción. Sé que ella tiene el poder y los conocimientos necesarios para reparar la vara o, al menos, decirme cómo puedo hacerlo por mí mismo.$B$BEl pomo$BSe entregó el pomo de la vara a los druidas de Dor\'danil. No obstante, esto supone un problema importante: los druidas están muertos.$B$BAhora, sus espíritus ocupan el área y ya no se muestran hospitalarios.' WHERE `entry`='411';
UPDATE `locales_page_text` SET `Text_loc6`='Puede que esto requiera más investigación. No estoy seguro de tener poder suficiente para luchar contra los espíritus de mis parientes muertos... Intentaré escarbar las profundidades de Dor\'danil y encontrar el pomo.$B$B(Nota: Durante mis preparativos en Astranaar, me he encontrado recientemente con un cazador de enanos que pasó bastante tiempo en Vallefresno, intentando ayudar a mi gente. Procedía de un lugar llamado Ventormenta.$B$BUna noche estuvimos hablando, mientras tomábamos algunas cervezas, y sus relatos me han dado nuevas esperanzas de poder encontrar el pomo...' WHERE `entry`='412';
UPDATE `locales_page_text` SET `Text_loc6`='Me dijo que recorrió todo Dor\'danil y que nunca vio un cofre o cualquier otra clase de contenedor que pudiera albergar el pomo.$B$BAsimismo, me dijo que babosas de la podredumbre habitaban el área y que devoraban prácticamente todo aquello que era más pequeño que ellas. Vio cómo \"comían\" mesas, esculturas, osos ¡e incluso gnomos!$B$BLas babosas parecen obtener gran parte del alimento a partir de objetos mágicos. Si el cofre que contiene el pomo estuviera encantado, habría muchas posibilidades de que las babosas se lo comieran.' WHERE `entry`='413';
UPDATE `locales_page_text` SET `Text_loc6`='Se trata de un juego, pero en este momento no dispongo de otras pistas...$B$BCuando averigüe más, lo escribiré en estas páginas, pero hasta entonces, intentaré localizar las partes de la vara y trataré de ayudar a Vallefresno.' WHERE `entry`='414';
UPDATE `locales_page_text` SET `Text_loc6`='Jin\'Zil,$B Sé que hace un tiempo que no te escribo, pero pensé que te gustaría saber que el último de los Sangrepluma ha sido asesinado. Sin embargo, el mérito no es mío. El asesino te ha entregado esta carta. Si quieres, puedo entregarte sus cabezas después de que la caravana haga sus rondas por los Baldíos. Sé que te gustan esta clase de cosas para tus ceremonias vudú.$B Cuídate, viejo amigo.$B$BDarsok Movedaga' WHERE `entry`='415';
UPDATE `locales_page_text` SET `Text_loc6`='Jin\'Zil,$B Sé que hace un tiempo que no te escribo, pero pensé que te gustaría saber que el último de los Sangrepluma ha sido asesinado. Sin embargo, el mérito no es mío. El asesino te ha entregado esta carta. Si quieres, puedo entregarte sus cabezas después de que la caravana haga sus rondas por los Baldíos. Sé que te gustan esta clase de cosas para tus ceremonias vudú.$B Cuídate, viejo amigo.$B$BDarsok Movedaga' WHERE `entry`='416';
UPDATE `locales_page_text` SET `Text_loc6`='Vale, deberás tener tus cargas NG-5 y tus detonadores preparados. Los he etiquetado para ti... El azul corresponde a la serrería. El rojo, a la mina Cortaviento.$B$BHe reconocido un poco el terreno y he encontrado suficientes explosivos en el Risco Cortaviento, de manera que no debería ser un problema para ti llegar hasta allí, colocar los explosivos y marcharte antes de detonarlos.$B$BRecuerda, esto es una distracción. Aléjate de los carros antes de que los goblins vayan a ver qué ha ocurrido y luego escabúllete detrás de ellos.' WHERE `entry`='417';
UPDATE `locales_page_text` SET `Text_loc6`='Por cierto, ten cuidado con ellos. Basta el nitromirgliceronio para reducirte a polvo. He colocado parte del líquido en tubos de cobre para mantenerlo estable y seguro.$B$BHe utilizado en las cajas del detonador el pergamino que te entregó Collin. Ahora, ellos enviarán un mensaje silencioso a las cargas NG-5 después de que las hayas preparado. He utilizado un sencillo mecanismo Stalthwargon para garantizar que la conductividad sea óptima y el flujo de nitromirgliceronio entre el fluido diferencial sea superior a la media.' WHERE `entry`='418';
UPDATE `locales_page_text` SET `Text_loc6`='Antes de que pueda volver a los detalles sobre mi diseño, déjame explicarte dónde debes colocar los explosivos.$B$BPlan A: la serrería.$BHay un carro justo en frente de la serrería (está al noreste de la zona deforestada y del ensamblaje para el almacenamiento de la madera). Sospecho que los planes de ingeniería se encuentran dentro de la serrería.$B$BPara colocar la carga, dirígete a la parte trasera del carro. Una vez que esté colocada, date prisa y aléjate del lugar.' WHERE `entry`='419';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando estés $Glisto:lista;, pulsa el detonador.$B$BMe he asegurado que haya una señal fuerte, canalizando cables de cobre y plata con un conjunto de antenas meltusianas bajo la cubierta de la caja. Esto debería garantizar que todo vaya perfectamente. Simplemente, ten cuidado de los goblins que estén cerca. Si te cogen con las manos en la masa, no creo que aprecien mucho mi creación.$B$BAh, por si aún no lo tienes claro, trata de alejarte del carro cuando explote. Puede hacerte bastante daño.' WHERE `entry`='420';
UPDATE `locales_page_text` SET `Text_loc6`='Plan B: Mina Cortaviento:$B$BHe visto que los goblins utilizan otro carro de explosivos situado al norte de la entrada de la mina. Creo que es un sitio bastante bueno para colocar una carga de NG-5 (roja). Una vez que hayas colocado la carga en la parte trasera del carro, toma distancia y pulsa el botón.$B$BSi los goblins no vienen corriendo a ver qué ha sucedido, probablemente es porque la montaña se les haya venido encima.' WHERE `entry`='421';
UPDATE `locales_page_text` SET `Text_loc6`='Las cartas de Ventura y Cía. están situadas fuera de la mina... Creo que están sobre unos cajones que están en el nivel inferior.$B$BBuena suerte... y recuerda...$B$BCarga azul: serrería$BCarga roja: mina$B$BAh, y, una vez que hayas cumplido esta misión, destruye esta carta. Mmm, debería inventar un medio para que estas cosas se autodestruyan una vez leídas. No es mala idea...' WHERE `entry`='422';
UPDATE `locales_page_text` SET `Text_loc6`='Las cartas de Ventura y Cía. están situadas fuera de la mina... Creo que están sobre unos cajones que están en el nivel inferior.$B$BBuena suerte... y recuerda...$B$BCarga azul: serrería$BCarga roja: mina$B$BAh, y, una vez que hayas cumplido esta misión, destruye esta carta. Mmm, debería inventar un medio para que estas cosas se autodestruyan una vez leídas. No es mala idea...' WHERE `entry`='423';
UPDATE `locales_page_text` SET `Text_loc6`='--Primer día:$B$B¡Finalmente, mi viaje a Forjaz me ha traído hasta Kalimdor!$B$BEl prospector Khazgorm de Bael Modan está haciendo grandes progresos. Hoy descubrió un fósil que los sabios del lugar consideran un trozo de hueso de un antiguo dios. Khazgorm tiene la certeza de que forma parte de un misterio mayor. Mientras supervisa la excavación, me ha encargado la tarea de descubrir los fósiles que unan la historia. $B$BDirígete al sur en busca de más pistas.' WHERE `entry`='424';
UPDATE `locales_page_text` SET `Text_loc6`='--Segundo día:$B$B Las tierras de los Baldíos son extensas. Mi viaje ha sido largo y arduo y, hasta el momento, no ha producido nada de interés para la Liga de Expedicionarios.$B$BEl único incidente notable tuvo lugar cerca del Camino de Oro. A la distancia, vi unas criaturas extrañas. Mitad cerdo, mitad humano. Rápidamente me escondí y vi pasar a las nauseabundas criaturas. ¡Menuda pestilencia dejaban al paso!' WHERE `entry`='425';
UPDATE `locales_page_text` SET `Text_loc6`='--Tercer día:$B$B¡Qué gran descubrimiento!$B$BMientras exploraba, he descubierto algo fascinante: muchos restos de lo que parece haber sido una criatura semejante a un jabalí. Su cráneo era mucho más grande que yo. Cada diente de la bestia podría compararse a medio enano... a uno entero. De las tamañas dimensiones del descubrimiento, creo que puede tratarse de los restos de uno de los dioses antiguos.' WHERE `entry`='426';
UPDATE `locales_page_text` SET `Text_loc6`='De regreso en el puesto de mando de la Liga de Expedicionarios en Forjaz, recuerdo una conferencia que pronunció el arqueólogo jefe Mostachogris. En ella mencionó un dios de esta clase, que fue adorado por extraños elfos de Darnassus, debido a su supuesta naturaleza altruista.$B$B¡Bah! Un momento. He oído cómo resoplaban fuera de la tienda. Seguramente, se trata de algunos jabalíes intentando coger las manzanas que tengo escondidas...' WHERE `entry`='427';
UPDATE `locales_page_text` SET `Text_loc6`='--Cuarto día:$B¡Capturado!$B$BMe han atacado hombres jabalí de la tribu Rajacieno. Creía que me iban a llevar a los grandes restos de jabalí del dios caído, pero bordearon los grandes fósiles y fueron directamente hacia el oeste de la calavera gigante, a través del Camino del Oro.' WHERE `entry`='428';
UPDATE `locales_page_text` SET `Text_loc6`='Me han arrastrado violentamente hasta un apestoso y espinado zarzal llamado Horado Rajacieno. Me han arrojado a una rústica cabaña y me han hecho prisionero junto con un elfo de la noche moribundo.$B$BNo tengo otras posesiones salvo este diario, mi pluma y mi reloj de bolsillo...$B$BAlguien se acerca...' WHERE `entry`='429';
UPDATE `locales_page_text` SET `Text_loc6`='--Quinto día:$B$B Mi compañero de celda, el elfo de la noche, sufre graves heridas. Los hombres jabalí le han pegado repetidamente y creo que a menos que una partida de rescate nos libre de este traicionero lugar, morirá en breve. Se desmaya a menudo. Cuando se despierta, intento darle de beber agua.' WHERE `entry`='430';
UPDATE `locales_page_text` SET `Text_loc6`='La última noche, fue capaz de hablar por primera vez. Es un druida, llamado Heralath, enviado a este lugar para verificar los rumores acerca de la existencia del dios jabalí caído.$B$BCon su débil voz, me contó que creía que la gran calavera de jabalí estaba situada en el lugar en el que el gran dios jabalí Agamaggan cayó combatiendo contra la Legión Ardiente hace unos 10000 años durante la Guerra de los Ancestros. Su voluminoso cuerpo quedó aplastado contra el suelo y su sangre penetró en la tierra. En el lugar bañado por su sangre crecieron retorcidos arbustos espinosos...' WHERE `entry`='431';
UPDATE `locales_page_text` SET `Text_loc6`='Heralath y su gente intentaban descubrir la fuente de la corrupción de este sitio. Me dijo que se creía que Agamaggan era un dios puro. Ciertamente, la razón por la que estas hediondas criaturas y los malignos arbustos marcaban esta tierra es un misterio.$B$BAntes de que pudiera obtener más información, Heralath volvió a caer en su sueño intermitente...' WHERE `entry`='432';
UPDATE `locales_page_text` SET `Text_loc6`='--Sexto día:$B$B El estado de Heralath empeora.$B$BHoy, sus ojos solo se abrieron una vez. Habló con dolor, pero fue claro. Si escapo de mi condena aquí en Horado Rajacieno, Díscolo Falfindel, que está en Thalanaar, debe saber que Charga Filonavaja enardece seguidores al sur de Los Baldíos. Su poder aumenta a diario. Debemos detener a la Bruja antes de que la plaga de su corrupción se extienda del Horado.' WHERE `entry`='433';
UPDATE `locales_page_text` SET `Text_loc6`='El pobre Heralath tiene la palidez de un hueso. Su fiebre empeora por minutos. Mientras tanto, los hombres jabalí se están preparando para una gran acción militar. Me temo que si no intento escapar rápido, mi destino será el mismo que el de mi compañero de celda.$B$BMi plan es escabullirme cuando los hombres jabalí partan a realizar su próxima batida. Mis guardianes son perezosos y, a menudo, duermen en sus turnos.' WHERE `entry`='434';
UPDATE `locales_page_text` SET `Text_loc6`='Una vez que me escape del Horado, iré hacia las Mil Agujas y, a continuación, hacia Thalanaar, en los confines del misterioso bosque de Feralas. Allí buscaré al druida llamado Díscolo Falfindel.$B$BLo que comenzó como un intento de explorar el pasado de mi gente se ha convertido en algo mucho más grande. La protección de la Alianza está en mis manos...' WHERE `entry`='435';
UPDATE `locales_page_text` SET `Text_loc6`='NÚMERO DE ENCARGO: 45TK-3$BCOMPRADOR: Kravel Karbarbán$B$BFACTURA:$B$BCarraca hidráulica (3)$BPernos de repetición (50)$B30\" tubos de cobre calibre 20 (12)$BTabaco marca Furiahonda (1)$BPrismas de alta graduación (3)' WHERE `entry`='436';
UPDATE `locales_page_text` SET `Text_loc6`='te debería entregar este mensaje en mano, pero debo irme, no sea que me pierda algún acontecimiento de gran importancia. Si deseas saber más acerca de mis observaciones sobre la torre de Althalaxx, $Gel portador:la portadora; de este mensaje me ha servido de gran ayuda y creo que se puede confiar en $Gél:ella;.$B$BA toda prisa,$BBalthule Golpesombra' WHERE `entry`='437';
UPDATE `locales_page_text` SET `Text_loc6`='El goblin Nazz Vaporio es realmente el hijo de un príncipe mercante goblin (¿Qué príncipe? El precio de esta información es alto).$B$BParece que le gusta mucho más gastar el dinero que ha hecho su familia que ganarlo por sí mismo. Así que ha robado una pequeña fortuna del tesoro familiar y ahora se oculta en el Desierto de Sal.$B$BSi su padre supiera dónde está, seguro que iría a buscarlo y lo volvería a llevar a casa... castigándolo por su insolencia. ' WHERE `entry`='438';
UPDATE `locales_page_text` SET `Text_loc6`='Sr. Pinchabujas:$B$B$GEl portador:La portadora; de esta carta y yo mismo estamos montando un negocio conjunto en el que nos gustaría que participaras.$B$BComo bien sabes, he pasado mucho tiempo en el Desierto de Sal (el negocio mencionado anteriormente está relacionado con las carreras que se organizan en este desierto.$B$BSi te interesan las carreras y, para ser más específicos, apostar a ellas, querrás continuar leyendo, ya que creo que tus conocimientos sobre Tuercespina serían muy útiles en nuestro negocio.' WHERE `entry`='439';
UPDATE `locales_page_text` SET `Text_loc6`='Sr. Pinchabujas:$B$BLa bebida que entregaste a mi socio ha tenido un gran éxito. No solo se trata de una bebida fuerte, que adormece los sentidos, sino que parece tener cierta cualidad adictiva. Estoy seguro de que los que la han probado querrán repetir.$B$BEstamos en deuda contigo, Loquillo.$B$BTe invito a venir al Desierto de Sal, o a alguien de tu confianza, para aprovechar las nuevas oportunidades en relación con el juego.$B$BKravel' WHERE `entry`='440';
UPDATE `locales_page_text` SET `Text_loc6`='Mi buen Martek:$B$BEstoy en un problema y necesito tu ayuda.$B$BMi hermano y yo estamos intentando organizar una carrera de coches y necesitamos pistones robustos. Nada de lo que hemos probado es lo suficientemente resistente. Nada soporta la velocidad de nuestro nuevo coche. De esta manera, ¡el motor se rompe constantemente!$B$BEres un gran experto trabajando el metal. Debes ayudarnos. Eres el único que puede hacerlo.' WHERE `entry`='441';
UPDATE `locales_page_text` SET `Text_loc6`='Para enfrentarte a Faucehielo, debes atraerlo con la carne de su presa favorita.$B$BDirígete a las montañas de Alterac en Azeroth y caza un león de montaña de gran tamaño. Mátalo y luego lleva su cuerpo a la Caverna Estrecha, un lugar que los bestiales wendigo consideran sagrado.$B$BColoca el cuerpo sobre la Llama de Uzel para que el olor de la carne quemada se disperse en el aire, hacia el exterior de la caverna.$B$BComprobarás que, transcurrido un tiempo, Faucehielo vendrá.' WHERE `entry`='442';
UPDATE `locales_page_text` SET `Text_loc6`='No deja de maravillarme lo útil que resulta el odio para que los demás cedan a tus deseos. Los orcos y los trols fueron los más fáciles de engañar, ya que su odio a la Alianza los convierte en serviles sujetos de la Facción Oscura. ¿Acaso tienen idea del poder superior al que sirven?$B$BEse paladín exasperante, Delgren el Purificador, se ha estado metiendo en nuestros asuntos del bosque de Vallefresno, mientras que su lacayo, Balthule, espía mi torre, creyendo que no me percato de su presencia. Delgren nos considera' WHERE `entry`='443';
UPDATE `locales_page_text` SET `Text_loc6`='una amenaza menor, tal vez otro culto a la muerte. No sabe que somos nada más y nada menos que... una de las facciones del Consejo de la Sombra.$B$BCuando era niño, me hablaron de un insecto cuya picadura era mortal, pero cuyo aspecto era el de una simple mosca, un puntito difícil de reconocer. Son las cosas que subestimas las que más te hieren, si dejas que la herida se encone.' WHERE `entry`='444';
UPDATE `locales_page_text` SET `Text_loc6`='Los coches cohete que compiten por los desiertos han sido preparados y requieren bastante mantenimiento. Por eso no debe sorprender que haya equipos de boxes por todas partes.$B$BEstos equipos comprueban constantemente el estado de sus coches y efectúan reparaciones y modificaciones cuando es necesario. Es la única manera de que los coches alcancen el máximo rendimiento.$B$BPor esta razón, buscamos la manera de provocar algún fallo en las maniobras del equipo de boxes para que el coche se averíe rápidamente.' WHERE `entry`='445';
UPDATE `locales_page_text` SET `Text_loc6`='Si somos capaces de controlar qué coche está en plena forma y cuál no, entonces... No tengo que explicarte las ventajas que esto nos proporciona en el juego.$B$BPor eso, Pinchabujas, necesitamos que nos proporciones algo para hacer que el equipo de boxes cometa fallos.$B$B¿Hay en Tuercespina alguna planta, hierba o bebida que pueda usarse para trastocar la mente de los gnomos y los goblins?$B$BK. Karbarbán' WHERE `entry`='446';
UPDATE `locales_page_text` SET `Text_loc6`='He investigado a fondo a los Aspectos y a sus creadores titanes, profundizando en lo que a un humano le llevaría toda la vida. Había cinco Aspectos cuando los titanes dejaron este mundo; se trataba de poderosos dragones cuyo cometido era proteger el mundo de Azeroth. Sus historias son extensas y variadas y, aún hoy, a pesar de toda la información que he recogido, sé que tengo mucho que aprender sobre estas magníficas criaturas.' WHERE `entry`='447';
UPDATE `locales_page_text` SET `Text_loc6`='Boticario Lydon,$B$BEspero que esta carta os llegue rápidamente y que su portador se encuentre sano y salvo. En el lejano Kalimdor han tenido lugar ciertos acontecimientos que hacen que requiramos de tus servicios.$B$BSeré breve.$B$BHay una crisis en la Sierra Espolón, cerca de Mulgore, tierra natal de los aliados tauren. La empresa goblin Ventura y Cía está extrayendo todo el mineral y talando toda la vegetación en aquellas montañas, enfureciendo a los espíritus de la montaña.' WHERE `entry`='448';
UPDATE `locales_page_text` SET `Text_loc6`='Imagino que la conmoción que estos espíritus les causan a través de los chamanes y druidas tauren los distraen. Y es que los tauren consideran la presencia de Ventura y Cía. en la Sierra Espolón una seria amenaza.$B$BTanto si son una amenaza como si no lo son, debemos ayudar a nuestros aliados.$B$BLydon, posees un vasto conocimiento sobre toxinas. ¿Podrías crear una que pudiéramos usar para extender una plaga entre los goblins en la Sierra Espolón?$B$BRecibe un sabio saludo,$BP. Zamah' WHERE `entry`='449';
UPDATE `locales_page_text` SET `Text_loc6`='Gran parte del conocimiento que poseo ahora no lo habría podido adquirir por mi cuenta. Por esta razón, siento una infinita gratitud hacia los elfos de la noche. Ahora conozco muchísimos detalles exclusivamente gracias a su ayuda. Como resultado de nuestra relación, tengo la impresión de que sus orígenes guardan un vínculo mucho más estrecho con los Aspectos que lo que creía inicialmente. No obstante, guardan sus secretos con tanto celo, que incluso a mí me ocultan toda la verdad.' WHERE `entry`='450';
UPDATE `locales_page_text` SET `Text_loc6`='Todos mis conocimientos sobre los Aspectos los comparto aquí para que sirvan como referencia en el futuro. Sé que serán de extrema utilidad, pues presiento que los dragones tendrán un impacto mucho mayor en el mundo con el paso del tiempo.' WHERE `entry`='451';
UPDATE `locales_page_text` SET `Text_loc6`='Alexstrasza:$BAlexstrasza fue la antigua y poderosa Reina de los Dragones, llamada la Protectora por los titanes. Fue la primera en ser creada por los titanes para proteger el mundo tras su partida. Se dice que presenció el nacimiento de todas las razas modernas en toda la faz de Azeroth. Su Vuelo Rojo, conocido por su orgulloso comportamiento, gobernó antiguamente sobre las demás especies de dragones.' WHERE `entry`='452';
UPDATE `locales_page_text` SET `Text_loc6`='Corrían ciertos rumores entre los elfos de la noche que sugerían que a Alexstrasza y al semidiós Cenarius los unía una fuerte amistad, y que fue él quien la llamó durante la primera invasión de la Legión Ardiente.' WHERE `entry`='453';
UPDATE `locales_page_text` SET `Text_loc6`='Ysera:$BYsera, la Soñadora, representa el subconsciente de los mortales. Es ella quien induce al Sueño Esmeralda. Aunque aprecia la imaginación y la creatividad, aprobando la ambición de las razas mortales, ella y su Vuelo consideran el mundo real como una mera ilusión. Ysera es una de las criaturas más agraciadas y poderosas que cualquier mortal haya podido contemplar.' WHERE `entry`='454';
UPDATE `locales_page_text` SET `Text_loc6`='Malygos:$BMalygos se conoce como Administrador de la Magia o Tejedor de Hechizos. Su aspecto natural no es el de un dragón al uso y se dice que puede cambiar de forma a voluntad. Se le ha visto muy pocas veces tras su retiro en el extremo norte y casi siempre guarda la apariencia de una suerte de insecto, pero cuando adquiere forma de dragón, él y sus vástagos se distinguen por ser de color azul, en toda su gama.' WHERE `entry`='455';
UPDATE `locales_page_text` SET `Text_loc6`='Nozdormu:$BNozdormu se conoce como Maestro del Tiempo o el Atemporal. Él y su escuadrilla tienen una tonalidad broncínea y son muy astutos. Nozdormu siempre se ha interesado en coleccionar artefactos del pasado, ya que poco le importa la realidad de las razas mortales o incluso los asuntos de sus compañeros, los Aspectos. Sin embargo, su indiferencia hacia los asuntos políticos de los demás no supone displicencia frente a lo que ocurre en el resto del mundo.' WHERE `entry`='456';
UPDATE `locales_page_text` SET `Text_loc6`='Considerados protectores, el Vuelo Bronce siente una fuerte atracción por ciertos aspectos de la civilización. El dragón y su escuadrilla son muy pacientes por naturaleza y se basan siempre en la observación, por lo que, antes de apresurarse, actúan siguiendo un plan. Nozdormu siempre intentará proteger el fluir del tiempo y a las razas mortales cuando se vean amenazadas por enemigos más poderosos.' WHERE `entry`='457';
UPDATE `locales_page_text` SET `Text_loc6`='Neltharion:$BNeltharion, llamado, tras su creación, el Guardián de la Tierra, fue el Aspecto más leal y protector de Azeroth. Su Vuelo Negro fue el responsable de la creación de las montañas y otras formaciones rocosas, incluyendo las fronteras entre las razas mortales. Qué provocó su descomposición sigue siendo un misterio, ciertamente, se desconoce si fue una influencia externa o algo que se corrompió en su interior. Me pregunto si tal vez los dioses antiguos no tuvieron que ver con la corrupción del gran dragón negro.' WHERE `entry`='458';
UPDATE `locales_page_text` SET `Text_loc6`='El dragón negro sufrió una transformación completa y, finalmente, Neltharion dejó de existir. Se convirtió en Alamuerte, una criatura llena de odio y maldad. Cuando abandonó el nombre de Guardián de la Tierra, tal y como lo conocían los elfos de la noche, se hizo llamar Aspecto de la Muerte o Xaxas, nombre distinto del que los titanes le había otorgado. Alamuerte es la personificación del caos y el mal, siempre sediento de destrucción. Podría decirse que es el primer mal que apareció en Azeroth tras el destierro de los dioses antiguos.' WHERE `entry`='459';
UPDATE `locales_page_text` SET `Text_loc6`='El dragón negro sufrió una transformación completa y, finalmente, Neltharion dejó de existir. Se convirtió en Alamuerte, una criatura llena de odio y maldad. Cuando abandonó el nombre de Guardián de la Tierra, tal y como lo conocían los elfos de la noche, se hizo llamar Aspecto de la Muerte o Xaxas, nombre distinto del que los titanes le había otorgado. Alamuerte es la personificación del caos y el mal, siempre sediento de destrucción. Podría decirse que es el primer mal que apareció en Azeroth tras el destierro de los dioses antiguos.' WHERE `entry`='466';
UPDATE `locales_page_text` SET `Text_loc6`='¡ENHORABUENA!$B$B¡Eres la primera persona en poseer el asombroso husmeador taltuza! Aunque se trata de una criatura asustadiza, estamos seguros de que su mullido pelaje y su increíble poder olfativo te atraparán.$B$BEn las siguientes páginas encontrarás información sobre tu husmeador taltuza:$B$B1. Alimentación y cuidados$B2. Comportamiento excéntrico (y adorable)$B3. Uso de la vara de mando de husmeador(R)$B$BNuevamente, enhorabuena. No te defraudará,$B$BMarwig Pernoxidado$BPropietario de Snuff Inc.' WHERE `entry`='467';
UPDATE `locales_page_text` SET `Text_loc6`='¡ENHORABUENA!$B$B¡Eres la primera persona en poseer el asombroso husmeador taltuza! Aunque se trata de una criatura asustadiza, estamos seguros de que su mullido pelaje y su increíble poder olfativo te atraparán.$B$BEn las siguientes páginas encontrarás información sobre tu husmeador taltuza:$B$B1. Alimentación y cuidados$B2. Comportamiento excéntrico (y adorable)$B3. Uso de la vara de mando de husmeador(R)$B$BNuevamente, enhorabuena. No te defraudará,$B$BMarwig Pernoxidado$BPropietario de Snuff Inc.' WHERE `entry`='506';
UPDATE `locales_page_text` SET `Text_loc6`='--Decimotercer día$BHa aumentado la actividad de las tropas cerca de Theramore. Muchos humanos abandonaron la ciudad y se trasladaron al oeste.$B$B--Decimocuarto día$BMás tropas abandonan la ciudad. Seguí a un pequeño grupo y vi cómo se quitaban los uniformes. ¿Desertores?$B$B--Decimoquinto día$BOí a unos soldados de Theramore hablar hoy de guardias desertores. Mis sospechas deben ser verdad.' WHERE `entry`='507';
UPDATE `locales_page_text` SET `Text_loc6`='El Gorrión Sangrante: Manifiesto$B$B (20) barriles de ron. Destino: Theramore$B(50) sacos de harina. Destino: Theramore$B(2) cajas de herramientas gnómicas variadas. Destino: Trinquete$B(30) rollos de paño de algodón. Destino: Theramore$B(4) cajas de especias. Destino: Theramore$B(3) pasajeros. Destino: Theramore' WHERE `entry`='527';
UPDATE `locales_page_text` SET `Text_loc6`='Jorgen,$BMis temores se han materializado: han actuado de un modo del que no los creía capaces. Jamás pensé que pudieran llegar tan cerca, y lo han hecho.$B$BTe pido ayuda para el portador de esta nota y que lo envíes hacia ÉL. Honestamente, dada la gravedad de este asunto, creo que nos ayudará. Si se tratara de cualquier otro peligro, probablemente continuaría ignorándonos, desdeñando los problemas de nuestra bella ciudad.$B$BGracias, viejo pescador. Siempre estaré en deuda contigo.$B$BTu amigo,$BDeLavey' WHERE `entry`='528';
UPDATE `locales_page_text` SET `Text_loc6`='Jorgen,$BMis temores se han materializado: han actuado de un modo del que no los creía capaces. Jamás pensé que pudieran llegar tan cerca, y lo han hecho.$B$BTe pido ayuda para el portador de esta nota y que lo envíes hacia ÉL. Honestamente, dada la gravedad de este asunto, creo que nos ayudará. Si se tratara de cualquier otro peligro, probablemente continuaría ignorándonos, desdeñando los problemas de nuestra bella ciudad.$B$BGracias, viejo pescador. Siempre estaré en deuda contigo.$B$BTu amigo,$BDeLavey' WHERE `entry`='547';
UPDATE `locales_page_text` SET `Text_loc6`='--Decimosexto día$BUleg y Thargil regresaron de su misión al puerto de Theramore. Vieron un nuevo barco en el puerto: el Gorrión Sangrante. El barco partió de Menethil en Azeroth.$B$BMañana, Uleg y Thargil regresarán al puerto y se colarán en el barco. Creen que hallarán documentos políticos que revelarán la relación entre Theramore y la Alianza.' WHERE `entry`='548';
UPDATE `locales_page_text` SET `Text_loc6`='--Decimosexto día$BUleg y Thargil regresaron de su misión al puerto de Theramore. Vieron un nuevo barco en el puerto: el Gorrión Sangrante. El barco partió de Menethil en Azeroth.$B$BMañana, Uleg y Thargil regresarán al puerto y se colarán en el barco. Creen que hallarán documentos políticos que revelarán la relación entre Theramore y la Alianza.' WHERE `entry`='571';
UPDATE `locales_page_text` SET `Text_loc6`='Con el éxito de Kel\'Thuzad en Lordaeron, el Rey Exánime realizó los preparativos finales para asaltar la civilización humana. Tras volcar su energía corrupta sobre diversos artefactos portátiles llamados calderas de peste, Ner\'zhul ordenó a Kel\'Thuzad que llevara las calderas a Lordaeron, ocultándolas en varios poblados leales.' WHERE `entry`='572';
UPDATE `locales_page_text` SET `Text_loc6`='Las calderas, protegidas por cultores seguidores, actuarían como generadores de peste, dispersándola a través de los indefensos campos y ciudades del norte de Lordaeron.$B$BEl plan del Rey Exánime funcionó perfectamente. Muchos poblados del norte de Lordaeron se contaminaron casi de inmediato. Como ocurrió en Rasganorte, los ciudadanos que contrajeron la peste murieron y renacieron como esclavos voluntarios.' WHERE `entry`='573';
UPDATE `locales_page_text` SET `Text_loc6`='Los seguidores de Kel\'Thuzad deseaban morir para renacer al servicio del señor de las tinieblas. Se regocijaban ante la perspectiva de la inmortalidad de los no-muertos. Con la propagación de la peste, cada vez más zombis ferales se alzaron en las tierras del norte. Kel\'Thuzad observó el ejército del Rey Exánime y lo nombró la Plaga, pues pronto marcharía hasta las puertas de Lordaeron... borrando a la humanidad de la faz del mundo.' WHERE `entry`='574';
UPDATE `locales_page_text` SET `Text_loc6`='Nadie sabe exactamente cómo se originó el universo. Algunos conjeturan que una terrible explosión cósmica lanzó hacia la vasta inmensidad de la Gran Oscuridad mundos infinitos que giraron en órbita, mundos que posteriormente albergarían diversas formas de vida, extraordinarias y terribles a la vez. Otros creen que el universo, tal como existe hoy, fue creado como un todo por una entidad singular y todopoderosa.' WHERE `entry`='575';
UPDATE `locales_page_text` SET `Text_loc6`='Si bien los orígenes exactos del caótico universo siguen sin esclarecerse, existe la certeza sobre el nacimiento de una raza de seres poderosos que puso orden en los mundos, para garantizar un futuro seguro para las criaturas que seguirían sus pasos.$B$BLos titanes, dioses colosales de piel metálica procedentes de los confines del cosmos, llegaron hasta aquí y empezaron a ocuparse de los planetas que encontraron. Configuraron estos espacios, levantando poderosas montañas y formando vastos océanos.' WHERE `entry`='576';
UPDATE `locales_page_text` SET `Text_loc6`='Originaron cielos y atmósferas tormentosas, siguiendo un plan insondable para crear orden a partir del caos. Incluso otorgaron poderes a las razas primitivas para que se ocuparan de sus trabajos y mantuvieran la integridad de sus respectivos universos.$B$BLos titanes, gobernados por una secta de élite conocida como Panteón, llevaron el orden a cientos de millones de mundos diseminados por la Gran Oscuridad del Más Allá durante los primeros años de la creación.' WHERE `entry`='577';
UPDATE `locales_page_text` SET `Text_loc6`='El benevolente Panteón, con la intención de salvaguardar las estructuradas sociedades de estas razas, estaba siempre atento a la amenaza de seres malignos y extradimensionales de El Vacío Abisal. El Vacío Abisal, una dimensión etérea de magia caótica que interconectaba miríadas de planetas, albergaba un número infinito de seres malignos y demoníacos cuya única finalidad era destruir la vida y agotar las energías del universo viviente.' WHERE `entry`='578';
UPDATE `locales_page_text` SET `Text_loc6`='--Décimoséptimo día$BUleg ha vuelto de su misión. Falló y Thargil fue capturado. Envié a Uleg a entregar este informe a Nazeer.$B$BMe quedaré aquí. Ha aumentado la actividad naval en la costa de Theramore. Seguiré vigilando e informaré de cualquier novedad.' WHERE `entry`='579';
UPDATE `locales_page_text` SET `Text_loc6`='¡Nuestro primer plan ya ha fracasado!$B$BPuño tuvo éxito al cambiar los turnos de los guardias que patrullaban los túneles esa noche, pero debido a movimientos imprevistos de ciertos ciudadanos de Ventormenta, nuestro objetivo pudo escapar del lugar antes de que pudiéramos capturarlo. No obstante, esto ya lo habíamos previsto. El plan B ya estaba en marcha incluso antes de realizar nuestra emboscada en Ventormenta.' WHERE `entry`='580';
UPDATE `locales_page_text` SET `Text_loc6`='Este plan es igualmente sencillo, pero requiere más paciencia. Ya hemos pagado a nuestro hombre en Menethil por este trabajo y lo ha llevado a cabo de forma brillante. Una vez que hallamos zarpado, no debería existir ningún problema en la captura de nuestro objetivo.' WHERE `entry`='581';
UPDATE `locales_page_text` SET `Text_loc6`='El ataque está previsto a unos cuantos kilómetros de su destino. Allí es donde nuestros \"aliados\" estarán esperando. No sé por qué nos hemos visto obligados a formar una alianza con las criaturas, pero no dejo de pensar que nos están coaccionando. En última instancia, no importa. ¡Siempre que el objetivo esté bajo nuestra custodia, habremos iniciado el proceso de recuperación de Ventormenta de las manos de quienes nos la robaron!' WHERE `entry`='582';
UPDATE `locales_page_text` SET `Text_loc6`='El ataque está previsto a unos cuantos kilómetros de su destino. Allí es donde nuestros \"aliados\" estarán esperando. No sé por qué nos hemos visto obligados a formar una alianza con las criaturas, pero no dejo de pensar que nos están coaccionando. En última instancia, no importa. ¡Siempre que el objetivo esté bajo nuestra custodia, habremos iniciado el proceso de recuperación de Ventormenta de las manos de quienes nos la robaron!' WHERE `entry`='606';
UPDATE `locales_page_text` SET `Text_loc6`='Maestro Barbalarga$B$BComo podrás saber, los Pico Tormenta sabemos apreciar lo bueno y la calidad de tus escudos es famosa entre nosotros.$B$BPor ello, es un placer para mí encomendarte la fabricación de un escudo. He incluido las especificaciones en la siguiente página:$B$B$B' WHERE `entry`='607';
UPDATE `locales_page_text` SET `Text_loc6`='Maestro Barbalarga$B$BComo podrás saber, los Pico Tormenta sabemos apreciar lo bueno y la calidad de tus escudos es famosa entre nosotros.$B$BPor ello, es un placer para mí encomendarte la fabricación de un escudo. He incluido las especificaciones en la siguiente página:$B$B$B' WHERE `entry`='626';
UPDATE `locales_page_text` SET `Text_loc6`='La devastadora Segunda Guerra contra la horda orca dejó la Alianza de Lordaeron en un estado de desolación y confusión. Los sanguinarios orcos, dirigidos por el poderoso Jefe de Guerra Orgrim Martillo Maldito, no solo destrozaron todo lo que encontraron a su paso en las tierras de Khaz Modan, en poder de los enanos, sino que también arrasaron muchas provincias centrales de Lordaeron. Los orcos implacables llegaron a arrasar elremoto reino elfo de Quel\'Thalas antes de que les pararan los pies.' WHERE `entry`='627';
UPDATE `locales_page_text` SET `Text_loc6`='Los ejércitos de la Alianza, dirigidos por Sir Anduin Lothar, Uther el Iluminado y el almirante Valiente, expulsaron a los orcos hacia el sur, hasta la tierra desolada de Azeroth, el primer reino que cayó ante el ataque despiadado de los orcos.$B$BLas fuerzas de la Alianza, bajo las órdenes de Sir Lothar, lograron expulsar a los clanes de Martillo Maldito de Lordaeron, confinándolos en las tierras de Azeroth controladas por los orcos. Las fuerzas de Lothar rodearon la ciudadela volcánica de la Cumbre de Roca Negra y sitiaron sus defensas.' WHERE `entry`='628';
UPDATE `locales_page_text` SET `Text_loc6`='En su encarnizada defensa, Martillo Maldito y sus lugartenientes llevaron a cabo una audaz ofensiva desde la Cumbre y combatieron a los paladines de Lothar en el centro de las Estepas Ardientes. Martillo Maldito y Lothar se enzarzaron en una titánica batalla que dejó a los dos poderosos combatientes apaleados y exhaustos. Si bien por poco Martillo Maldito derrota a Lothar, la muerte del gran héroe no tuvo el efecto que el Jefe de Guerra habría deseado.' WHERE `entry`='629';
UPDATE `locales_page_text` SET `Text_loc6`='Turalyon, el lugarteniente más leal de Lothar, cogió el escudo de este manchado de sangre y convocó a sus hermanos apesadumbrados para emprender un atroz contraataque. Bajo los raídos estandartes de Lordaeron y Azeroth, las tropas de Turalyon masacraron a casi todas las fuerzas restantes de Martillo Maldito, sometiéndolas a una gloriosa, pero terrible derrota.' WHERE `entry`='630';
UPDATE `locales_page_text` SET `Text_loc6`='Para los andrajosos y dispersos supervivientes orcos no quedaba otra opción que escapar al último bastión de las fuerzas orcas: el Portal Oscuro.$B$BTuralyon y sus guerreros persiguieron a los orcos supervivientes por el inmundo Pantano de las Penas, hasta Las Tierras Devastadas corruptas en las que se hallaba el Portal Oscuro. Allí, junto al colosal portal, la horda destrozada y la inquebrantable Alianza se enfrentaron en lo que sería la última y más sangrienta batalla de la Segunda Guerra.' WHERE `entry`='631';
UPDATE `locales_page_text` SET `Text_loc6`='Inferiores en número y enloquecidos por la maldición de su sed de sangre, los orcos sucumbieron inevitablemente a la ira de la Alianza. A Martillo Maldito lo hicieron prisionero y lo escoltaron hasta Lordaeron, mientras sus clanes rotos fueron acorralados y expulsados al norte, de nuevo en Lordaeron.' WHERE `entry`='632';
UPDATE `locales_page_text` SET `Text_loc6`='Unos pocos meses tras el final de la batalla de Nethergarde, las fuerzas del Portal Oscuro se unieron y abrieron una nueva entrada a Draenor. Los clanes orcos restantes, bajo el mando del anciano chamán Ner?zhul, volvieron a atacar Azeroth. En el intento de robar ciertos artefactos mágicos que aumentarían el poder de Ner\'zhul, los orcos planearon abrir nuevos portales en Draenor que les permitieran escapar a su mundo maldito para siempre.' WHERE `entry`='633';
UPDATE `locales_page_text` SET `Text_loc6`='Convencidos de que Ner\'zhul estaban planeando una nueva ofensiva contra la Alianza, el rey Terenas de Lordaeron envió sus tropas a Draenor para acabar con la amenaza orca de una vez por todas. Bajo las órdenes de Khadgar y el general Turalyon, las fuerzas de la Alianza pelearon contra los orcos en las tierras ardientes. Aun con la ayuda de la élfica capitana forestal Alleria, el enano Kurdran y el Soldado veterano Danath, Khadgar no pudo evitar que Ner\'zhul abriera sus portales a otros mundos.' WHERE `entry`='634';
UPDATE `locales_page_text` SET `Text_loc6`='Las terribles tormentas mágicas causadas por las energías convergentes de los portales empezaron a destruir el mundo, ya devastado. Ner\'zhul, seguido de sus más fieles siervos, logró escapar a través de uno de los portales, mientras Khadgar luchaba desesperadamente para devolver a sus compañeros a Azeroth. Ante la certeza de que estaban atrapados en el mundo agonizante, Khadgar y sus compañeros decidieron destruir el Portal Oscuro para evitar que Azeroth fuera destruido por el violento ataque de Draenor.' WHERE `entry`='635';
UPDATE `locales_page_text` SET `Text_loc6`='Según cuentan todos, los héroes lograron destruir el portal y salvar a Azeroth, pero tanto si escaparon como si no, el terrible final de Draenor resulta evidente.' WHERE `entry`='636';
UPDATE `locales_page_text` SET `Text_loc6`='Diez mil años antes de que los orcos y los humanos se enfrentaran en la Primera Guerra, el mundo de Azeroth era un vasto continente rodeado por mares infinitos. Aquella masa de tierra, conocida como Kalimdor, albergaba numerosas razas y criaturas dispares, pugnando por sobrevivir entre los salvajes elementos del mundo naciente. En el centro del continente oscuro había un lago misterioso de energías incandescentes.' WHERE `entry`='637';
UPDATE `locales_page_text` SET `Text_loc6`='El lago, conocido posteriormente como el Pozo de la Eternidad, fue el corazón de la magia y de los poderes naturales del mundo: Absorbiendo la energía de la Gran Oscuridad infinita del más allá, el Pozo actuaba como fuente mística, lanzando sus poderosos flujos por todo el espacio, diseminando la vida en todas sus formas, variadas y sorprendentes.$B$BA través del tiempo, una tribu primitiva de humanoides nocturnos llegó cautelosamente hasta los confines del fascinante lago encantado.' WHERE `entry`='638';
UPDATE `locales_page_text` SET `Text_loc6`='Los nómadas humanoides ferales, atraídos por las extrañas energías del Pozo, fabricaron rudimentarias casas junto a sus serenas orillas. Con el paso del tiempo, el poder cósmico del Pozo afectó a la extraña tribu, haciéndola fuerte, sabia y prácticamente inmortal. La tribu adoptó el nombre de kaldorei, que en su lengua significaba \"hijos de las estrellas\". Para celebrar el nacimiento de su sociedad, construyeron magníficas estructuras y templos en la periferia del lago.' WHERE `entry`='639';
UPDATE `locales_page_text` SET `Text_loc6`='Los kaldorei, o elfos de la noche, tal como se les conoció posteriormente, adoraban a la diosa de la luna Elune, convencidos de que, durante el día, dormía en las refulgentes profundidades del Pozo. Los antiguos sacerdotes y videntes de los elfos de la noche estudiaron el Pozo con insaciable curiosidad, dedicándose a desentrañar sus insondables secretos y poderes. A medida que su sociedad prosperó, los elfos de la noche exploraron las tierras de Kalimdor y descubrieron a sus innumerables moradores.' WHERE `entry`='640';
UPDATE `locales_page_text` SET `Text_loc6`='Las únicas criaturas que les dieron tregua fueron los antiguos y poderosos dragones. Aunque las enormes bestias serpenteantes solían recluirse, ayudaban en gran medida a proteger las tierras conocidas de potenciales enemigos. Los elfos de la noche creían que los dragones se consideraban los protectores del mundo y que era mejor dejarlos solos con sus secretos.' WHERE `entry`='641';
UPDATE `locales_page_text` SET `Text_loc6`='Con el tiempo, la curiosidad de los elfos de la noche les condujo a conocer y entablar amistad con diversos seres poderosos, entre los cuales estaba Cenarius, un semidiós de los bosques primigenios. El benévolo Cenarius simpatizaba con los inquisitivos elfos de la noche y pasaba largas horas instruyéndolos en el conocimiento del mundo natural. Los serenos kaldorei desarrollaron una fuerte empatía por los bosques vivientes de Kalimdor y se deleitaban en el armonioso equilibrio de la naturaleza.' WHERE `entry`='642';
UPDATE `locales_page_text` SET `Text_loc6`='Sin embargo, con el paso del tiempo, que parecía eterno, la civilización de los elfos de la noche se expandió territorial y culturalmente. Sus templos, caminos y viviendas poblaban la totalidad del continente oscuro. Azshara, la bella e inteligente reina de los elfos de la noche, construyó un magnífico e inmenso palacio a la orilla del Pozo, en el que habitaba su servidumbre selecta, rodeada de fastuosas riquezas.' WHERE `entry`='643';
UPDATE `locales_page_text` SET `Text_loc6`='La selecta servidumbre, llamada Quel\'dorei o nobleza, estaba al servicio de la reina y se consideraba superior al resto de sus hermanos de casta inferior. Si bien la reina Azshara era amada por todos sus súbditos, la nobleza era odiada en secreto por las masas envidiosas.$B$BAzshara, que compartía con los sacerdotes la curiosidad por conocer los secretos del Pozo de la Eternidad, ordenó a la educada nobleza que desentrañara sus misterios y los difundieran por todas partes.' WHERE `entry`='644';
UPDATE `locales_page_text` SET `Text_loc6`='Los nobles se enfrascaron en su trabajo y estudiaron el Pozo incansablemente. Con el tiempo, desarrollaron la habilidad de manejar y controlar las energías cósmicas del Pozo. A medida que sus insensatos experimentos avanzaron, los miembros de la nobleza advirtieron que podían usar sus nuevos poderes para crear o destruir a voluntad. Los desafortunados nobles se tropezaron con la magia primitiva y habían resuelto dominarla.' WHERE `entry`='645';
UPDATE `locales_page_text` SET `Text_loc6`='Si bien sabían que, si se manipulaba irresponsablemente, la magia era inherentemente peligrosa, Azshara y sus nobles empezaron a practicar sus hechizos con imprudente abandono. Cenarius y muchos de los viejos y eruditos elfos de la noche advirtieron que jugar con las volátiles artes del encantamiento solo podría traer la ruina. Pero Azshara y sus seguidores continuaron extendiendo tenazmente sus incipientes poderes.' WHERE `entry`='646';
UPDATE `locales_page_text` SET `Text_loc6`='A medida que sus poderes crecieron, se produjo un cambio evidente en Azshara y los nobles. Los altivos y distantes aristócratas se volvieron cada vez más insensibles y crueles con los demás elfos de la noche. Un oscuro y perturbador manto envolvió la otrora fascinante belleza de Azshara. Se distanció de sus súbditos más cercanos y se negó a tratar con nadie que no fuera sus fieles sacerdotes aristócratas.' WHERE `entry`='647';
UPDATE `locales_page_text` SET `Text_loc6`='Un joven y audaz estudiante llamado Furion Tempestira, que había dedicado gran parte del tiempo al estudio de los efectos del Pozo, empezó a sospechar que un terrible poder estaba corrompiendo a los nobles y a su amada reina. Si bien consideraba inconcebible el mal que los acechaba, sabía que las vidas de los elfos de la noche pronto cambiarían para siempre...' WHERE `entry`='648';
UPDATE `locales_page_text` SET `Text_loc6`='-Totalmente redondo, cubre toda el área, desde el puño hasta el codo.$B$B-Clavos en los bordes. Pieza de plata.$B$B-Un clavo grande de plata en el centro, con un ancho de tres dedos. Pieza de plata.$B$B-De roble, reforzado con hierro.$B$BMi nombre, Gringer Pico Tormenta, grabado en el borde superior.$B' WHERE `entry`='649';
UPDATE `locales_page_text` SET `Text_loc6`='El pago se realizará por los medios habituales. Abona al portador de esta petición una remuneración estándar por la entrega. Puedes cargarlo en el precio del escudo.$B$B$B$BUn cordial saludo y muchas gracias,$B--Gringer Pico Tormenta' WHERE `entry`='650';
UPDATE `locales_page_text` SET `Text_loc6`='La llegada de los no-muertos a Azeroth no se produjo inicialmente con la corrupción del príncipe Arthas, sino mucho antes de aquel acontecimiento. Los hechos que provocaron la caída de Lordaeron se desencadenaron mucho antes de ese momento: con la llegada de los clanes orcos y la Legión Ardiente.' WHERE `entry`='669';
UPDATE `locales_page_text` SET `Text_loc6`='Los orcos de Draenor (el mundo del que procedían originalmente) eran nobles y chamánicos. Eran poderosos guerreros, corpulentos y de férrea voluntad. Esta era la razón por la que la Legión Ardiente los buscaba. Kil\'jaeden, un demonio de gran poder en la Legión, deseaba someter a los clanes orcos y usarlos como escudo contra los mundos que la Legión Ardiente pretendía conquistar.' WHERE `entry`='670';
UPDATE `locales_page_text` SET `Text_loc6`='Kil\'jaeden se dirigió al líder de los clanes, un poderoso chamán llamado Ner\'zhul. Prometió transmitirle a Ner\'zhul infinitos poderes místicos y conocimientos ocultos si hacía un pacto con el demonio capaz de someter a los clanes de la Legión Ardiente. Este pacto no solo selló para siempre su destino, sino el de todo su mundo.' WHERE `entry`='671';
UPDATE `locales_page_text` SET `Text_loc6`='El tiempo transcurrió y Ner\'zhul se dio cuenta de que el destino de los clanes estaba en manos de la Legión Ardiente, por lo que el chamán orco empezó a desafiar a Kil\'jaeden. El demonio, disgustado con Ner\'zhul, se dirigió a Gul\'dan, el propio aprendiz del chamán, mucho más propenso a la corrupción que Ner\'zhul.' WHERE `entry`='672';
UPDATE `locales_page_text` SET `Text_loc6`='De hecho, fue Gul\'dan el responsable del declive del chamanismo entre los clanes. Las promesas de poder y control sobre el enemigo hicieron sus ofertas aún más tentadoras. Con la ayuda de Kil\'jaeden, Gul\'dan empezó a manipular a los clanes. Los orcos, otrora nobles, se convirtieron en bestias salvajes y corruptas. Con sangre demoníaca inoculada en sus cuerpos, los orcos fueron mucho más despiadados y bárbaros.' WHERE `entry`='673';
UPDATE `locales_page_text` SET `Text_loc6`='Aunque Kil\'jaeden había corrompido a Gul\'dan tras fracasar con Ner\'zhul, odiaba al chamán, por lo que juró respetar su pacto original. Kil\'jaeden capturó a Ner\'zhul y sometió al chamán a una tortura física increíblemente lenta. Mientras Ner\'zhul suplicaba que lo matasen, Kil\'jaeden le recordó que su pacto original seguía vigente. Kil\'jaeden mató al orco, pero solo físicamente. Kil\'jaeden capturó el espíritu del chamán orco antes de que partiera al Vacío Abisal.' WHERE `entry`='674';
UPDATE `locales_page_text` SET `Text_loc6`='Encerró el espíritu de Ner\'zhul en un bloque de hielo procedente de El Vacío Abisal. Atrapado en el hielo, infundió aún más poder al témpano. La pérdida de su cuerpo físico y la absorción de aquel increíble poder marcaron el inicio de la transformación de Ner\'zhul.' WHERE `entry`='675';
UPDATE `locales_page_text` SET `Text_loc6`='Kil\'jaeden arrojó el bloque de hielo a través de múltiples dimensiones hasta Azeroth, aterrizando en el continente de Rasganorte e implantando su inmensa maldad en nuestro mundo. Ner\'zhul se había ido para siempre, dejando tras de sí un trono de hielo y odio. El antaño respetado líder de los chamanes se había convertido en el increíblemente poderoso Rey Exánime.' WHERE `entry`='676';
UPDATE `locales_page_text` SET `Text_loc6`='Puesto que Kil\'jaeden no confiaba en el Rey Exánime, envió a sus aterradores guerreros a vigilar su espíritu, para así asegurarse de que el rey siguiera sus órdenes. Los vampíricos esbirros vinieron a Azeroth de buen grado, atraídos por la destrucción y el poder del Rey Exánime, ante la posibilidad borrar toda una raza de la faz del planeta.' WHERE `entry`='677';
UPDATE `locales_page_text` SET `Text_loc6`='Transcurrió más de una década. Durante ese tiempo, el Rey Exánime usó sus vastos poderes para controlar las mentes de las criaturas de Rasganorte, a quienes ordenó construir una gran ciudad sobre su trono congelado. Una vez que Rasganorte fue conquistado, el Rey Exánime sabía que tenía que empezar a infiltrarse en Lordaeron. Atrapado en el hielo, el antiguo chamán orco halló nuevas mentes para manipularlas y controlarlas. Su llamada atravesó continentes enteros.' WHERE `entry`='678';
UPDATE `locales_page_text` SET `Text_loc6`='Las invocaciones del Rey Exánime no pasaron desapercibidas, especialmente para un puñado de poderosos individuos. Entre ellos estaba el archimago Kel\'Thuzad, miembro de la junta de gobierno de Dalaran, el Kirin Tor. Su interés por las fuerzas ocultas infringía la política del Kirin Tor, que se oponía al aprendizaje de la magia negra. Kel\'Thuzad abandonó el Kirin Tor y todos sus vínculos con el pensamiento convencional y juró aprender todos los secretos del Rey Exánime.' WHERE `entry`='679';
UPDATE `locales_page_text` SET `Text_loc6`='Ambos sellaron un pacto. A cambio de servir al Rey Exánime, Kel\'Thuzad obtendría la inmortalidad e inmensos poderes. La primera tarea que Kel\'Thuzad habría de realizar consistía en usar su inmensa riqueza e influencia en Lordaeron para fundar el Culto de los Malditos. El Culto prometía igualdad y vida eterna a todos sus miembros, siempre y cuando juraran obediencia a Ner\'zhul, el dios del Culto de los Malditos.' WHERE `entry`='680';
UPDATE `locales_page_text` SET `Text_loc6`='Ner\'zhul creó, a continuación, artefactos diseñados para diseminar la peste entre las civilizaciones humanas de Lordaeron. Entregó los artefactos a Kel\'Thuzad y ordenó al zahorí que los extendiera por todo el territorio. Los seguidores de su culto debían proteger estos artefactos a toda costa.' WHERE `entry`='681';
UPDATE `locales_page_text` SET `Text_loc6`='Una vez extendida, la peste empezará a filtrarse en la tierra, afectando a sus ignorantes moradores. Kel\'Thuzad pudo advertir cómo el ejército del Rey Exánime crecía rápidamente, haciéndose con el control de grandes extensiones de tierra. Este ejército se conoció como la Plaga, pues su único propósito era el exterminio de la humanidad de la tierra de Azeroth.' WHERE `entry`='682';
UPDATE `locales_page_text` SET `Text_loc6`='El pago se efectuará a través de los conductos habituales. Y den el portador de esta solicitud la propina de entrega standart. Puede añadirle el costo del escudo.$BSinceramente, y muchas gracias,$BGringer Pico Tormenta' WHERE `entry`='689';
UPDATE `locales_page_text` SET `Text_loc6`='La palabra TRAIDOR aparece escrita toscamente sobre el epitafio finamente grabado, cuyo texto dice:$B$BAquí yace Yuriv.$BPadre, esposo y paladín.$BSus hijos deberán saber que su dedicación a la Luz fue incuestionable.$BNunca exigió a los demás nada que él no hiciera.' WHERE `entry`='690';
UPDATE `locales_page_text` SET `Text_loc6`='La palabra TRAIDOR aparece escrita toscamente sobre el epitafio finamente grabado, cuyo texto dice:$B$BAquí yace Yuriv.$BPadre, esposo y paladín.$BSus hijos deberán saber que su dedicación a la Luz fue incuestionable.$BNunca exigió a los demás nada que él no hiciera.' WHERE `entry`='695';
UPDATE `locales_page_text` SET `Text_loc6`='Diario - Cuarto día$B$BLlevo 4 días varado en la isla, a solas con mis pensamientos. Los plátanos son sabrosos, pero cuesta mucho trepar para alcanzarlos. Cuando no estoy buscando comida o protegiéndome de la periódica lluvia, todos mis pensamientos se centran en el rescate.$B$BNo estaría tan esperanzado de no ser por las cajas de botellas y papel que fueron arrojadas a esta orilla. Ahora me río por todo el tiempo que he estado maldiciendo en el barco por ir acompañado de un cargamento de alquimistas y escribas.' WHERE `entry`='696';
UPDATE `locales_page_text` SET `Text_loc6`='Diario - Día 512$B$BLos plátanos han empezado a hablar conmigo y he aprendido mucho acerca de su cultura. He abandonado la insensible destrucción de sus hogares y el consumo de sus crías. Cuán poco sabía de la gran civilización que estaba destruyendo.$B$BPasando a otra cosa, creo que se me están acabando las botellas y el papel. Cuando veía inicialmente las enormes pilas de papel que llegaron hasta aquí, pensé que nunca se acabarían. Ay de mí, mi diario pronto llegará a su fin.' WHERE `entry`='697';
UPDATE `locales_page_text` SET `Text_loc6`='La tierra de Azeroth alberga incontables maravillas. Flora, fauna, culturas y magia pueblan su superficie. Ciertamente, los curiosos encontrarán aquí una ilimitada variedad de formas de vida y maravillas sorprendentes. Basta echar un ligero vistazo.$B$BSi se mira con suficiente detenimiento, se abre una ventana a mundos desconocidos y cada uno alberga sus propios secretos.$B$BDe la misma manera que cada uno guarda sus propios horrores.' WHERE `entry`='698';
UPDATE `locales_page_text` SET `Text_loc6`='Este es el propósito de mi libro: catalogar a aquellos seres, extraños mefistos decididos a destruir nuestras tierras, para que los exploradores que los encuentren sepan lo que tienen delante.$B$BSi te consideras un guardián de Azeroth, sigue leyendo. y conoce a tu enemigo.$B$BUr$BMago de Dalaran' WHERE `entry`='699';
UPDATE `locales_page_text` SET `Text_loc6`='El mefisto sobre el que escribo es el huargen.$B$BEl folklore popular recoge historias sobre estas criaturas, pues ¿qué hijo de granjero no ha oído historias de lobizones bestiales acechando en los bosques y las marismas, fuera de este poblado?$B$BEstas historias encierran cierta verdad; tal vez nos advierten acerca de la presencia de los huargen, convertidos en mito para asustarnos.' WHERE `entry`='700';
UPDATE `locales_page_text` SET `Text_loc6`='Pero antes de rechazar tales historias, debo asegurar al lector: los huargen son reales. Quizás no sean de este mundo, pero existen caminos entre sus moradas y las nuestras, ocultos poderes que los atraen hasta nosotros.$B$BEs mejor no pronunciar los conjuros que invocan a estas bestias, pues allí donde van, siembran el terror y la destrucción.' WHERE `entry`='701';
UPDATE `locales_page_text` SET `Text_loc6`='Los huargen se reconocen por su similitud con los lobos de nuestro mundo. Los huargen se distinguen fácilmente por su grueso pelaje, sus orejas puntiagudas y sus alargados hocicos, similares a los de los lobos que conocemos.$B$BPero las diferencias también son claras: el grueso pelaje cubre el cuerpo musculoso de la bestia, que luce dos largos colmillos y zarpas afiladas como puñales. Su aullido esconde una malevolencia que ninguna otra bestia natural posee.' WHERE `entry`='702';
UPDATE `locales_page_text` SET `Text_loc6`='El hábitat del huargen es un lugar oscuro, un lugar de pesadilla. Si en este mundo existen espacios a salvo de los malignos huargen, mis investigaciones no reflejan tales bastiones.$B$BSi tenemos en cuenta la ferocidad y la maldad de los huargen, lo más probable es que estos bastiones no existan.$B$BSe dice que los huargen viven a gusto en su mundo, pues, aunque algunos poseen mágicos poderes, no han intentado acercarse a Azeroth por impulso propio.$B$BEso nos hace afortunados.' WHERE `entry`='703';
UPDATE `locales_page_text` SET `Text_loc6`='Como he dicho anteriormente, algunos huargen son diestros en las artes místicas, utilizando poderes oscuros y siniestros.$B$BSon habituales en ellos las maldiciones y los venenos sobrenaturales, de modo que queda advertido: aquellos que se enfrenten a un mago huargen deberán protegerse del poder de la sombra.' WHERE `entry`='704';
UPDATE `locales_page_text` SET `Text_loc6`='Realmente deseo que ningún zahorí de Dalaran busque a los huargen, aun cuando sean plenamente conscientes de lo que hacen. Pues no hay acuerdo posible, no revelarán secreto alguno y nada bueno puede obtenerse de estas bestias.$B$BEs mejor dejarlas en paz en su mundo. Si llegan al nuestro y no son destruidos, estaremos en grave peligro...' WHERE `entry`='705';
UPDATE `locales_page_text` SET `Text_loc6`='Realmente deseo que ningún zahorí de Dalaran busque a los huargen, aun cuando sean plenamente conscientes de lo que hacen. Pues no hay acuerdo posible, no revelarán secreto alguno y nada bueno puede obtenerse de estas bestias.$B$BEs mejor dejarlas en paz en su mundo. Si llegan al nuestro y no son destruidos, estaremos en grave peligro...' WHERE `entry`='710';
UPDATE `locales_page_text` SET `Text_loc6`='Helgrum,$B$BDeseo dar mi enhorabuena a aquel que haya dado la orden de establecer nuestra base en Kargarth, en las Tierras Inhóspitas. Es un lugar perfecto para adiestrar a nuestras tropas. El clima riguroso, su fauna salvaje y la falta absoluta de asistencia convierten a este enclave en un crisol que solo dará lugar a los mejores guerreros y exploradores.$B$BBien pensado. Bien hecho.' WHERE `entry`='711';
UPDATE `locales_page_text` SET `Text_loc6`='Describiré las tierras vecinas teniendo en cuenta las tareas de reconocimiento de los exploradores de Kargath.$B$BSe trata de colinas rojizas y rocosas y de planicies secas, resultando poco habitables.$BEl sol es abrasador y hay fuertes vientos.$BNo hay lagos ni corrientes. Ni siquiera charcas aisladas. Para encontrar agua, los nativos deben cavar pozos profundos y llenos de barro, o bien succionar la escasa humedad de los cactus o hierbajos espinosos.$B$BEn resumen: aquí la vida es difícil, en un entorno muy caluroso.' WHERE `entry`='712';
UPDATE `locales_page_text` SET `Text_loc6`='Las Tierras Inhóspitas están llenas de enemigos. Los ogros vagan por las arenas ardientes, tendiendo emboscadas a los incautos. Los enanos Hierro Negro habitan unos cuarteles al este de Kargath y luchan contra sus primos de tez clara en Loch Modan, situado al norte. Unas bestias brutales y primitivas llamadas troggs dan caza a todo ser vivo y lo atacan salvajemente.$B$BY en las Tierras Inhóspitas del este, en el Barranco Lethlor, habitan los dragones. No sabemos cuántos son ni qué tamaño tienen, pues los exploradores que se acercan demasiado nunca regresan.' WHERE `entry`='713';
UPDATE `locales_page_text` SET `Text_loc6`='No hace falta decir que los que acampamos en Kargath estamos muy contentos con nuestra misión. Aquí nos hacemos fuertes, porque si nos descuidamos, morimos.$B$BY no quisiera que fuera distinto.$B$BNeeka Marca de Sangre$BJefa de exploradores, Kargath' WHERE `entry`='714';
UPDATE `locales_page_text` SET `Text_loc6`='Helgrum, $BMis felicitaciones a quien dio la orden de establecer nuestra base de Kargath en las Tierras Inhóspitas. Es el lugar ideal para entrenar a nuestras tropas. Su duro clima, fiera fauna, guerreros indígenas y total ausencia de apoyo exterior hacen de este lugar un crisol del que sólo los mejores guerreros y exploradores saldrán. Una buena planificación.$BBien hecho.' WHERE `entry`='730';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><BR/><H1 align=\"center\">Eliza</H1><BR/><BR/><BR/><BR/><P align=\"center\">Amada esposa, que la tierra sacie tu apetito.</P></BODY></HTML>' WHERE `entry`='731';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><BR/><H1 align=\"center\">Eliza</H1><BR/><BR/><BR/><BR/><P align=\"center\">Amada esposa, que la tierra sacie tu apetito.</P></BODY></HTML>' WHERE `entry`='750';
UPDATE `locales_page_text` SET `Text_loc6`='Estas son las especificaciones del pistón:$B$BLongitud: 32 EB$BAncho: 12 EB$BPeso: 23,5 GD a 25,5 GD$BConducción: 70 RDP$BFuerza de corte: 98 o superior$BTasa de combobulación: \"Superfuerte\" o superior$B$BEn caso de que hayas olvidado las medidas gnómicas:$BEB = \"Extensión de los bigotes\"$BGD = \"Gramos diminutos\"$BRDP = \"Rizos de los dedos de los pies\"' WHERE `entry`='751';
UPDATE `locales_page_text` SET `Text_loc6`='Eh, colega. Lleva esta carta a Frang, en el Valle de los Retos. Suele estar fuera de El Cubil, entrenando a jóvenes guerreros que desean luchar por la Horda.$B$BNecesita guerreros astutos como tú para enseñar a los orcos a pelear usando algo más que sus músculos. Eres más rápido y listo que la mayoría, pero no te jactes de eso: a los orcos no les gusta que los insulten. Son buenos aliados y quizás te enseñen un par de cosas.$B$BTar’sere' WHERE `entry`='752';
UPDATE `locales_page_text` SET `Text_loc6`='Eh, colega. Lleva esta carta a Frang, en el Valle de los Retos. Suele estar fuera de El Cubil, entrenando a jóvenes guerreros que desean luchar por la Horda.$B$BNecesita guerreros astutos como tú para enseñar a los orcos a pelear usando algo más que sus músculos. Eres más rápido y listo que la mayoría, pero no te jactes de eso: a los orcos no les gusta que los insulten. Son buenos aliados y quizás te enseñen un par de cosas.$B$BTar’sere' WHERE `entry`='770';
UPDATE `locales_page_text` SET `Text_loc6`='ALIMENTACIÓN Y CUIDADOS:$B$BNos comprometemos a proporcionarte todo lo que necesitas para cuidar a tu mascota. Nuestros clientes esperan este nivel de servicio de Snuff Inc, ¡y estamos de acuerdo en ofrecerlo!$B$BPara ello, hemos diseñado unos robustos cajones para husmeador taltuza con pequeños orificios, perfectos para mantener a tu mascota en un lugar seguro, alejado de la luz.$B$BPara tu comodidad, dentro de cada cajón hay un recipiente con gránulos de comida, ¡impregnados con el atrayente aroma de los tubérculos hojazul (el alimento favorito de husmeadores taltuza)!' WHERE `entry`='771';
UPDATE `locales_page_text` SET `Text_loc6`='ALIMENTACIÓN Y CUIDADO:$B$BNos comprometemos a ofrecerle todo lo necesario para cuidar de su mascota. Nuestros clientes solo esperan este nivel de servicio de Tabaco Inc, y ¡estamos de acuerdo! Para ello, hemos diseñado robustos trasportines con agujeros pequeños, perfectos para mantener a tu mascota segura y fuera y de la luz. Para su comodidad, en el inetrior hemos creado un comedero de albóndigas, ¡impregnado con un irresistible olor a Tubérculo hajazul (albóndigas favoritas del husmeador)!' WHERE `entry`='790';
UPDATE `locales_page_text` SET `Text_loc6`='Describir las tierras vecinas teniendo en cuenta las tareas de reconocimiento de los exploradores de Kargath.$B$BSe trata de colinas rojizas y rocosas y de planicies secas, resultando poco habitables.$BEl sol es abrasador y hay fuertes vientos.$BNo hay lagos ni corrientes. Ni siquiera charcas aisladas. Para encontrar agua, los nativos deben cavar pozos profundos y llenos de barro, o bien succionar la escasa humedad de los cactus o hierbajos espinosos.$B$BEn resumen: aquí la vida es difícil, en un entorno muy caluroso.' WHERE `entry`='791';
UPDATE `locales_page_text` SET `Text_loc6`='Las Tierras Inhóspitas están llenas de enemigos. Los ogros vagan por las arenas ardientes, tendiendo emboscadas a los incautos. Los enanos Hierro Negro habitan unos cuarteles al este de Kargath y luchan contra sus primos de tez clara en Loch Modan, situado al norte. Unas bestias brutales y primitivas llamadas troggs dan caza a todo ser vivo y lo atacan salvajemente.$B$BY en las Tierras Inhóspitas del este, en el barranco Lethlor, habitan los dragones. No sabemos cuántos son ni qué tamaño tienen, pues los exploradores que se acercan demasiado nunca regresan.' WHERE `entry`='792';
UPDATE `locales_page_text` SET `Text_loc6`='No hace falta decir que los que acampamos en Kargath estamos muy contentos con nuestra misión. Aquí nos hacemos fuertes, porque si nos descuidamos, morimos.$B$BY no quisiera que fuera distinto.$B$BNeeka Marca de Sangre$BJefa de exploradores, Kargath' WHERE `entry`='793';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestro mundo está cargado de historia. Una historia de razas modernas y antiguas. Una historia de guerras pasadas y presentes.$B$BUna historia de dioses que vienen del cielo. Y una historia de dioses antiguos que dormitan sobre la tierra.$B$BAhora hablaré de esos seres, los dioses antiguos. Aquellos que habitaron y rugieron por todo el territorio de Azeroth cuando el mundo estaba en ciernes.' WHERE `entry`='794';
UPDATE `locales_page_text` SET `Text_loc6`='Los dioses antiguos son la voluntad de nuestro mundo. En cada tormenta se escucha la risa estruendosa de un dios antiguo. Sus penetrantes miradas son teas incandescentes. A su paso, la tierra retumba y se resquebraja, mientras las criaturas menores gritan de pánico y rasgan su propia carne en medio de la desesperación.$B$BSu desesperación tiene razón de ser, pues, al igual que el fuego no se detiene ante la mano curiosa de un niño, los dioses antiguos no se preocupan por los seres inferiores. Con suerte, nos tratan como peones. A veces, incluso, somos sus juguetes.' WHERE `entry`='795';
UPDATE `locales_page_text` SET `Text_loc6`='Fueron los primeros amos del mundo y reinaron sembrando el terror. Pero ahora duermen encadenados, mientras sus siervos vagan por el mundo y nosotros, insignificantes mortales, no estamos a la altura de su poder.$B$BAquellos que intentan enfrentarse a ellos son devorados. Pero los que son conscientes de su situación, los que suplican de rodillas a los siervos de los dioses antiguos, los que sacrifican su mente y su alma, solo ellos son bien recibidos.' WHERE `entry`='796';
UPDATE `locales_page_text` SET `Text_loc6`='Aku\'mai, Princesa de la Profundidad, sirve a los dioses antiguos. Habita en las cavernas de Brazanegra, bendiciéndolas con su antigua sabiduría. Conducida hasta las Profundidades por mortales antiguos, Aku’mai es símbolo de divinidad. Apenas goza de una pequeña parte del poder y la fiereza de los dioses antiguos y, aun así, estas cualidades son inconmensurables en ella.$B$BY por ello es venerada. Y también es temida y amada.$B$BLorgalis' WHERE `entry`='797';
UPDATE `locales_page_text` SET `Text_loc6`='Aku\'mai, Princesa de la Profundidad, sirve a los dioses antiguos. Habita en las cavernas de Brazanegra, bendiciéndolas con su antigua sabiduría. Conducida hasta las Profundidades por mortales antiguos, Aku’mai es símbolo de divinidad. Apenas goza de una pequeña parte del poder y la fiereza de los dioses antiguos y, aun así, estas cualidades son inconmensurables en ella.$B$BY por ello es venerada. Y también es temida y amada.$B$BLorgalis' WHERE `entry`='810';
UPDATE `locales_page_text` SET `Text_loc6`='El plan se ha puesto en marcha.$B$BUtiliza como campamento base el que antaño fuera el gran bastión de fe, la Vigilia de los Héroes, y empezaremos a enviarte refuerzos desde los Páramos de Poniente cuando nos mandes un mensaje que indique que ya está preparado.$B$BEl área en cuestión no ha sido olvidada por los ciudadanos y Soldados de Ventormenta y debería ser un centro de operaciones perfecto desde el cual realizar nuestros ataques.$B$B¡Los Defias recuperarán Ventormenta!$B$BAguardo tu respuesta.' WHERE `entry`='811';
UPDATE `locales_page_text` SET `Text_loc6`='Los asquerosos troggs son fáciles de engañar. Su odio por nuestra especie hace que resulten mucho más fáciles de dominar para utilizarlos como peones. Me llena de asombro que pueda existir cualquier tipo de alianza, pero lo cierto es que nosotros suponemos una amenaza mucho mayor para su existencia... al menos por ahora.$B$BNo obstante, los ataques harán que ambos bandos se debiliten y resultará fácil destruirlos. Llévate la granja al sur del lago de Helm y espera nuevas instrucciones.$B$BForjaz será nuestra.' WHERE `entry`='812';
UPDATE `locales_page_text` SET `Text_loc6`='Los asquerosos troggs son fáciles de engañar. Su odio por nuestra especie hace que resulten mucho más fáciles de dominar para utilizarlos como peones. Me llena de asombro que pueda existir cualquier tipo de alianza, pero lo cierto es que nosotros suponemos una amenaza mucho mayor para su existencia... al menos por ahora.$B$BNo obstante, los ataques harán que ambos bandos se debiliten y resultará fácil destruirlos. Llévate la granja al sur del lago de Helm y espera nuevas instrucciones.$B$BForjaz será nuestra.' WHERE `entry`='830';
UPDATE `locales_page_text` SET `Text_loc6`='En las páginas siguientes, encontrarás un listado con todo el material necesario para fabricar una armadura de mallas endurecida con fuego. Buena suerte, guerrero.$B$B$BFuren Barbalarga' WHERE `entry`='831';
UPDATE `locales_page_text` SET `Text_loc6`='Colmillos de araña agostados:$B$BEl mineral de incendicita que se encuentra en una cueva situada en las inmediaciones de Roca Thelgen ha agostado a las arañas que allí habitan.$B$BEl veneno de sus colmillos hierve y hace reacción con los propios colmillos, dando lugar a una sustancia muy resistente.' WHERE `entry`='832';
UPDATE `locales_page_text` SET `Text_loc6`='Cuernos carbonizados:$B$BAl igual que los colmillos de araña agostados, los cuernos carbonizados de las quimeras que habitan en la Vega Carbonizada, en la Cima del Espolón de Kalimdor, poseen cualidades muy apreciadas.$B$BTras ser aplanadas a golpes y tratadas, se vuelven extremadamente resistentes a la tensión física.' WHERE `entry`='833';
UPDATE `locales_page_text` SET `Text_loc6`='Cuerno galvanizado:$B$BLa dureza del escaso cuerno galvanizado es la clave fundamental de la armadura que voy a crear, así que es preciso conseguirlo.$B$BSe puede obtener de la también escasa quimera matriarca que habita en la Vega Carbonizada.' WHERE `entry`='834';
UPDATE `locales_page_text` SET `Text_loc6`='Vial de flogisto:$B$BLa última fase de la creación de la armadura requiere una forja a temperaturas extremas. Para obtener la temperatura necesaria, necesito un vial de flogisto.$B$BEl único fabricante de esta sustancia es el legendario jabaespín Roogug, y no te la dará sin antes luchar. Roogug habita en el Horado Rajacieno, en el sur de los Baldíos.' WHERE `entry`='835';
UPDATE `locales_page_text` SET `Text_loc6`='En las siguientes páginas, encontraras una lista de materiales necesarios para hacer una armadura endurecida al fuego.$B$BBuena suerte, guerrero.$B$BFuren Barbalarga' WHERE `entry`='850';
UPDATE `locales_page_text` SET `Text_loc6`='Colmillo de Araña Agostado:$B$BLa mena de incendicita que se encuentra en una cueva cerca de la vecindad de Roca Thelgen ha agostado las arañas que allí habitan.$B$BEl veneno en sus colmillos hierve y reacciona con los mismos colmillos para crear una sustancia muy resistente.' WHERE `entry`='851';
UPDATE `locales_page_text` SET `Text_loc6`='Cuernos carbonizados:$B$BAl igual que los colmillos de araña agostados, los cuernos calcinados de la quimera que residen en la Vega carbonizada, en la Sierra Espolón en Kalimdor, poseen una cualidad muy apreciada.$B$BMachacados y adecuadamente tratados, se hacen sumamente resistentes a la tensión física.' WHERE `entry`='852';
UPDATE `locales_page_text` SET `Text_loc6`='Cuerno galvanizado:$B$BLa rara dureza de los cuernos galvanizados es el eje central de toda la pieza de armadura que vaya a crear, por lo que también deben ser adquiridos.$B$BSe pueden conseguir de la rara Quimera Matriarca en la Vega carbonizada.' WHERE `entry`='853';
UPDATE `locales_page_text` SET `Text_loc6`='Vial de flogisto:$B$BEn la última etapa de la creación de una armadura es necesaria una forja muy caliente. Para conseguir el nivel adecuado de calor que debe tener un vial de flogisto.$B$BEl único fabricante conocido de esta sustancia es la legendaria Jabaespín, Roogug, y no va a renunciar a ella sin pelear. Roogug habita en lo profundo del Horado Rajacieno en el sur de Los Baldíos.' WHERE `entry`='854';
UPDATE `locales_page_text` SET `Text_loc6`='A continuación, encontrarás los ingredientes necesarios para el hechizo de invocación del gran Ciclonio. ¡Que la fortuna te acompañe!$B$BBath\'rah el Vigía del viento' WHERE `entry`='855';
UPDATE `locales_page_text` SET `Text_loc6`='Vidarraíz:$B$BSe encuentra en estado salvaje o puede obtenerse de las lodobestias parramustias del marjal Revolcafango. Su poder será necesario para extraer la esencia vital de Ciclonio.' WHERE `entry`='856';
UPDATE `locales_page_text` SET `Text_loc6`='Colmillos de Sangrapellejo:$B$BLos colmillos de los trols Sangrapellejo de la Vega de Tuercespina tienen una extraña cualidad. La perversidad de los Sangrapellejo se concentra en sus colmillos que, cuando se muelen hasta obtener un fino polvo, sus poderes se desencadenan.' WHERE `entry`='857';
UPDATE `locales_page_text` SET `Text_loc6`='Esencia del exilio:$B$BEsta esencia se obtiene mediante la fusión de tres talismanes de los exiliados elementales de las Tierras Altas de Arathi. Reúne talismanes del trueno, talismanes ardientes y talismanes blasonados de los elementales y luego vierte ocho de cada uno en mi caldera.$B$BA partir de los talismanes se formará la esencia del exilio.' WHERE `entry`='858';
UPDATE `locales_page_text` SET `Text_loc6`='Esencia del exilio:$B$BEsta esencia se obtiene mediante la fusión de tres talismanes de los exiliados elementales de las Tierras Altas de Arathi. Reúne talismanes del trueno, talismanes ardientes y talismanes blasonados de los elementales y luego vierte ocho de cada uno en mi caldera.$B$BA partir de los talismanes se formará la esencia del exilio.' WHERE `entry`='870';
UPDATE `locales_page_text` SET `Text_loc6`='$N,$BAntes de continuar, permíteme que te vuelva a dar las gracias por todo lo que hiciste por mi mujer. Le salvaste la vida y no me bastan cien vidas para agradecértelo. Ella lo es todo para mí. Si hubiera muerto... no sé qué habría hecho.$B$BAquí tienes un listado con todo lo que tienes que reunir para fabricarte un arma sagrada. He puesto todos los elementos ordenados según lo fácil que resulta conseguirlos. Espero que eso te facilite las cosas.' WHERE `entry`='871';
UPDATE `locales_page_text` SET `Text_loc6`='Un poco de madera de roble de Piedrablanca: Esta madera abundaba en las minas que hay debajo de Arroyo de la Luna, en los Páramos de Poniente, antes de que fueran usurpadas. De hecho, la usaban para construir algunos de los enrejados y herramientas más importantes, debido a su resistencia al calor y a la presión.$B$BFue llevada hasta allí después de que la trajeran en barco desde algún lugar de ultramar. Se trata de una madera escasa suficientemente resistente como para usarla en la fabricación de tu arma. En el caso de que aún haya restos de esta madera en las minas, lo más probable es que los goblins Tallamadera tengan un poco.' WHERE `entry`='872';
UPDATE `locales_page_text` SET `Text_loc6`='Mineral refinado: Hay un enano en Thelsamar, un herrero al que he instruido durante un tiempo, llamado Bailor Petramano. Obtiene una aleación muy especial que creo que sería perfecta para tu arma. Esperaba un cargamento de esa aleación cuando estaba en Forjaz, pero aún no he recibido noticias de él.$B$BEncuéntralo y averigua qué ha pasado con ese cargamento. Y si mi antiguo maestro ya no está en Loch Modan, al menos me gustaría saber qué ha sido de él.' WHERE `entry`='873';
UPDATE `locales_page_text` SET `Text_loc6`='Martillo de herrería: Antes de la llegada de la Peste y de la traición de Arthas, yo vivía en una ciudad llamada Piroleña, enclavada en el hermoso bosque de Argénteos. El bosque ha perdido el esplendor del pasado, y el fuerte en el que aprendí los secretos del acero ya no es lo que era, pues ahora lo habitan criaturas de maldad inimaginable.$B$BCuando mi mujer y yo escapamos de lo que ahora se conoce como el Castillo de Colmillo Oscuro, abandonando algunas de nuestras más queridas pertenencias.' WHERE `entry`='874';
UPDATE `locales_page_text` SET `Text_loc6`='Entre ellas estaba el martillo de herrería que me dejó mi padre. Cuando era joven no hice caso a sus palabras al afirmar que ese martillo estaba encantado. Fue el último recuerdo que tuve de él antes de que la Plaga lo matara.$B$BTráeme mi martillo, $N, para que vuelva a infundirme todo el entusiasmo. Si realmente está encantado, solo nos será útil a ambos.$B$BLo dejé en los establos en el patio principal del fuerte. A menos que las criaturas que allí viven hayan decidido dedicarse a la herrería, espero que siga ahí.' WHERE `entry`='875';
UPDATE `locales_page_text` SET `Text_loc6`='Una gema Kor - Según me ha contado, estas gemas las utilizan algunos hechiceros para obtener su energía y sus poderes, lo que los hace aún más peligrosos. Un elfo de la noche a quien un día ayudé, Thundris Tejevientos, podrá darte más detalles, pues irás de mi parte. Creo que ciertas energías almacenadas en la gema pueden servir para crear tu arma.$B$BCuando hablamos por última vez, Thundris estaba en Auberdine, un pueblo en medio de Costa Oscura, el bosque corrupto que salpica la costa bajo la sombra de la tierra de los elfos de la noche.' WHERE `entry`='876';
UPDATE `locales_page_text` SET `Text_loc6`='Una gema Kor - Según me ha contado, estas gemas las utilizan algunos hechiceros para obtener su energía y sus poderes, lo que los hace aún más peligrosos. Un elfo de la noche a quien un día ayudé, Thundris Tejevientos, podrá darte más detalles, pues irás de mi parte. Creo que ciertas energías almacenadas en la gema pueden servir para crear tu arma.$B$BCuando hablamos por última vez, Thundris estaba en Auberdine, un pueblo en medio de Costa Oscura, el bosque corrupto que salpica la costa bajo la sombra de la tierra de los elfos de la noche.' WHERE `entry`='877';
UPDATE `locales_page_text` SET `Text_loc6`='Una gema Kor - Según me ha contado, estas gemas las utilizan algunos hechiceros para obtener su energía y sus poderes, lo que los hace aún más peligrosos. Un elfo de la noche a quien un día ayudé, Thundris Tejevientos, podrá darte más detalles, pues irás de mi parte. Creo que ciertas energías almacenadas en la gema pueden servir para crear tu arma.$B$BCuando hablamos por última vez, Thundris estaba en Auberdine, un pueblo en medio de Costa Oscura, el bosque corrupto que salpica la costa bajo la sombra de la tierra de los elfos de la noche.' WHERE `entry`='890';
UPDATE `locales_page_text` SET `Text_loc6`='Los símbolos y pictogramas que rellenan las páginas de este manual son esotéricos y están más allá de la comprensión.' WHERE `entry`='891';
UPDATE `locales_page_text` SET `Text_loc6`='Los símbolos y pictogramas que rellenan las páginas de este manual son esotéricos y están más allá de la comprensión.' WHERE `entry`='910';
UPDATE `locales_page_text` SET `Text_loc6`='Consigue un trozo de jade.Se ocultan en diversos lugares. Se trata de una piedra escasa, pero si hablas con un herrero o un ingeniero, es posible que les quede alguna.' WHERE `entry`='911';
UPDATE `locales_page_text` SET `Text_loc6`='Obtener Zarza electrificada.$B$BPara conseguir una de ellas, reúne 10 Palo totémico Secacorteza de los Trolls Secacorteza de las Tierras Altas de Arathi. Lleva los palos al Círculo del Vínculo Exterior, un círculo de piedras en las mismas tierras altas. Coloque los palos en la piedra central del círculo y espera a que los golpee un rayo.$B$BCuando lo haga, se formará la Zarza electrificada.' WHERE `entry`='912';
UPDATE `locales_page_text` SET `Text_loc6`='Consigue un trozo de jade. Se ocultan en diversos lugares. Se trata de una piedra escasa, pero si hablas con un herrero o un ingeniero, es posible que les quede alguna.' WHERE `entry`='930';
UPDATE `locales_page_text` SET `Text_loc6`='$N, estos son los objetos necesarios para fabricar tu armadura:$B$BLingotes de hierro humeante de los kóbolds Cortaviento que hay en la mina Cortaviento de la Cima del Espolón.$B$BAzurita en polvo de los mineros de Trabalomas.$B$BBarras de hierro de los mineros o de los depósitos de hierro, en el caso de que te dediques a la minería.$B$BVial de flogisto de un jabaespín llamado Roogug, en el Horado Rajacieno.' WHERE `entry`='931';
UPDATE `locales_page_text` SET `Text_loc6`='$N, estos son los objetos necesarios para fabricar tu armadura:$B$BLingotes de hierro humeante de los kóbolds Cortaviento que hay en la mina Cortaviento de la Cima del Espolón.$B$BAzurita en polvo de los mineros de Trabalomas.$B$BBarras de hierro de los mineros o de los depósitos de hierro, en el caso de que te dediques a la minería.$B$BVial de flogisto de un jabaespín llamado Roogug, en el Horado Rajacieno.' WHERE `entry`='950';
UPDATE `locales_page_text` SET `Text_loc6`='(Solo resultan visibles algunos fragmentos del diario a causa de las manchas).$B$BUldaman, Tercer día$B$BNo puedo creer lo idiota que he sido. Dejar que los enanos Forjatiniebla me atacaran por sorpresa solo le ocurre a los novatos. Mis heridas se curan lentamente y estoy infectado por su veneno. Noto cómo la energía me abandona. No obstante, tengo que seguir explorando...$B$B... para empeorar las cosas, ¡el collar que $Gel:la; gnomo hizo para mí se ha hecho añicos! Tres de las gemas más grandes engarzadas en él se han perdido. Debo encontrarlas, ya que, sin ellas, el collar carece de todo poder...' WHERE `entry`='951';
UPDATE `locales_page_text` SET `Text_loc6`='La instauración de Ameth\'Aran$B$BDesde hace mucho tiempo los hijos de las estrellas habitaban las orillas de las brillantes aguas del Pozo de la Eternidad. De todos era sabido que Elune, luz del eterno crepúsculo, con aspecto y diosa de la luna, moraba sus aguas cuando descansada de su trabajo. A la orilla del pozo los hijos de las estrellas construyeron sus hogares, en honor de Elune. Incluso se entrenaron a fijar su mirada al cielo, en las noches de luna.' WHERE `entry`='953';
UPDATE `locales_page_text` SET `Text_loc6`='La caida de Ameth\'Aran$B$BLa Tierra tembló cuando los árboles ancestrales del bosque encantado fueron arrancados y derribados. Las bosques y claros habitadospor los hijos y las hijas de Cenarius y las torres de piedra de los hijos de las Estrellas rodaron por los suelos. Fue nuestra radiante reina, incluso en la desesperación, y en el caos que se derivó de las batallas. El cielo encantador cambio de color con las descargas de magia, y las explosiones que amenazaban con partir el mundo en dos.' WHERE `entry`='954';
UPDATE `locales_page_text` SET `Text_loc6`='(Sólo partes del diario son legibles a través de las manchas.)$B$BUldaman,$BDía 3$B$BNo puedo creer lo tonto que fui. Ser noqueado por los asquerosos enanos Forjaniebla solo le ocurre a los novatos. Mis heridas cicatrizan lentamente; posiblemente esté envenenado. Siento que la sabia de mi vida se escapa. Debo explorar el terreno, tal vez... ...empeoraría el asunto, ¡el collar que me hicieron los gnomos se me ha roto! Tres de las mayores gemas ensartadas en él se han perdido. Tengo que buscarlas; sin ellas el collar no tiene ningún poder...' WHERE `entry`='970';
UPDATE `locales_page_text` SET `Text_loc6`='Uldaman, cuarto día$B$BPude encontrar la primera en las profundidades de Uldaman, ya que uno de los troggs más salvajes que he visto estaba blandiendo un gran zafiro que habría encajado perfectamente en el collar. Escuché a los enanos Forjatiniebla mencionar el nombre de las bestias: Grimlok. Vaya panda de guardianes que tiene el tosco de Grimlok, no obstante...' WHERE `entry`='971';
UPDATE `locales_page_text` SET `Text_loc6`='Uldaman, quinto día$B$B... el trogg, cuando arrancó el topacio a sus \"hermanos\" asesinados, se dirigió a la caverna lateral que hay cerca de la entrada de la sala...$B$B... rápidamente para colocar el topacio en una urna grande y llamativa que había en la parte trasera de la cámara...$B$B... ¡sobre todo, enanos! Además, había tres de ellos. Actualmente los utilizan como escudos vivientes contra los troggs. Llamarlos habría sido un suicido, ya que esto habría atraído a los apestosos troggs... y yo en este estado calamitoso... Sin embargo, en cuanto dé con la tercera gema, voy a...' WHERE `entry`='972';
UPDATE `locales_page_text` SET `Text_loc6`='Uldaman, Sexto día$B$B... ¡qué hambre! Me he quedado sin provisiones, hasta tal punto que un pan mohoso sería ahora un festín. De hecho, vi cómo la cabeza de un trogg se convertía en un jugoso filete delante de mí...' WHERE `entry`='973';
UPDATE `locales_page_text` SET `Text_loc6`='Uldaman, Séptimo día$B$B... es mi última oportunidad para reparar mis errores. Creo que el rubí está cerca de las últimas barricadas que los enanos construyeron contra los troggs. Las batallas que libran, sin duda por el control de las gemas y otros artefactos de este sitio, son continuas. Lo más probable es que esos viles enanos lo hayan escondido por esa zona. De todas maneras, tengo que hacer algo al respecto. Tal vez sea mejor salir a la superficie repentinamente...' WHERE `entry`='974';
UPDATE `locales_page_text` SET `Text_loc6`='Uldaman, Séptimo día$B$B... es mi última oportunidad para reparar mis errores. Creo que el rubí está cerca de las últimas barricadas que los enanos construyeron contra los troggs. Las batallas que libran, sin duda por el control de las gemas y otros artefactos de este sitio, son continuas. Lo más probable es que esos viles enanos lo hayan escondido por esa zona. De todas maneras, tengo que hacer algo al respecto. Tal vez sea mejor salir a la superficie repentinamente...' WHERE `entry`='990';
UPDATE `locales_page_text` SET `Text_loc6`='Busca una zarza electrificada. Para obtenerla, reúne 10 palos de tótem de los trols Secacorteza procedentes de las Tierras Altas de Arathi. Lleva los palos al Círculo de Vínculo Exterior, un círculo de piedras que hay en las tierras altas. Coloca los palos sobre la piedra central del círculo y espera a que un rayo caiga encima de ellos. Cuando esto ocurra, se formará la zarza electrificada.' WHERE `entry`='991';
UPDATE `locales_page_text` SET `Text_loc6`='$GEl portador:La portadora; de este certificado obtendrá el respeto y la consideración que merece un ratero o un ladrón.' WHERE `entry`='992';
UPDATE `locales_page_text` SET `Text_loc6`='$GEl portador:La portadora; de este certificado obtendrá el respeto y la consideración que merece un ratero o un ladrón.' WHERE `entry`='1010';
UPDATE `locales_page_text` SET `Text_loc6`='¡He encontrado un método para entrar en la cámara de Khaz?mul!$B$BCoge el medallón de Gni?kiv que hay en mi cofre.$B$BDerrota al trogg Revelosh en la cámara, delante del cuarto de mapas, y recupera el bastón de Tsol.$B$BUne el medallón y el bastón para formar el Bastón de Prehistoria.$B$BUsa el bastón en el cuarto de mapas para abrir la puerta que conduce a la cámara de Khaz?mul.$B$B¡Haz todo esto y la cámara será tuya!$BBaelog' WHERE `entry`='1011';
