UPDATE `locales_page_text` SET `Text_loc6`='¿Estoy perdiendo el juicio? Hoy le abrí la cabeza a uno de mis patrulleros más fieles para poder ver mejor dentro de su alma. Mientras observaba detenidamente su conducto respiratorio destrozado, pensé: ¡No he comido nada en varios días!' WHERE `entry`='1153';
