UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">MANUAL DE INSTRUCCIONES DE LA TORRE DE CRISTAL</H1><BR/><P align=\"center\">\"Una guía para coleccionar y usar los cristales de poder del cráter de Un\'Goro\"</P><BR/><BR/><P align=\"left\">Capítulo 1: La torre del norte</P><BR/><P align=\"left\">Capítulo 2: La torre del este</P><BR/><P align=\"left\">Capítulo 3: La torre del oeste</P><BR/></BODY></HTML>' WHERE `entry`='1510';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">Capítulo 1: LA TORRE DEL NORTE</H1><BR/><BR/><IMG src=\"Interface\\Pictures\\11482_crystals_mini_north\" align=\"left\"/><P align=\"right\">La torre del norte<BR/>acepta cristales<BR/>amarillos, rojos,<BR/>y verdes.<BR/></P><BR/><BR/><BR/><BR/><P align=\"left\">La torre se centra en la energía que se puede generar usando los cristales amarillos como fuente principal de poder.</P></BODY></HTML>' WHERE `entry`='1511';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">Combinación de cristales del norte</H1><BR/><P align=\"left\">Hay dos efectos posibles: uno produce daño y el otro lo anula. </P><BR/><P align=\"left\">CARGA DE CRISTAL: Requiere cristales rojos y amarillos. <BR/>Esto produce una gran explosión.</P><BR/><P align=\"left\">CRISTAL DE RESTAURACIÓN: Requiere cristales amarillos y verdes. <BR/>Este cristal te proporcionará un aura de curación.</P></BODY></HTML>' WHERE `entry`='1512';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">DIAGRAMA DE LA TORRE DEL NORTE</H1><BR/><IMG src=\"Interface\\Pictures\\11482_crystals_north\"/></BODY></HTML>' WHERE `entry`='1513';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">Capítulo 2: LA TORRE DEL ESTE</H1><BR/><BR/><IMG src=\"Interface\\Pictures\\11482_crystals_mini_east\" align=\"left\"/><P align=\"right\">La torre del este<BR/>acepta cristales<BR/>azules, amarillos<BR/>y verdes.<BR/></P><BR/><BR/><BR/><BR/><P align=\"left\">La torre se centra en la energía que se puede generar usando los cristales azules como fuente principal de poder.</P></BODY></HTML>' WHERE `entry`='1514';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">Combinación de cristales del este</H1><BR/><P align=\"left\">Hay dos efectos posibles: uno proporciona puntos adicionales para el espíritu y el otro crea un escudo de ataque.</P><BR/><P align=\"left\">FUERZA DE LOS CRISTALES: Requiere el uso de cristales azules y verdes.<BR/>Esto aumentará tu espíritu.</P><BR/><P align=\"left\">AGUJA DE CRISTAL: Requiere cristales azules y amarillos.<BR/>Creará un escudo que hará daño a los atacantes.</P></BODY></HTML>' WHERE `entry`='1515';
