UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">Capítulo 3: LA TORRE DEL OESTE</H1><BR/><BR/><IMG src=\"Interface\\Pictures\\11482_crystals_mini_west\" align=\"left\"/><P align=\"right\">La torre del oeste<BR/>acepta cristales<BR/>rojos, verdes<BR/>y azules.<BR/></P><BR/><BR/><BR/><BR/><P align=\"left\">La torre se centra en la energía que se puede generar usando los cristales rojos como fuente principal de poder.</P></BODY></HTML>' WHERE `entry`='1531';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">Combinación de cristales del oeste</H1><BR/><P align=\"left\">Hay dos efectos posibles: uno que incrementa tu protección de daño y otro que debilita las defensas del enemigo.</P><BR/><P align=\"left\">SALA DE CRISTAL: Requiere cristales rojos y verdes.<BR/>Esto aumentará tu armadura.</P><BR/><P align=\"left\">COSECHA DE CRISTALES: Requiere cristales azules y rojos.<BR/>Esto puede reducir la armadura de un objetivo específico.</P></BODY></HTML>' WHERE `entry`='1532';
