UPDATE `locales_page_text` SET `Text_loc6`='Compendio de matar dragones de Foror es un libro grueso completamente lleno de escritos. Lamentablemente, el texto parece ser confuso y no tiene sentido para tí.' WHERE `entry`='2637';
