UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">TRATADO SOBRE LOS RANGOS MILITARES</H1><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRankAlliance\" align=\"left\"/><BR/><P align=\"right\">Lo que sigue son</P><P align=\"right\">los rangos militares</P><P align=\"right\">de la Alianza,</P><P align=\"right\">precedido por la</P><P align=\"right\">información tal y como</P><P align=\"right\">la conocen las razas</P><P align=\"right\">de la Horda. Cada rango</P><P align=\"right\">se muestra en dos</P><P align=\"right\">secciones: los oficiales y luego, los soldados rasos, con los rangos correspondientes a cada uno, enumerados en orden descendente, del superior al inferior. ¡Larga vida a la Alianza!</P></BODY></HTML>' WHERE `entry`='2654';
