UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 1</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank14\" align=\"left\"/><P align=\"right\">Gran Señor de la Guerra</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank13\" align=\"left\"/><P align=\"right\">Señor de la Guerra</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank12\" align=\"left\"/><P align=\"right\">Gran general</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank11\" align=\"left\"/><P align=\"right\">General</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank10\" align=\"left\"/><P align=\"right\">Campeón</P><BR/></BODY></HTML>' WHERE `entry`='2658';
UPDATE `locales_page_text` SET `Text_loc6`='Si echas un vistazo en el interior del Ojo, aparecerán en tu mente imágenes de una gran batalla.$B$BEste terrible conflicto parece ocurrir en algún lugar de Lordaeron.$B$BSolo se distingue una solitaria figura femenina, de pie y rodeada de miles de cadáveres, esquivando una marea de la Plaga. No tiene esperanza alguna de hacer frente a la marea enemiga.$B$BDe sus manos brotan haces de luz, abriéndose camino entre centenares de no-muertos.' WHERE `entry`='2659';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA HORDA</H1><BR/><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank05\" align=\"left\"/><P align=\"right\">Gran capataz</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank04\" align=\"left\"/><P align=\"right\">Capataz primero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank03\" align=\"left\"/><P align=\"right\">Capataz</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank02\" align=\"left\"/><P align=\"right\">Bruto</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank01\" align=\"left\"/><P align=\"right\">Explorador</P><BR/></BODY></HTML>' WHERE `entry`='2660';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA HORDA Y DE LA ALIANZA</H1><BR/><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRankHorde\" align=\"left\"/><BR/><P align=\"right\">¡Throm\'ka! En este</P><P align=\"right\">libro encontrarás</P><P align=\"right\">muchas listas de</P><P align=\"right\">rangos militares</P><P align=\"right\">de la Horda y</P><P align=\"right\">de la insignificante Alianza.</P><P align=\"right\">Primero aparecen los rangos de la</P><P align=\"right\">Horda y luego de la Alianza,</P><P align=\"right\">cada una con listados de rangos de oficiales y de soldados. Está ordenada de tal manera que los más poderosos aparecen al principio y los más débiles al final.</P></BODY></HTML>' WHERE `entry`='2661';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 1</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank14\" align=\"left\"/><P align=\"right\">Gran Señor de la Guerra</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank13\" align=\"left\"/><P align=\"right\">Señor de la Guerra</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank12\" align=\"left\"/><P align=\"right\">Gran general</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank11\" align=\"left\"/><P align=\"right\">General</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank10\" align=\"left\"/><P align=\"right\">Campeón</P><BR/></BODY></HTML>' WHERE `entry`='2662';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 2</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank09\" align=\"left\"/><P align=\"right\">Centurión</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank08\" align=\"left\"/><P align=\"right\">Legionario</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank07\" align=\"left\"/><P align=\"right\">Guardia de sangre</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank06\" align=\"left\"/><P align=\"right\">Guardia de piedra</P><BR/></BODY></HTML>' WHERE `entry`='2663';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA HORDA</H1><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank05\" align=\"left\"/><P align=\"right\">Gran capataz</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank04\" align=\"left\"/><P align=\"right\">Capataz primero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank03\" align=\"left\"/><P align=\"right\">Capataz</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank02\" align=\"left\"/><P align=\"right\">Bruto</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank01\" align=\"left\"/><P align=\"right\">Explorador</P><BR/></BODY></HTML>' WHERE `entry`='2664';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE LOS OFICIALES DE LA ALIANZA</H1><BR/><P align=\"center\">Parte 1</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank14\" align=\"left\"/><P align=\"right\">Gran mariscal</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank13\" align=\"left\"/><P align=\"right\">Mariscal de campo</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank12\" align=\"left\"/><P align=\"right\">Mariscal</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank11\" align=\"left\"/><P align=\"right\">Comandante</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank10\" align=\"left\"/><P align=\"right\">Teniente coronel</P><BR/></BODY></HTML>' WHERE `entry`='2665';
UPDATE `locales_page_text` SET `Text_loc6`='Al parecer, todo para nada. Poco después, en torno a la sacerdotisa se agolpan los cadáveres de los campesinos que la rodeaban...$B$BLos caídos se han levantado.' WHERE `entry`='2666';
UPDATE `locales_page_text` SET `Text_loc6`='Simone la Seductora:$B$BEncontrarás a Simone contaminando el cráter Un\'Goro. No te dejes engañar por su disfraz. Aproxímate a ella con cuidado y rétala a un combate.' WHERE `entry`='2671';
UPDATE `locales_page_text` SET `Text_loc6`='Klinfran el Enloquecido:$B$BKlinfran vaga por las Estepas Ardientes. Acércate a él con precaución y oblígale a mostrarte su verdadera forma.' WHERE `entry`='2672';
UPDATE `locales_page_text` SET `Text_loc6`='Solenor el Destripador:$B$BEn los áridos parajes de Silithus vaga el desalmado Solenor el Destripador. Indudablemente, aparecerá disfrazado y, definitivamente, es un peligro. Acércate a él y oblígale a transformarse.' WHERE `entry`='2673';
UPDATE `locales_page_text` SET `Text_loc6`='Artorius el Fatídico:$B$BArtorius trae la corrupción y la maldición a todo lo que toca en Cuna del Invierno. Encuéntralo y mátalo.' WHERE `entry`='2674';
UPDATE `locales_page_text` SET `Text_loc6`='Ten cuidado, cazador, debes combatir a estos demonios según sus reglas. Si sienten que no son superiores a ti, sin duda huirán.$B$BVE SOLO.' WHERE `entry`='2675';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA ALIANZA</H1><BR/><P align=\"center\">OFICIALES</P><BR/><P align=\"center\">Gran mariscal</P><P align=\"center\">Mariscal de campo</P><P align=\"center\">Mariscal</P><P align=\"center\">Comandante</P><P align=\"center\">Teniente coronel</P><P align=\"center\">Campeón caballero</P><P align=\"center\">Capitán caballero</P><P align=\"center\">Teniente caballero</P><P align=\"center\">Caballero</P><BR/><P align=\"center\">TROPA</P><BR/><P align=\"center\">Alférez</P><P align=\"center\">Sargento primero</P><P align=\"center\">Sargento</P><P align=\"center\">Cabo</P><P align=\"center\">Soldado</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2676';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA HORDA</H1><BR/><P align=\"center\">OFICIALES</P><BR/><P align=\"center\">Gran Señor de la Guerra</P><P align=\"center\">Señor de la Guerra</P><P align=\"center\">Gran general</P><P align=\"center\">General</P><P align=\"center\">Campeón</P><P align=\"center\">Centurión</P><P align=\"center\">Legionario</P><P align=\"center\">Guardia de sangre</P><P align=\"center\">Guardia de piedra</P><BR/><P align=\"center\">TROPA</P><BR/><P align=\"center\">Gran capataz</P><P align=\"center\">Capataz primero</P><P align=\"center\">Capataz</P><P align=\"center\">Bruto</P><P align=\"center\">Explorador</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2677';
UPDATE `locales_page_text` SET `Text_loc6`='Para vincular a un corcel nefasto, debes realizar estas tres tareas:$B$BCrear un círculo de invocación superior.$B$BDentro del círculo, abre un portal hacia Xoroth y conduce al terrorecus a través del portal.$B$BDerrota al corcel nefasto y luego domina su espíritu.$B$BEn las siguientes páginas encontrarás una explicación detallada de cómo realizar esta tarea. Puede que no sea fácil, pero has demostrado ser muy capaz. Sin duda, con concentración y habilidad, el corcel nefasto será tuyo.$B$BContinúa leyendo,$BMor\'zul Sangredoble' WHERE `entry`='2691';
UPDATE `locales_page_text` SET `Text_loc6`='Instrumentos de un ritual$B$BAntes de que inicies tu trabajo, deberás disponer de los siguientes instrumentos mágicos:$B$BJarra de J\'eevee$BMagnetita negra$BGlifos xorothianos$B$BMi sirviente Gorzeeki lo tiene y te lo puede ofrecer a buen precio. No intentes siquiera realizar el ritual si no dispones de todos estos instrumentos. Todos resultan esenciales.$B$BEn las siguientes páginas, describiré cómo utilizar cada uno de ellos.' WHERE `entry`='2692';
UPDATE `locales_page_text` SET `Text_loc6`='Círculo de vínculo superior$B$BEs preciso crear un círculo de vínculo superior en un lugar en el que la magia tenga fuerza. En medio de las ruinas de Eldre\'Thalas, encontrarás un sitio de estas características, también llamado La Masacre. En Eldre\'Thalas se encuentra prisionero un ser de gran poder, Immol\'thar. En el pedestal de su prisión debes realizar el ritual para crear el círculo.$B$BEncuentra tu camino hasta el pedestal y luego deja que J\'eevee salga de su tarro.' WHERE `entry`='2693';
UPDATE `locales_page_text` SET `Text_loc6`='La Campana, la Rueda y la Vela$B$BCuando liberes a J\'eevee, este colocará la Campana, la Rueda y la Vela, y aparecerá un círculo. Este es el comienzo del ritual. Debes estar alerta. Los objetos antes mencionados transmiten una gran cantidad de energía y son propensos a fallar. Cuando esto ocurra, deberás usar la magnetita para reiniciarlos, antes de que fracase todo el ritual.$B$BSi los tres objetos han fallado antes de que logres reiniciarlos, el ritual terminará y deberás comenzar de cero.' WHERE `entry`='2694';
UPDATE `locales_page_text` SET `Text_loc6`='Además de transmitir las energías del ritual, la Campana, la Rueda y la Vela tienen propiedades únicas.$B$BCuando suena, la Campana de Dethmoora confiere vigor y energía a los brujos que estén dentro del círculo.$B$BCuando gira, la Rueda de la Marcha Negra protege de cualquier daño a los que están dentro del círculo.$B$BLa Vela del Apocalipsis, cuando arde, envía una extraña energía a los enemigos que entran en el círculo.$B$BGracias a estos dones, es muy importante que todos estos objetos funcionen durante el ritual.' WHERE `entry`='2695';
UPDATE `locales_page_text` SET `Text_loc6`='Tienes que tener en cuenta que la magnetita, utilizada para reiniciar la Campana, la Rueda o la Vela si fracasan, requiere fragmentos de alma. Cada vez que reinicies un objeto ritual con la magnetita, se consumirá uno de tus fragmentos de alma, así que asegúrate de tener suficientes reservas de fragmentos antes de iniciar el ritual.' WHERE `entry`='2696';
UPDATE `locales_page_text` SET `Text_loc6`='Cómo completar el ritual$B$BPuedes anotar tus progresos utilizando las runas mágicas situadas a lo largo del borde del círculo. Cuando aparezcan nueve runas, el ritual se habrá completado y verás cómo surge la energía desde el círculo recargado.$B$BDesde aquí, podrás convocar a los glifos xorothianos y abrir un portal hacia Xoroth para hacer pasar un corcel nefasto por él.$B$BDerrota al corcel nefasto y libera su espíritu. Enfréntate al espíritu y este quedará embelesado. Entonces, serás recompensado con el secreto de su invocación.' WHERE `entry`='2697';
UPDATE `locales_page_text` SET `Text_loc6`='Cómo completar el ritual$B$BPuedes anotar tus progresos utilizando las runas mágicas situadas a lo largo del borde del círculo. Cuando aparezcan nueve runas, el ritual se habrá completado y verás cómo surge la energía desde el círculo recargado.$B$BDesde aquí, podrás convocar a los glifos xorothianos y abrir un portal hacia Xoroth para hacer pasar un corcel nefasto por él.$B$BDerrota al corcel nefasto y libera su espíritu. Enfréntate al espíritu y este quedará embelesado. Entonces, serás recompensado con el secreto de su invocación.' WHERE `entry`='2711';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><BR/><P align=\"left\">Aquí yace Durotan, primer jefe del clan Lobo Gélido y padre de Thrall, nuestro honorable Jefe de Guerra. Fue el más valiente de los nuestros, traicionado por aquellos que querían ver esclavizado a nuestro pueblo. Durotan dio su vida para que ganáramos la libertad. Lo glorificamos: su legado ha llegado hasta nosotros a través de su hijo.</P><BR/><P align=\"left\">Drek\'Thar, Clarividente de los Lobo Gélido </P></BODY></HTML>' WHERE `entry`='2731';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS MILITARES DE LA HORDA</H1><BR/><P align=\"center\">OFICIALES</P><BR/><P align=\"center\">Gran Señor de la Guerra</P><P align=\"center\">Señor de la Guerra</P><P align=\"center\">Gran General</P><P align=\"center\">General</P><P align=\"center\">Campeón</P><P align=\"center\">Centurión</P><P align=\"center\">Legionario</P><P align=\"center\">Guardia de sangre</P><P align=\"center\">Guardia de piedra</P><BR/><P align=\"center\">SOLDADOS</P><BR/><P align=\"center\">Gran capataz</P><P align=\"center\">Capataz primero</P><P align=\"center\">Capataz</P><P align=\"center\">Bruto</P><P align=\"center\">Explorador</P><BR/><BR/></BODY></HTML>' WHERE `entry`='2770';
UPDATE `locales_page_text` SET `Text_loc6`='Pelando la cebolla$BGuía para desmantelar a los Pico Tormenta$B-Por Drek\'Thar$B$BEn estas páginas encontrarás amplios conocimientos sobre tácticas de guerra y política. Un soldado instruido es un soldado que se apoya en la experiencia. Utiliza este libro como guía en tu batalla por el Valle de Alterac.' WHERE `entry`='2771';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 1 - El frente$B$BDesmantelar el ejército de los Pico Tormenta es una tarea similar a la de pelar una cebolla. Para llegar al núcleo, hay que empezar eliminando las capas exteriores.$B$BLas líneas del frente, que comprenden fundamentalmente a los montaraces de Pico Tormenta y los Centinelas de la Alianza, están ligadas al búnker del capitán. Es el capitán Balinda Corapernal el que da poder a estas unidades y proporciona los refuerzos necesarios cuando las líneas están bajo sitio. Golpea al capitán y las líneas del frente se quebrarán.' WHERE `entry`='2772';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 2 - Las Torres Gemelas$B$BNo te equivoques, Vanndar Pico Tormenta es un enemigo astuto y, ciertamente, no es nada tonto. Una vez rota la línea del frente, es preciso destruir los objetivos defensivos de carácter secundario. Se trata de las torres gemelas de Piedrahogar y Ala Gélida, que controlan los Soldados de Pico Tormenta, lejos de Dun Baldar y de las patrullas Pico Tormenta.' WHERE `entry`='2773';
UPDATE `locales_page_text` SET `Text_loc6`='Ten cuidado, Soldado, ya que ambas torres están muy bien fortificadas y están capitaneadas por uno de los Comandantes de élite de Vanndar. Cuando tus fuerzas rompan las fortificaciones, asegúrate de que el Comandante que está en su interior haya sido... silenciado. Esta última tarea también forma parte del desmantelamiento de Pico Tormenta.' WHERE `entry`='2774';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 3 - Los cuatro Comandantes$B$BLa tercera capa de cebolla del ejército Pico Tormenta comprende a los cuatro Comandantes. A este respecto, Vanndar ha imitado a nuestras gloriosas defensas de Lobo Gélido. Lógicamente, él te diría otra cosa... Pero me estoy desviando de la cuestión.$B$BLos cuatro Comandantes controlan el flujo y reflujo de los Soldados de Pico Tormenta que defienden Dun Baldar con valentía. Ocúpate de silenciarlos y los Soldados Pico Tormenta caerán.$B$BAhora solo queda una capa por pelar.' WHERE `entry`='2775';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 4 - Los búnkeres de Dun Baldar$B$BComo ya habrás advertido, las capas defensivas de Pico Tormenta están colocadas de tal manera que impidan que un movimiento enérgico pueda desmantelar todo el ejército. Antes de que preguntes, sí, coincide exactamente con la forma en que están desplegadas nuestras fuerzas... y no, no pienso modificar nuestra estructura defensiva. Ellos nos copiaron a nosotros, ¿por qué habría de cambiar?' WHERE `entry`='2776';
UPDATE `locales_page_text` SET `Text_loc6`='¿Por dónde iba? Ah, sí, los búnkeres de Dun Baldar (situados en Dun Baldar), controlan a los defensores de Pico Tormenta, las unidades de guardia de élite y los hombres de mayor confianza de Vanndar. Destruye las dos torres y los refuerzos enviados para ayudar a Vanndar dejarán de existir.' WHERE `entry`='2777';
UPDATE `locales_page_text` SET `Text_loc6`='Epílogo$B$BUna vez que hayas hecho todo esto, te queda el centro de la cebolla: El dulce núcleo. Vanndar Pico Tormenta estará solo e indefenso. Las reglas de conducta militar requieren que, antes de realizar tareas de limpieza, le preguntemos si desea rendirse. En cuanto le veas, asegúrate de mencionarle lo de la rendición y luego... mátalo.' WHERE `entry`='2778';
UPDATE `locales_page_text` SET `Text_loc6`='La alcachofa de los Lobo Gélido: Relatos sobre la gloria de Pico Tormenta$BPor Vanndar Pico Tormenta$B$B¿Qué son los Lobo Gélido? La respuesta es sencilla: Los Lobo Gélido son salvajes que intentan acabar con nuestra imprescindible soberanía imperial. Al igual que ocurre con una alcachofa, los Lobo Gélido cuentan con un espinoso conjunto de defensas acorazadas y también, como ocurre con la alcachofa, una vez que se extrae la capa exterior, el corazón queda expuesto y listo para ser engullido con una suave salsa de ajo.$B$B¡Delicioso!' WHERE `entry`='2779';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 1 - El frente$B$BLas líneas del frente de las defensas de los Lobo Gélido están formadas por los guerreros Lobo Gélido. Estos guerreros están desplegados en las líneas del frente, desde la fortaleza del capitán Galvangar: Baluarte Sangre Fría. La forma más eficaz de eliminar esta capa de las defensas consiste en destruir el Baluarte Sangre Fría.$B$BPon la fortaleza bajo sitio y destruye al capitán Galvangar. Una vez que lo hayas logrado, las líneas del frente se desmoronarán.' WHERE `entry`='2780';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 2 - Sangre Fría y Torre de la Punta$B$BUna vez desaparecida la primera capa, la segunda capa de la \"alcachofa\" quedará expuesta. Mediante la destrucción de las torres Lobo Gélido, situadas cerca de las líneas del frente, lograrás neutralizar a los Legionarios y a las patrullas que se apiñan en la retaguardia.$B$BLas torres están bien fortificadas y cada una de ellas alberga uno de los Comandantes de élite de Drek\'Thar. Hay que eliminarlos. De esta manera, el camino para desmantelar las defensas Lobo Gélido quedará expedito.' WHERE `entry`='2781';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 3 - Los cuatro Comandantes$B$BSi has seguido sabiamente esta guía, en este momento, dos de los Comandantes de Drek\'Thar deberían estar muertos. ¡Buen trabajo, Soldado! No obstante, su resolución continúa siendo un problema. Debes completar la tarea de romper la cadena de mando, acabando con los dos Comandantes restantes.$B$BUna vez que hayan desaparecido los cuatro Comandantes, los legionarios Lobo Gélido que cuidan el Bastión Lobo Gélido quedarán aislados y sin superiores. ¡La fruta estará madura!' WHERE `entry`='2782';
UPDATE `locales_page_text` SET `Text_loc6`='Capítulo 4 - Las torres Lobo Gélido del este y el oeste$B$BSolo te queda una capa para alcanzar la victoria. Las unidades de guardianes más poderosas y en quienes más confía Drek\'Thar: Los Guardianes Lobo Gélido.$B$BLos Guardianes Lobo Gélido proceden de pelotones de la reserva situados en las torres Lobo Gélido del este y el oeste. Destruye estas torres y verás cómo los guardianes Lobo Gélido restantes huyen de forma vergonzosa.' WHERE `entry`='2783';
UPDATE `locales_page_text` SET `Text_loc6`='Epílogo$B$B\"Sumerge la alcachofa en crema de ajo y mayonesa. Acompaña la comida con una reposada y embriagadora cerveza.\"$B$BAhora Drek\'Thar está solo, con los dos perros que lo acompañan. Captura el Puesto de auxilio Lobo Gélido para que te sirva de ayuda en la batalla y destruye a Drek\'Thar. Cuando el general caiga, esta tierra volverá finalmente a su legítimo dueño: Los Pico Tormenta.' WHERE `entry`='2784';
UPDATE `locales_page_text` SET `Text_loc6`='Te convocamos, Zanza de Zuldazar.$B$BBendice a los que pidan tu ayuda, Loa Zanza. Bendice a aquellos que establecerían una alianza con el pueblo de Zandalar.$B$BAyúdanos en tiempo de necesidad. Ayúdanos, Loa. Danos el poder para derrotar a nuestros enemigos. Danos el poder para derrotar una vez más al Dios de la Sangre.' WHERE `entry`='2785';
UPDATE `locales_page_text` SET `Text_loc6`='¡Torneo de pesca de Tuercespina!$BDomingo por la tarde de las 14.00 a las 16.00.$B$BPesca en cualquier punto situado en la costa de Tuercespina (exceptuando la Bahía del Botín) y fíjate en los cardúmenes de pezrico que aparecerán a lo largo de la costa. Asegúrate de que tu boya de pesca cae en medio del cardumen y, en lugar de pescar la variedad habitual de Tuercespina, obtendrás un pez especial. ¡La primera persona que capture cuarenta pezricos puede pedir al maestro de pesca Anzuelo, que está en la Bahía del Botín, que se los cambie por un premio!$B$BIncluso si no eres la primera persona que capture los cuarenta pezricos, aún puedes cambiarlos por dinero en efectivo. Además, aquellos que pesquen otras variedades especiales ¡también recibirán premios!' WHERE `entry`='2786';
UPDATE `locales_page_text` SET `Text_loc6`='Su nombre trajo la luz a la oscura Azeroth.$B$BSu valor derribó los muros del sufrimiento.$B$BLos Mano de Plata nunca harían nada mejor$B$Bque dar al mundo el caballero que se sacrificó por los demás.' WHERE `entry`='2787';
UPDATE `locales_page_text` SET `Text_loc6`='No fue la fama lo que perseguía,$B$Bni convertirse en señor para su gente.$B$BPor el contrario, escogió luchar por Lordaeron,$B$Bcon la esperanza de que los suyos ya no tuvieran que hacerlo.' WHERE `entry`='2788';
UPDATE `locales_page_text` SET `Text_loc6`='El trágico relato de este caballero sagrado$B$Bnunca terminará con su impía muerte.$B$BLo glorificamos ¡y le daremos siempre las gracias!$B$B¡Uther siempre conocerá la gloria y la Luz!' WHERE `entry`='2789';
UPDATE `locales_page_text` SET `Text_loc6`='Su nombre nunca morirá.$BSu sacrificio siempre$Bnos mostrará el camino.$B$BUna vez rotas las cadenas$Bque estrangulaban nuestro honor,$Bya no podrán esclavizarnos.' WHERE `entry`='2790';
UPDATE `locales_page_text` SET `Text_loc6`='¿Puedes oír ese grito?$BEs un grito de batalla de la Horda:$B¡Victoria o muerte!$B$BDebemos recordar$Bsu fuerza cara a la muerte.$BSu sueño ahora se ha hecho realidad.' WHERE `entry`='2791';
UPDATE `locales_page_text` SET `Text_loc6`='¡Peligros por todas partes!$BLos enemigos intentan$Bencadenarnos de nuevo.$B$BCuando luchemos, pensad en$Baquel que sabía lo que había que hacer.$B¡Grito Infernal por siempre!' WHERE `entry`='2792';
UPDATE `locales_page_text` SET `Text_loc6`='Han pasado casi mil años desde la \'Guerra del Mar de Dunas\'. Durante esa guerra, en el corazón del desierto de Silithus, Fandral Corzocelada hubo de soportar una gran tragedia y una gran carga. Se cree que los acontecimientos descritos a continuación son los que finalmente conformaron la desagradable manera de ser del archidruida.' WHERE `entry`='2793';
UPDATE `locales_page_text` SET `Text_loc6`='En el pasado, una antigua raza de insectoides sensibles, conocida como los qiraji, competía por dominar la mayor parte del territorio de Kalimdor. Fueron los presuntuosos e imperialistas Corzocelada y su ejército de elfos de la noche los que se enfrentaron cara a cara a los qiraji en la batalla.' WHERE `entry`='2794';
UPDATE `locales_page_text` SET `Text_loc6`='Padre e hijo lucharon fervientemente para contener la marea creciente de enjambres de insectos en Silithus. Desde la fortaleza de Ahn\'Qiraj, una corriente de silítidos, que parecía interminable, continuó reforzando las filas de las fuerzas invasoras qiraji. Todo Silithus estaba bajo sitio.' WHERE `entry`='2795';
UPDATE `locales_page_text` SET `Text_loc6`='En una decisión que Corzocelada lamentaría, el Corzocelada más joven, Valstann, ordenó defender el puesto de los elfos de la noche de la Aldea de Viento del Sur.$BDesafortunadamente, Valstann perdió su vida el mismo día que la Aldea del Viento del Sur cayó bajo los ejércitos de Ahn\'Qiraj.' WHERE `entry`='2796';
UPDATE `locales_page_text` SET `Text_loc6`='Impertérrito y con el corazón endurecido, un vengativo Fandral Corzocelada habría de seguir adelante. La derrota en Silithus produciría una gran pérdida para la nación de los elfos de la noche y, posiblemente, para todo el mundo.$B$BLa batalla se desarrolló por espacio de meses. Todo Silithus fue tomado por los qiraji. Corzocelada y su ejército fueron obligados a retroceder hacia Tanaris. Con un ejército diezmado e innumerables inocentes caídos, la arrogancia con la que se había conducido anteriormente Fandral Corzocelada desapareció por completo.' WHERE `entry`='2797';
UPDATE `locales_page_text` SET `Text_loc6`='Con su orgullo totalmente devastado, Corzocelada habría de sentir, por segunda vez en su vida, el temblor en la boca del estómago que solo puede producir el miedo. Luego, fue en Tanaris donde Corzocelada se acercó a Anacronos, progenie de Nozdormu, y rogó la ayuda del Vuelo Bronce.' WHERE `entry`='2798';
UPDATE `locales_page_text` SET `Text_loc6`='Inicialmente, Anacronos rechazó la posibilidad de implicar al Vuelo Bronce en los asuntos de razas inferiores. Pero cuando los qiraji atacaron descaradamente las Cavernas del Tiempo, Anacronos accedió a enviar refuerzos a los elfos de la noche.' WHERE `entry`='2799';
UPDATE `locales_page_text` SET `Text_loc6`='Pronto, Anacronos se dio cuenta de que incluso el poderoso Vuelo Bronce no podría contener la marea de las fuerzas invasoras qiraji. Por cada cincuenta insectos aniquilados mediante su aliento, un centenar esperaban para ocupar su lugar. Con la caída de la noche, las líneas de los elfos empezaron a resquebrajarse, y con la posibilidad de ser empujados hacia el norte en las tierras salvajes de Feralas cada vez más cerca, Anacronos decidió que los hijos de los Aspectos debían conocer la situación.' WHERE `entry`='2800';
UPDATE `locales_page_text` SET `Text_loc6`='En primer lugar, convocó al Vuelo de Ysera. Merithra del Sueño respondería a su llamada y, a partir de ahí, se darían por enterados el Vuelo de Alexstrasza y el Vuelo de Malygos.' WHERE `entry`='2801';
UPDATE `locales_page_text` SET `Text_loc6`='Los dragones se reunirían en la estratosfera, encima de Silithus: Cientos de metros por encima de la fortaleza de Ahn\'Qiraj.$B$BFue Anacronos, hijo de Nozdormu, Aspecto del Tiempo.$B$BDesde el Sueño Esmeralda vendría Merithra, hija de Ysera, Aspecto de la Naturaleza.$B$BCaelestrasz, hijo de Alexstrasza, Aspecto de la Vida, representaría al Vuelo Rojo.$B$BArygos, hijo de Malygos, Aspecto de la Magia, representaría al Vuelo Azul.' WHERE `entry`='2802';
UPDATE `locales_page_text` SET `Text_loc6`='Los dragones observaron como una interminable corriente de silítidos y qiraji salían en tropel de Ahn\'Qiraj. Pronto les quedó claro que esta guerra no podía acabar mediante el uso de la fuerza. Desde el interior del templo principal, miles y miles de combatientes qiraji estaban esperando. Con el paso de cada segundo, se incubaban y preparaban más y más, listos para asaltar el mundo.' WHERE `entry`='2803';
UPDATE `locales_page_text` SET `Text_loc6`='Arygos fue el primero en darse cuenta de las extrañas emanaciones. El templo monolítico situado en el cuadrante sur de Ahn\'Qiraj irradiaba una oscura magia, distinta de todo lo que hasta ahora conocía procedente de los qiraji. Aunque lo intentaron, nadie fue capaz de penetrar en el templo para localizar la fuente de esta energía. Solo algo de inmenso poder podría autoprotegerse por completo. Tal vez... algo controla a los insectos. Quizás algo incluso controle a los qiraji.' WHERE `entry`='2804';
UPDATE `locales_page_text` SET `Text_loc6`='Los dragones sabían que tenían que reaccionar con rapidez, no fuera que el mundo sufriera otra catástrofe de proporciones épicas. Tras una cuidadosa deliberación, se decidió que la mejor acción que cabía emprender consistiría en detener los ejércitos anfitriones en su origen o cerca del mismo.' WHERE `entry`='2805';
UPDATE `locales_page_text` SET `Text_loc6`='Se crearía una gran barrera, originada desde la esencia de los elfos de la noche y de los dragones. Sería una barrera mágica que podría contener a los silítidos y a sus Señores Supremos dentro de los muros de Ahn\'Qiraj, previniendo futuras incursiones. El propio Ahn\'Qiraj se convertiría en una prisión.' WHERE `entry`='2806';
UPDATE `locales_page_text` SET `Text_loc6`='Día 1$BNuestro peregrinaje ha acabado. Finalmente, llegamos a Silithus, donde vamos a establecer contacto. Hemos venido de varios sitios para colaborar en lo que es una tarea monumental.$B$BHay una tremenda excitación en el aire.$B$B Mañana dedicaremos todas nuestras energías a construir lo que nos permitirá entrar en comunicación con ELLOS.$B$B¡Mañana vamos a construir una piedra de viento!' WHERE `entry`='2807';
UPDATE `locales_page_text` SET `Text_loc6`='Día 2$BLa localización de una piedra de viento ha sido objeto de una gran discusión. Ni siquiera el más sabio de nosotros puede determinar con exactitud a qué lugar se refería exactamente la antigua profecía.$B$BHe estado con el grupo original del norte e, indudablemente, el tiempo demostrará que estamos en lo cierto. El argumento de que las líneas de energía se intersecan en ángulos perjudiciales en este lugar resulta ridículo e inocente.$B$BHemos reunido a gran velocidad los materiales de construcción necesarios.' WHERE `entry`='2808';
UPDATE `locales_page_text` SET `Text_loc6`='Día 15$BNuestra dedicación ha tenido su recompensa. Somos el primer campamento que finaliza la construcción de una piedra de viento. ¡Si los otros idiotas se hubieran quedado con nosotros, habríamos terminado la tarea en menos de una semana!$B$BMañana iniciaremos los rituales que nos permitirán invocar al barón Kazum en persona. Ha pedido ser el primero en comprobar el sistema y, puesto que se enfada con facilidad, no hemos podido decirle que no.$B$BEspero que esté muy contento con nosotros.' WHERE `entry`='2809';
UPDATE `locales_page_text` SET `Text_loc6`='Para entrega inmediata a todos los miembros del Martillo Crepuscular.$B$BLa búsqueda del traidor, Ortell, progresa de forma muy satisfactoria. Nuestros exploradores están estrechando el cerco en relación con una red de cavernas situadas en los Páramos de Poniente.$B$BLa traición de Ortell no caerá en el olvido y la posibilidad de que se fugue representa un gran riesgo para la seguridad de nuestra organización.$B$BCualquier individuo que oculte información sobre el paradero de Ortell será castigado debidamente.$B$BSeñor Crepuscular Everun' WHERE `entry`='2810';
UPDATE `locales_page_text` SET `Text_loc6`='Para entrega inmediata a todos los miembros del Martillo Crepuscular.$B$BConsiderando el estado actual de las cosas en Silithus, la distribución de todos los textos de El creyente verdadero debe realizarse utilizando una escolta de no menos de cuatro miembros armados del Martillo Crepuscular.$BCualquier miembro que infrinja esta nueva política será castigado sumariamente.$B$BSeñor Crepuscular D\'Sak' WHERE `entry`='2811';
UPDATE `locales_page_text` SET `Text_loc6`='Para entrega inmediata a todos los miembros del Martillo Crepuscular.$B$BLa siguiente información describe el uso de piedras de viento para la comunicación con nuestros maestros del Consejo Abisal.$B$BCualquier uso frívolo de las piedras de viento será castigado con la mayor dureza.$B$BSeñor Crepuscular Havunth' WHERE `entry`='2812';
UPDATE `locales_page_text` SET `Text_loc6`='Templarios Abisales$B$BComo iniciado, se te permite establecer contacto con los Templarios Abisales para celebrar rituales rutinarios. No olvides ponerte siempre la vestimenta adecuada cuando utilices piedras de viento para evitar que se activen nuestros encantamientos defensivos.$B$BSi surge cualquier clase de emergencia no prevista y no hay un sobrestante presente, estás autorizado a invocar a un Templario Abisal.$B$BPara conocer los detalles acerca de los rituales de invocación pertinentes, consulta el número de este mes de El creyente verdadero.' WHERE `entry`='2813';
UPDATE `locales_page_text` SET `Text_loc6`='Duques Abisales$B$BSolo a los acólitos en posesión de un medallón de estatus se les permite establecer contacto con los Duques Abisales. Los medallones de estatus solo deben ser utilizados por aquellos individuos para los cuales se han expedido.$B$BLos miembros del Martillo Crepuscular que hayan mantenido una comunicación no autorizada con los Duques Abisales o que hayan utilizado sin permiso los medallones de estatus serán castigados y me ocuparé personalmente de infligirles el castigo.' WHERE `entry`='2814';
UPDATE `locales_page_text` SET `Text_loc6`='El Alto Consejo de los Señores Abisales$B$BSolo los Señores Crepusculares podrán tener contacto con el príncipe Skaldrenox, el Alto Mariscal Whirlaxis, el Barón Kazum o Lord Skwol.$B$BBajo ninguna circunstancia, un miembro de bajo rango del Martillo Crepuscular tiene permiso para entrar en comunicación con el Alto Consejo. Todo extraño que sea sorprendido utilizando una piedra de viento de esta manera, quedará reducido a cenizas en el acto.' WHERE `entry`='2815';
UPDATE `locales_page_text` SET `Text_loc6`='Estado del culto, volumen 92$B$BCon el corazón exultante os saludo, hermanos míos, pues la situación de nuestro culto no podría ser más dichosa. Somos legión y nuestras metas se acercan cada vez más a su cumplimiento.$B$BSe nos unen nuevos reclutas. Todo son alabanzas a nuestros señores en Costa Oscura, se inclinan con reverencia en la Garganta de Fuego, abrazan al sirviente de nuestros maestros, Aku\'mai, en las Cavernas de Brazanegra. ¡Ciertamente, hermanos y hermanas, pronto un enjambre de creyentes cubrirá todo Azeroth!' WHERE `entry`='2816';
UPDATE `locales_page_text` SET `Text_loc6`='Estado del culto, volumen 127$B$B¡Realmente es un día muy feliz! ¡Nos han sometido a prueba en Silithus y hemos aprobado!$B$BLos informes acerca de la destrucción de nuestro campamento situado en el extremo norte pueden suponer un reto para los corazones de los no creyentes, pero esta matanza no debe considerarse como una tragedia. ¡No!' WHERE `entry`='2817';
UPDATE `locales_page_text` SET `Text_loc6`='¡Si el Alto Consejo Abisal derrama nuestra sangre, esto debe considerarse como una bendición! ¡Debemos envidiar a nuestros camaradas caídos, ya que ahora sus espíritus residen en el vientre de la bestia más justa!$B$B¡Alabad a los dioses antiguos! ¡Alabad a los siervos! ¡Alabad al Martillo Crepuscular!' WHERE `entry`='2818';
UPDATE `locales_page_text` SET `Text_loc6`='Estado del culto, volumen 233$B$B¡Hermanos y hermanas, la oportunidad vuelve a llamar a nuestra puerta! El Círculo Cenarion, un culto infame de amantes de la naturaleza, está enviando agentes en contra nuestra. Seguramente, intentan evitar que invoquemos piedras rojas. ¡No podemos permitirlo!$B$BLas piedras rojas son esenciales para nuestros nuevos aliados. No podemos permitir que los perros del Círculo Cenarion tengan éxito. Manteneos firmes, mis fieles amigos. ¡Manteneos firmes!' WHERE `entry`='2819';
UPDATE `locales_page_text` SET `Text_loc6`='¿Cómo sabemos que nos quieren?$B$BEs posible que algunos se pregunten por qué los dioses antiguos y sus esbirros nos aman. ¿Acaso no nos matarán? ¿Acaso no causarán sufrimiento y dolor a todos? ¿Cómo es posible que seres tan inclinados a la malevolencia alberguen amor en sus corazones?$B$BExiste una respuesta simple a esta pregunta. Es tan simple que ni siquiera es preciso formularla.' WHERE `entry`='2820';
UPDATE `locales_page_text` SET `Text_loc6`='La fe. La fe es la fuente de donde mana la alegría de la servidumbre. La fe nos dará calor en las frías noches de Silithus. La fe nos mantendrá en la gracia de nuestros Señores cuando lleguen los días del final.$B$BLa fe nos salvará.' WHERE `entry`='2821';
UPDATE `locales_page_text` SET `Text_loc6`='No se puede entender una sola palabra en este libro. ¡No sé en qué idioma está escrito!' WHERE `entry`='2822';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><IMG src=\"Interface\\Pictures\\21037_crudemap_256\"/></BODY></HTML>' WHERE `entry`='2828';
UPDATE `locales_page_text` SET `Text_loc6`='Querido diario:$B$BHoy, mi archienemigo Narain Sabelotodo ha intentado engañarme, enviándome compinches para cubrir su sitio en la ejecución. SU ejecución. ¿Podéis creerlo?$B$BHe tenido un montón de problemas diseñando este diabólico plan para apartarle de esa maldita choza ¿y ahora hace esto? Da igual... Estoy demasiado $Genfadado:enfadada; para seguir escribiendo. Volveré más tarde.' WHERE `entry`='2829';
UPDATE `locales_page_text` SET `Text_loc6`='Querido diario:$B$BDespués de mi última anotación, salí de mi guarida realmente ofuscado. Vaya, si lo estaba. Decidí rediseñar los uniformes de mis esbirros para reflejar mejor mi angustia. Me siento un poco mejor, pero algo falta. Vuelvo enseguida...' WHERE `entry`='2830';
UPDATE `locales_page_text` SET `Text_loc6`='Querido diario:$B$BAhora sí me siento bien. Le he pegado a uno de mis esbirros hasta hacerlo llorar como una niña pequeña.$B$BMmm... Ahora siento una especie de malestar. ¡Espera un momento! ¡Maldición, soy un genio del MAL! El mal, ¿sabes? Estoy tan disgustado. Vuelvo enseguida.' WHERE `entry`='2831';
UPDATE `locales_page_text` SET `Text_loc6`='Querido diario:$B$BLe he dado un abrazo y le he dicho que continúe con su buena labor. Ahora me siento mejor. Que esto quede entre nosotros, diario.$B$B¿Por dónde iba? ¡Ah, sí, NARAIN! ¡AHHH! Odio a ese tío. Después de todo, fue Narain el que descubrió la curva en todos los exámenes, cuando ambos estudiábamos en el Instituto Gnomeregan para Manitas.$B$BFui expulsado de la escuela, ¿te lo puedes creer? ¡En lo que a mí respecta, Gnomeregan tiene lo que se merece!$B$BEsto es todo por hoy. ¡Que duermas bien, diario!' WHERE `entry`='2832';
UPDATE `locales_page_text` SET `Text_loc6`='Querido diario:$B$BMe debo estar haciendo mayor. He olvidado por completo lo que te estaba diciendo de Narain y de su engaño. ¡De cualquier manera, me enfadé tanto cuando vi a un señuelo disfrazado de Narain en lugar de este, que ordené al Número Dos que destruyera a ese idiota!$B$BMonté en mi máquina voladora y empecé a destrozar su precioso, estúpido y simple libro en dracónico. ¡Qué fanfarrón! ¿Quién lee en dracónico? ¡Lo odio tanto!' WHERE `entry`='2833';
UPDATE `locales_page_text` SET `Text_loc6`='Querido diario:$B$BLo siento, pero me estoy durmiendo. Estuve volando enfadado mientras arrojaba capítulos enteros de este estúpido libro. ¡Vi un demonio debajo mío en Negro Rumor y decidí utilizar el primer capítulo para formar un tornado mágico de papel y hacer añicos su cuerpo! Fue liberador.$BMagia + papel = diversión.$B$BMe hizo tan feliz, que decidí volar hacia los Reinos del Este. Mi primera parada fue en Núcleo de Magma. ¡Narain siempre habló de este sitio! \"Núcleo de Magma esto y Ragnaros lo otro...\"' WHERE `entry`='2834';
UPDATE `locales_page_text` SET `Text_loc6`='¿Sabes una cosa? Si le gusta tanto, quizás podría intentar volver a recopilar su libro, yendo a Núcleo de Magma y rescatando el capítulo de entre las cenizas! ¡Ja!$B$BMientras estaba en el vecindario, pensé que algunos de esos técnicos esclavizantes en el laboratorio del viejo Victor Nefarius podrían encontrar un uso para este material dracónico. ¡Bombas fuera!' WHERE `entry`='2835';
UPDATE `locales_page_text` SET `Text_loc6`='Esto está empezando a convertirse en un deporte. ¡Realmente estaba muy entusiasmado rompiendo el maldito libro!$B$BLanzar los siguientes trozos fue muy fácil.$B$BEntrañas, hogar de los asquerosos muertos y, posiblemente, el sitio donde preparan el mejor chile, mereció un capítulo. Ventormenta, la capital del queso, el otro. Querido diario: ¿alguna vez has añadido queso de Ventormenta al chile de Entrañas? ¿HOLA? Parece que suenan algunas alarmas.' WHERE `entry`='2836';
UPDATE `locales_page_text` SET `Text_loc6`='Estaba tan cansado en este momento, que me dediqué a dar vueltas con la máquina voladora y me fui a casa. Pero antes realizaré otras dos paradas. Di un paseo con mi máquina voladora sobre Lord Kazzak y sus demonios en Escara Impía (detesta que lo haga) y otro cerca de la chimenea de Onyxia.$B$BEstoy $Gcansado:cansada;. Me voy a dormir.' WHERE `entry`='2837';
UPDATE `locales_page_text` SET `Text_loc6`='A quien pueda interesar,$B$BComo me gustan estos juegos que jugamos.$B$BSólo puedo suponer que si estás leyendo esta carta, que de algún modo, a través de la intervención divina, o milagro, consiguiste vencerme. Absurdo, lo sé, pero cosas más raras han pasado en la historia de este mundo.' WHERE `entry`='2838';
UPDATE `locales_page_text` SET `Text_loc6`='Como todas las cosas en la vida, hay un lado glorioso, centelleando de tragedia, el caos y el mal de esta triste historia. Aunque me has matado, lo has hecho de modo tan lento y chapucero que me ha quedado tiempo para escribir esta carta. DESPUÉS de que destruí tu única esperanza de salvar este mundo. El polvo de cristalitos rojos es todo lo que queda de tu Fragmento de Cetro.' WHERE `entry`='2839';
UPDATE `locales_page_text` SET `Text_loc6`='Disfruta la Némesis y el Armamento de Tempestira que hallarás en mi tesoro. Créeme cuando te digo esto: Tengo el gran placer de saber que hice lo que mi padre no pudo...$B$Bsinceramente,$B$BLord Victor Nefarius$B\"Nefarian\"' WHERE `entry`='2840';
UPDATE `locales_page_text` SET `Text_loc6`='En nombre de los Pastos de Bosquehumeante, te vuelvo a dar las gracias por ocuparte de esto. No estamos seguros de cuál es el grupo que tiene a Metzen, pero investigar cualquiera de los dos puede constituir un buen punto de partida.$B$BEl tiempo es oro, $Gamigo mío:amiga mía; y, en este caso, tenemos muy poco. Sé rápido en tus tareas de rescate, ya que estos grupos no deben ser subestimados en lo que respecta a lo que son capaces de hacer.$B$BEn cualquier caso, las dos páginas siguientes son las notas de rescate que hemos recibido. ¡Buena suerte!' WHERE `entry`='2841';
UPDATE `locales_page_text` SET `Text_loc6`='Si quieres que el reno vuelva con vida, deberás ser rápido con la recompensa. Los Piratas de los Mares del Sur no se muestran amables cuando hay retrasos en el pago y sabemos lo valiosa que resulta esta bestia para TU forma de piratería.$B$BTrae la suma de 1000 piezas de oro a la entrada de la cala del Aparejo Perdido en Tanaris. Cualquier intento por rescatar al reno hará que Metzen dé un paseo muy largo sobre una plancha muy corta.$B$BSí...$B$BLos Piratas de los Mares del Sur' WHERE `entry`='2842';
UPDATE `locales_page_text` SET `Text_loc6`='Tu apreciada mascota no lo está pasando demasiado bien con el implacable calor de la Garganta de Fuego... Si eres listo, deberás cumplir nuestras peticiones sin demora.$B$BDebes traernos cinco rubíes estrellados y la suma de 700 piezas de oro. Coloca todo en un solo paquete sin marca alguna. Deja el paquete en las puertas del Paso de Fraguapiedra en el plazo de una semana.$B$BSi no accedes a nuestras peticiones, Metzen se convertirá en el plato principal del Festival de Invierno de los enanos Hierro Negro.$B$BMmmm... reno...' WHERE `entry`='2843';
UPDATE `locales_page_text` SET `Text_loc6`='Más rápido que las águilas llegaron sus corceles,$B$By él silbó, grito y los llamó por su nombre;$B$B\"¡Arre, Mercer! ¡Arre, Chilton! ¡Arre, Jordan y Kaplan!$B$B¡Adelante, Nagle! ¡Adelante, Pardo! ¡Adelante, Goodman y Metzen!$B$B¡Hacia lo alto del porche! ¡Hacia lo alto del muro!$B$B¡Ahora salid de aquí! ¡Fuera de aquí! ¡Fuera de aquí todos!\"' WHERE `entry`='2844';
UPDATE `locales_page_text` SET `Text_loc6`='Espero fervientemente que, a través de mi investigación, pueda encontrar una fuente suplementaria de energía mágica que sea segura para mi gente. Sin La Fuente del Sol, debemos encontrar una forma de continuar con nuestro modo de vida sin sucumbir a la tentación de la magia arcana.$B$B¡Creo que el futuro de los Sin\'dorei puede servir, una vez más, como ejemplo brillante para todos!' WHERE `entry`='2846';
UPDATE `locales_page_text` SET `Text_loc6`='Todavía no ha habido suerte. La poca magia a la que tengo acceso directo se debe canalizar a través de la Cumbre. Pero sí que tengo varias ideas intrigantes. Haré que mis aprendices más brillantes sigan estos caminos de investigación y veré lo que se nos ocurre.' WHERE `entry`='2847';
UPDATE `locales_page_text` SET `Text_loc6`='Ha pasado algún tiempo desde la última vez que escribí algo en estas páginas. Sigue sin haber nada prometedor. Ayer recibí un mensaje que decía que uno de los alumnos de la Isla del Caminante del Sol, un tal Felendren, no siguió los consejos de sus mentores y sucumbió a la aflicción.$B$BRedoblaré nuestros esfuerzos.' WHERE `entry`='2848';
UPDATE `locales_page_text` SET `Text_loc6`='Nada. Pero no perderé la esperanza. Los Sin\'dorei no se pueden permitir estar en posición de dependencia mágica en este momento, cuando estamos sitiados en el sur.$B$BHe ideado un enfoque totalmente nuevo y, si tiene éxito, me permitirá filtrar las \"impurezas\" que hay en algunas fuentes de energía mágica viles que he de separar para el futuro. Debo proceder con precaución.' WHERE `entry`='2849';
UPDATE `locales_page_text` SET `Text_loc6`='¡Asombroso! Hemos tenido algo de éxito, aunque la cantidad de magia que pudimos extraer era minúscula. Voy a apartar a la mayoría de los aprendices de sus estudios para centrarnos en este enfoque nuevo y prometedor.$B$BCon algo de suerte, deberíamos poder refinar el proceso y matar dos pájaros de un tiro ? una fuente de energía abundante para nosotros, y una forma de contrarrestar cualquier magia vil que podamos encontrar en el futuro.' WHERE `entry`='2850';
UPDATE `locales_page_text` SET `Text_loc6`='¡No! Mientras experimentaba con el nuevo proceso, mi mejor aprendiz, Telethayon, de repente y sin aviso se marchitó antes mis ojos, sucumbiendo al estado que aflige a mis hermanos. Intenté pararlo, pero ya era demasiado tarde. No me quedó más remedio que liberarlo de su sufrimiento.$B$BTal es el precio del descubrimiento, pero ya siento que me pesa demasiado.' WHERE `entry`='2851';
UPDATE `locales_page_text` SET `Text_loc6`='Es demasiado como para poder soportarlo... dos aprendices más han sucumbido. Estamos siendo tan cuidadosos... no entiendo qué ha ido mal.$B$BTendré que abandonar estas investigaciones y empezar de cero.' WHERE `entry`='2852';
UPDATE `locales_page_text` SET `Text_loc6`='Llegué demasiado tarde. Un tercer aprendiz había estado compartiendo la investigación vil con algunos de los otros sin yo saberlo. Voy a intentar contener la situación, pero, primero, debo alejar a los aprendices que no se han visto afectados.$B$BLo haré haciendo saber a todos que me voy de sabático al Retiro del Errante, con la esperanza de hallar un nuevo enfoque para el problema.$B$BDebo encontrar una forma de compensar este horrible error de juicio.' WHERE `entry`='2853';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA HORDA</H1><BR/><P align=\"center\">Parte 2</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank09\" align=\"left\"/><P align=\"right\">Centurión</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank08\" align=\"left\"/><P align=\"right\">Legionario</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank07\" align=\"left\"/><P align=\"right\">Guardia de sangre</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank06\" align=\"left\"/><P align=\"right\">Guardia de piedra</P><BR/></BODY></HTML>' WHERE `entry`='2854';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA HORDA</H1><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank05\" align=\"left\"/><P align=\"right\">Gran capataz</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank04\" align=\"left\"/><P align=\"right\">Capataz primero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank03\" align=\"left\"/><P align=\"right\">Capataz</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank02\" align=\"left\"/><P align=\"right\">Bruto</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank01\" align=\"left\"/><P align=\"right\">Explorador</P><BR/></BODY></HTML>' WHERE `entry`='2855';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE OFICIALES DE LA ALIANZA</H1><BR/><P align=\"center\">Parte 2</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank09\" align=\"left\"/><P align=\"right\">Campeón caballero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank08\" align=\"left\"/><P align=\"right\">Capitán caballero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank07\" align=\"left\"/><P align=\"right\">Teniente caballero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank06\" align=\"left\"/><P align=\"right\">Caballero</P><BR/></BODY></HTML>' WHERE `entry`='2856';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">RANGOS DE SOLDADOS DE LA ALIANZA</H1><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank05\" align=\"left\"/><P align=\"right\">Alférez</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank04\" align=\"left\"/><P align=\"right\">Sargento primero</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank03\" align=\"left\"/><P align=\"right\">Sargento</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank02\" align=\"left\"/><P align=\"right\">Cabo</P><BR/><IMG src=\"Interface\\PvPRankBadges\\PvPRank01\" align=\"left\"/><P align=\"right\">Soldado</P><BR/></BODY></HTML>' WHERE `entry`='2857';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML>\n<BODY>\n<H1 align=\"center\">\nAnthony Ray Stark\n</H1>\n<H2 align=\"center\">\n1961 - 2005\n</H2>\n</BODY>\n</HTML>' WHERE `entry`='2858';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">BLANDÓN DE EVOCACIÓN: GUÍA DEL USUARIO</H1><BR/><P align=\"center\">Dónde encontrar lugares encantados</P><BR/><P align=\"center\">INTRODUCCIÓN</P><BR/><P align=\"left\">En estas páginas se encuentran las localizaciones de los espíritus caídos y corruptos y de las almas implicadas en el Incidente del amuleto de Lord Valthalak. Aunque ya has salvado a mis antiguos compañeros, tengo la impresión de que los repugnantes ecos de la maldad que alguna vez torturaron sus espíritus aún palpitan en aquellos lugares encantados. Tal era el poder maléfico del espíritu de Lord Valthalak.</P><BR/><P align=\"left\">Asimismo, se puede utilizar el blandón para invocar a Lord Valthalak, aunque, como en el caso anterior, es preciso tomar extremas precauciones al hacerlo.</P><BR/></BODY></HTML>' WHERE `entry`='2859';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">INTRODUCCIÓN, continuación...</H1><BR/><P align=\"left\">Este blandón funciona exactamente como el Blandón de señalización. Simplemente, colócalo en cualquiera de los cinco lugares encantados y te servirá para convocar a los espíritus de los muertos.</P><BR/><P align=\"left\">A modo de nota, ya que sé que te interesará saberlo, el Estandarte de Provocación puede utilizarse de forma similar en el estado de las Profundidades de Roca Negra. Asegúrate de darle una lección a Theldren y a sus matones por mí, cada vez que tengas la oportunidad cuando estés allí.</P><BR/><P align=\"left\">Si deseas más explicaciones, sigue leyendo.</P><BR/></BODY></HTML>' WHERE `entry`='2860';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">ISALIEN</H1><BR/><P align=\"center\">Sacerdotisa de la Luna de los elfos de la noche</P><BR/><P align=\"left\">Cuando Isalien decidió separarse del resto, lo hizo con la intención de realizar un peregrinaje a La Masacre, la ciudad ancestral de los elfos de la noche, caída en desgracia, conocida una vez como Eldre\'Thalas. Allí aún existe una biblioteca que atesora grandes conocimientos, un santuario que unos pocos elfos de la noche pudieron salvar de la marea. Allí intentó encontrar respuestas sobre su herencia e información acerca de los fragmentos de amuleto. Asimismo, cabe destacar que era muy amiga de Falrin Tallarbol.</P><BR/></BODY></HTML>' WHERE `entry`='2861';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">ISALIEN, continuación...</H1><BR/><P align=\"left\">Desafortunadamente, cuando ella y su inseparable hipogrifo Empíreo entraban en la ciudad, fueron atacados por Alzzin el Formaferal y sus esbirros. Allí cayó para no volver a levantarse jamás. Tal vez fue para bien que Isalien nunca desvelara los secretos de la magia negra que Lord Valthalak volcó en una parte de su esencia que impregna el amuleto.</P><BR/><P align=\"left\">La estancia al aire libre donde habita Alzzin es uno de estos lugares encantados.</P><BR/></BODY></HTML>' WHERE `entry`='2862';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">MOR PEZUÑA GRIS</H1><BR/><P align=\"center\">Druida tauren y primero en caer</P><BR/><P align=\"left\">Mor Pezuña Gris siempre fue un compañero fiel e incondicional en la Espada Enmascarada. Desafortunadamente, fue el primero en caer ante los malvados esbirros de Lord Valthalak.</P><BR/><P align=\"left\">Cuando huimos de Valthalak, fuimos perseguidos por un verdadero ejército de merodeadores y asesinos espectrales. La huida de la Cumbre de Roca Negra fue, cuando menos, caótica. Desgraciadamente, Mor cayó durante el trayecto, aunque sospecho que fue empujado. En cualquier caso, cayó en la parte inferior de la cumbre.</P><BR/></BODY></HTML>' WHERE `entry`='2863';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">MOR PEZUÑA GRIS, continuación...</H1><BR/><P align=\"left\">Más tarde, descubrimos que Mor había logrado sobrevivir a la caída. Pero no hubo un final feliz para mi buen amigo: fue tomado prisionero de los trols de Tazz\'Alaor. No mucho más tarde, mientras estaba siendo torturado por el maestro de guerra Voone, un grupo de asesinos de Valthalak entraron en escena y acabaron el trabajo.</P><BR/><P align=\"left\">En caso de que intentes liberar el eco del espíritu de Mor Pezuña Gris debes saber que la estancia del maestro de guerra Voone es un lugar encantado.</P><BR/></BODY></HTML>' WHERE `entry`='2864';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">JARIEN Y SOTHOS</H1><BR/><P align=\"center\">Hermanos y aspirantes a miembros de las Cruzada Escarlata</P><BR/><P align=\"left\">Jarien y Sothos son dos de las personas más intolerantes que he conocido. Aun así, su talento como combatientes era excepcional y tenían dinero, razón por la cual supongo que Anthion, el líder de la Espada Enmascarada, siguió contando con ellos.</P><BR/><P align=\"left\">Los hermanos, finalmente, abrazaron las enseñanzas y la filosofía de la Cruzada Escarlata. Siempre habían manifestado cierto fervor a la hora de vencer a cualquier miembro de la Plaga con el que nos cruzáramos, y se sospechaba que su familia tenía lazos con la organización.</P><BR/></BODY></HTML>' WHERE `entry`='2865';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">JARIEN Y SOTHOS, continuación...</H1><BR/><P align=\"left\">De esta manera se encontraron haciendo frente a las pruebas que debían pasar todos aquellos que intentaran unirse a la Cruzada. Estaban particularmente interesados en conseguir una posición como caballeros dentro de las filas, algo que, aparentemente, requería pasar pruebas más difíciles. Más adelante, se descubrió que Sothos había fracasado en su prueba final. Jarien no se mostró de acuerdo y, en abierto desafío, insistió en que él merecía estar entre los caballeros. Según hemos logrado saber, el gran cruzado Dathrohan acabó con ambos en sus propias estancias.</P><BR/><P align=\"left\">De esta manera, otro de estos lugares encantados es el Trono Carmesí, que está dentro de El Bastión Escarlata en Stratholme.</P><BR/></BODY></HTML>' WHERE `entry`='2866';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">KORMOK</H1><BR/><P align=\"center\">Ogro nigromante de dos cabezas</P><BR/><P align=\"left\">¿Qué se puede decir de Kormok? Era un ogro de dos cabezas de la variedad colérica que, en realidad, no solía ser tan malo. Cuando lo capturamos por primera vez fue como prisionero de un trabajo que habíamos aceptado en las Estepas Ardientes. Su ayuda demostró ser muy valiosa, ya que colaboró con nosotros para librarnos de una situación muy embarazosa y, de esta manera, se convirtió en uno de los magos más importantes de la compañía.</P><BR/><P align=\"left\">Lo extraño acerca de Kormok era la naturaleza completamente distinta de sus dos cabezas. Una era totalmente alegre e, incluso, un poco tonta, mientras que la otra era astuta, calculadora, y tenía un brillo de muerte en sus ojos. En realidad, era un poco escalofriante.</P><BR/></BODY></HTML>' WHERE `entry`='2867';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">KORMOK, continuación...</H1><BR/><P align=\"left\">Más tarde, en el transcurso de nuestros viajes, Kormok quedó expuesto a la parte nigromántica de la magia. Desafortunadamente, la cabeza espeluznante mostraba afinidad con los espíritus muertos resucitados y experimentaba un perverso placer en ello, especialmente en la invocación de los espíritus exiliados de la Isla del Purgatorio.</P><BR/><P align=\"left\">Tras la disolución de la compañía, Kormok se alistó en Scholomance para proseguir con su \'educación\'. Los asesinos espectrales penetraron en la escuela fácilmente y los asesinaron ante los ojos de su nuevo maestro Ras Levescarcha.</P><BR/><P align=\"left\">Esto convirtió la estancia de Ras Murmuhielo en uno de los lugares encantados en donde cabe utilizar el blandón con éxito.</P><BR/></BODY></HTML>' WHERE `entry`='2868';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">LORD VALTHALAK</H1><BR/><P align=\"center\">Noble dracónida y antiguo General de Cumbre de Roca Negra</P><BR/><P align=\"left\">Antes del General Drakkisath, estaba Lord Valthalak, un brujo dracónida de inmenso poder. Resulta habitual que el liderazgo de la Cumbre cambie de manos y, desafortunadamente para nosotros, desempeñamos un papel en el último cambio de manos del poder.</P><BR/><P align=\"left\">Un zahorí-contrabandista de la Bahía del Botín, Gremnik Saltatrabas, nos encargó que robáramos el libro de hechizos de Valthalak. No sabíamos si quería estudiarlo o venderlo (o ambas cosas), aunque esto ya no viene al caso. Lo que importa es que nos infiltramos en la parte alta de las Cumbres de Roca Negra y acabamos con Lord Valthalak en su guarida. Aquí es donde realmente empezó la diversión.</P><BR/></BODY></HTML>' WHERE `entry`='2869';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">LORD VALTHALAK, continuación...</H1><BR/><P align=\"left\">No teníamos ni idea de la magnitud del conocimiento de Valthalak sobre la manipulación de almas o, probablemente, nunca nos tomamos el trabajo de averiguarlo. Pero, finalmente, lo hicimos y resultó ser la última tarea que realizaríamos.</P><BR/><P align=\"left\">Tras eliminar a Lord Valthalak y robar su libro de hechizos, Kormok debió de percibir algo en relación con su amuleto. Insistía en tomarlo como su parte del botín, lo que provocó rápidamente una acalorada discusión y casi una pelea. Por entonces, no sabíamos que el amuleto realmente albergaba buena parte del espíritu de Valthalak. Para nuestro desagrado, lo averiguamos mucho después.</P><BR/></BODY></HTML>' WHERE `entry`='2870';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">LORD VALTHALAK, continuación...</H1><BR/><P align=\"left\">Rompiendo en pedazos el amuleto, escapamos del fantasma de Valthalak, así como de su ejército de asesinos espectrales. Y el ruido que hicimos mientras luchábamos por el amuleto despertó a los defensores que quedaban de la Cumbre.</P><BR/><P align=\"left\">Casi todos logramos salir de Roca Negra y así considerábamos que estábamos seguros. Pero, incluso desde el más allá, Valthalak envió a sus esbirros contra nosotros, por lo que no nos sirvió de nada ocultarnos. Pronto, la Espada Enmascarada se partió tras la misión, así que nos convertimos en presa fácil.</P><BR/><P align=\"left\">Como sabrás, esto convierte a la morada de Lord Valthalak, actual estancia de la Bestia en la Cumbre de Roca Negra, en el último de los lugares encantados.</P><BR/></BODY></HTML>' WHERE `entry`='2871';
UPDATE `locales_page_text` SET `Text_loc6`='A todos los hombres y mujeres sanos:$B$BLa Plaga ha regresado y, para hacer frente a esta amenaza, debemos dejar a un lado todos nuestros conflictos. El Alba Argenta acabará con todos aquellos que no empuñen las armas contra la amenaza de la Plaga.$B$BLos que han decidido defender nuestras tierras están reunidos en la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este. Juntos, venceremos.$B$BGuardián de los Pergaminos$BEl Alba Argenta' WHERE `entry`='2872';
UPDATE `locales_page_text` SET `Text_loc6`='Mi querida Emily:$B$BHace pocos días abandonamos el campamento en este lugar dejado de la mano de la Luz, siguiendo las órdenes del rey de regresar a casa. A pesar de lo inhóspito del paisaje, mi corazón estaba henchido de gozo, ya que sabía que tras el frío y penoso viaje hacia la costa y más allá, podría solazarme entre tus brazos.$B$BHoy alcanzamos la costa y descubrimos que nuestros barcos, con los que íbamos a regresar, se habían convertido en cascajos quemados. No podemos irnos y no tenemos otra opción que internarnos en el corazón de esta abismal tierra baldía.$B$BHe ido hasta el mismo fin del mundo para mantenerte a salvo, Emily... y ahora... desearía con todo mi corazón haberme quedado contigo en Lordaeron.$B$BEstás presente en cada momento de vigilia. En esta tierra gélida, tú eres mi fuente de calor, mi amada, y nadie podrá quitarme eso.$B$BMaxwell' WHERE `entry`='2878';
UPDATE `locales_page_text` SET `Text_loc6`='Reuben,$B$Bte escribo a sabiendas que quizás nunca leas esto. No puedo permanecer inmóvil escuchando los golpes contra los muros de la Vega del Amparo. Los no-muertos rodean nuestra aldea y nos atacan constantemente. Tenemos que defender a nuestra gente hasta que lleguen los refuerzos.$B$BMe rompí la pierna durante el último ataque y aquí me ves, inútil y dejando una brecha en nuestras filas. No se escucha otro sonido que el de la batalla y la muerte. El miedo se huele en el aire.$B$BEl príncipe Arthas está aquí, peleando en el frente con sus hombres. Si él no estuviese aquí ya hubiésemos muerto hace tiempo. Su amor por este territorio y su gente es abrumante. Sigo sus órdenes con esmero, con mi pierna rota o no, y por ello no puedo quedarme aquí. Tengo que ayudar con mi espada en el frente. Espero que estas palabras te lleguen en un mejor momento.$B$BTu amigo,$BLeagrem' WHERE `entry`='2880';
UPDATE `locales_page_text` SET `Text_loc6`='Heliana:$B$BYo, Vargus, vivo, a pesar de que tú desees lo contrario. Desprecio profundamente ir de un lado a otro metido en esta ridícula armadura.$B$BNuestras últimas órdenes nos hicieron marchar hacia Andorhal en busca de grano o cualquier otra idiotez. ¿Por qué habrían de importarme los almacenes de alimento en Andorhal? Estar en el ejército es completamente absurdo.$B$BTía Heliana, ten la seguridad de que regresaré para hacerme con tu herencia. Aunque quedara gravemente herido y sin alimento, el puro rencor me llevaría en andas para hacerme con la recompensa.$B$BVargus$B$B' WHERE `entry`='2881';
UPDATE `locales_page_text` SET `Text_loc6`='Queridísima Amelia:$B$BEsta noche he visto cosas que me rondarán hasta el final de mis días.$B$BStratholme está en llamas... y nosotros somos los causantes del incendio.$B$BNuestro príncipe nos condujo a las calles de la ciudad por la noche. Ordenó entrar en las casas del pueblo y... matarlos mientras dormían. Estaban apestados, según Lord Arthas y, antes de que nos mataran, debían ser asesinados.$B$BFue una carnicería. Cientos murieron silenciosamente bajo las espadas de aquellos que habían jurado protegerlos. No pude permanecer ni un minuto más allí:$B$BHuí. Aunque me haya convertido en un desertor, no puedo cometer semejantes atrocidades. En cada casa no veía más que tu rostro o los de nuestros niños, confundidos con los de las víctimas. Si estar contra esto significa ser un traidor, entonces lo soy.$B$BEspero poder encontrar el camino de regreso que me lleve a estar contigo, pero los caminos son inseguros. Transmite a nuestros hijos mi amor en mi ausencia.$B$BJames' WHERE `entry`='2882';
UPDATE `locales_page_text` SET `Text_loc6`='Samla:$B$BA medida que seguimos a Lord Arthas en dirección al norte, siempre hacia el norte, mi corazón se entristecía cada vez más. Si bien una vez estuvo tocado por la Luz, ahora siento la oscuridad del espíritu del joven paladín. Su celo está oscurecido por una inquietante y extraña pesadilla que inunda su alma, imposible de adivinar... $B$BPronto avistaremos tierra en los gélidos páramos. Aunque muchos de sus hombres han enfermado por el frío y por las batallas con las pútridas bestias, Arthas nos comunicó que lo que está buscando en el hielo cambiará el sentido de la batalla. Pero sus palabras no me han tranquilizado, porque después de pronunciarlas... la macabra sonrisa que se dibujó en sus ondulados labios me provocó un escalofrío más profundo que el de cualquier ventisca.$B$BReza por nosotros, Samla, y reza por nuestro mundo,$B$BTorgal' WHERE `entry`='2883';
UPDATE `locales_page_text` SET `Text_loc6`='Querida Pamela:$B$BMañana entraremos en batalla en Andorhal y me temo, amor mío, que esta va a ser mi última carta. Los no-muertos no se cansan y nosotros solo somos humanos. No podemos enfrentarnos a ellos. Pero no te inquietes, ya que, aunque estamos seguros de que vamos a morir, nuestras esperanzas permanecen firmes. ¡La Luz vencerá!$B$BY, querida mía, me alegra saber que, mientras estos oscuros terrores derriban nuestros muros y caen sobre nosotros, mis últimos pensamientos no serán de muerte. Te recordaré entre mis brazos y, finalmente, tendré paz.$B$BChristoph' WHERE `entry`='2884';
UPDATE `locales_page_text` SET `Text_loc6`='Hola, $N. Al parecer, he extraído cierta información procedente de esta nota.$B$BSobre James, me temo que apenas he encontrado nada. Sin embargo, he descubierto ciertos datos familiares sobre Amelia.$B$BSi bien ni ella ni sus hijos sobrevivieron a la guerra, su hermano, un hombre llamado Alexander Calder, sí sobrevivió. Puedes encontrarlo en la Caverna Abandonada en Forjaz, estudiando la peligrosa magia de la Legión Ardiente.$B$BElla tenía una prima a la que estaba muy unida, una mujer conocida ahora como Deze Delirioníveo, que apoyó la batalla por la Cuenca de Arathi, en el territorio de Orgrimmar.$B$BCuando estés cerca de cualquiera de ellos, deberías ir a visitarlos. Estoy seguro de que estarán muy interesados en recibir noticias de la pobre Amelia.' WHERE `entry`='2885';
UPDATE `locales_page_text` SET `Text_loc6`='Saludos, $N. He pensado que los resultados de mi investigación podrían ser de tu interés.$B$BAl parecer, tanto a Reuben como a Leagrem los sobrevivieron, por así decirlo, sus familiares. Incluso si ya saben lo que ocurrió con el Soldado perdido, visitar a cualquiera de ellos puede valer la pena.$B$BPuedes encontrar a la ex mujer de Reuben, Joanna Salablanca, en el Barrio de la Guerra de Entrañas, en donde pasa la mayor parte del tiempo. No estoy seguro de cómo recibirá la noticia sobre su ex marido.$B$BEn lo que respecta a Leagrem, su tía es la supervisora del orfanato de Ventormenta, un trabajo ciertamente admirable.$B$BHagas lo que hagas en la vida, te deseo la mejor de las suertes en tus empeños.' WHERE `entry`='2886';
UPDATE `locales_page_text` SET `Text_loc6`='$N, espero que cuando recibas esta carta te encuentres bien. Tras investigar un poco, he descubierto ciertos datos sobre aquellos que escribieron la nota que recuperaste.$B$BEl padre de Samla conduce a los Renegados y a los Trols durante el peregrinaje a Cima del Trueno, siguiendo los caminos de su fe. Su nombre es Miles Galés y puedes encontrarlo junto con sus fieles no-muertos en las Pozas de las Visiones.$B$BMientras tanto, a Torgal lo sobrevivió su prima Elissa Dumas. Según he sabido, es una ermitaña y pasa sus días en el Templo de la Luna en Darnassus.$B$BTe ruego que visites a cualquiera de estas personas, $N. Quizás nadie les haya traído noticias sobre la suerte de Torgal o de Samla.' WHERE `entry`='2887';
UPDATE `locales_page_text` SET `Text_loc6`='El Guardián me entregó la carta que descubriste, $N, y creo que apreciarás en lo que vale tener noticias frescas.$B$BEl último \"Vargus\" superviviente que se menciona en la carta ha sido encontrado en el desierto de Silithus, en el Fuerte Cenarion. Se le conoce como Garon Hutchins.$B$BLa próxima vez que vayas a los páramos, deberías hablar con él. Está muy interesado en conocer los datos que obran en nuestro poder.' WHERE `entry`='2888';
UPDATE `locales_page_text` SET `Text_loc6`='Saludos. Por fin he encontrado un poco de tiempo para examinar la carta que encontraste sobre la Plaga. Aunque me tomó cierto trabajo, creo haber descubierto a un pariente vivo de los fallecidos.$B$BLa hermana de Maxwell, una tal Zarena Cromvento, dirige una pequeña armería en la Bahía del Botín. Desconozco cuál era el grado de relación con su hermano, pero si pasas por ahí, deberías concertar una cita con ella. Poder disponer de datos fehacientes sobre estas cuestiones es algo muy valioso y poco habitual en estos tiempos.' WHERE `entry`='2889';
UPDATE `locales_page_text` SET `Text_loc6`='Finalmente he descubierto ciertos datos sobre el antiguo Soldado que combatiste. El rastro ha sido difícil de seguir, ya que su árbol genealógico no está debidamente documentado, pero he logrado encontrar a un pariente consanguíneo.$B$B¿Puedes creer que el custodio Alen, de la Capilla de la Esperanza de la Luz, es en realidad el hijo de Christoph? Oír noticias sobre su padre seguro que le agradará mucho. Si alguna vez vuelves a estar de servicio en El Alba Argenta en las Tierras de la Peste, por favor, habla con él. Sería todo un detalle.' WHERE `entry`='2890';
UPDATE `locales_page_text` SET `Text_loc6`='En recuerdo al Comandante Supremo aliado, Anduin Lothar: un hombre que lo sacrificaría todo en defensa de su rey, su gente, su hogar...$B$BDejad que nuestros enemigos sepan nuestros nombres. Dejad que nuestros aliados honren nuestro paso. Somos los hijos de Lothar. $B$BGeneral Turalyon' WHERE `entry`='2891';
UPDATE `locales_page_text` SET `Text_loc6`='Aquí yacen los restos de un peón fuerte de corazón. Intentó luchar, pero solo se tiró un pedo.' WHERE `entry`='2892';
UPDATE `locales_page_text` SET `Text_loc6`='Artículo 1 de 1' WHERE `entry`='2904';
UPDATE `locales_page_text` SET `Text_loc6`='Aquí está el bote de cenizas. Estas son las cenizas de mi cordura, mi pasión, y mi conducta. Todo, cada uno de ellos absolutamente destruido. Que todos aquellos que vean estas tierras desoladas del fuego del infierno recuerden este peón caído. El derramó la sangre de la Alianza, y se sacrificó por la Horda hasta enloquecer completamente a causa de los malvados y desalmados que devoraban todo aquello que consideraba más querido. Que mientras festejen su victoria, sufran su ira. Quizá no en este mundo, pero en cualquier otro venidero. Es mi declaración, mi solemne juramento, y mi eterna promesa. Vengaré mi sufrimiento.' WHERE `entry`='2905';
UPDATE `locales_page_text` SET `Text_loc6`='Inspecciona la extensión de las islas e informa.$B$BRecuerda, ¡que no te pillen!$B$BNo me falles, Candress.$B$BS.' WHERE `entry`='2906';
UPDATE `locales_page_text` SET `Text_loc6`='El esbozo muestra Shadra\'Alor y sus tres templos, con varias \'X\' marcadas, que corresponden a las ubicaciones de los huevos robados. Las marcas aparecen en el extremo sur del tempo oriental, cerca de una cabaña al noreste del templo oriental, cerca de un altar al este del lago central y en la ladera del templo sur, cerca de la palabra \"exilio.\"$B$BLas notas de Barbapluma indican que planeaba entregar esta información a Falstad Martillo Salvaje, de quien esperaba que enviara un grupo de guerreros para rescatar los huevos.' WHERE `entry`='2907';
UPDATE `locales_page_text` SET `Text_loc6`='Diathorus,$B$BConfío en que esta carta finalmente llegará a ti. Estos demonios son estúpidos. Lo único en lo que piensan son sus garras, sus látigos o en agujerear con las púas de su cabeza. ¡Y no me atrevo a usar uno de los infernales por miedo a que sea reducido a cenizas!$B$BLamentablemente, anhelo los días en los que podíamos usar a los orcos. Al menos ellos tenían medio cerebro y podían seguir órdenes. ¿Quizás haya alguna oportunidad de tomar algunos esclavos nuevos de algún lugar cercano?' WHERE `entry`='2909';
UPDATE `locales_page_text` SET `Text_loc6`='Los informes de los elfos de la noche que ayudan a los draenei son ciertos. Están intentando establecer una nueva base de operaciones en Canción del Bosque, al norte, cerca de Satyrnaar. Deberíamos debatir sobre aunar fuerzas para atacar antes de la próxima luna llena de este patético mundo. Si los aplastamos ahora, nunca conseguirán un punto de apoyo en nuestro umbral.$B$BSaludos,$B$BGorgannon$B$BP.D.: ¡Deja de usar toda esa tinta de sangre en las cartas de amor para mis azotadoras! Las elfas de la noche vírgenes escasean en estos tiempos.' WHERE `entry`='2910';
UPDATE `locales_page_text` SET `Text_loc6`='Diathorus,$B$BConfío en que esta carta finalmente llegará a ti. Estos demonios son estúpidos. Lo único en lo que piensan son sus garras, sus látigos o en agujerear con las púas de su cabeza. ¡Y no me atrevo a usar uno de los infernales por miedo a que sea reducido a cenizas!$B$BLamentablemente, anhelo los días en los que podíamos usar a los orcos. Al menos ellos tenían medio cerebro y podían seguir órdenes. ¿Quizás haya alguna oportunidad de tomar algunos esclavos nuevos de algún lugar cercano?' WHERE `entry`='2911';
UPDATE `locales_page_text` SET `Text_loc6`='Ha llegado el momento de recuperar algunas de las razas menores, en mi opinión. El Puesto del Hachazo, al noroeste, es un objetivo tentador. Deberíamos debatir sobre aunar fuerzas para atacar antes de la próxima luna llena de este patético mundo. Si los subyugamos ahora, llegarán a recordar lo que la palabra \"Horda\" solía significar verdaderamente.$B$BSaludos,$B$BGorgannon$B$BP.D.: ¡Deja de usar toda esa tinta de sangre en las cartas de amor para mis azotadoras! Las elfas de la noche vírgenes escasean en estos tiempos.' WHERE `entry`='2912';
UPDATE `locales_page_text` SET `Text_loc6`='Aunque casi fue vencido $BSu coraje prevaleció $BTomó su espada, la desenvainó$BY atacó al vil gentío $B$BDesde ese violento día $BNunca una flecha o espada $BEncontraría, en batalla o reyerta,$BSu camino hasta el señor de los enanos' WHERE `entry`='2913';
UPDATE `locales_page_text` SET `Text_loc6`='...pero ella ya lo sabía. Se había resignado a vivir como un paria después de vender su alma a pedazos. La venganza lo había valido, o al menos eso pensaba ella.$B$BPero ahora tenía una oportunidad de recobrar su humanidad y todo lo que pedía el extraño a cambio era su confianza.$B$B\"Lo haré\", dijo Katreyn. \"No tengo nada que perder.\"' WHERE `entry`='2914';
UPDATE `locales_page_text` SET `Text_loc6`='Artículo 3 de 4.' WHERE `entry`='2915';
UPDATE `locales_page_text` SET `Text_loc6`='Vazruden,$B$BDesde que el Portal Oscuro se ha vuelto a abrir, el Bastión del Honor y Thrallmar han estado recibiendo refuerzos importantes de Azeroth. He visto informes en los que se sugiere que están planeando un ataque a la ciudadela y, mientras, nosotros tenemos que enfrentarnos también con la Legión Ardiente. ¡Esto es inaceptable!$B$B¡No pongas en peligro la operación del Horno de Sangre! Los viles orcos que se fabrican allí son la clave para nuestro éxito. ¡Refuerza las defensas de las murallas y destina más guardias al perímetro inmediatamente!$B$BConfía en mí, no quieres que te haga una visita.$B$BIllidan' WHERE `entry`='2916';
UPDATE `locales_page_text` SET `Text_loc6`='Vazruden,$B$BDesde que el Portal Oscuro se ha vuelto a abrir, el Bastión del Honor y Thrallmar han estado recibiendo refuerzos importantes de Azeroth. He visto informes en los que se sugiere que están planeando un ataque a la ciudadela y, mientras, nosotros tenemos que enfrentarnos también con la Legión Ardiente. ¡Esto es inaceptable!$B$B¡No pongas en peligro la operación del Horno de Sangre! Los viles orcos que se fabrican allí son la clave para nuestro éxito. ¡Refuerza las defensas de las murallas y destina más guardias al perímetro inmediatamente!$B$BConfía en mí, no quieres que te haga una visita.$B$BIllidan' WHERE `entry`='2917';
UPDATE `locales_page_text` SET `Text_loc6`='Yo lo hice. No recuerdo haberlo hecho, pero no puede ser de otra manera.$B$BHe cometido pecados en otros momentos de mi vida. Incluso maté a un hombre que no lo merecía. Pero esto es diferente.$B$BHe cambiado. $B$BNo elegí estos asesinatos. Una bestia vive dentro de mí.$B$BMe cazarán y huirán de mí. Tengo que esconderme. Correr. Enterrar los cuerpos.$B$BEstoy saliendo cuando me doy cuenta de lo que realmente me molesta.$B$BNo siento culpa.' WHERE `entry`='2918';
UPDATE `locales_page_text` SET `Text_loc6`='Sirviente,$B$BLos planes de varios años se han perdido. ¡Los draenei y su lamentable líder han escapado del alcance del Maestro una vez más! Es más, ¡me han robado MI navío y lo han estrellado contra Azeroth! $B$BAunque es cierto que la agente eredar del Maestro ha fracasado, pudo transmitirnos la ubicación de El Exodar. Afortunadamente, su identidad sigue siendo un misterio para los draenei. Puede que todavía podamos usarla para nuestros fines.' WHERE `entry`='2919';
UPDATE `locales_page_text` SET `Text_loc6`='Ponte en contacto con nuestra espía y preparaos para un ataque a gran escala a los draenei. Matadlos a todos y recuperad mi navío. Eres el único enlace que tengo en Azeroth que puede reparar el daño que esas bestias han causado en El Exodar.$B$BNo me falles, gnomo. Si tu ansia de riqueza interfiere con mis órdenes, pasarás la eternidad en oración. Rezarás para que venga la muerte y te libere de las torturas a las que daré rienda suelta si vacilas.$B$BRey Caminante del Sol' WHERE `entry`='2920';
UPDATE `locales_page_text` SET `Text_loc6`='P.D Nuestro gran magíster ha recibido respuesta de Terrallende acerca de la pregunta de Thrall. La respuesta es, con gran certeza, sí.$B$B<Esta carta porta el sello adicional de Lady Sylvanas Brisaveloz>' WHERE `entry`='2922';
UPDATE `locales_page_text` SET `Text_loc6`='Estimados líderes de la Horda,$B$BCon gran placer yo - Lor\'themar Theron, señor regente de Quel\'Thalas ? anuncio la caída del comandante de la Plaga conocido por nosotros como Dar\'Khan Drathir.$B$BEl punto de apoyo de Dar\'Khan en nuestras tierras fue un asunto comentado por los críticos de la raza sin\'dorei en numerosas negociaciones anteriores. Esperamos que este suceso aborde todas y cada una de las objeciones anteriores que sus ilustrísimas pudieran haber abrigado.$B$B<Firmado>Lor\'themar Theron' WHERE `entry`='2923';
UPDATE `locales_page_text` SET `Text_loc6`='Estimados líderes de la Horda,$B$BCon gran placer yo - Lor\'themar Theron, señor regente de Quel\'Thalas ? anuncio la caída del comandante de la Plaga conocido por nosotros como Dar\'Khan Drathir.$B$BEl punto de apoyo de Dar\'Khan en nuestras tierras fue un asunto comentado por los críticos de la raza sin\'dorei en numerosas negociaciones anteriores. Esperamos que este suceso aborde todas y cada una de las objeciones anteriores que sus ilustrísimas pudieran haber abrigado.$B$B<Firmado>Lor\'themar Theron' WHERE `entry`='2924';
UPDATE `locales_page_text` SET `Text_loc6`='Reparación de naves transdimensionales para tontos, por Jamus Kaesi$B$B<Las finas páginas de este libro están hechas con un material resplandeciente y llenas de diagramas sumamente técnicos sobre cómo conservar en buen estado y reparar un barco transdimensional. Esta copia en concreto está abierta por un capítulo llamado \"Grietas de la Espiral Vectorial y cómo evitarlas\".>' WHERE `entry`='2925';
UPDATE `locales_page_text` SET `Text_loc6`='P.D Nuestro gran magíster ha recibido respuesta de Terrallende acerca de la pregunta de Thrall. La respuesta es, con gran certeza, sí.$B$B<Esta carta porta el sello adicional de Lady Sylvanas Brisaveloz>' WHERE `entry`='2926';
UPDATE `locales_page_text` SET `Text_loc6`='P.D Nuestro gran magíster ha recibido respuesta de Terrallende acerca de la pregunta de Thrall. La respuesta es, con gran certeza, sí.$B$B<Esta carta porta el sello adicional de Lady Sylvanas Brisaveloz>' WHERE `entry`='2927';
UPDATE `locales_page_text` SET `Text_loc6`='$N,$B$BLa creación de la Corcesca templada en sangre requiere una variedad de materiales volátiles y pocos frecuentes, que dan como resultado un arma temible de artesanía superior. Enumeraré los materiales por orden del más fácil hasta el más difícil de obtener.$B$BNo olvides que también debes obtener tu insignia de Caballero de sangre antes de que se acepten los materiales y pueda comenzar el proceso de forja.' WHERE `entry`='2930';
UPDATE `locales_page_text` SET `Text_loc6`='Sangre del Colérico - Para poder templar adecuadamente el metal de tu arma, debe templarse en la sangre del Colérico. Aunque la sangre de la mayoría de los demonios contiene una cantidad de poder considerable, la sangre común no es suficiente para forjar tu corcesca.$B$BLas preguntas discretas a los brujos de Orgrimmar han probado ser útiles para ubicar un suministro de la sangre. Los cultores de la Hoja Abrasadora de la Sima Ígnea poseen un orbe lleno del tipo de sangre que necesitaremos para tu arma. Me dicen que el orbe lo vigila un orco que se llama a sí mismo Jergosh el Convocador.$B$BSé prudente, como en todos tus asuntos con poderes demoníacos. Nunca se sabe lo que puede ocurrir cuando se maneja una sustancia tan poderosa.' WHERE `entry`='2931';
UPDATE `locales_page_text` SET `Text_loc6`='Caja de lingotes forjados con sangre - Por sí solo, el acero es un metal excelente, pero un arma forjada con él es mediocre. Los lingotes forjados con sangre presentan un material superior creado en un proceso que es un secreto bien guardado. Se rumorea que cualquier herrero lo suficientemente audaz como para manejar y trabajar el metal se vuelve maldito, pero nuestros artesanos han demostrado que eso es falso.$B$BLa reputación del metal aumenta su escasez, pero los Renegados han enviado un mensaje que dice que puede que exista un suministro en el Castillo de Colmillo Oscuro. Un diestro herrero de nombre Landen Fontana tuvo la curiosidad suficiente como para pasar a escondidas una caja de lingotes dentro del castillo. No se sabe dónde guarda su alijo Fontana, pero nuestro informador nos dice que el herrero es prisionero en la mazmorra del castillo. Utiliza todos los recursos que estén a tu disposición para obtener los lingotes.' WHERE `entry`='2932';
UPDATE `locales_page_text` SET `Text_loc6`='Una gema kor corrupta - Los libros del magíster registran que estas gemas son utilizadas por algunos taumaturgos para mantener energía para sus encantos, para hacerlos aún más letales. Aunque los oficiales taumaturgos pueden utilizar versiones purificadas de la gema, son demasiado débiles para nuestros propósitos.$B$BEn las cuevas de las Cavernas de Brazanegra, los taumaturgos nagas han atribuido a las gemas kor sus propios poderes oscuros, aumentándolas de una forma que las hace perfectas para usarlas en tu corcesca. Mátalos sin piedad hasta que hayas obtenido la gema que necesitarás.' WHERE `entry`='2933';
UPDATE `locales_page_text` SET `Text_loc6`='La insignia que llevarás como adepto la llevó por última vez un valiente e intrépido Caballero de sangre de la familia Estrella del Alba. Su servicio ejemplar es un buen augurio para tu propia vida en la orden.$B$BEl Caballero de sangre de la familia Estrella del Alba dirigió un ataque audaz, pero infructuoso contra el Bastión de la Plaga en Ciudad de la Muerte. Su cuerpo descansa donde murió, enzarzado en combate con nuestros mayores enemigos. Encuéntralo, $N, y tráeme su insignia. Tu éxito le llevará honor a él, a ti y a todos los verdaderos Caballeros de sangre.' WHERE `entry`='2934';
UPDATE `locales_page_text` SET `Text_loc6`='¡Has descubierto la ubicación del santuario! Tras un mayor reconocimiento, detectas una pulsación más fuerte del extraño poder que se ha adueñado de la Isla. Te sientes algo incómodo, de pie junto al santuario... y tal vez un poco preocupado.$B$BEl cartel bronce que se encuentra en el lateral del santuario reza:$B$B\"Aquí se encuentra el Santuario de Dath\'Remar, un tributo adecuado para un noble elfo. Que todo el que mire este monumento recuerde su sacrificio contra la Plaga y su dedicación a la causa de nuestra supervivencia duradera. Todo el que prospera en Quel\'Thalas lo hace gracias a él.\"' WHERE `entry`='2936';
UPDATE `locales_page_text` SET `Text_loc6`='Observé mientras lo golpeaban sin compasión. Su despiadado teniente actuaba como si tal crueldad fuera un acto de normalidad. A través de la salvaje paliza, sentía que yo mismo me quebrara.$B$BMientras observaba cómo el vindicador Saruan recibía sus golpes, comencé a sollozar. No por miedo... Lloré de tristeza. Ver a un draenei del Triunvirato tratado de tal forma. Si yo pudiera haberme liberado de mis ataduras. Si yo pudiera...' WHERE `entry`='2937';
UPDATE `locales_page_text` SET `Text_loc6`='Tras lo que pareció ser una eternidad, el vindicador perdió la conciencia. La tortura era demasiado, incluso para él. El cruel Matis, como descubrí que le llamaban intentó despertar a Saruan salpicando su devastado cuerpo con agua con contagio. Pero Saruan no despertó.' WHERE `entry`='2938';
UPDATE `locales_page_text` SET `Text_loc6`='Quise gritar, pero solo escapó de mis labios un débil jadeo. Me azotaron de inmediato por aquel acto de rebeldía. Mientras yacía en el suelo, pude ver a Matis paseándose delante del cuerpo de Saruan. Parecía visiblemente preocupado. Afirmó que Sironas, Sirona o alguna entidad tal haría que se le arrancara la carne de los huesos si Saruan había perecido debido a la paliza.' WHERE `entry`='2939';
UPDATE `locales_page_text` SET `Text_loc6`='Que la entidad Sironas tenía planes para Saruan...$B$B< La escritura se detiene repentinamente y una larga línea se desplaza hasta salirse de la página. Aquel draenei murió mientras escribía.>' WHERE `entry`='2940';
UPDATE `locales_page_text` SET `Text_loc6`='Lady Liadrin,$B$BEl reciente problema con la criatura cautiva ha dañado parte de nuestro equipo más esencial. Restablecer control sobre él ha desgastado a mis magistri y mis recursos. He presentado informes del incidente al Gran magister Rommath, pero ha respondido con lentitud. Me temo que, sin los materiales siguientes, puede que nos veamos obligados a reducir nuestras operaciones, y sabes lo que eso significaría para tus caballeros.$B$BPor si hay algo que puedes hacer para ayudarnos, en la página siguiente he incluido una lista de los materiales. Agradecería enormemente cualquier ayuda que tu orden pudiera proporcionar.$B$BMagíster Astalor Jurasangre' WHERE `entry`='2941';
UPDATE `locales_page_text` SET `Text_loc6`='* 40 Paños rúnicos$B$B* 6 Barras de arcanita$B$B* 10 Soleas$B$B* 5 Runas oscuras$B$B* 150 Piezas de oro' WHERE `entry`='2942';
UPDATE `locales_page_text` SET `Text_loc6`='Declaración del Rey del Sol $B$BHalcones del Sol ? mis queridos y leales hijos ? vuestro rey se encomienda a vosotros. Vuestra presencia en Azeroth es de suma importancia para mí y para el Maestro.$B$BEl portal del Sol pronto estará preparado para transportar refuerzos desde El Castillo de la Tempestad directamente a la Isla Bruma de Sangre. $B$BRecuperaremos El Exodar. Mataremos a los bastardos de piel azul donde estén. Velen pagará por sus crímenes.$B$BRey Kael\'thas, el Caminante del Sol' WHERE `entry`='2943';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><IMG src=\"Interface\\Pictures\\24475_gordawg_256\"/></BODY></HTML>' WHERE `entry`='2944';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><BR/><H1 align=\"center\">LAS VERDES COLINAS DE TUERCESPINA</H1><BR/><H3 align=\"center\">por Hemet Nesingwary</H3></BODY></HTML>' WHERE `entry`='2945';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestro primer día fue todo lo bien que se puede esperar que vayan los primeros días. La mayor parte del tiempo nos preocupamos de realizar los preparativos necesarios para establecer un campamento base. Encontré un lugar ideal, junto a la ensenada de un río de agua dulce. A juzgar por los viejos y abandonados muelles que hay cerca, este emplazamiento estuvo habitado hace algún tiempo. Por lo que respecta a los habitantes primitivos, solo el tiempo puede contar esa historia.' WHERE `entry`='2946';
UPDATE `locales_page_text` SET `Text_loc6`='Para esta expedición he reunido a Ajeck Rouack y a Sir S. J. Erlgadin, junto con mi sirviente de confianza, Barnil Jarropetra. Libré muchas batallas al lado del padre de Ajeck en defensa de la Alianza. Verla crecida es bastante especial. Su padre la instruyó bien en las artes de las armas. Su habilidad con el arco me hace preguntarme si corre sangre élfica por esas venas.' WHERE `entry`='2947';
UPDATE `locales_page_text` SET `Text_loc6`='Sir S. J. Erlgadin proviene de la aristocracia humana. Su padre, el conde Erlgadin, era célebre por su generosidad. Fue el conde quien presionó por una mejora en las condiciones de trabajo de la Hermandad de los Albañiles durante la restauración de Ventormenta después de la Segunda Gran Guerra.' WHERE `entry`='2948';
UPDATE `locales_page_text` SET `Text_loc6`='En los años que siguieron, después de que Ventormenta traicionara a la hermandad de los albañiles, Sir Erlgadin se fue volviendo más rencoroso sobre el papel de los nobles en el Reino. Ya no deseaba defender la posición que la estirpe de su padre había ganado para él en la Casa de Nobles.$B$BPero me estoy yendo por las ramas. El propósito de esta historia no es que sirva como tratado político o biografía. Este es el relato de mis experiencias en busca de caza mayor por las verdes colinas de Tuercespina....' WHERE `entry`='2949';
UPDATE `locales_page_text` SET `Text_loc6`='Nos levantamos al amanecer. Barnil empezó a preparar el desayuno. Advertí que Ajeck estaba un poco distraída. La caminata del día prometía ser larga y nos esperaban peligrosos episodios de caza. Cualquier descuido podía ponernos en una situación difícil. No obstante, Ajeck parecía incapaz de desviar su mirada de Barnil, que estaba de pie frente al río, limpiando su equipo.' WHERE `entry`='2950';
UPDATE `locales_page_text` SET `Text_loc6`='Justo cuando estaba a punto de cuestionar la falta de interés de Ajeck en la estrategia de caza del día, alargó la mano hacia su arco, lo tensó con una flecha y la soltó justo hacia el pobre Barnil. Pero no era a Barnil a quien Ajeck disparaba, pues, cuando Barnil se echó a un lado boquiabierto, un gran crocolisco de río flotó a la superficie con la flecha de Ajeck perfectamente colocada entre sus grandes ojos.' WHERE `entry`='2951';
UPDATE `locales_page_text` SET `Text_loc6`='Nos pusimos en camino hacia el oeste, a través de la espesa maleza de la enmarañada jungla. Con pasos lentos y cuidadosos, avanzamos a través de un espeso follaje en busca de presa. La mañana pasó en un silencio frustrante. Nada se agitaba en la Vega, ni una brisa. Hacia la tarde, la expedición se mostraba impaciente. Barnil ya no andaba con los pasos cautelosos de un depredador que rastrea a su presa. En su lugar, caminaba pesada y torpemente por el camino, a menudo pisando ruidosamente hojas secas o ramas caídas.' WHERE `entry`='2952';
UPDATE `locales_page_text` SET `Text_loc6`='Durante semejante tropiezo, Erlgadin posó su mano pesadamente sobre el hombro de Barnil. Ajeck y yo apenas advertimos la escena, suponiendo que el hombre solo estaba dando una merecida reprimenda a Barnil por su descuido. No obstante, Erlgadin hizo un gesto lento con la cabeza en dirección a un árbol caído. Desde allí nos acechaban dos ojos negros penetrantes justo encima de un par de afilados colmillos.' WHERE `entry`='2953';
UPDATE `locales_page_text` SET `Text_loc6`='La bestia era un tigre macho de Tuercespina. Antes de que pudiera amartillar el rifle, Erlgadin levantó la ballesta y disparó en dirección al animal. El virote dejó su marca y sorprendió a la bestia con fuerza en el costado izquierdo. El tigre hizo un intento en vano por huir, pero la herida era demasiado grave. La bestia fue dando traspiés durante varios trágicos segundos, hasta que Barnil lo remató lanzando un hacha.' WHERE `entry`='2954';
UPDATE `locales_page_text` SET `Text_loc6`='La matanza provocó un ánimo festivo en la expedición. Barnil sirvió aguamiel para disfrute de todos. Pero nuestros festejos fueron efímeros. Mientras preparábamos el cadáver para transportarlo de vuelta al campamento base, un rugido espantoso nos cogió desprevenidos a todos. En todos mis años nunca he oído nada que helara la sangre de tal modo.' WHERE `entry`='2955';
UPDATE `locales_page_text` SET `Text_loc6`='En un rocoso precipicio en lo alto, perfilado por el sol poniente, podía distinguir el felino depredador más grande que he visto jamás. Pude liberar una descarga con mi rifle, pero el felino siguió su camino. Rugió una vez más, en esta ocasión vez con más fuerza que la anterior, y desapareció.$B$BRecogimos nuestras pertenencias y nos dirigimos solemnemente de vuelta al campamento.' WHERE `entry`='2956';
UPDATE `locales_page_text` SET `Text_loc6`='Había prometido a la expedición que pasaríamos el día siguiente cazando panteras, ya que sus pieles son muy valoradas en Azeroth. Esta demanda se debe a que todos los cazadores, tramperos y comerciantes de pieles arriesgan valientemente su vida en nombre de la Alianza.' WHERE `entry`='2957';
UPDATE `locales_page_text` SET `Text_loc6`='Ajeck y Sir Erlgadin estaban ansiosos por aprender a cazar eficazmente con un rifle de los enanos. Hice que los dos humanos dejaran sus primitivas armas en el campamento base. Barnil y yo los equipamos con algunas de las mejores armas de fuego de Forjaz.' WHERE `entry`='2958';
UPDATE `locales_page_text` SET `Text_loc6`='Ese día nos aventuramos al sur, siguiendo algunos rastros recientes de pantera. Pronto llegamos a un barranco que se cruzaba por un enorme puente colgante. No pude evitar pensar en la descriptiva obra de Brann sobre aquella región cuando vi aquella maravilla de la ingeniería. Se suponía tan a menudo que los trols nativos eran una raza primitiva e inculta, pero, mientras observaba fijamente la artesanía maestra del puente, pude reconocer la habilidad con la que los albañiles trols superaron la hazaña, aparentemente imposible.' WHERE `entry`='2959';
UPDATE `locales_page_text` SET `Text_loc6`='Al poco tiempo, Ajeck rastreó a la pantera en dirección al sudoeste. Anduvimos en silencio, con las armas preparadas, anticipándonos a nuestra presa. Un chasquido de ramas en un bosquecillo cercano llamó nuestra atención de inmediato. Allí había algo. Una mirada seria a Barnil bastó para transmitirle mis pensamientos. Barnil bajó su rifle lentamente. Esta presa no era para nosotros, sino para nuestros compañeros humanos. Innumerables panteras habían perdido la vida al enfrentarse a nuestros humeantes cañones. Esta presa era para los humanos.' WHERE `entry`='2960';
UPDATE `locales_page_text` SET `Text_loc6`='Tanto Ajeck como Sir Erlgadin se mantuvieron erguidos, con las armas al nivel de la hirsuta maleza que había bajo unos árboles que se mecían de un lado a otro. El sol del mediodía ardía con fuerza sobre nosotros. Una lenta gota de sudor rodó por la sien de Erlgadin, al tiempo que retiraba el gatillo. Tras el chasquido, el follaje exuberante se abrió en dos y una pantera negra, espécimen hermoso, saltó veloz sobre la llanura.' WHERE `entry`='2961';
UPDATE `locales_page_text` SET `Text_loc6`='Los humanos tenían la mira puesta en la pantera mientras esta corría rodeando la arboleda. Los cañones de los rifles se movían en paralelo con el animal. Barnil me miró impaciente y yo negué con la cabeza para que no abriera fuego. Esta cacería era para los humanos, no para Barnil ni para mí. Erlgadin lanzó un disparo, que erró completamente en su empeño de alcanzar a la pantera. Por lo visto, no estaba preparado para soportar la violenta repercusión de la explosión del rifle.' WHERE `entry`='2962';
UPDATE `locales_page_text` SET `Text_loc6`='El arma dio una violenta sacudida. El cañón rodó lateralmente y cayó bajo el rifle de Ajeck. Ajeck había elegido ese preciso momento para apretar el gatillo. El rifle, con la mira orientada torpemente hacia la fila de árboles, se disparó con un estruendo inconfundible. Una bandada de pájaros chilló desde la copa de un árbol, dispersándose por todas partes. Una columna de humo emergió del árbol. Sobrecogidos, vimos cómo una tremenda rama caía sobre la pantera que intentaba escapar, partiéndole el lomo.' WHERE `entry`='2963';
UPDATE `locales_page_text` SET `Text_loc6`='A medida que pasaban las semanas, nuestras reservas de piel de pantera y tigre se hicieron inmensas. Decidí que era el momento de que la expedición se centrara en un nuevo reto: los raptores.$B$BLos humanos, aunque agradecieron el adiestramiento que les ofrecimos Barnil y yo, decidieron abstenerse de cazar con armas de fuego. Ajeck se sentía mucho más cómoda con un arco delicadamente tensado y Sir Erlgadin nunca dejó el campamento sin su resistente ballesta.' WHERE `entry`='2964';
UPDATE `locales_page_text` SET `Text_loc6`='Nos pusimos en camino con las primeras luces, en dirección al sur, más allá de las ruinas de Tkashi. Barnil expresó su preocupación por el hecho de que pudiéramos encontrarnos con miembros de la tribu Sangrapellejo. Recordé a Barnil que los Sangrapellejo estaban más preocupados por destruir a su enemigo tribal, los Machacacráneos. No hace falta decir que eso no consoló a Barnil lo más mínimo. Sin embargo, yo tenía un rifle cargado, una cartera llena de pólvora y tres cazadores letales conmigo para aliviar cualquier preocupación de un emboscada poco amistosa.' WHERE `entry`='2965';
UPDATE `locales_page_text` SET `Text_loc6`='Me he encontrado ante un infernal enorme en el campo de batalla, mientras el ejército de la Legión Ardiente avanzaba desde todas direcciones. Un grupo rebelde de trols me parece tan inofensivo como una liebre en las colinas de Dun Morogh.$B$BPasamos ante las ruinas de Tkashi sin suceso alguno, para alivio de Barnil. El grupo procedió a dirigirse hacia el oeste, hacia el Mare Magnum, bordeando las ruinas de Zul\'Kunda justo al sur. Mientras ascendíamos los altos riscos del mar, vimos a nuestro primer raptor.' WHERE `entry`='2966';
UPDATE `locales_page_text` SET `Text_loc6`='La bestia ni siquiera llegó a detectar nuestra presencia. De hecho, el único saludo que recibió de la expedición fue una bala entre los ojos.$B$BSir Erlgadin soltó un caluroso *hurra* mientras Ajeck asintió con la cabeza en mi dirección en señal de entusiasta aprobación. Rebusqué en mi saca en busca de mi pipa, con la esperanza de celebrarlo fumando. Barnil comenzó a subir la ladera a toda prisa para recuperar el cadáver del raptor. Miré fijamente la bestia caída con la satisfacción que acompaña a cada gran matanza.' WHERE `entry`='2967';
UPDATE `locales_page_text` SET `Text_loc6`='Sin embargo, no pude disfrutar de la victoria del cazador durante mucho tiempo, pues al contemplar el horizonte, numerosas siluetas aparecieron en la cresta de la colina, justo encima del pobre Barnil.$B$B*¡Huye, Barnil!*, grité. Ajeck, Sir Erlgadin y yo lanzamos una descarga de balas y flechas por encima de Barnil, directo sobre los raptores. Alguien logró una muerte en medio de la confusión.' WHERE `entry`='2968';
UPDATE `locales_page_text` SET `Text_loc6`='Nuestros disparos, que dirigimos apresuradamente, fueron suficientes para comprar la huida de Barnil. Barnil lanzó un clamor colina abajo y se volvió a unir al grupo. Corrimos a refugiarnos en la jungla; una manada de feroces raptores colazote acechaba todos nuestros movimientos.$B$BLos cazadores eran, ahora, los cazados.' WHERE `entry`='2969';
UPDATE `locales_page_text` SET `Text_loc6`='Conduje a la expedición hasta el mar, con el fin de que la costa nos protegiera de los raptores. Con nuestro apresuramiento, nos habíamos desviado demasiado al norte, a una altitud peligrosamente elevada. Nos habíamos equivocado. Fue culpa mía. Nos detuvimos delante de un escarpado acantilado, con los raptores justo detrás de nosotros.' WHERE `entry`='2970';
UPDATE `locales_page_text` SET `Text_loc6`='Avancé lentamente con el arma levantada. Había conducido a estos valientes cazadores a su fin. Tenía que morir defendiéndolos. Los raptores colazote son particularmente feroces, conocidos por ser unos sanguinarios implacables. Eran muchos más que nosotros. Pero si hubiera dejado que nos mataran a mis camaradas y a mí, sin antes derramar parte de su propia sangre, no me lo habría perdonado.' WHERE `entry`='2971';
UPDATE `locales_page_text` SET `Text_loc6`='Ajeck y Sir Erlgadin dispusieron sus armas, flanqueándome a cada lado, de espaldas al mar. Barnil dejó escapar un suspiro derrotado y blandió su hacha. Los colazotes estaban casi sobre nosotros. Su firme zancada se había hecho más lenta. Ahora acechaban a su presa, pues sabían que nos tenían atrapados.' WHERE `entry`='2972';
UPDATE `locales_page_text` SET `Text_loc6`='Entonces ocurrió algo milagroso. Desde donde estábamos escuchamos el inconfundible y aterrador rugido del gran tigre blanco. A pesar de ser numerosos, los raptores dieron media vuelta y se dispersaron. Solo vimos el breve destello blanco de un tigre que salió disparado junto a nosotros, abalanzándose sobre uno de los raptores. No fue necesario dar ninguna orden. Los cuatro miembros de la expedición sabíamos que era el momento de salir corriendo.' WHERE `entry`='2973';
UPDATE `locales_page_text` SET `Text_loc6`='Corrimos a toda velocidad hasta el campamento base, sin reducir la marcha en ningún momento. Más tarde aquella noche, nos sentamos en silencio alrededor de la hoguera, sabiendo que un extraño golpe de suerte nos había salvado la vida. Esos son los riesgos que corre el cazador de caza mayor. Jugamos con el destino. No obstante, todos, en algún momento de nuestras vidas, debemos afrontar las afiladas garras del destino. Este enano se alegra, porque ese momento aún no ha llegado a las verdes colinas de Tuercespina.' WHERE `entry`='2974';
UPDATE `locales_page_text` SET `Text_loc6`='Estúpido,$B$BEncuentra comida. Azul buen sabor. Lantresor decir estar bien. Traer comida a Zoquete. Zoquete comer primero, después comer estúpido. ¿De acuerdo? Ir rápido o Zoquete comer tú.$B$BZoquete' WHERE `entry`='2975';
UPDATE `locales_page_text` SET `Text_loc6`='(1) Raliq el Borracho: Ogro. Nunca deja cantina. Malo. Feo. Gordo. Malo. También muy gordo.$B$B[Pág. siguiente]' WHERE `entry`='2976';
UPDATE `locales_page_text` SET `Text_loc6`='(2) Coosh\'coosh: ese molesto hombrecillo-espora de la Marisma de Zangar tiene jardín de setas en la frontera entre la Marisma de Zangar y Terokkar. Perdió muchas piezas de oro con Sal\'salabim en juego de \"Qué es ese olor.\" Intentó pagar a Sal\'salabim con una estúpida seta llamada Fluochampiñón. ¡Dice que tan bueno como oro! ¡Ja!$B$B[Pág. siguiente]' WHERE `entry`='2977';
UPDATE `locales_page_text` SET `Text_loc6`='(3) Floon: Arakkoa. Perdió muchas monedas de oro con Sal\'salabim. Sal\'salabim mató a hombre-pájaro por no pagar. Ahora el fantasma de hombre-pájaro camina por la parte noroeste del Vertedero de Huesos.' WHERE `entry`='2978';
UPDATE `locales_page_text` SET `Text_loc6`='La raíz de Olemba es una raíz bastante común que se encuentra en el bosque de Terokkar. Busca raíces respingonas cerca de los árboles de Olemba, que conforman la mayor parte del bosque.$B$BLa baya de la marisma crece únicamente en la marisma de Zangar. Solo se encuentra en el Bastión Ango\'rosh, situado en la región del noroeste del pantano.¡Cuidado con los ogros que protegen con fervor las preciadas bayas!$B$BLa fronda de Telaari es, quizás, la hierba más fácil de conseguir de esta lista. Encontrarás las hojas al sur de Garadar, en la cuenca Telaari. Crecen cerca del agua.$B$BLa última hierba de la lista crece solo en las montañas Filospada, situadas al norte de la marisma de Zangar. El espinazo de dragón crece en los extremos de las formaciones de rocas dentadas que protegen los cañones. Sabrás de qué hablo cuando veas Filospada por primera vez. Las formaciones de rocas parecen una hilera de dientes afilados y puntiagudos tras otra.' WHERE `entry`='2979';
UPDATE `locales_page_text` SET `Text_loc6`='Tus obras ya están causando gran malestar a la Legión, $N. Mis exploradores acaban de informarme que la Legión ha recurrido al envío de comunicados a través de mensajería. ¡Hemos logrado con éxito cortar las tropas de su asentamiento!$B$BLo próximo que te pido es más de lo que le pediría a un soldado raso.$B$BEl correo de la Legión viaja desde las Pozas de Aggonar al Campamento Forja: Mageddon, al norte de aquí. Debes interceptar el correo en Mageddon, hacerte con él, y volver a mí con la misiva de la Legión.' WHERE `entry`='2981';
UPDATE `locales_page_text` SET `Text_loc6`='Sirviente. Estos orcos están empezando a ser más que una pequeña molestia. Quiero personalmente ver atacar a ti y a nuestras fuerzas las defensas del Portal Oscuro dirigir toda su energía para arrasar Thrallmar. Es un orden, Arazzius' WHERE `entry`='2982';
UPDATE `locales_page_text` SET `Text_loc6`='Para otorgar al bebedor de la poción la visión tintada para ver a los muertos, se deben reunir ciertos componentes materiales.$B$BPrimero, un globo ocular de gronn montés del Nagrand occidental, cerca de los campamentos Forja malditos de la Legión Ardiente, al sur de la Colina Mazo de Guerra.$B$BTambién se encuentran en el Nagrand occidental los vientorrocs superiores. Solo servirá un ejemplar perfecto.$B$BPor último, tanto en el Nagrand oriental como occidental se encuentra grasa de uñagrieta anciano.$B$B¡Reúne estos objetos y tráemelos! ¡Date prisa!$B$BNitrin' WHERE `entry`='2983';
UPDATE `locales_page_text` SET `Text_loc6`='<Después de una serie de páginas de aburrida \"ciencia\" que carecen de sentido alguno, hay una nota garabateada en la parte inferior de la última página....>$B$BPerdona, jefe. Parece que arrancará correr muy pronto la Tormenta Abisal. Es probable que esto alcance la mayor parte de Terrallende.$B$BNo estoy 100% seguro, pero creo que el problema tiene relacción con lo que los Elfos de Sangre están haciendo en las Forjas de Maná.$B$B¡Lo bueno es que estamos construyendo aquel cohete!$B$B-- Nicuproso$B$BP.D. ¿Cree que podría conseguir una plaza en el cohete? ¡Seguro que necesitará un Abisólogo mientras viaja través del Vacío Abisal!' WHERE `entry`='2984';
UPDATE `locales_page_text` SET `Text_loc6`='General,$B$BEstamos defendiendo nuestra posición en los Peldaños del Destino, pero la Legión envía una oleada tras otra para asaltar nuestras filas. Deben de tener una zona de preparación cercana que nuestros exploradores aún no han ubicado. Aunque las fuerzas de la Alianza están de nuestra parte, me temo que la Legión, superior en número, invadirá nuestras defensas. Envíe toda la ayuda que pueda, no fuera que el Portal cayera y nos quedáramos atrapados en este mundo de pesadilla para siempre.$B$BTeniente General Orion' WHERE `entry`='2985';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando Nahni subió deslizándose, el experimentado guerrero la miró duramente. \"Supongo que estás aquí para cobrar la recompensa por matar a aquellos múrlocs?\"$B$BSus ojos se desviaron a la deslumbrante colada que se ceñía al costado del guerrero. \"Eso depende de cuál sea la recompensa, Marcus.\" Se retorció el pelo con coquetería, fingiendo no advertir la forma incómoda en que se movía el guerrero en su presencia. \"Puede que no la quiera.\"' WHERE `entry`='2986';
UPDATE `locales_page_text` SET `Text_loc6`='Marcus dio un paso hacia ella, encrespándose con una mezcla de temor e ira. \"¡La recompensa no es negociable!\" Calló durante un momento mientras se recomponía y se apretujó contra su diminuto cuerpo. Sus labios se encontraron con vehemencia, fundiendo su hechizo de armadura gélida en un torrente de vapor sofocante.$B$B\"Entonces, ¿hay otro paso en esta misión?\", bromeó, mientras sus ojos brillaban de agitación.$B$B<Esto sigue por varios cientos de páginas más, sin adelantar el argumento.>' WHERE `entry`='2987';
UPDATE `locales_page_text` SET `Text_loc6`='Este extraño objeto parece ser algún tipo de dispositivo de ingeniería, aunque no se parece a nada que hayas visto antes.$B$BA juzgar por su apariencia, se diría que, sea lo que sea, ya se ha utilizado.$B$BY, a juzgar por la distribución de los cadáveres que hay por aquí, se supone que, sea lo que sea, se utilizó con efectos mortíferos.' WHERE `entry`='2988';
UPDATE `locales_page_text` SET `Text_loc6`='Comandante,$B$BLa Clave se debe entregar a Zuluhed. Permanecerá bajo su custodia hasta nuevo aviso.$B$B-Lord Illidan Tempestira' WHERE `entry`='2989';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><H1 align=\"center\">LAMENTO DE LOS ALTONATO </H1><BR/><IMG src=\"Interface\\FlavorImages\\BloodElfLogo-small\"/></BODY></HTML>' WHERE `entry`='2990';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Traducción libre...</P><BR/><P>Por la luz, por la luz del sol,</P><P>Hijos de la sangre,</P><P>Nuestros enemigos se abren paso.</P><P>Hijos de la sangre,</P><P>Por la luz,</P><P>Débiles hijos de la sangre,</P><P>Se abren paso.</P><P>Oh, hijos de la sangre,</P><P>Por la luz del sol.</P><P>Débiles hijos de la sangre,</P><P>Se abren paso.</P><P>Oh, hijos de la sangre,</P><P>Por la luz del sol,</P><P>El sol.</P></BODY></HTML>' WHERE `entry`='2991';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Versión thalassiana...</P><BR/><P>Anar\'alah, anar\'alah belore,</P><P>Sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah,</P><P>Shindu sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah belore.</P><P>Shindu sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah belore,</P><P>Belore.</P></BODY></HTML>' WHERE `entry`='2992';
UPDATE `locales_page_text` SET `Text_loc6`='Estimado canciller,$B$BLa tarea de llevar a los gigantes de Sombraluna al redil está en tus hábiles manos. Usa tu encanto de elfo de sangre para que vean las cosas a mi manera. Si eso no funciona, extermina hasta al último de ellos...$B$BLord Illidan Tempestira' WHERE `entry`='2993';
UPDATE `locales_page_text` SET `Text_loc6`='En todo, los paladines deben reflejar la Luz, que complementa nuestra fuerza. Esforzarse para ser divino para uno de nuestra especie no significa que nos esforcemos por la bondad ? nos esforzamos para ser buenos en todas las acciones.' WHERE `entry`='2994';
UPDATE `locales_page_text` SET `Text_loc6`='Aunque se te llame para golpear al mal en estos duros tiempos, siempre debes recordar que es ayudar a los demás lo que te distinguirá de los demás ciudadanos. Compasión, paciencia, valentía ? estas cosas significan tanto para un paladín como la fuerza en la batalla.$B$BApréndetelo bien y nunca lo olvides.' WHERE `entry`='2995';
UPDATE `locales_page_text` SET `Text_loc6`='LIBERAR CUMPLIMIENTO DE RESPONSABILIDAD.$B$BEL PILOTO DE PRUEBAS ES TOTALMENTE CONSCIENTE Y RECONOCE QUE HAY RIESGO DE LESIÓN, MUTILACIÓN O MUERTE EN FORMA DE: PULVERIZACIÓN, ELECTROCUCIÓN, TRAUMATISMO CONTUSO EN ÓRGANOS VITALES, CONSUMO POR FAUNA INDÍGENA O PARADOJAS DEL TIEMPO/ESPACIO DURANTE EL USO DEL CEFIRIUM CAPACITORIUM (EL \"DISPOSITIVO\"). POR LA PRESENTE, EL PILOTO DE PRUEBAS ELIGE ENTRAR VOLUNTARIAMENTE EN EL DISPOSITIVO Y ASUMIR TODOS LOS RIESGOS ANTERIORMENTE MENCIONADOS.$B$BEL PILOTO DE PRUEBAS ACUERDA RENUNCIAR, LIBERAR Y DISPENSAR DE CUALQUIERA RECLAMACIÓN POR LESIÓN O DAÑO CONTRA TALLY PILLAZAS Y SU FAMILIA, Y, EN CASO DE SU MUERTE, ACUERDA DONAR SUS RESTOS AL ESTADO DE PILLAZAS PARA SU ESTUDIO.$B$Bfirmado,$BX:___$N' WHERE `entry`='2996';
UPDATE `locales_page_text` SET `Text_loc6`='Saludos, erudito Antonivich:$B$BEspero que esta carta te halle en Thrallmar y que tus estudios sean de provecho. Me basta con mis artilugios en Rompeloma para mis propios experimentos, aunque a veces desearía estar en Entrañas con herramientas y sirvientes...$B$BAh, los lamentos de un cadáver.$B$BCumplidos aparte, tengo noticias: la sangre que $N recogió de los orcos viles muestra una concentración de contaminaciones demoníacas. Es casi idéntica a la de los orcos viles originarios de Azeroth... la de Mannoroth.' WHERE `entry`='2997';
UPDATE `locales_page_text` SET `Text_loc6`='$N,$B$BLa Clave de Condenación es sin duda un conjuro poderoso. Antigua y caótica, la clave ha sido responsable de muchas tragedias en la historia de nuestros mundos. Es decir, no es algo único de Draenor.$B$BEl símbolo ardiente era la marca de Kael\'Thas. Su conocimiento de las palabras antiguas explica mucho.$B$BTu descubrimiento ha despertado a los naaru. A\'dal ha solicitado tu presencia en la Ciudad de Shattrath. Si decides venir, búscame en el Bancal de la Luz.$B$BHumildemente tuyo,$B$BKhadgar' WHERE `entry`='2998';
UPDATE `locales_page_text` SET `Text_loc6`='Antonivich,$B$BEn primer lugar, déjame decirte que tu $Gúltimo:última; agente, $N, ha realizado sus obligaciones muy satisfactoriamente. He tomado posesión de su cuerpo y ahora escribo esto de su propia mano. Me produce tal diversión, que espero tener algún día la fortuna de poseerte, ¡oh, poderoso boticario!$B$BBueno, el informe según me lo has pedido: La Ciudadela del Fuego Infernal se ve frecuentada por un demonio más fuerte que ninguno que haya conocido. Supongo que Mannoroth ? el señor del foso de fama de la Tercera Gran Guerra ? poseía la misma fuerza, pero pocos la tienen. Sin embargo, siento que hay un aire de confinamiento en este demonio. No me sorprendería que actuara contra su voluntad, o quizás esté encarcelado, o esté bajo el dominio de un poder aún mayor.$B$BEl tiempo lo dirá...' WHERE `entry`='2999';
UPDATE `locales_page_text` SET `Text_loc6`='El tiempo lo dirá, pues también he notado un giro del destino en los muros de la Ciudadela del Fuego Infernal.$B$BVosotros, los inmigrantes de Azeroth ? ese horrible, verde y suntuoso lugar donde seres como yo hallan un infierno de Luz y vida ? veréis la cara de ese demonio. Y veréis que los males que hay en la Ciudadela del Fuego Infernal son solo la punta ? si me permites la metáfora ? de un enorme iceberg.$B$B¡Qué gloriosas batallas se avecinan! ¡Si pudiera estar allí para probar el dolor y el miedo que habrá!$B$BBueno, acabaré mi prosaica escritura, estimado herbolario: puede que tu cuerpo esté muerto, Antonivich, pero tu alma sigue muy intacta, y yo la tendré y me daré un festín con ella con un deleite sublime el día que bajes la guardia... ¡y encuentre el camino a tu interior!$B$BEsperaré hasta el Cuarto Día del Juicio si hace falta, pero... ¡serás mío!$B$BBuenas noches,$B-D' WHERE `entry`='3000';
UPDATE `locales_page_text` SET `Text_loc6`='<En esta página tan quemada no quedan palabras reconocibles.>' WHERE `entry`='3001';
UPDATE `locales_page_text` SET `Text_loc6`='Después de que te marchases de Sotoeterno, continué mi estudio del Libro del Cuervo y he descubierto información que te ayudará a derrotar al dios Cuervo.$B$BAl liberar a estos espíritus, se han vinculado a tí, así que espero que se ofrezcan a ayudarte en tu lucha contra el dios Cuervo.$B$BCuando desencadenes a los espíritus por primera vez, estarán inactivos, como lo han estado durante siglos. Usando tus hechizos de Sanación en el tiempo, podrás imbuirlos de energía, devolviéndolos a la vida y logrando su ayuda en el combate. Cuando la magia se desvanezca, regresaran a su sueño.$B$BA continuación tienes una recopilación mis notas sobre cada espíritu pájaro:' WHERE `entry`='3002';
UPDATE `locales_page_text` SET `Text_loc6`='El espíritu del falcón$B$BEl espíritu del falcón está imbuido con velocidad y agilidad increíbles, muy reconocido entre los arakkoa por su gran habilidad para la caza.$B$BSi energizas este espíritu con un hechizo de Sanación en el tiempo te prestará parte de su velocidad al invocar hechizos y en el combate cuerpo a cuerpo.' WHERE `entry`='3003';
UPDATE `locales_page_text` SET `Text_loc6`='El espíritu águila$B$BLa tradición arakkoa mantiene que el espíritu águila no era tan veloz como su hermano el falcón, pero que poseía la mayor destreza en la caza. El espíritu águila usará sus habilidades contra tus enemigos si usas un hechizo de Sanación en el tiempo para despertarlo de su sueño.$B$B.' WHERE `entry`='3004';
UPDATE `locales_page_text` SET `Text_loc6`='El espíritu del halcón$B$BLa tablilla habla maravillas del halcón, como si en algún momento hubiese estado aliado con el Cuervo. Un antiguo símbolo arakkoa de venganza, el espíritu a menudo era invocado por víctimas de actos malvados. Usar un hechizo de Sanación en el tiempo para despertar al espíritu te permitirá compartir su poder, dañando a aquellos que te hagan daño.' WHERE `entry`='3005';
UPDATE `locales_page_text` SET `Text_loc6`='En el nombre de nuestro gran maestro... ¡Por la gloria de Illidan!$BMor\'ghor,$B$BTe envío a este desdichado como siervo de Illidan. Aunque que no tiene futuro como Caballero de la Muerte, quizá sus habilidades sean de tu agrado. Somételo. Moldéalo y conviértelo en un guerrero de los Illidari.$B$BY si no está a la altura... si muestra síntomas de debilidad... deséchalo. ¡A las fosas!$B$BLord Teron Sanguino' WHERE `entry`='3006';
UPDATE `locales_page_text` SET `Text_loc6`='No desesperes. Tu ogro especial está ahí en alguna parte.' WHERE `entry`='3007';
UPDATE `locales_page_text` SET `Text_loc6`='Deja tu espada, tu maza y tu lanza. Los crsitales nos han dicho que le demos una oportunidad a la paz.' WHERE `entry`='3008';
UPDATE `locales_page_text` SET `Text_loc6`='¡No tienes temer con una cabeza llena de la cerveza!' WHERE `entry`='3009';
UPDATE `locales_page_text` SET `Text_loc6`='¡Dos cabezas son mejor que una! ¡Así siempre tendrás un amigo!' WHERE `entry`='3010';
UPDATE `locales_page_text` SET `Text_loc6`='La sabiduría llega de muchas formas, pero siempre fluye de los cristales.' WHERE `entry`='3011';
UPDATE `locales_page_text` SET `Text_loc6`='¡Piensa en positivo! Una panza grande para uno es un par de manijas del amor para otro.' WHERE `entry`='3012';
UPDATE `locales_page_text` SET `Text_loc6`='Mañana encontrarás a alguien especial, así que recuerda: consigue más amigos con bondad que peleas hasta la muerte.' WHERE `entry`='3013';
UPDATE `locales_page_text` SET `Text_loc6`='Números de la suerte: 13 9 17 4' WHERE `entry`='3014';
UPDATE `locales_page_text` SET `Text_loc6`='Tu pasión por el baile hace sonreír a todo el mundo.' WHERE `entry`='3015';
UPDATE `locales_page_text` SET `Text_loc6`='Eres especial, no te preocupes por lo que tus amigos, parientes, colegas, cualquier extraño, o tu conciencia te diga.' WHERE `entry`='3016';
UPDATE `locales_page_text` SET `Text_loc6`='Cuando todo falla, es momento de recapacitar.' WHERE `entry`='3017';
UPDATE `locales_page_text` SET `Text_loc6`='En estado de ebriedad me siento como una deidad.' WHERE `entry`='3018';
UPDATE `locales_page_text` SET `Text_loc6`='Aviso: Los despojos no son seguros para el consumo humano.' WHERE `entry`='3019';
UPDATE `locales_page_text` SET `Text_loc6`='¡Has sido Sgelegido:elegida; por la grán mano de metal de los cielos! Ahora manos a la obra.' WHERE `entry`='3020';
UPDATE `locales_page_text` SET `Text_loc6`='Deja dormir la gran mentira.' WHERE `entry`='3021';
UPDATE `locales_page_text` SET `Text_loc6`='El Comité de Explotación de Ogri\'la te ha traído este mensaje. Hecho en Gadgetzan.' WHERE `entry`='3022';
UPDATE `locales_page_text` SET `Text_loc6`='El significado de la vida es ... (El resto de este mensaje se ha comido por accidente).' WHERE `entry`='3023';
UPDATE `locales_page_text` SET `Text_loc6`='Lo siento, no has tenido suerte esta vez, por favor inténtalo de nuevo.' WHERE `entry`='3024';
UPDATE `locales_page_text` SET `Text_loc6`='Si haces un enemigo hoy, mátalo rapidamente.' WHERE `entry`='3025';
UPDATE `locales_page_text` SET `Text_loc6`='Busca una meta más alta en la vida. Así evitarás a los seres inferiores.' WHERE `entry`='3027';
UPDATE `locales_page_text` SET `Text_loc6`='En nombre de nuestro gran maestro... ¡Por la gloria de Illidan!$B$BLos componentes comunes del cañón de almas se consiguen en distintas profesiones. Los ingenieros y los mineros te podrán abastecer de las barras de acero vil, el núcleo de poder de korio, y el marco de adamantita.$B$BPara la esencia arcana inmaculada, tendrás que viajar al Bosque de Terokkar. En medio del bosque, sobre la avanzada de la Horda del Bastión Rompepedras, encontrarás el hogar de Sar\'this, un arakkoa herético. El área se conoce como Lago Jorune.$B$BHabla con Sar\'this y dile lo que buscas. Acuérdate de mencionar que vienes de mi parte, pues me debe un favor o dos.$B$BHaz lo que Sar\'this te diga y tendrás la esencia.$B$BBalthas' WHERE `entry`='3028';
UPDATE `locales_page_text` SET `Text_loc6`='La última vez que estuve en las salas, el rey Garra Ikiss llevaba la punta de Terokk consigo como símbolo de su autoridad. Probablemente aún la lleve para convencer a sus seguidores de que es la reencarnación de Terokk.$B$BMi hermano, Tejeoscuro Syth, llevaba la máscara de Terokk cuando dirigía los rituales Sethekk.$B$BGuardan la saga de Terokk en un círculo de runas en la gran cámara abovedada fuera de la del propio rey Garra Ikiss.' WHERE `entry`='3029';
UPDATE `locales_page_text` SET `Text_loc6`='Es imposible que la influencia de Mannoroth llegue hasta Terrallende, la bestia lleva muerta años, pero me temo que otro señor del foso sea el protagonista. Un señor del foso con el poder de Mannoroth... uno capaz de corromper a los orcos indígenas.$B$BContinuaré con mis estudios de la sangre Foso Sangrante pero te insto a que sigas la pista. Y, me temo que te lleve a las profundidades de la Ciudadela del Fuego Infernal.$B$BUn saludo erudito,$BBoticario Albreck' WHERE `entry`='3030';
UPDATE `locales_page_text` SET `Text_loc6`='¡Escucha, Budd!$B$BEsta es la última vez que te mando algo hasta que me pagues todo lo que debes.$B$B¡No quiero más excusas!$B$BGriftah' WHERE `entry`='3031';
UPDATE `locales_page_text` SET `Text_loc6`='Garn:$B$BNo tengo que recordarte la importancia de tu misión: proteger tu precioso cargamento a toda costa. Tal y como prometí, el éxito y la discreción se verán muy bien recompensados.$B$BHaz que tus \"socios\" mantengan el piquito cerrado, haz caso a la tripulación y todo saldrá bien. Cuento con que tendrás éxito.' WHERE `entry`='3032';
UPDATE `locales_page_text` SET `Text_loc6`='Te he enviado un ejército de tamaño considerable, anciano, pero cuidado cuando realices cualquier movimiento. Lo primero es detener el avance de la Alianza en nuestro territorio. Algunos de los humanos más aventureros han comenzado a construir una gran edificación en la frontera con Los Baldíos.$B$BOcúpate de que sepan que no son bienvenidos más allá de las fronteras de su despreciable Theramore. Por último, una advertencia, anciano: no informes de tus planes a los ogros de Murohelecho. Esos brutos son incapaces de guardar un mínimo de discreción.$B$BAgashem.' WHERE `entry`='3033';
UPDATE `locales_page_text` SET `Text_loc6`='Uno de los seres más terroríficos que he visto en toda mi vida es Tethyr. Solo he visto una vez a esa bestia, ¡y me considero afortunado de haber salido vivo!$B$BEra una noche oscura y de tormenta. A través de las agitadas olas del mar y de la lluvia torrencial, apenas se distinguía la luz del faro de Theramore. Hicimos todo lo posible para navegar hacia él. ¡De repente, esa bestia salió de entre las olas delante de nuestro barco!' WHERE `entry`='3034';
UPDATE `locales_page_text` SET `Text_loc6`='La terrorífica y enorme boca de esa bestia se abrió, lanzando agua helada a nuestra embarcación. Aquellas terribles mandíbulas detuvieron la proa de nuestro barco, arrancándola del casco. Los marineros y el cargamento se vieron violentamente precipitados al mar a medida que los restos del barco desaparecían entre las olas.$B$BLo último que recuerdo es que estaba volando desde el casco roto, golpeándome contra las oscuras olas. El mundo se volvió oscuro y creí que ahí quedaría mi último aliento. Cuando desperté en la enfermería de Theramore, supe que alguien debía contar nuestra historia.$B' WHERE `entry`='3035';
UPDATE `locales_page_text` SET `Text_loc6`='La terrorífica y enorme boca de esa bestia se abrió, lanzando agua helada a nuestra embarcación. Aquellas terribles mandíbulas detuvieron la proa de nuestro barco, arrancándola del casco. Los marineros y el cargamento se vieron violentamente precipitados al mar a medida que los restos del barco desaparecían entre las olas.$B$BLo último que recuerdo es que estaba volando desde el casco roto, golpeándome contra las oscuras olas. El mundo se volvió oscuro y creí que ahí quedaría mi último aliento. Cuando desperté en la enfermería de Theramore, supe que alguien debía contar nuestra historia.' WHERE `entry`='3036';
UPDATE `locales_page_text` SET `Text_loc6`='Hijos: Joel y Gina. Esposa: Suzannah' WHERE `entry`='3037';
UPDATE `locales_page_text` SET `Text_loc6`='No permanezcas ante mi tumba llorando,$Bno estoy allí, no estoy descansando.$B$BEstoy en los miles de vientos que los árboles mueven,$Ben Rasganorte sobre la brillante nieve.$B$BSoy la suave lluvia que cae gentilmente$Ben los campos de Páramos de Poniente.$B$BEstoy en la calma matutina,$Bde la selva de Tuercespina.$B$BEstoy en el redoble magistral,$Bde las pezuñas en Nagrand al pisar.$B$BEn las cálidas y brillantes estrellas me muevo,$Bmientras soñando sobre Darnassus me elevo.$B$BEstoy en los pájaros cantores$By en las cosas que inspiran pasiones.$B$BNo permanezcas ante mi tumba llorando,$Bno estoy allí, no estoy descansando.' WHERE `entry`='3039';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Bien, $N, aquí tienes una lista de las cosas que voy a necesitar para nuestra nueva súper secreta... eh... súper arma. De momento, llamémosla \"Rayo reductor prototípico ultraavanzado de Ingeniosa\".</P><BR/><P>¡Hazme caso, va a ser más revolucionario que esa antigua máquina para aumentar el tamaño del mundo!</P><BR/><P>Da igual. No le des muchas vueltas de momento. ¡Tú tráeme lo que te he pedido y nos haremos de oro!</P></BODY></HTML>' WHERE `entry`='3040';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><H1 align=\"center\">Mis herramientas</H1><BR/><P>Lo primero es lo primero... necesito mis herramientas. ¡Ningunas otras valdrían para un proyecto tan complicado como este!</P><BR/><P>Cuando tuvimos que correr para escapar de la Estación de Bombeo y salvar la vida -esa es otra historia- no tuve tiempo de recogerlas. ¡Vamos a ver! ¡Es lógico! Me iba la vida en ello. Lo que sí recuerdo es que las dejé bajo la Estación de Bombeo, al este.</P></BODY></HTML>' WHERE `entry`='3041';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><H1 align=\"center\">Condensador sobrecargado</H1><BR/><P>Cualquier ingeniero que se precie debería poder fabricarte unos cuantos en un periquete si tú no sabes. Qué demonios, te los haría yo, pero ahora no tengo mis herramientas, ¿recuerdas?</P><BR/><P>Así que haz lo imposible para traerme uno. Sin él, nuestro proyecto no va a llegar a ninguna parte. Si es necesario, ¡dirígete a los continentes del sur y busca en la casa de subastas!</P></BODY></HTML>' WHERE `entry`='3042';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><H1 align=\"center\">Un puñado de polvo de Rocanar</H1><BR/><P>Esto último puede parecer algo complicado, pero es necesario dar forma a la lente del rayo solo con el polvo más fino. Así que hay que usar el del mejor de los elementales.</P><BR/><P>Rocanar debería valer. Lo han visto en algún lugar al sureste del Tramo Helado entre Unu\'pe y el Zigurat de los Lamentos. Ve y destroza su exterior helado para alcanzar su núcleo de roca y tráeme un puñado.</P></BODY></HTML>' WHERE `entry`='3043';
UPDATE `locales_page_text` SET `Text_loc6`='Primera guardia, siete campanas$BCedric hallado en el armario del ron de nuevo. Disciplinado.' WHERE `entry`='3044';
UPDATE `locales_page_text` SET `Text_loc6`='Primera guardia, ocho campanadas$BCambio de guardia. Todo en orden.' WHERE `entry`='3045';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia intermedia, una campanada$BCedric hallado intentando acceder al mueble del vino del capitán. Disciplinado.' WHERE `entry`='3046';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia intermedia, dos campanadas$BCedric trepa las jarcias, se enreda y cae. Atendido por el cirujano del barco. Enviado abajo a dormir la mona.' WHERE `entry`='3047';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia intermedia, cuatro campanadas$BBotella de ron hallada en los pantalones de Cedric. Disciplinado.' WHERE `entry`='3049';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia intermedia, cinco campanadas$BRopa de Cedric hallada. Cedric hallado en otro lado. Cedric disciplinado.' WHERE `entry`='3050';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia intermedia, seis campanadas$BCedric canta a voz en grito. Despierta al capitán. El capitán disciplina a Cedric.' WHERE `entry`='3051';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia intermedia, ocho campanadas$BCambio de guardia. Todo tranquilo. Dos hombres enviados a buscar a Cedric.' WHERE `entry`='3052';
UPDATE `locales_page_text` SET `Text_loc6`='Guardia de la mañana, una campanada$BFuego en los aposentos del Capitán. Todos en pie. Lanzamos la pólvora por la borda.' WHERE `entry`='3053';
UPDATE `locales_page_text` SET `Text_loc6`='Hace mucho tiempo que se cuenta la historia de El Cuerno de Furia Elemental alrededor de las fogatas de los campamentos de los taunka. Generaciones de guerreros han crecido escuchando estas historias y han buscado el escondite del cuerno en vano. Como los otros, el poderoso Pezuña Tempestuosa prometió buscar el cuerno y dejó atrás su pueblo y a su familia.$B$BHaciendo frente con valentía al penetrante frío y a los feroces elementales de Pico Filoescarcha, Pezuña Tempestuosa ascendió la montaña solo. Una vez en la cumbre, fue recibido por el propio Viento del Norte. Sabiendo que el viento no le entregaría el cuerno sin luchar, el valiente guerrero lo desafió.' WHERE `entry`='3054';
UPDATE `locales_page_text` SET `Text_loc6`='Durante cinco días, Pezuña Tempestuosa luchó contra el viento. Al amanecer del sexto día, desterró el viento de Filoescarcha y se hizo con el cuerno. Maltrecho y herido, Pezuña Tempestuosa inició su viaje de vuelta hacia su poblado.$B$BEl Viento del Norte reunió gran cantidad de elementales inferiores y le atacó cuando casi había alcanzado su destino. El guerrero debilitado no pudo defenderse del ataque y el Viento del Norte se vengó. Rompió el cuerno en dos, encerró cada piezas en un adorno lustroso y se las dio a dos poderosos cabecillas górloc como prueba de su autoridad.$B$B<El texto original acaba de forma abrupta, pero alguien, quizás la Domadora del viento Barah parece haber hecho un apéndice más abajo.>$B$BTal y como les había ordenado el Viento del Norte, los cabecillas górloc pasaron estas preciosas reliquias a sus hijos y nietos. Hoy, las mitades del cuerno permanecen a los cabecillas Gorgogluglú y Borbogluglú.' WHERE `entry`='3055';
UPDATE `locales_page_text` SET `Text_loc6`='SE BUSCA: HEMET NESINGWARY$B$BPOR CRIMENES CONTRA LA NATURALEZA!$B$BInterpretación del artista de \"El Extinguidor\" en su hábitat natural: el fuego eterno del submundo.$B$BUbicación del último holocausto natural: Nagrand, Terrallende$B$BTambién conocido como: El Gran Cazador, El Carnicero de las Tierras Inhóspitas, El Destripador de Tuercespina, El Viejo de la Muerte, Hemet, Nessie, Viejo Sucio, Viejo Sucio D, Padre de Recompensas, El Extinguidor.' WHERE `entry`='3057';
UPDATE `locales_page_text` SET `Text_loc6`='ct,t' WHERE `entry`='3058';
UPDATE `locales_page_text` SET `Text_loc6`='Con el éxito de Kel\'Thuzad en Lordaeron, el Rey Exánime hizo los preparativos finales para su asalto a la civilización humana.Dejando su energía en una serie de artefactos portátiles llamados calderos de peste, Ner\'zhul ordenó a Kel\'Thuzad el transporte de los calderos a Lordaeron, donde serían ocultados en varios pueblos del culto controlados.' WHERE `entry`='3063';
UPDATE `locales_page_text` SET `Text_loc6`='Los calderos, protegidos por los miembros del culto leal, actuarian entonces como generadores de plaga, filtrando la plaga a través de granjas fuera de sospecha y ciudades del norte de Lordaeron.' WHERE `entry`='3064';
UPDATE `locales_page_text` SET `Text_loc6`='Leales siervos de los señores elementales, HA LLEGADO NUESTRA HORA.$B$BHemos pasado demasiado tiempo en las sombras, ofreciendo nuestro tiempo, sirviendo a nuestros maestros, esperando el final que ya conocíamos. ¡Ahora que las llamas de aplacamiento de Ragnaros iluminan la noche, tenemos en nuestro poder las herramientas para instigar a una guerra y un caos de proporciones catastróficas!$B$BEl Señor del Fuego está encarcelado en nuestro mundo. No tiene todo su poder, aunque su fuerza sigue siendo formidable. Con un oponente igual de formidable en este reino, el enfrentamiento resultaría en la gran guerra elemental que traerá el fin que tanto hemos esperado.' WHERE `entry`='3065';
UPDATE `locales_page_text` SET `Text_loc6`='Hay un gran señor de la Escarcha al servicio de Neptulon conocido como Ahune. En estos mismos momentos reúne sus fuerzas para hacerle la guerra a Ragnaros. Todo lo que necesita es una entrada a nuestro mundo; una entrada que nosotros proporcionaremos. Contamos con los aliados necesarios. Tenemos el lugar. Tenemos la fuerza y la voluntad.$B$BLas negociaciones finales con nuestros nuevos y leales aliados e invitados tendrá lugar en un lugar seguro al noroeste de nuestra primera situación en Vallefresno.$B$BAntes de que este \"festival\" de masas ignorantes llegue a su fin, Ahune se enfrentará a Ragnaros en la sombra de Roca Negra. El mundo se estremecerá con la liberación de estas fuerzas.' WHERE `entry`='3066';
UPDATE `locales_page_text` SET `Text_loc6`='Garomosh,$B$BTe envío a un representante de El Etereum. El embajador Duyheen me cuenta que hay más como él que se unirían a la causa. Tantea el terreno e intenta encontrar un uso para él.$B$BSi resulta de ayuda, quizá considere aceptar a más de los suyos.$B$B¡No seas idiota, quema esta carta en cuanto la hayas leído!' WHERE `entry`='3067';
UPDATE `locales_page_text` SET `Text_loc6`='¡Lok\'tar ogar! Victoria o muerte: estas son las palabras que me unen a la Horda. Ya que es la verdad más fundamental y sagrada de cualquier guerrero de la Horda.$B$BEntrego mi carne y sangre libremente al Jefe de Guerra. Soy el instrumento del deseo de mi Jefe de Guerra. Soy un arma del mando de mi Jefe de Guerra.$B$BA partir de este momento y hasta el final de mis días y el momento de mi muerte: ¡Por la Horda!' WHERE `entry`='3068';
UPDATE `locales_page_text` SET `Text_loc6`='$N,$B$BSi estás leyendo esta carta significa que sigues con vida y de una pieza: o al menos aún tienes tus ojos.$B$BDebo disculparme por el secretismo. Agmar exige que se lea todo el correo antes de su entrega: hay demasiados traidores y ladrones aquí como bien indica el cartel de Se Busca. Hay temas delicados que la nueva guardia podría malinterpretar con facilidad.$B$BPara un soldado de la Horda, perder es absoluto. Perder significa la muerte y no hay negociación o interpretación posible con la muerte. Solo puedes esperar que su muerte fuese honorable.' WHERE `entry`='3069';
UPDATE `locales_page_text` SET `Text_loc6`='Pero la victoria... La victoria puede significar muchas cosas. Como seguramente has notado, los Kor\'kron tienen la fuerza de su lado. El Jefe de Guerra ha enviado a su guardia de élite a asegurarse de que se hacen con la victoria en Rasganorte. Estos, junto contigo y otros héroes, están empujando al Rey Exánime y sus fuerzas hacia el inevitable final. Con cada reto que superas, estamos un paso más cerca de limpiar nuestro mundo de Arthas y de la Plaga.$B$BHe ahí el dilema. Ya que, verás, nuestras fuerzas en Rasganorte trabajan bajo los auspicios del joven Grito Infernal. Cada victoria refuerza la moral de las fuerzas de la Horda allí y se contagia por todo Azeroth.' WHERE `entry`='3070';
UPDATE `locales_page_text` SET `Text_loc6`='Es poco afortunado, entonces, que Grito Infernal use unas tácticas tan salvajes. A medida que la victoria se aproxima, Grito Infernal gana más justificación para sus métodos, lo cual nos acerca a la vez a un lugar al que no hemos ido en muchos años: un lugar oscuro.$B$BHe enviado a mi hijo a dirigir nuestras fuerzas a la Puerta de Cólera. Sé que luchará con honor y espero que las fuerzas se den cuenta de su coraje y tenacidad y los imiten. Está en mi corazón y en mi fuerza en un lugar donde no puedo estar... Serás mis ojos y mis oídos. Juntos, lo lograremos.$B$BSangre y rayos... Que nuestra llegada cause ambos.$B$BColmillosauro' WHERE `entry`='3071';
UPDATE `locales_page_text` SET `Text_loc6`='Todos nuestros esfuerzos darán su fruto. Nuestros maestros se levantarán el uno contra el otro en una gloriosa batalla que partirá Azeroth en dos.$B$BEstamos viviendo el final de los tiempos, hermanos. No descuidéis vuestras tareas. Aguantad. ¡Pronto nos alzaremos triunfantes!$B' WHERE `entry`='3072';
UPDATE `locales_page_text` SET `Text_loc6`='Mi reina,$B$BHemos descubierto que el Vuelo Azul había liberado la intersección de líneas Ley situada en los Jardines Reposo Lunar. Sus fuerzas usaron una aguja de flujo para destruir la piedra que la tapaba, pero en vez de guiar el poder de vuelta a El Nexo, usaron una serie de focos para transportarlo con torpeza y aumentar el poder bajo el suelo del Santuario de Dragones Azur.$B$BHuelga decir que esto ha tenido consecuencias tremendas allí donde la magia se ha filtrado a través de la tierra. Los Ancestros del bosque, los Kalu\'ak, y otras criaturas de la zona se han visto afectadas negativamente. Por suerte, $N, $Gel:la; $r que se encuentra ante ti, desempeñó un papel decisivo en su investigación y control.$B$BEn mi opinión, $N podría ser de gran ayuda en la lucha contra los problemas que afectan al Reposo del Dragón. Te pido humildemente que consideres valerte de su ayuda; a mí me resultó de incalculable valor.$B$BSiempre con respeto,$B$BModera$BArchimaga y miembro de Los Seis' WHERE `entry`='3073';
UPDATE `locales_page_text` SET `Text_loc6`='Mi reina,$B$BHemos descubierto que el Vuelo Azul había liberado la intersección de líneas Ley situada en los Jardines Reposo. Sus fuerzas usaron una aguja de flujo para destruir la piedra que la tapaba, pero en vez de guiar el poder de vuelta a El Nexo, usaron una serie de focos para transportarlo con torpeza y aumentar el poder bajo el suelo del Santuario de Dragones Azur.$B$BHuelga decir que esto tiene consecuencias tremendas cada vez que la magia se filtra a través de la tierra. Los Ancestros del bosque, los Kalu\'ak, y otras criaturas de la zona se han visto afectadas negativamente. $N, $Gel:la; $r que se encuentra ante ti, jugó un papel decisivo en su investigación y control.$B$BEn mi opinión, $N podría ser de gran ayuda en la lucha contra los problemas que afectan al Reposo del Dragón. Te pido humildemente que consideres valerte de su ayuda, que a mí me resultó bastante útil.$B$BSiempre con respeto,$B$BAethas Atracasol$BArchimago y miembro de Los Seis' WHERE `entry`='3074';
UPDATE `locales_page_text` SET `Text_loc6`='¡Hola!$B$BNo se me da muy bien escribir cartas, así que la supervisora me ha ayudado a escribirte esta. Solo quería darte las GRACIAS porque eres $Gun héroe:una heroína; y quizá algún día yo también pueda salvar a gente como tú.$B$BBueno, ahora me tengo que ir a jugar, así que ¡GRACIAS DE NUEVO por ser tan $Gbueno:buena; y fuerte, además de super $Gguapo:guapa;!$B$B--Tu admirador secreto' WHERE `entry`='3075';
UPDATE `locales_page_text` SET `Text_loc6`='<En esta página tan quemada no quedan palabras reconocibles.>' WHERE `entry`='3076';
UPDATE `locales_page_text` SET `Text_loc6`='Ah\'tusa reptó en silencio a través del gigantesco túnel subterráneo, mirando inquieta por encima de su hombro por si venía el veloz tranvía. \"¿Dónde está...?\" farfulló enfadada, deteniéndose repentinamente cuando algo se movió detrás de ella. Sus ojos se entrecerraron peligrosamente mientras su daga se deslizaba fuera de su funda.$B$BUna voz ronca resonó en la cámara vacía, \"Creía que a los pícaros les gustaba estar detrás de su objetivo.\" Se dio la vuelta rápidamente, dirigiendo su hoja contra un impenetrable escudo de fuerza. \"Y yo creía que los paladines eran castos\", respondió con una mueca de confianza.' WHERE `entry`='3077';
UPDATE `locales_page_text` SET `Text_loc6`='Inclinándose hacia delante, contó en voz alta hasta que la burbuja invisible desapareció con un ruidito audible. \"¿Doce segundos? Sé que tienes habilidades que duran más.\"$B$BUnos dedos huesudos trabajaban sin esfuerzo bajo su armadura, desatando su peto y dejándolo expuesto al frío aire. \"No te preocupes, mi dulce Marcus, esta noche no vamos a acabar... solo estoy empezando.\" Él rodeó con sus brazos lo que quedaba de su cintura, obteniendo una carcajada de excitación al mordisquear las vértebras de su cuello.$B$B<El resto del libro tiene varias secciones que están muy desgastadas por el uso.>' WHERE `entry`='3078';
UPDATE `locales_page_text` SET `Text_loc6`='El diario de la alta general Abbendis' WHERE `entry`='3079';
UPDATE `locales_page_text` SET `Text_loc6`='La voz susurró, \"Ven a mí\". Desde el principio sabía que era la Luz Sagrada que me hablaba en sueños. ¡Por fin! Tras tantos años de oraciones y buenas acciones, llega la limpieza de la plaga de no-muertos de la faz de Azeroth. Tras todos los errores y las resurrecciones.$B$B¡Por fin!' WHERE `entry`='3080';
UPDATE `locales_page_text` SET `Text_loc6`='Ha vuelto a ocurrir. \"Ven a mí...\", ordenó la Luz.$B$BMe levanté con escalofríos a pesar de que no hacía frío en mis aposentos. ¡Voy a redoblar mis esfuerzos! Mañana le diré al sumo abad que quiero más oraciones. ¡Debo tomar todas las medidas posibles!$B$BLa Luz Sagrada ha tomado nota de nuestra buena labor. ¡Puedo sentirlo!' WHERE `entry`='3081';
UPDATE `locales_page_text` SET `Text_loc6`='¡Esta vez no estaba soñando! Era muy real, durante más de un minuto, en mitad del día cálido y soleado, me salía un aliento de vaho y frío. Uno de los sacerdotes se dio cuenta y se arrodilló para rezar.$B$BSin embargo, nadie más oyó la voz. Al menos, este testigo demuestra que no me estoy volviendo loca. Quizás podría pedirle a Landgren que rece por ello.$B$BMañana haré que Jordan y Calle tengan en cuenta hasta el mínimo detalle a la hora de reclutar. Tenemos un montón de infieles que solo desean acabar con los no-muertos. ¡Eso no basta!' WHERE `entry`='3082';
UPDATE `locales_page_text` SET `Text_loc6`='Tabla de contenidos$B$BCapítulo 1 ? Los muertos y tú$B$BCapítulo 2 ? Mantener una figura de necrófago: siete ejercicios sencillos$B$BCapítulo 3 ? No dejar que entre el frío: elegir un ataúd con aislamiento$B$BCapítulo 4 ? Garantías del cementerio: 50 señales inequívocas de amor$B$B<El libro está en perfectas condiciones y parece que nadie lo ha leído>' WHERE `entry`='3083';
UPDATE `locales_page_text` SET `Text_loc6`='Dejaré aquí a la mayor parte de la Cruzada para que continúe realizando operaciones contra los no-muertos en nuestro patio trasero. Me imagino que cuando acabemos lo que hemos empezado, la mayoría volverán a sus hogares para vivir en paz.$B$BEso suena bien. Siempre hemos sido la sal de la tierra, alzándonos para recuperar nuestros hogares de manos de la corrupción de la Plaga, para devolverle a Lordaeron su antigua gloria. Para volver a una época anterior a la Plaga, anterior a Arthas y al regicidio... anterior al Rey Exánime.' WHERE `entry`='3084';
UPDATE `locales_page_text` SET `Text_loc6`='Entre los hombres corre el rumor de que pronto llegará el día en que todo cambiará para la Cruzada Escarlata. El obispo Calle le ha puesto un nombre: lo llama el Alba Carmesí.$B$BReflexionaré acerca de esto, aunque en mis huesos puedo sentir su verdad. Rezo para que nos traiga más alegrías que penas.' WHERE `entry`='3085';
UPDATE `locales_page_text` SET `Text_loc6`='La Luz ha vuelto a hablar con gran urgencia. Salí del sueño con una sensación de impaciencia. No defraudaré. No debe haber más retrasos. ¡Debemos ponernos en marcha!$B$BQué gran coincidencia (tuve que controlarme para no mostrar signos de alivio): esta tarde el capitán Shely nos procuró un buen número de barcos para el viaje. O quizás no fuera una coincidencia... La Luz Sagrada expresa su voluntad de maneras que no somos capaces de comprender.$B$BTomaré la Locura del Pecador como bandera. Creo que su nombre es más que adecuado.' WHERE `entry`='3086';
UPDATE `locales_page_text` SET `Text_loc6`='Ahora sé por qué la Luz me ha instado a ponerme en camino. A altas horas de la noche apareció en el aire sobre nosotros una necrópolis de la Plaga ¡y de ella empezaron a manar los esbirros del infierno!$B$BUna nueva raza de caballeros de la Muerte lidera el asalto. El número de muertos ya es catastrófico. Con una Plaga capaz de atacarnos a su voluntad desde arriba y en cualquier lugar parece imposible encontrar un modo apropiado de defensa.$B$BMe temo que nuestros planes de expedición a Rasganorte han terminado incluso antes de empezar.' WHERE `entry`='3087';
UPDATE `locales_page_text` SET `Text_loc6`='Me han informado de que Vega del Amparo y sus aledaños ya han comenzado a reunir a la multitud. El propio alto comandante Galvar Purasangre pretende liderarlos para salvarnos. Sus esfuerzos serán en vano.$B$BTengo que asegurarme de que mis mejores mensajeros atraviesen las filas enemigas y le avisen de que no lo haga. Vega del Amparo debe prepararse para atrincherarse y reunir al resto de la Cruzada.$B$BCon algo de suerte, llegarán allí antes de que anochezca.' WHERE `entry`='3088';
UPDATE `locales_page_text` SET `Text_loc6`='No hemos tenido noticias de ninguno de mis mensajeros esta mañana. Está claro que ninguno consiguió llegar a Vega del Amparo. Las Tierras de la Peste están perdidas. Purasangre llegará con su ejército y los aniquilarán allí mismo.$B$BEsta tarde he tenido una visión de la Luz. Vi que destruían todo lo que hemos construido aquí. El mensaje era claro: me decía que me llevara a los supervivientes más fieles y abandonase a la Cruzada a su suerte.$B$BMás tarde, Landgren me dijo que él había tenido la misma visión. No llego a comprender la razón por la que la Luz nos habrá hecho emprender una acción tan deshonrosa, pero no tengo el derecho de preguntar... Estoy aquí para obedecer y obedeceré.' WHERE `entry`='3089';
UPDATE `locales_page_text` SET `Text_loc6`='Observé, temblando, Nuevo Avalon, sospecho que por última vez. Nuestro destino está en Rasganorte. Por alguna razón me invaden malos presentimientos. La misión frente a mí debiera liberarme de estas preocupaciones. Las borraré de mi mente.$B$BPuede que, si hay suerte, el alto comandante Purasangre, de algún modo, soporte y prepare a los supervivientes. ¡Yo soy cobarde, un perro que huye con el rabo entre las piernas!' WHERE `entry`='3090';
UPDATE `locales_page_text` SET `Text_loc6`='Me dicen que el viaje va a durar dos meses. Los otros barcos no pueden alcanzar la velocidad de la Locura. Llevan a bordo la mayoría de nuestras fuerzas y equipamiento y no son mucho más que simples cargueros a vela, pero llegarán a su destino.$B$BNo es que me apetezca mucho el viaje, pero por la Luz aguantaré los mareos. Solo debo evitar que el resto se dé cuenta.' WHERE `entry`='3091';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">El diario de la alta general Abbendis</H1><BR/><IMG src=\"Interface\FlavorImages\ScarletCrusadeLogo\"/></BODY></HTML>' WHERE `entry`='3092';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>La voz susurró, \"Ven a mí\". Desde el principio sabía que era la Luz Sagrada que me hablaba en sueños. ¡Por fin! Tras tantos años de oraciones y buenas acciones, llega la limpieza de la plaga de no-muertos de la faz de Azeroth. Tras todos los errores y las resurrecciones.</P><BR/><P>¡Por fin!</P></BODY></HTML>' WHERE `entry`='3093';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Ha vuelto a ocurrir. \"Ven a mí...\", ordenó la Luz.</P><BR/><P>Me desperté helada, a pesar de que no hacía frío en mis aposentos. ¡Voy a redoblar mis esfuerzos! Mañana le diré al sumo abad que quiero más oraciones. ¡Nada de quedarse a medias!</P><BR/><P>La Luz Sagrada ha tomado nota de nuestra buena labor. ¡Puedo sentirlo!</P></BODY></HTML>' WHERE `entry`='3094';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>¡Esta vez no estaba soñando! Era muy real, durante más de un minuto, en mitad del día cálido y soleado, me salía un aliento de vaho y frío. Uno de los sacerdotes se dio cuenta y se arrodilló para rezar.</P><BR/><P>Sin embargo, nadie más oyó la voz. Al menos, este testigo demuestra que no me estoy volviendo loca. Quizás podría pedirle a Landgren que rece por ello.</P><BR/><P>Mañana haré que Jordan y Calle tengan en cuenta hasta el mínimo detalle a la hora de reclutar. Tenemos un montón de infieles que solo desean acabar con los no-muertos. ¡Eso no basta!</P></BODY></HTML>' WHERE `entry`='3095';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Tanto el comandante como el obispo se mostraron receptivos, aunque tampoco les quedaba otro remedio. El obispo Calle en particular parecía muy entusiasmado. Habló de una nueva cruzada y juró que descubriría a los débiles de fe que se ocultaban entre nuestras líneas.</P><BR/><P>Le dije que se lo tomase con calma. La idea no es destrozar la Cruzada. No obstante, me gusta la idea de formar un cuadro de élite con los más leales para llevar a cabo la voluntad de la Luz en Rasganorte. Temo que su amistad con LeCraft le está corrompiendo poco a poco. Aunque ambos tienen sus ideas.</P></BODY></HTML>' WHERE `entry`='3096';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Dejaré aquí a la mayor parte de la Cruzada para que continúe realizando operaciones contra los no-muertos en nuestro patio trasero. Me imagino que cuando acabemos lo que hemos empezado, la mayoría volverán a sus hogares para vivir en paz.</P><BR/><P>Eso suena bien. Siempre hemos sido la sal de la tierra, alzándonos para recuperar nuestros hogares de manos de la corrupción de la Plaga, para devolverle a Lordaeron su antigua gloria. Para volver a una época anterior a la Plaga, anterior a Arthas y al regicidio... anterior al Rey Exánime.</P></BODY></HTML>' WHERE `entry`='3097';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Entre los hombres corre el rumor de que pronto llegará el día en que todo cambiará para la Cruzada Escarlata. El obispo Calle le ha puesto un nombre: lo llama el Alba Carmesí.</P><BR/><P>Reflexionaré acerca de esto, aunque en mis huesos puedo sentir su verdad. Rezo para que nos traiga más alegrías que penas.</P></BODY></HTML>' WHERE `entry`='3098';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>La Luz ha vuelto a hablar con gran urgencia. Salí del sueño con una sensación de impaciencia. No defraudaré. No debe haber más retrasos. ¡Debemos ponernos en marcha!</P><BR/><P>Qué gran coincidencia (tuve que controlarme para no mostrar signos de alivio): esta tarde el capitán Shely nos procuró un buen número de barcos para el viaje. O quizás no fuera una coincidencia... La Luz Sagrada expresa su voluntad de maneras que no somos capaces de comprender.</P><BR/><P>Tomaré la Locura del Pecador como bandera. Creo que su nombre es más que adecuado.</P></BODY></HTML>' WHERE `entry`='3099';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Ahora sé por qué la Luz me ha instado a ponerme en camino. A altas horas de la noche apareció en el aire sobre nosotros una necrópolis de la Plaga ¡y de ella empezaron a manar los esbirros del infierno! </P><BR/><P>Una nueva raza de caballeros de la Muerte lidera el asalto. El número de muertos ya es catastrófico. Con una Plaga capaz de atacarnos a su voluntad desde arriba y en cualquier lugar parece imposible encontrar un modo apropiado de defensa.</P><BR/><P>Me temo que nuestros planes de expedición a Rasganorte han terminado incluso antes de empezar.</P></BODY></HTML>' WHERE `entry`='3100';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Me han informado de que Vega del Amparo y sus aledaños ya han comenzado a reunir a la multitud. El propio alto comandante Galvar Purasangre pretende liderarlos para salvarnos. Sus esfuerzos serán en vano.</P><BR/><P>Tengo que asegurarme de que mis mejores mensajeros atraviesen las filas enemigas y le avisen de que no lo haga. Vega del Amparo debe prepararse para atrincherarse y reunir al resto de la Cruzada.</P><BR/><P>Con algo de suerte, llegarán allí antes de que anochezca.</P></BODY></HTML>' WHERE `entry`='3101';
UPDATE `locales_page_text` SET `Text_loc6`='En honor al Rey Varian Wrynn$B$Bconocido como LO\'GOSH el \"Lobo fantasmal\" para algunos$B$BSu milagroso retorno tras largos años de privaciones en el exilio ha encendido una nueva llama en el espíritu y coraje de la Alianza.$B$BMarcharemos con atrevimiento en estas tierras peligrosas abrazando su valor y tenacidad en nuestros corazones.$B$B- Alto señor Bolvar Fordragon' WHERE `entry`='3102';
UPDATE `locales_page_text` SET `Text_loc6`='Día 1: La basura humana parece está completamente feliz ya que no ha notado mi presencia, tal y como estaba planeado. Están listos para el bombardeo y solo es cuestión de tiempo que registre su posición.$B$BDía 2: Registro completado: una tarea trivial para un tipo tan preparado como yo. No entiendo por qué me ha tocado esta tarea: deberían habérsela asignado a J-Y C. Ese ridículo acento que tiene me parece justificarlo. $B$BDía 3: El aire de la primera bocanada se está viciando. Quizá debería salir a la superficie un momento para tomar otra bocanada de aire en unos días. Aún me aburro hasta llorar: ¿cuándo llegará la maldita señal?$B$B< Sigue echando pestes... >' WHERE `entry`='3103';
UPDATE `locales_page_text` SET `Text_loc6`='Día 12: Ah, qué alivio. La segunda bocanada de aire me ha sabido a gloria. Para mi sorpresa, aún no he recibido la señal y juraría que uno de los cangrejos intenta tamborilear con mi dedo gordo del pie. $B$B< Se queja de otras cosas... >' WHERE `entry`='3104';
UPDATE `locales_page_text` SET `Text_loc6`='Día 36: Al final se me ha acabado la tercera bocanada de aire y las venas de mi codo izquierdo se ha vuelto a llenar de burbujas mientras subía. Ahora el tamborileo de los cangrejos es una compañía constante, una percusión de fondo en mi pequeño rincón del infierno particular. Está claro, esos desdichados se han olvidado de mí. Si J-Y estuviese aquí abajo, ¡seguro que no se habrían olvidado de él! ¡Nunca dejarían a Hans o a Sylvia atrás! ¿Por qué a mí? $B$B< Las entradas terminan siendo garabatos descorazonados... >' WHERE `entry`='3105';
UPDATE `locales_page_text` SET `Text_loc6`='1. Si no te gusta la potencia de un veneno, duplica los ingredientes o redúcelos a la mitad.$B2. Si un veneno no funciona de la forma que deseabas, es porque estás más equivocado de lo que creías estar.$B3. Prototipo, Prototipo, Prototipo (los gnomos hacen grandes temas de prueba).$B4. Los venenos deben ser rápidos en empezar a tener efecto y difíciles de detenerlos.$B5. Simples ingredientes trabajan juntos para crear una interesante complejidad.' WHERE `entry`='3106';
UPDATE `locales_page_text` SET `Text_loc6`='Al principio la escritura es pollo ininteligible, pero puedes distinguir referencias en color rosa..' WHERE `entry`='3107';
UPDATE `locales_page_text` SET `Text_loc6`='Mi padre me ha vuelto a enviar a cuidar a la vieja Bess. Creo que le gusta ver cómo me ataca. No es culpa mía si no le gusto como los otros... Solo porque una vez me comí una manzana que era para ella. Aún no sé ni cómo se enteró de eso.' WHERE `entry`='3109';
UPDATE `locales_page_text` SET `Text_loc6`='A la alta general Abbendis$BNuevo Avalon, Tierras Escarlata$B$BAlta general Abbendis:$B$BEspero que, cuando reciba esta carta, goce de buena salud. Me llamo Dansel Adams, cazador de profesión y cruzado Escarlata de sangre. Le escribo esta carta para informarle de que he presenciado un milagro.' WHERE `entry`='3110';
UPDATE `locales_page_text` SET `Text_loc6`='Durante una práctica de purga de paganos en el Bosque de la Peste, mi grupo y yo nos vimos interrumpidos por un chirrido ensordecedor que provenía del cielo. Cuando miramos hacia arriba hacia Naxxramas, nos quedamos con la boca hasta el suelo. Así se lo digo, Alta General, ¡fue una visión inverosímil! La terrorífica ciudadela se movía. Al principio, despacio, y luego con un rugido tormentoso... ¡paf! ¡Desapareció!$B$BEso es, Alta general, ¡Naxxramas ha desaparecido! ¡Au revoir! ¿No podría ser sino un milagro de la Luz? ¡Yo digo que no!' WHERE `entry`='3111';
UPDATE `locales_page_text` SET `Text_loc6`='Que así sea, que los impuros y sucios bárbaros sean juzgados y sentenciados los siguientes. ¡Bendita sea la Luz!$B$BPor favor, Alta General, permitid que nuestros hermanos y hermanas conozcan este milagro.$B$BQue la Luz os guarde por toda la eternidad para que podáis extender la buena nueva hasta el fin de los tiempos.$B$BRespetuosamente,$B$BD.Adams$B\"El cierre\"' WHERE `entry`='3112';
UPDATE `locales_page_text` SET `Text_loc6`='1. Dejarse crecer una barba bien desaliñada, pues disimula las imperfecciones faciales de cara a la Luz. $B$B2. Purificarse las manos en leche de yegua adulta para que estén suaves y flexibles en el momento de llevar la ofrenda. $B$B3. Bañarse en los ríos, permitiendo que los gloriosos dones naturales de la Luz limpien la piel.' WHERE `entry`='3113';
UPDATE `locales_page_text` SET `Text_loc6`='4. Cambiarse con regularidad los hábitos sagrados, para que la apariencia y los olores no ofendan a los que sirven a la Luz. $B$B5. Mantener en todo momento una postura erguida, para no cansar ni curvar la columna con posturas descuidadas. $B$B<La lista se extiende a varias páginas más de menudencias>' WHERE `entry`='3114';
UPDATE `locales_page_text` SET `Text_loc6`='Desde el despacho del sumo abad Landgren:$BPuede parecer que en tiempos difíciles de conflicto y confusión uno debe preocuparse por sí mismo, protegiendo a su familia y seres queridos, excluyendo todo lo demás. Pero ese es un sentimiento falso... Sin la luz de esperanza que constituye la Iglesia, la desesperación podría inundar rápidamente hasta el último poro de la esencia de tu familia. Todos los estómagos llenos, pies calientes y sonrisas de felicidad que los bienes mundanos pueden comprar no salvarán a tu familia del vacío sigiloso que provocará el incumplimiento de tu responsabilidad hacia la Luz.' WHERE `entry`='3115';
UPDATE `locales_page_text` SET `Text_loc6`='Da a la Iglesia. Las penurias soportadas durante la falta de bienes mundanos actúan como forja para el alma, quemando las impurezas y elevando a tu ser a otro plano. No te avergüences por los rugidos de tu estómago hambriento, por el mordisco de una piedra punzante sobre tu pie desnudo o por el frío del viento invernal. Estos no son sino medios por los que la Luz comunica su inspiración al alma. Estos sacrificios aseguran que las alabanzas hechas a la Luz que tanto inspiran al culto se mantengan adecuadamente. $B$BAbstente de los placeres de la carne, y acércate más a la Luz.' WHERE `entry`='3116';
UPDATE `locales_page_text` SET `Text_loc6`='El tema de la indulgencia es algo peliagudo, pero hay veces en la vida en que se convierte en un mal necesario. Una falta de juicio, una palabra cruel pronunciada sin reflexión, un puñetazo o patada dados con rabia... Todas estas cosas son defectos del hombre mortal, y la Iglesia debe ser consciente de ello. $B$BSin embargo, tal consciencia requiere un precio. Para pecados de palabras crueles, un mero puñado de plata limpiará el alma. Para pecados de fuerza física, un puñado de oro expurgará los defectos de la carne mortal. Otro tipo de pecados requerirán una confesión completa y una contribución algo más generosa.' WHERE `entry`='3117';
UPDATE `locales_page_text` SET `Text_loc6`='Golpear: $B Tortazo : 2 monedas de oro$B Golpe bajo: 3 monedas de oro$B Golpe en el pecho: 1 moneda de oro y 45 monedas de plata$B $B<Continúa una larga lista detallada de precios y otros pecados>' WHERE `entry`='3118';
UPDATE `locales_page_text` SET `Text_loc6`='Purificar el cuerpo con esmero. Purificar el alma con oración. Purificar a tus enemigos con fuego. El dolor no es un enemigo, sino un aliado que abrazar, tener cerca y otorgar tanto a los incrédulos como a los creyentes. $B$BLa resistencia a los caminos de la Luz es un signo de impureza, y debe extirparse de la carne de la comunidad.' WHERE `entry`='3119';
UPDATE `locales_page_text` SET `Text_loc6`='Alta General:$B$BLos ejércitos de Vega del Amparo y Tirisfal están a menos de un día de viaje de Nuevo Avalon. Cabalgamos con la Luz brillando sobre nuestra espalda y con el viento bajo nuestros talones. Pronto, ¡la Plaga deberá lidiar con el poder pleno de la Cruzada Escarlata!$B$BQue la Luz le bendiga.$B$BAlto comandante Galvar Purasangre' WHERE `entry`='3120';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><H1 align=\"center\">El Camino de la Redención</H1><BR/><IMG src=\"Interface\\FlavorImages\\ScarletCrusadeLogo\"/></BODY></HTML>' WHERE `entry`='3121';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>La voz susurró, \"Ven a mí\". Desde el principio sabía que era la Luz Sagrada que me hablaba en sueños. ¡Por fin! Tras tantos años de oraciones y buenas acciones, llega la limpieza de la plaga de no-muertos de la faz de Azeroth. Tras todos los errores y las resurrecciones.</P><BR/><P>¡Por fin!</P></BODY></HTML>' WHERE `entry`='3122';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Ha vuelto a ocurrir. \"Ven a mí...\", ordenó la Luz.</P><BR/><P>Esta vez me levanté con escalofríos a pesar de que no hacía frío en mis aposentos. ¡Voy a redoblar mis esfuerzos! Mañana le diré al sumo abad que quiero más oraciones. ¡Debo tomar todas las medidas posibles!</P><BR/><P>La Luz Sagrada ha tomado nota de nuestra buena labor. ¡Puedo sentirlo!</P></BODY></HTML>' WHERE `entry`='3123';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>¡Esta vez no estaba soñando! Era muy real, durante más de un minuto, en mitad del día cálido y soleado, me salía un aliento de vaho y frío. Uno de los sacerdotes se dio cuenta y se arrodilló para rezar.</P><BR/><P>Sin embargo, nadie más oyó la voz. Al menos, este testigo demuestra que no me estoy volviendo loca. Quizás podría pedirle a Landgren que rece por ello.</P><BR/><P>Mañana haré que Jordan y Calle tengan en cuenta hasta el mínimo detalle a la hora de reclutar. Tenemos un montón de infieles que solo desean acabar con los no-muertos. ¡Eso no basta!</P></BODY></HTML>' WHERE `entry`='3124';
UPDATE `locales_page_text` SET `Text_loc6`='El comandante y el obispo se mostraron muy receptivos, aunque tampoco les quedaba otro remedio. En particular, el obispo Calle parecía muy entusiasmado. Habló de una nueva cruzada y juró que descubriría a los débiles de fe de entre nuestras filas.$B$BLe dije que se lo tomara con calma. No vamos a destruir la Cruzada sino a formar un cuadro de élite de los más fieles para hacer la voluntad de la Luz en Rasganorte. Temo que la amistad del hombre con LeCraft le está corrompiendo. Aunque cada uno tiene sus ideas.$B$BPero sigo reflexionando sobre lo que contó Calle acerca de una nueva cruzada. Creo que cuando esta nueva fuerza esté formada, le daré un nuevo nombre. Tiene que evocar nuestra nueva misión, y al mismo tiempo estar vinculado al pasado. El abad y yo rezaremos por esto.' WHERE `entry`='3125';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Nuestras oraciones han sido escuchadas. El sumo abad se ha sentido particularmente conmovido por la fuerza, la claridad y la determinación de la voz de la Luz.</P><BR/><P>Los que vamos hacia el norte, los más fieles, seremos conocidos como El Embate Escarlata.</P><BR/><P>¡Seremos un embate sobre Rasganorte! La enfermedad de la Plaga amenaza con rebosar en la cima del mundo para ahogarnos a todos. ¡Ha llegado el momento de llevar la lucha hasta la puerta del mismísimo Rey Exánime!</P></BODY></HTML>' WHERE `entry`='3126';
UPDATE `locales_page_text` SET `Text_loc6`='Observé, temblando, Nuevo Avalon, sospecho que por última vez. Nuestro destino está en Rasganorte. Por alguna razón me invaden malos presentimientos. La misión frente a mí debiera liberarme de estas preocupaciones. Las borraré de mi mente.$B$BEl Alba Carmesí está sobre nosotros.' WHERE `entry`='3127';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Observé Nuevo Avalon con inquietud, temiendo que fuese la última vez. El destino de nuestra causa se encuentra en Rasganorte. Por alguna razón, me llena una sensación de mal agüero. La misión que se avecina debería hacerme olvidar mis preocupaciones. Las borraré de mi mente.</P><BR/><P>El Alba Carmesí está con nosotros. </P></BODY></HTML>' WHERE `entry`='3129';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>No hemos tenido noticias de ninguno de mis mensajeros esta mañana. Está claro que ninguno consiguió llegar a Vega del Amparo. Las Tierras de la Peste están perdidas. Purasangre llegará con su ejército y los aniquilarán allí mismo.</P><BR/><P>Esta tarde he tenido una visión de la Luz. Vi que destruían todo lo que hemos construido aquí. El mensaje era claro: me decía que me llevara a los supervivientes más fieles y abandonase a la Cruzada a su suerte.</P></BODY></HTML>' WHERE `entry`='3143';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><BR/><P>Más tarde, Landgren me dijo que él había tenido la misma visión. No llego a comprender la razón por la que la Luz nos habrá hecho emprender una acción tan deshonrosa, pero no tengo el derecho de preguntar... Estoy aquí para obedecer y obedeceré.</P><BR/><P>Observé, temblando, Nuevo Avalon, sospecho que por última vez. Nuestro destino está en Rasganorte. Por alguna razón me invaden malos presentimientos. La misión frente a mí debiera liberarme de estas preocupaciones. Las borraré de mi mente.</P></BODY></HTML>' WHERE `entry`='3144';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Puede que, si hay suerte, el alto comandante Purasangre, de algún modo, soporte y prepare a los supervivientes. ¡Yo soy cobarde, un perro que huye con el rabo entre las piernas!</P><BR/><P>Me dicen que el viaje va a durar dos meses. Los otros barcos no pueden alcanzar la velocidad de la Locura. Llevan a bordo la mayoría de nuestras fuerzas y equipamiento y no son mucho más que simples cargueros a vela, pero llegarán a su destino.</P><BR/><P>No es que me apetezca mucho el viaje, pero por la Luz aguantaré los mareos. Solo debo evitar que el resto se dé cuenta.</P></BODY></HTML>' WHERE `entry`='3145';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Llevo tiempo sin escribir. Lo único que puedo hacer es ponerme en pie e intentar no vomitar. Los hombres empiezan a preguntarse por qué estoy encerrada en mis habitaciones la mayor parte del tiempo. No sería bueno para la moral que me vieran así.</P><BR/><P>Ya no debe de quedar mucho. Rezo para que así sea. Ya llevamos seis semanas y cada día parece que el tiempo empeora. Solo puedo esperar que en Rasganorte no haga tan mal tiempo. No estoy hecha para el frío.</P><BR/><P>La Luz Sagrada lleva en silencio mucho tiempo.</P></BODY></HTML>' WHERE `entry`='3146';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>¡Nos atacaron de repente! ¡Unos gigantes en un enorme barco vikingo salieron de la bruma como si fueran fantasmas! Eran más silenciosos que un muerto.</P><BR/><P>Perdí un barco y todos los hombres que se encontraban en él. Luchamos con gran valor y habilidad, si consideramos el hecho de que recibimos el entrenamiento más básico en combate naval.</P><BR/><P>Después, los gritos de nuestros hombres capturados por los gigantes retumbaron por el agua. Después de un rato volvió el silencio. El obispo Calle condujo las oraciones de los hombres.</P></BODY></HTML>' WHERE `entry`='3147';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Esta mañana me desperté haciendo cambios a mis mapas. La Luz guiaba mi mano, mostrándome el lugar exacto al que debemos ir.</P><BR/><P>¡Estamos cerca!</P></BODY></HTML>' WHERE `entry`='3148';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>¡Recalada!</P><BR/><P>Al llegar con mi bote de remos a la playa, coloqué nuestro estandarte y la Luz Sagrada me invadió, hablando desde mi interior. Hoy es el Alba Carmesí... el gran día que tanto hemos esperado. Este es el lugar para Nueva Vega del Amparo. Ya no somos la Cruzada Escarlata, ¡ahora somos El Embate Escarlata!</P><BR/><P>¡Seremos un embate sobre Rasganorte! La enfermedad de la Plaga amenaza con rebosar en la cima del mundo para ahogarnos a todos. ¡Ha llegado el momento de llevar la lucha hasta la puerta del mismísimo Rey Exánime!</P></BODY></HTML>' WHERE `entry`='3149';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Ha pasado casi un mes y la construcción avanza a pasos agigantados. He estado demasiado ocupada para escribir. Mis exploradores me dicen que estas tierras están llenas de dragones y otras extrañas bestias. Viviremos aislados hasta que estemos preparados.</P><BR/><P>En mitad del oficio de hoy el sumo abad anunció que pronto recibiríamos una visita, una que la Luz nos enviaba para liderarnos a la victoria.</P><BR/><P>No sé qué pensar. ¿Por qué no me lo dijo la Luz? ¿Acaso no he servido con lealtad? ¿Ahora un extraño me va a reemplazar?</P></BODY></HTML>' WHERE `entry`='3150';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>¡Anoche el almirante Barean Viento Oeste se presentó ante mi puerta! Todos creían que había muerto en estas costas hace años.</P><BR/><P>No parecía suficientemente mayor y, a pesar de ello, supe que era él. Afirmó que era el único superviviente de su flota y que sobrevivió por gracia divina de la Luz.</P></BODY></HTML>' WHERE `entry`='3151';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Nos quedamos hablando hasta el alba. Me aseguró que no tenía ninguna intención de quedarse con mi puesto, pero que la Luz le había ordenado que viajara por el Cementerio de Dragones para servirme de consejero y comandante, solo en título. Afirmó que ocurriría un cambio importante en Rasganorte. La Alianza y la Horda iban a acudir en masa en respuesta a una enorme plaga que el Rey Exánime estaba a punto de lanzar sobre ellos.</P></BODY></HTML>' WHERE `entry`='3152';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Los hombres tienen en alta estima al gran Almirante, especialmente el Sumo abad Landgren y el obispo Calle. Al parecer, la Luz Sagrada le susurró una nueva bendición al almirante mientras dormía, y este se la comunicó a Landgren. Algunos de los hombres se han convertido al sacerdocio y ahora reciben el nombre de \"sacerdotes cuervo\".</P><BR/><P>El único que parece no estar impresionado es Jordan. Supongo que es lógico. Probablemente sienta que su puesto esté en peligro si el mío también lo está.</P></BODY></HTML>' WHERE `entry`='3153';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Hay algo que no va bien. No pondría la mano en el fuego, pero sé que no puedo fiarme completamente del almirante. En realidad no ha hecho nada malo, ¡al contrario! Sin embargo, tengo que fiarme de mis instintos.</P><BR/><P>Rezaré para comprender. La Luz nos trajo al almirante para que nos guiara hacia la victoria y no soy quién para poner en duda sus decisiones. Seguiré obedeciendo. Tengo fe.</P></BODY></HTML>' WHERE `entry`='3154';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Han pasado otros dos meses. Hemos hecho grandes avances en la construcción de Nueva Vega del Amparo. El muro y los barracones casi están terminados. Los hombres de Kaleiki trabajan de manera milagrosa.</P><BR/><P>No he sentido la inspiración necesaria para escribir. He estado evitando plasmar aquí mis pensamientos por alguna razón. La Luz no me ha visitado últimamente, aunque el almirante me asegura que no hay nada de lo que preocuparse.</P></BODY></HTML>' WHERE `entry`='3155';
UPDATE `locales_page_text` SET `Text_loc6`='<HTML><BODY><P>Mis hombres me informan de que un pequeño grupo de Renegados ha establecido un campamento a lo largo de la ladera al norte. Parece que al otro lado de la colina la Alianza ha comenzado a construir una base mucho mayor.</P><BR/><P>El almirante dice que deberíamos dejarlos tranquilos. Hay otras fuerzas de la Horda reunidas al oeste que vendrían en su ayuda en caso de que atacáramos. No me da buena espina, pero creo que su razonamiento es lógico.</P></BODY></HTML>' WHERE `entry`='3156';
UPDATE `locales_page_text` SET `Text_loc6`='Skor\'zad miró nerviosamente por un lado de la columna. Tal y como había planeado, la vio flotando allí, a solas en sus habitaciones. ¿Había tomado la decisión correcta al visitar la habitación privada del alma en pena a esas horas de la noche? De ser descubierto, los nigromantes le volverían a meter bajo tierra sin duda alguna. Peor aún, ni siquiera sabía si sus sentimientos eran correspondidos y además, Madame Sidnari era famosa por su escasa paciencia.' WHERE `entry`='3157';
UPDATE `locales_page_text` SET `Text_loc6`='Las pocas dudas que tenía el zombi quedaron rápidamente descartadas cuando pudo contemplar su belleza. Sus inconfundibles rasgos élficos, un recordatorio de su forma en su vida anterior, ahora eran aún más hermosos gracias a las heridas supurantes y costras que salpicaban su rostro. Su piel morada, casi traslucida estaba cubierta de forma frugal por vendajes que abrazaban su desaseada figura. La fría carne se unía con lo efímero en su torso sin piernas que descansaba sobre una esfera azulada de energía, flotando a varios centímetros sobre el frío suelo de granito. Skor\'zad se relajó un poco, y se limpió el sudor y el icor que bañaban su prominente frente.' WHERE `entry`='3158';
UPDATE `locales_page_text` SET `Text_loc6`='Aparentemente sin notar su presencia, ella se deslizó hasta la única ventana en la habitación y miró al exterior, al campo de batalla. Ante ella se extendía la desolación. Seis horas atrás, los gritos agudos de los niños humanos rasgaban el aire, pero ahora... solo quedaba el dulce silencio de la muerte.' WHERE `entry`='3159';
UPDATE `locales_page_text` SET `Text_loc6`='El viento comenzó a jugar con su pelo, agitando sus mechones parecidos a gusanos alrededor de las cuencas vacías de sus ojos. Skor\'zad, de pronto consciente de que estaba inclinándose hacia delante lleno de lujuria, volvió a esconderse tras la columna, aterrorizado ante la idea de que la plañidera oscura pudiera haberle visto. Ella sí le había visto, pero no tenía nada que temer.' WHERE `entry`='3160';
UPDATE `locales_page_text` SET `Text_loc6`='\"Skor\'zad,\" dijo ella. \"Te estaba esperando. Hoy has luchado bien.\" El alma en pena lanzó una mirada tímida sobre su putrefacto hombro derecho, permitiendo que sus vendajes se soltasen levemente. \"Es hora de que... recojas tu recompensa.\"' WHERE `entry`='3161';
UPDATE `locales_page_text` SET `Text_loc6`='<Las siguientes páginas parecen estar pegadas unas con otras.>' WHERE `entry`='3162';
UPDATE `locales_page_text` SET `Text_loc6`='Tartek el Iracundo. Una de las abominaciones más poderosas que jamás haya creado la Plaga. La ensambló el exánime Amnennar el Gélido para ayudar en la creación de una base en Zahúrda Rajacieno. Tartek, sin ayuda de nadie, acabó con más de 1.200 jabaespines antes de caer. Su sacrificio le proporcionó cadáveres suficientes a Amnennar para crear rápidamente un ejército de jabaespines no-muertos propio, consiguiendo así el primer gran puesto destacado de la Plaga en Kalimdor. Parte de los restos de Tartek fueron reanimados y utilizados para crear a Glotón, el lugarteniente actual de Amnennar.' WHERE `entry`='3163';
UPDATE `locales_page_text` SET `Text_loc6`='Obrahiim, el Viajante. Fue un arquitecto brillante, vital en el diseño de Naxxramas y la mente genial detrás de la creación de Acherus. Ascendió rápidamente de peón esqueleto de rango bajo hasta llegar a ser uno de los consejeros de más confianza de Kel\'Thuzad.$B$BSiempre fue un estudioso; viajaba por el mundo en busca de conocimiento, recibiendo la inspiración para sus primeras obras de la arquitectura de los nerubianos y la magia de los brujos orcos. Fue visto por última vez hace varios años cuando se dirigía al oeste para estudiar las ruinas de Zin-Azshari en Kalimdor.' WHERE `entry`='3164';
UPDATE `locales_page_text` SET `Text_loc6`='Diodor el Maldito. Nigromante humano, uno de los primeros miembros del Culto de los Condenados, un héroe condecorado en la Primera Guerra. Lideró con éxito un contingente de la Plaga hasta la victoria durante el primer y segundo asalto de la Marea de Verano contra el Monasterio Escarlata. Murió en acto de servicio durante el tercer (y último) asalto de la Marea de Verano, que fue repelido con éxito por parte de los agentes de la Cruzada Escarlata, que ya habían descubierto su modus operandi.' WHERE `entry`='3165';
UPDATE `locales_page_text` SET `Text_loc6`='Kirkessen el Ferviente. Poderoso exánime que responde directamente ante el propio Rey Exánime. Un veterano condecorado en incontables batallas que diseñó y lideró el Asedio de los Sanguinos, uno de los mayores asaltos organizados contra la Capilla de la Esperanza de la Luz. A pesar de su apabullante ventaja, el ataque fue contrarrestado rápidamente y sus tropas sufrieron enormes bajas. El propio Kirkessen recibió un golpe crítico de Lord Maxwell Neófitus y ya no puede adoptar una forma corpórea.' WHERE `entry`='3166';
UPDATE `locales_page_text` SET `Text_loc6`='OESTE DE RASGANORTE: los informes del Escuadrón de Vigilancia Aérea en el Glaciar de Corona de Hielo muestran que la comunicación con el Señor Exánime Invierno Helado se ha cortado de forma repentina. \"Invierno Helado fue visto por última vez pilotando la necrópolis Talramas sobre la Tundra Boreal en una misión confidencial,\" informó el Ingeniero de la Plaga Karomon, el oficial jefe de información del Escuadrón. \"Nuestros diagnósticos muestran que sus cristales de comunicación funcionan a la perfección... es solo que... están callados como muertos.\"' WHERE `entry`='3167';
UPDATE `locales_page_text` SET `Text_loc6`='BRECHA DE LA MUERTE, REINOS DEL ESTE: el instructor Razuvious habló hace unos días, reiterando su política sobre los motines y la insubordinación. \"Cualquier caballero de la Muerte que ose desertar a los Renegados conocerá un destino peor que la muerte. Su sufrimiento no conocerá fin, me ocuparé de ello personalmente.\"' WHERE `entry`='3169';
UPDATE `locales_page_text` SET `Text_loc6`='CEMENTERIO DE DRAGONES: el príncipe Valanar está buscando a un caballero de la Muerte excepcional para viajar hasta el Cementerio de Dragones, la llanura helada central de Rasganorte y liderar a un grupo de soldados de élite en un ataque contra el vuelo rojo. \"Vuestro entrenamiento casi ha terminado,\" dijo en un discurso público reciente. \"¡Ha llegado la hora de atacar a aquellos que podrían proteger a los vivos! ¡Les mostraremos el verdadero significado de Cementerio de Dragones!\"' WHERE `entry`='3171';
UPDATE `locales_page_text` SET `Text_loc6`='Recientemente han visto a Alexstrasza, reina de los dragones rojos y posiblemente la enemiga más poderosa de la Plaga en Rasganorte, construyendo su propio ejército, aunque se cree que sus esfuerzos están dirigidos contra los renegados del vuelo azul.' WHERE `entry`='3172';
UPDATE `locales_page_text` SET `Text_loc6`='ENTRAÑAS: nuestras fuentes dentro de la ciudad informan que la Horda se está movilizando para organizar un asalto a Rasganorte. \"La tropas de la Horda, la mayoría de las cuales regresan del despliegue en Terrallende, se están reuniendo ahora y preparándose para asaltar Corona de Hielo.\"' WHERE `entry`='3173';
UPDATE `locales_page_text` SET `Text_loc6`='Además, los agentes encubiertos informan de que la Sociedad de Boticarios Reales de los Renegados ha aumentado enormemente su actividad. \"Aunque a estas alturas aún no hay nada confirmado, los rumores indican que la Sociedad ha avanzado mucho en su investigación de la plaga. Una vez más, son solo habladurías pero se aconseja a todas las fuerzas que eviten a los alquimistas Renegados.\"' WHERE `entry`='3174';
UPDATE `locales_page_text` SET `Text_loc6`='Los caballeros de la Muerte de Acherus$B$BNombre: Jayde$BEstado: vivo$BComentario: sediento de sangre' WHERE `entry`='3175';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Sixen$BEstado: vivo$BComentario: charlatán.' WHERE `entry`='3176';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Munch$BEstado: vivo$BComentario: destructivo.' WHERE `entry`='3177';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Cobalt$BEstado: fallecido$BComentario: murió de una caída. Tenía pocas luces. Probablemente se evitó una vida de sufrimiento.' WHERE `entry`='3178';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Armonía$BEstado: fallecido$BComentario: el nombre de este caballero de la Muerte violaba la decencia común de los Renegados. Eliminado por el Rey Exánime.' WHERE `entry`='3179';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Melt$BEstado: vivo*$BComentario: se ha convertido en un necrófago.' WHERE `entry`='3180';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Milton$BEstado: vivo$BComentario: vagabundea.' WHERE `entry`='3181';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Minitalis$BEstado: vivo$BComentario: ve cosas que no existen.' WHERE `entry`='3182';
UPDATE `locales_page_text` SET `Text_loc6`='Nombre: $N$BEstado: $Gvivo:viva;$BComentario: necesita más acción.$B$B<El libro continúa durante cientos de páginas.>' WHERE `entry`='3183';
