UPDATE `locales_page_text` SET `Text_loc6`='Nombre: Cobalt$BEstado: fallecido$BComentario: murió de una caída. Tenía pocas luces. Probablemente se evitó una vida de sufrimiento.' WHERE `entry`='3187';
